import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import axios from "axios";
import { vi } from "date-fns/locale";
import moment from "moment";
import { useEffect, useState } from "react";
import { Button, Dropdown, Form, Modal } from "react-bootstrap";

import { TextField } from "@mui/material";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { toast } from "react-toastify";
import Status from "../../../../components/Status";
import api from "../../../../configs/api";
export function LichSuDaoTao(props) {

  const [isLoading, setIsLoading] = useState(false);

  const [isAdd, setIsAdd] = useState(true);
  const [idSelected, setIdSelected] = useState("");
  const [idDelete, setIdDelete] = useState("");
  const [daoTaoList, setDaoTaoList] = useState([]);

  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(10);
  const [totalRows, setTotalRows] = useState(0);

  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});

  const [fileDinhKem, setFileDinhKem] = useState([]);
  const [deleteDinhKem, setDeleteDinhKem] = useState([]);

  const [visibleDelete, setvisibleDelete] = useState(false);
  const [visibleInstDelete, setvisibleIsntDelete] = useState(false);
  const closeModalDelete = () => setvisibleDelete(false);
  const showModalDelete = () => setvisibleDelete(true);
  const closeModal = () => setvisibleIsntDelete(false);
  const showModal = () => setvisibleIsntDelete(true);

  const [onHide, setOnHide] = useState(false);

  const [ngayCap, setNgayCap] = useState(new Date());

  const empty_json = {};
  const json = {
    nhan_vien_id: props?.userId,
  };

  useEffect(() => {
    setField("ngay_cap_GCN", new Date(ngayCap).getTime())
  }, [])

  useEffect(() => {
    if (props?.userId) {
      getDaoTaoList({});
    }
  }, [props?.userId]);

  useEffect(() => {
    setOnHide(props?.onHide);
  }, [props.data]);

  const handlePageChange = (page) => {
    setPage(page);
    getDaoTaoList({ page_number: page });
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(api.API_DS_DAO_TAO + `?page=${page}&per_page=${newPerPage}`, {
        nhan_vien_id: props?.userId,
      })
      .then(({ data }) => {
        if (data?.results) {
          setDaoTaoList(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {
    const { ten_truong, so_tiet_hoc, so_GCN_dao_tao, ngay_cap_GCN } = form;
    const newErrors = {};
    if (!ten_truong || ten_truong === "") newErrors.ten_truong = "Empty";
    if (!so_tiet_hoc || so_tiet_hoc === "") newErrors.so_tiet_hoc = "Empty";
    if (!so_GCN_dao_tao || so_GCN_dao_tao === "")
      newErrors.so_GCN_dao_tao = "Empty";
    if (fileDinhKem.length < 1)
      newErrors.fileDinhKem = "Vui lòng chọn chứng từ đính kèm !";

    return newErrors;
  };

  const handleNgayCap = (newValue) => {
    setNgayCap(newValue);
    setField("ngay_cap_GCN", new Date(newValue).getTime());
  };

  const resetState = () => {
    closeModal();
    setForm({
      ngay_cap_GCN: new Date(new Date()).getTime(),
      trang_thai: true,
    });
    setNgayCap(new Date());
    setFileDinhKem([]);
  };

  const getDaoTaoList = ({ page_number = page, size = perPage }) => {
    setIsLoading(true);
    axios
      .post(api.API_DS_DAO_TAO + `?page=${page_number}&per_page=${size}`, {
        nhan_vien_id: props?.userId,
      })
      .then(async ({ data }) => {
        setDaoTaoList(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteDaoTao = async () => {
    await axios
      .delete(api.API_DAO_TAO + "_by_id/" + idDelete)
      .then(async ({ data }) => {
        toast.success("Xóa thông tin bằng cấp thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        closeModalDelete();
        resetState();
        if (page == 1) {
          getDaoTaoList({ page_number: 1 });
        } else if (daoTaoList?.length == 1) {
          getDaoTaoList({ page_number: page - 1 });
        } else {
          getDaoTaoList({ page_number: page });
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getDetailDaoTao = async (row) => {
    setForm({ ...row });
    setFileDinhKem(row?.chung_tu_dinh_kem);
    setNgayCap(new Date(Number(row.ngay_cap_GCN)));
    setIsAdd(false);
    setIdSelected(row?.id);
  };

  const renderChooseFile = ({ title, name, arrayFileMap, setFile }) => {
    return (
      <div className="col-12">
        <div className="row">
          <div className="col-12">
            <button className="btn btn-success btn-sm my-3">
              <UploadControl name={name}>Thêm chứng từ đính kèm</UploadControl>
            </button>
          </div>
          <div className="col-12">
            {arrayFileMap?.length > 0
              ? arrayFileMap.map((e, i) => {
                  return (
                    <button
                      key={i}
                      className="btn ml-2 mb-2"
                      style={{
                        backgroundColor: "#e8e8e8",
                        padding: "12px 10px 12px 16px",
                        marginRight: "12px",
                      }}
                    >
                      <span>{e?.name}</span>
                      <i
                        style={{
                          display: "inline",
                          color: "red",
                          marginLeft: "8px",
                        }}
                        onClick={() => {
                          let arrFile = arrayFileMap;
                          arrFile = arrFile.filter(
                            (item) => item.name !== e?.name
                          );
                          setFile(arrFile);
                          deleteDinhKem.push(e?.url);
                        }}
                        className="fas fa-times-circle"
                      ></i>
                    </button>
                  );
                })
              : null}
          </div>
        </div>
      </div>
    );
  };

  const UploadControl = ({
    children,
    value,
    onChange,
    disabled,
    accept,
    name,
  }) => {
    return (
      <label htmlFor={`contained-button-${name}`} className="m-0 w-100">
        <input
          disabled={disabled}
          style={{ display: "none" }}
          id={`contained-button-${name}`}
          // multiple
          type="file"
          accept="audio/*|video/*|image/*|MIME_type"
          onChange={async (event) => {
            await setFileDinhKem([...fileDinhKem, event.target.files[0]]);
            findFormErrors();
          }}
        />

        {children}
      </label>
    );
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else if (isAdd === true) {
      await setIsLoading(true);

      const {
        ten_truong,
        so_GCN_dao_tao,
        so_tiet_hoc,
        ngay_cap_GCN,
      } = form;

      const data = new FormData();

      await data.append("nhan_vien_id", props?.userId)
      await data.append("ten_truong", ten_truong);
      await data.append("so_GCN_dao_tao", so_GCN_dao_tao);
      await data.append("so_tiet_hoc", so_tiet_hoc);
      await data.append("ngay_cap_GCN", ngay_cap_GCN);
      for (let x = 0; x < fileDinhKem.length; x++) {
        await data.append("chung_tu_dinh_kem[]", fileDinhKem[x]);
      }

      await axios
        .post(api.API_DAO_TAO, data, {
          headers: {
            "content-type": "multipart/form-data",
          },
        })
        .then(async ({ data }) => {
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getDaoTaoList({ page, perPage });
          setIsLoading(false);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    } else if (isAdd === false) {
      await setIsLoading(true);

      const {
        ten_truong,
        so_GCN_dao_tao,
        so_tiet_hoc,
        ngay_cap_GCN,
      } = form;

      const data = new FormData();

      await data.append("nhan_vien_id", props?.userId)
      await data.append("ten_truong", ten_truong);
      await data.append("so_GCN_dao_tao", so_GCN_dao_tao);
      await data.append("so_tiet_hoc", so_tiet_hoc);
      await data.append("ngay_cap_GCN", ngay_cap_GCN);

      for (let x = 0; x < fileDinhKem.length; x++) {
        await data.append("chung_tu_dinh_kem[]", fileDinhKem[x]);
      }

      if (deleteDinhKem.length > 0) {
        for (let d = 0; d < deleteDinhKem.length; d++) {
          await data.append(
            "delete_dinh_kem_anh_chan_dung[]",
            deleteDinhKem[d]
          );
        }
      }
      await axios
        .put(api.API_DAO_TAO + "_by_id/" + idSelected, data, {
          headers: {
            "content-type": "multipart/form-data",
          },
        })
        .then(async ({ data }) => {
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getDaoTaoList({ page, perPage });
          setDeleteDinhKem([]);
          setIsLoading(false);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const columns = [
    {
      name: "Tên trường",
      selector: (row) => <span>{row?.ten_truong}</span>,
      grow: 4,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Số GCN đào tạo",
      selector: (row) => <span>{row?.so_GCN_dao_tao}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Ngày cấp GCN",
      selector: (row) => (
        <span>{moment(row?.ngay_cap_GCN).format("DD/MM/YYYY")}</span>
      ),
      grow: onHide ? 3 : 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Số tiết học",
      selector: (row) => <span>{row?.so_tiet_hoc}</span>,
      grow: onHide ? 3 : 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    // {
    //     name: "Đính kèm",
    //     selector: (row) =>
    //         row?.chung_tu_dinh_kem.map((item, index) => (
    //             <div>
    //                 <a
    //                     key={index}
    //                     href={api.API_URL + item?.url}
    //                     className="mb-3"
    //                     style={{ display: "inline-block" }}
    //                 >
    //                     {" "}
    //                     {item?.ten}
    //                 </a>{" "}
    //                 <br />
    //             </div>
    //         )),
    //     grow: 5,
    //     style: {
    //         cursor: "pointer",
    //         color: "#202124",
    //     },
    // },
    {
      name: "Trang thái",
      selector: (row) => <span>{row.trang_thai}</span>,
      grow: 3,
      cell: (row) => <Status status={row.trang_thai} />,
    },
    {
      name: " ",
      grow: onHide ? 1 : 0,
      cell: (row) =>
        onHide ? null : (
          <div className="container my-3">
            <Dropdown>
              <Dropdown.Toggle variant="link" style={{ float: "right" }}>
                <i
                  className="fas fa-bars"
                  style={{ marginLeft: "3px", marginRight: "-3px" }}
                ></i>
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Item
                  onClick={() => {
                    setIsAdd(false);
                    showModal();
                    getDetailDaoTao(row);
                  }}
                >
                  <i className="fas fa-eye text-primary me-3"></i>Xem chi tiết
                </Dropdown.Item>
                <Dropdown.Item
                  onClick={() => {
                    showModalDelete();
                    setIdDelete(row.id);
                  }}
                >
                  <i className="fas fa-trash text-danger me-3"></i>Xóa
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </div>
        ),
    },
  ];

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };

  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "14px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
      <div className="bg-white card-box px-6 py-6 mx-3 my-3 ">
        <div className="row pb-5">
          <div className="col-md-6 col-sm-6">
            <label className="fw-bold fs-5">ĐÀO TẠO LIÊN TỤC</label>
          </div>
          {onHide ? null : (
            <div
              className="col-md-6 col-sm-6 d-flex justify-content-end"
              data-kt-user-table-toolbar="base"
            >
              <button
                type="button"
                className="btn btn-sm btn-primary d-flex"
                style={{ alignItems: "center" }}
                onClick={async () => {
                  // setIsAdd(true);
                  showModal();
                }}
              >
                <i className="fas fa-plus me-1"></i>
                Thêm
              </button>
            </div>
          )}
        </div>

        <DataTable
          noDataComponent={"Không có dữ liệu."}
          sortServer
          progressPending={isLoading}
          columns={columns}
          data={daoTaoList}
          customStyles={customStyles}
          pagination
          highlightOnHover
          pointerOnHover
          paginationServer
          paginationTotalRows={totalRows}
          onChangeRowsPerPage={handlePerRowsChange}
          onChangePage={handlePageChange}
          paginationComponentOptions={paginationOptions}
          progressComponent={
            <div
              style={{
                padding: "24px",
              }}
            >
              <Oval
                arialLabel="loading-indicator"
                color="#007bff"
                height={40}
              />
            </div>
          }
        />

        {/* Infor Modal */}
        <Modal
          size="lg"
          show={visibleInstDelete}
          onHide={() => {
            closeModal();
          }}
        >
          <Modal.Body>
            <div className="row mt-3 mb-5">
              <div className="col-12 text-center">
                <span className="fs-1 fw-bold">
                  {isAdd ? "Thêm mới đào tạo" : "Thông tin đào tạo"}
                </span>
              </div>
            </div>
            <div className="row">
              <div className="col-6 mb-4">
                <span className="required fw-bold">Tên trường</span>
                <Form.Control
                  type="text"
                  className="mt-2"
                  placeholder="Nhập tên trường"
                  value={form?.ten_truong ? form?.ten_truong : null}
                  style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                  onChange={(e) => {
                    setField("ten_truong", e.target.value);
                  }}
                />
                <span className="text-danger">
                  {errors?.ten_truong
                    ? "Tên trường đào tạo không được bỏ trống"
                    : ""}
                </span>
              </div>
              <div className="col-6 mb-4">
                <span className="required fw-bold">
                  Tương đương số tiết học
                </span>
                <Form.Control
                  type="number"
                  className="mt-2"
                  placeholder="Nhập số tiết học tương đương"
                  value={form?.so_tiet_hoc ? form?.so_tiet_hoc : null}
                  style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                  onChange={(e) => {
                    setField("so_tiet_hoc", e.target.value);
                  }}
                />
                <span className="text-danger">
                  {errors?.so_tiet_hoc ? "Số tiết học không được bỏ trống" : ""}
                </span>
              </div>
              <div className="col-6 mb-4">
                <span className="required fw-bold">Số GCN đào tạo</span>
                <Form.Control
                  type="text"
                  className="mt-2"
                  placeholder="Nhập số giấy chứng nhận"
                  value={form?.so_GCN_dao_tao ? form?.so_GCN_dao_tao : null}
                  style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                  onChange={(e) => {
                    setField("so_GCN_dao_tao", e.target.value);
                  }}
                />
                <span className="text-danger">
                  {errors?.so_GCN_dao_tao
                    ? "Số giấy chứng nhận không được bỏ trống"
                    : ""}
                </span>
              </div>
              <div className="col-6 mb-4">
                <span className="required fw-bold d-block mb-2">
                  Ngày cấp GCN đào tạo
                </span>
                <DesktopDatePicker
                  label=" "
                  maxDate={new Date()}
                  inputFormat="dd/MM/yyyy"
                  value={ngayCap}
                  onChange={handleNgayCap}
                  renderInput={(params) => <TextField {...params} />}
                />
                <span className="text-danger">
                  {errors?.ngay_cap_GCN
                    ? "Ngày cấp giấy chứng nhận không được bỏ trống"
                    : ""}
                </span>
              </div>
            </div>
          </Modal.Body>
          <Modal.Footer style={{ width: "100%" }}>
            <div className="row w-100">
              <div className="col-12">
                <span className="required fs-2 fw-bold">
                  Danh sách chứng từ đính kèm:{" "}
                </span>

                {renderChooseFile({
                  arrayFileMap: fileDinhKem,
                  setFile: (value) => {
                    setFileDinhKem(value);
                  },
                })}
                <span className="text-danger">
                  {errors?.fileDinhKem
                    ? "Vui lòng chọn chứng từ đính kèm!"
                    : ""}
                </span>
              </div>
              <div className="col-12 text-center mt-5">
                <Button
                  variant="secondary"
                  style={{ marginRight: 10 }}
                  onClick={() => {
                    closeModal();
                  }}
                >
                  Quay lại
                </Button>
                <Button
                  variant="primary"
                  onClick={(e) => {
                    onSubmit(e);
                    // closeModal()
                  }}
                >
                  Đồng ý
                </Button>
              </div>
            </div>
          </Modal.Footer>
        </Modal>

        {/* Delete Modal */}
        <Modal
          centered
          show={visibleDelete}
          onHide={() => {
            closeModalDelete();
          }}
        >
          <Modal.Header className="bg-blue2">
            <Modal.Title className="text-white" style={{ fontSize: "22px" }}>
              Bạn có chắc chắn muốn xóa bằng cấp này ?
            </Modal.Title>
          </Modal.Header>
          <Modal.Body
            className="row d-flex"
            style={{ justifyContent: "center" }}
          >
            <img
              width={350}
              height={175}
              className="mb-5"
              src="/media/assets/deleteTrash.gif"
              alt=""
            />
            <div className="col-12 text-center mt-5">
              <Button
                variant="secondary"
                style={{ marginRight: 10 }}
                onClick={() => {
                  closeModalDelete();
                }}
              >
                Quay lại
              </Button>
              <Button
                variant="primary"
                onClick={() => {
                  deleteDaoTao();
                  closeModalDelete();
                }}
              >
                Đồng ý
              </Button>
            </div>
          </Modal.Body>
          {/* <Modal.Footer>

                </Modal.Footer> */}
        </Modal>
      </div>
    </LocalizationProvider>
  );
}
