import { useEffect, useState } from "react";
import { KTCard, KTSVG } from "../../../../_metronic/helpers";
import { PageTitle } from "../../../../_metronic/layout/core";
import PopupCapNhatYeuCauLienKetDS from "../popup/PopupCapNhatYeuCauLienKetDS";
import { useDispatch, useSelector } from "react-redux";
import {
  authActions,
  selectCurrentUser,
} from "../../../redux-module/auth/authSlice";

import { BangCap } from "../duoc-si/bang-cap/BangCap";
import { LichSuThucHanh } from "../duoc-si/lich-su-thuc-hanh/LichSuThucHanh";
import { LichSuDaoTao } from "../duoc-si/lich-su-dao-tao/LichSuDaoTao";
import { LichSuChungChi } from "../duoc-si/lich-su-chung-chi/LichSuChungChi";
import axios from "axios";
import api from "../../../configs/api";
import LoadingIndicator from "../../../components/LoadingIndicator";
import { TrangThaiLienKetDuocSi } from "../../../data";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";


export function ChungChiHanhNgheDuoc() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const currentUser = useSelector(selectCurrentUser);
  const [showModalYCLK, setShowModalYCLK] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [ThongTinChungChi, setThongTinChungChi] = useState(null);

  const closeModalCapNhatYeuCauLK = () => setShowModalYCLK(false);
  const showModalCapNhatYeuCauLK = () => setShowModalYCLK(true);

  useEffect(() => {
    getChungChiUser();
  }, []);

  const getChungChiUser = async () => {
    await setIsLoading(true);
    axios
      .get(api.API_THONG_TIN_LIEN_KET_BAN_THAN)
      .then(({ data }) => {
        setThongTinChungChi(data?.results);
        // const trang_thai = data?.results?.da_cap_chung_chi
        // if ( trang_thai && trang_thai === false ) {
        //   updateTrangThaiCapChungChi();
        // }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  // const updateTrangThaiCapChungChi = async () => {
  //   const json = new FormData();

  //   await json.append("da_cap_chung_chi", true);

  //   axios 
  //     .put(api.API_QL_NGUOI_DUNG_ID + "/" + thongTinDuocSi.id, json)
  //     .then(({ data }) => {
  //       toast.success("Cập nhật thông tin thành công", {
  //         position: "top-right",
  //         autoClose: 2000,
  //         hideProgressBar: false,
  //         closeOnClick: true,
  //         pauseOnHover: true,
  //         draggable: true,
  //         progress: undefined,
  //         toastId: "success",
  //       });
  //     })
  //     .catch((error) => {
  //       toast.error(error?.data?.errors, {
  //         position: "top-right",
  //         autoClose: 2000,
  //         hideProgressBar: false,
  //         closeOnClick: true,
  //         pauseOnHover: true,
  //         draggable: true,
  //         progress: undefined,
  //         toastId: "error",
  //       });
  //     })
  //     .finally(() => {
  //       setIsLoading(false);
  //     });
  // }

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];

  const checkStatus = (id) => {
    let status = TrangThaiLienKetDuocSi.find((e) => e.id === id);
    return <span className={`text-${status.color}`}>{status.name}</span>;
  };

  return (
    <>
      <div className="row w-100">
        <div className="col-12 mx-3 my-3">
          <PopupCapNhatYeuCauLienKetDS
            show={showModalYCLK}
            hide={closeModalCapNhatYeuCauLK}
            getChungChiUser={getChungChiUser}
          />
          <PageTitle breadcrumbs={usersBreadcrumbs}>
            Chứng chỉ hành nghề
          </PageTitle>

          <div className="col-xxl-3">
            <KTCard className="bg-white h-100">
              <div className="col-xxl-12 py-5 px-5">
                <div className="text-center mb-5">
                  <div className="symbol symbol-150px symbol-circle text-center">
                    <img alt="Pic" src="/media/assets/noPicture.jpeg" />
                  </div>
                  <div className="mt-2 fs-2 fw-bold">
                    {currentUser?.ho_ten != null ? currentUser?.ho_ten : ""}
                  </div>
                </div>

                <div className="row mb-1">
                  <div className="col-xl-5 text-blue2 fw-bold fs-6 ">Email</div>
                  <div className="col-xl-7 mb-2 fs-6">
                    {currentUser?.email != null ? currentUser?.email : ""}
                  </div>
                </div>

                <div className="row mb-1">
                  <div
                    onClick={() => {
                      dispatch(authActions.changeToken());
                    }}
                    className="col-xl-5 text-blue2 fw-bold fs-6 "
                  >
                    Ngày sinh
                  </div>
                  <div className="col-xl-7 mb-2 fs-6">
                    {currentUser?.ngay_sinh || ""}
                  </div>
                </div>

                <div className="row mb-1">
                  <div className="col-xl-5 text-blue2 fw-bold fs-6 ">
                    CMND/CCCD
                  </div>
                  <div className="col-xl-7 mb-2 fs-6">
                    {currentUser?.ma_cong_dan || ""}
                  </div>
                </div>

                <div className="row mb-1">
                  <div className="col-xl-5 text-blue2 fw-bold fs-6 ">
                    Điện thoại
                  </div>
                  <div className="col-xl-7 mb-2 fs-6">
                    {currentUser?.dien_thoai || ""}
                  </div>
                </div>

                <div className="text-center mt-2">
                  <button className="btn btn-sm btn-primary"
                    onClick={() => navigate("/thong-tin-duoc-si")}>
                    <KTSVG
                      path="/media/icons/duotune/art/art005.svg"
                      className="svg-icon-2"
                    />
                    Cập nhật thông tin
                  </button>
                </div>
              </div>
            </KTCard>
          </div>

          <div className="col-xxl-9">
            <div className="row">
              <div className="col-xl-3 mb-2">
                <a
                  href="/#"
                  className="card bg-danger hoverable card-xl-stretch mb-xl-5"
                >
                  <div className="card-body">
                    <div className="text-white fw-bolder fs-1 mb-2 text-center">
                      1.000
                    </div>

                    <div className="fw-bold text-white fs-7 text-center">
                      SỐ CƠ SỞ ĐÃ THỰC HÀNH
                    </div>
                  </div>
                </a>
              </div>

              <div className="col-xl-3 mb-2">
                <a
                  href="/#"
                  className="card bg-primary hoverable card-xl-stretch mb-xl-5"
                >
                  <div className="card-body">
                    <div className="text-white fw-bolder fs-1 mb-2 text-center">
                      1.000
                    </div>

                    <div className="fw-bold text-white fs-7 text-center">
                      THỰC HÀNH CHUYÊN MÔN
                    </div>
                  </div>
                </a>
              </div>

              <div className="col-xl-3 mb-2">
                <a
                  href="/#"
                  className="card bg-success hoverable card-xl-stretch mb-xl-5"
                >
                  <div className="card-body">
                    <div className="text-white fw-bolder fs-1 mb-2 text-center">
                      1.000
                    </div>

                    <div className="fw-bold text-white fs-7 text-center">
                      CHỨNG NHẬN ĐÀO TẠO
                    </div>
                  </div>
                </a>
              </div>

              <div className="col-xl-3 mb-2">
                <a
                  href="/#"
                  className="card bg-warning hoverable card-xl-stretch mb-xl-5"
                >
                  <div className="card-body">
                    <div className="text-white fw-bolder fs-1 mb-2 text-center">
                      1.000
                    </div>

                    <div className="fw-bold text-white fs-7 text-center">
                      GIỜ ĐÀO TẠO
                    </div>
                  </div>
                </a>
              </div>
            </div>
            <div className="col-xl-12">
              <KTCard className="py-5 px-5">
                <div className="fw-bold fs-4 mb-5">CHỨNG CHỈ HÀNH NGHỀ</div>
                {isLoading && <LoadingIndicator />}
                {!isLoading && !ThongTinChungChi ? (
                  <>
                    <div className="text-tb-chungchi text-center p-20">
                      Nếu bạn đã được SYT TP. HCM cấp giấy chứng nhận hành nghề
                      dược, hãy gửi yêu cầu liên kết để quản lý tại đây
                    </div>
                    <div className="text-center">
                      <button
                        onClick={() => {
                          showModalCapNhatYeuCauLK();
                        }}
                        className="btn btn-outline-primary"
                      >
                        Cấp chứng chỉ hành nghề dược
                      </button>
                    </div>
                  </>
                ) : (
                  !isLoading &&
                  ThongTinChungChi && (
                    <>
                      <div className="row">
                        <div className="col-xl-6">
                          <div className="row mb-2">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6">
                              Số giấy phép:
                            </div>
                            <div className="col-xl-7 mb-2 fs-6">
                              {ThongTinChungChi?.so_giay_phep || ""}
                            </div>
                          </div>
                        </div>

                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6 ">
                              Nơi cấp:
                            </div>
                            <div className="col-xl-7 mb-2 fs-6">
                              {ThongTinChungChi?.co_quan_cap || ""}
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="row mb-2">
                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6 ">
                              Ngày hiệu lực:
                            </div>
                            <div className="col-xl-7 mb-2 fs-6">
                              {" "}
                              {ThongTinChungChi?.ngay_hieu_luc || ""}
                            </div>
                          </div>
                        </div>

                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6">
                              Phụ trách chuyên môn tại:
                            </div>
                            <div className="col-xl-7 mb-2 fs-6">
                              {ThongTinChungChi?.phu_trach_chuyen_mon || ""}
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="row mb-2">
                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6">
                              Văn bằng chuyên môn:
                            </div>
                            <div className="col-xl-7 mb-2 fs-6">30/05/2022</div>
                          </div>
                        </div>

                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6 ">
                              Phạm vi hoạt động chuyên môn:
                            </div>
                            <div className="col-xl-7 mb-2 fs-6">
                              185/CCHN-D-SYT-HCM
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="row mb-2">
                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6">
                              Ngày hết hạn:
                            </div>
                            <div className="col-xl-7 mb-2 fs-6">30/05/2022</div>
                          </div>
                        </div>

                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6">
                              Hình thức:
                            </div>
                            <div className="col-xl-7 mb-2 fs-6">
                              185/CCHN-D-SYT-HCM
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="row mb-2">
                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-12 text-blue2 fw-bold fs-6">
                              Đủ điều kiện hành nghề dược tại các vị trí:
                            </div>
                            {/* <div className="col-xl-7">30/05/2022</div> */}
                          </div>
                        </div>

                        <div className="col-xl-6">
                          <div className="row">
                            <div className="col-xl-5 text-blue2 fw-bold fs-6">
                              Trạng thái:
                            </div>
                            <div className="col-xl-7 fs-6">
                              {ThongTinChungChi?.trang_thai_ho_so
                                ? checkStatus(ThongTinChungChi?.trang_thai_ho_so)
                                : ""}
                              <button
                                onClick={() => {
                                  showModalCapNhatYeuCauLK();
                                }}
                                className="btn btn-sm pl-5 btn-primary"
                                style={{ marginLeft: 10 }}
                              >
                                Chỉnh sửa
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </>
                  )
                )}
              </KTCard>
            </div>
          </div>
        </div>

      </div>

      <div className="gy-5 g-xl-8 my-5">
        <div className="col-xl-12">
          <BangCap userId={currentUser.id} hideButtons={true} />
          <LichSuThucHanh userId={currentUser.id} hideButtons={true} />
          <LichSuDaoTao userId={currentUser.id} hideButtons={true} />
          <LichSuChungChi userId={currentUser.id} />
        </div>
      </div>
    </>
  );
}
