sourcesContent:
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { enAU } from "date-fns/locale";

import TextField from "@mui/material/TextField";
import axios from "axios";
import { useEffect, useState } from "react";
import { Button, Dropdown, Form, Modal } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../../configs/api";

export function BangCap(props) {
  const [isAdd, setIsAdd] = useState(true);
  const [bangCapList, setBangCapList] = useState({});
  const [vanBangList, setVanBangList] = useState([]);
  const [noiTotNghiepList, setNoiTotNghiepList] = useState([]);

  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(10);
  const [totalRows, setTotalRows] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [idSelected, setIdSelected] = useState("");
  const [idDelete, setIdDelete] = useState("");

  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const [valueVanBang, setValueVanBang] = useState();
  const [valueNoiTotNghiep, setValueNoiTotNghiep] = useState();
  const [valueHinhThuc, setValueHinhThuc] = useState();
  const [valueXepLoai, setValueXepLoai] = useState(null);

  const [fileDinhKem, setFileDinhKem] = useState([]);
  const [deleteDinhKem, setDeleteDinhKem] = useState([]);

  const [visibleDelete, setvisibleDelete] = useState(false);
  const [visibleInstDelete, setvisibleIsntDelete] = useState(false);
  const closeModalDelete = () => setvisibleDelete(false);
  const showModalDelete = () => setvisibleDelete(true);
  const closeModal = () => setvisibleIsntDelete(false);
  const showModal = () => setvisibleIsntDelete(true);

  const [ngayCap, setNgayCap] = useState(new Date());

  const [onHide, setOnHide] = useState(false);

  const hinhthucdaotao = [
    { value: "Chính quy", label: "Chính quy" },
    { value: "Thường xuyên", label: "Thường xuyên" },
  ];
  const xeploai = [
    { value: "Xuất sắc", label: "Xuất sắc" },
    { value: "Giỏi", label: "Giỏi" },
    { value: "Khá", label: "Khá" },
    { value: "Trung bình", label: "Trung bình" },
  ];

  const empty_json = {};
  const json = {
    nhan_vien_id: props?.userId,
  };

  useEffect(() => {
    if (props?.userId) {
      getBangCapList({});
    }
  }, [props?.userId]);

  useEffect(() => {
    setOnHide(props?.onHide);
    if (visibleInstDelete) {
      getVanBangList();
      getNoiTotNghiepList();
    }
  }, [visibleInstDelete]);

  const handlePageChange = (page) => {
    setPage(page);
    getBangCapList({ page_number: page });
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(api.API_DS_BANG_CAP + `?page=${page}&per_page=${newPerPage}`, json)
      .then(({ data }) => {
        if (data?.results) {
          setBangCapList(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => { })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {
    const {
      van_bang_chuyen_mon_id,
      noi_tot_nghiep_id,
      ngay_cap,
      so_hieu,
      hinh_thuc_dao_tao,
    } = form;
    const newErrors = {};
    if (!van_bang_chuyen_mon_id || van_bang_chuyen_mon_id === "")
      newErrors.van_bang_chuyen_mon_id =
        "Văn bằng chuyên môn không được bỏ trống!";
    if (!noi_tot_nghiep_id || noi_tot_nghiep_id === "")
      newErrors.noi_tot_nghiep_id = "Nơi tốt nghiệp không được bỏ trống!";
    if (!ngay_cap || ngay_cap === "")
      newErrors.ngay_cap = "Ngày cấp không được bỏ trống!";
    if (!so_hieu || so_hieu === "")
      newErrors.so_hieu = "Số hiệu không được bỏ trống!";
    if (!hinh_thuc_dao_tao || hinh_thuc_dao_tao === "")
      newErrors.hinh_thuc_dao_tao = "Hình thức đào tạo không được bỏ trống!";
    if (fileDinhKem.length < 1)
      newErrors.fileDinhKem = "Vui lòng chọn chứng từ đính kèm !";

    return newErrors;
  };

  const handleNgayCap = (newValue) => {
    setNgayCap(newValue);
    setField("ngay_cap", new Date(newValue).getTime());
  };

  const resetState = async () => {
    setForm({
      so_hieu: "",
      ngay_cap: new Date(ngayCap).getTime(),
    });
    setErrors({});
    setNgayCap();
    setValueVanBang();
    setValueXepLoai();
    setValueHinhThuc();
    setFileDinhKem([]);
    setValueNoiTotNghiep();
    closeModal();
  };

  const getBangCapList = ({ page_number = page, size = perPage }) => {
    setIsLoading(true);
    axios
      .post(api.API_DS_BANG_CAP + `?page=${page_number}&per_page=${size}`, {
        nhan_vien_id: props?.userId,
      })
      .then(async ({ data }) => {
        setBangCapList(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteBangCap = async () => {
    await axios
      .delete(api.API_BANG_CAP + "/" + idDelete)
      .then(async ({ data }) => {
        toast.success("Xóa thông tin bằng cấp thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        closeModalDelete();
        resetState();
        if (page === 1) {
          getBangCapList({ page_number: 1 });
        } else if (bangCapList?.length === 1) {
          getBangCapList({ page_number: page - 1 });
        } else {
          getBangCapList({ page_number: page });
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getDetailBangCap = async (row) => {
    await setValueVanBang({
      value: row?.van_bang_chuyen_mon_id,
      label: row?.van_bang_chuyen_mon,
    });
    await setValueNoiTotNghiep({
      value: row?.noi_tot_nghiep_id,
      label: row?.noi_tot_nghiep,
    });
    await setValueHinhThuc({
      value: row?.hinh_thuc_dao_tao,
      label: row?.hinh_thuc_dao_tao,
    });
    await setValueXepLoai({
      value: row?.xep_hang,
      label: row?.xep_hang,
    });

    await setNgayCap(new Date(Number(row.ngay_cap)));
    await setFileDinhKem(row?.chung_tu_dinh_kem);
    await setForm({ ...row });
    await setIsAdd(false);
    await setIdSelected(row?.id);
  };

  const getVanBangList = () => {
    axios
      .post(api.API_LIST_VAN_BANG + `?page=${1}&per_page=${100}`, empty_json)
      .then(async ({ data }) => {
        data?.results.forEach((element) => {
          let a = {};
          a.value = element.id;
          a.label = element.ten;
          vanBangList.push(a);
        });
      })
      .catch((error) => {
        toast.error("Không thể lấy danh sách văn bằng chuyên môn", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getNoiTotNghiepList = () => {
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${1}&per_page=${100}`,
        empty_json
      )
      .then(async ({ data }) => {
        data?.results.forEach((element) => {
          let a = {};
          a.value = element.id;
          a.label = element.ten;
          noiTotNghiepList.push(a);
        });
      })
      .catch((error) => {
        toast.error("Không thể lấy danh sách nơi tốt nghiệp", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const renderChooseFile = ({ title, name, arrayFileMap, setFile }) => {
    return (
      <div className="col-12">
        <div className="row">
          <div className="col-12">
            <button className="btn btn-success btn-sm my-3">
              <UploadControl name={name}>Thêm chứng từ đính kèm</UploadControl>
            </button>
          </div>
          <div className="col-12">
            {arrayFileMap?.length > 0
              ? arrayFileMap.map((e, i) => {
                return (
                  <button
                    key={i}
                    className="btn ml-2 mb-2"
                    style={{
                      backgroundColor: "#e8e8e8",
                      padding: "12px 10px 12px 16px",
                      marginRight: "12px",
                    }}
                  >
                    <span>{e?.name}</span>
                    <i
                      style={{
                        display: "inline",
                        color: "red",
                        marginLeft: "8px",
                      }}
                      onClick={() => {
                        let arrFile = arrayFileMap;
                        arrFile = arrFile.filter(
                          (item) => item.name !== e?.name
                        );
                        setFile(arrFile);
                        deleteDinhKem.push(e?.url);
                      }}
                      className="fas fa-times-circle"
                    ></i>
                  </button>
                );
              })
              : null}
          </div>
        </div>
      </div>
    );
  };

  const UploadControl = ({
    children,
    value,
    onChange,
    disabled,
    accept,
    name,
  }) => {
    return (
      <label htmlFor={`contained-button-${name}`} className="m-0 w-100">
        <input
          disabled={disabled}
          style={{ display: "none" }}
          id={`contained-button-${name}`}
          // multiple
          type="file"
          accept="audio/*|video/*|image/*|MIME_type"
          onChange={async (event) => {
            await setFileDinhKem([...fileDinhKem, event.target.files[0]]);
            findFormErrors();
          }}
        />

        {children}
      </label>
    );
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    const newErrors = findFormErrors();

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else if (isAdd === true) {
      await setIsLoading(true);

      const {
        van_bang_chuyen_mon_id,
        noi_tot_nghiep_id,
        ngay_cap,
        so_hieu,
        hinh_thuc_dao_tao,
        xep_hang,
        ghi_chu,
      } = form;

      const data = new FormData();

      await data.append("nhan_vien_id", props?.userId);
      await data.append("so_hieu", so_hieu);
      await data.append("ngay_cap", ngay_cap);
      await data.append("hinh_thuc_dao_tao", hinh_thuc_dao_tao);
      await data.append("van_bang_chuyen_mon_id", van_bang_chuyen_mon_id);
      await data.append("noi_tot_nghiep_id", noi_tot_nghiep_id);
      await data.append("xep_hang", xep_hang);
      await data.append("ghi_chu", ghi_chu);
      for (let x = 0; x < fileDinhKem.length; x++) {
        await data.append("chung_tu_dinh_kem[]", fileDinhKem[x]);
      }

      await axios
        .post(api.API_BANG_CAP, data, {
          headers: {
            "content-type": "multipart/form-data",
          },
        })
        .then(async ({ data }) => {
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getBangCapList({ page, perPage });
          setIsLoading(false);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    } else if (isAdd === false) {
      await setIsLoading(true);

      const {
        van_bang_chuyen_mon_id,
        noi_tot_nghiep_id,
        ngay_cap,
        so_hieu,
        hinh_thuc_dao_tao,
        xep_hang,
        ghi_chu,
      } = form;

      const data = new FormData();

      await data.append("nhan_vien_id", props?.userId);
      await data.append("so_hieu", so_hieu);
      await data.append("ngay_cap", ngay_cap);
      await data.append("ghi_chu", ghi_chu);
      await data.append("hinh_thuc_dao_tao", hinh_thuc_dao_tao);
      await data.append("van_bang_chuyen_mon_id", van_bang_chuyen_mon_id);
      await data.append("noi_tot_nghiep_id", noi_tot_nghiep_id);
      await data.append("xep_hang", xep_hang);

      for (let x = 0; x < fileDinhKem.length; x++) {
        await data.append("chung_tu_dinh_kem[]", fileDinhKem[x]);
      }

      if (deleteDinhKem.length > 0) {
        for (let d = 0; d < deleteDinhKem.length; d++) {
          await data.append(
            "delete_dinh_kem_anh_chan_dung[]",
            deleteDinhKem[d]
          );
        }
      }

      await axios
        .put(api.API_BANG_CAP + "/" + idSelected, data, {
          headers: {
            "content-type": "multipart/form-data",
          },
        })
        .then(async ({ data }) => {
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getBangCapList({ page, perPage });
          setDeleteDinhKem([]);
          setIsLoading(false);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const columns = [
    {
      name: "Nơi tốt nghiệp",
      selector: (row) => <span>{row?.noi_tot_nghiep}</span>,
      grow: 4,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Văn bằng chuyên môn",
      selector: (row) => <span>{row?.van_bang_chuyen_mon}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Số hiệu",
      selector: (row) => <span>{row?.so_hieu}</span>,
      grow: onHide ? 3 : 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Đính kèm",
      selector: (row) => (
        <>
          {row?.chung_tu_dinh_kem.length > 0 ? (
            <>
              {row?.chung_tu_dinh_kem.map((item, index) => (
                <div key={index}>
                  <a
                    key={index}
                    target="_blank"
                    rel="noreferrer"
                    href={api.API_URL + item?.url}
                    className="mt-1"
                    style={{ display: "inline-block" }}
                  >
                    {item?.name}
                  </a>
                  <br />
                </div>
              ))}
            </>
          ) : (
            <span> </span>
          )}
        </>
      ),
      grow: 5,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: " ",
      grow: onHide ? 1 : 0,
      cell: (row) =>
        onHide ? null : (
          bangCapList.length === 1 ?
            <div className="d-flex px-0">
              <button className="btn btn-link" title="Cập nhật" 
                onClick={() => {
                  setIsAdd(false);
                  showModal();
                  getDetailBangCap(row);
                }}> 
                <i className="fas fa-eye text-primary me-3"></i> 
              </button>
              <button className="btn btn-link" title="Xóa"> <i className="fas fa-trash text-danger me-3"></i> </button>
            </div>
            :
            <div className="container my-3">
              <Dropdown>
                <Dropdown.Toggle variant="link" style={{ float: "right" }}>
                  <i
                    className="fas fa-bars"
                    style={{ marginLeft: "3px", marginRight: "-3px" }}
                  ></i>
                </Dropdown.Toggle>

                <Dropdown.Menu
                  flip={true}
                  bsPrefix="dropdown-menu dropdown-box"
                >
                  <Dropdown.Item
                    onClick={() => {
                      setIsAdd(false);
                      showModal();
                      getDetailBangCap(row);
                    }}
                  >
                    <i className="fas fa-eye text-primary me-3"></i>Cập nhật
                  </Dropdown.Item>
                  <Dropdown.Item
                    onClick={() => {
                      showModalDelete();
                      setIdDelete(row.id);
                    }}
                  >
                    <i className="fas fa-trash text-danger me-3"></i>Xóa
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            </div>

        ),
    },
  ];

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };

  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "14px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
      <div className="bg-white card-box py-6 px-6 mx-3 my-3">
        <div className="row pb-5">
          <div className="col-md-6 col-sm-6">
            <label className="fw-bold fs-5">BẰNG CẤP</label>
          </div>
          {props?.onHide ? null : (
            <div
              className="col-md-6 col-sm-6 d-flex justify-content-end"
              data-kt-user-table-toolbar="base"
            >
              <button
                type="button"
                className="btn btn-sm btn-primary d-flex"
                style={{ alignItems: "center" }}
                onClick={async () => {
                  resetState();
                  setIsAdd(true);
                  showModal();
                }}
              >
                <i className="fas fa-plus me-1"></i>
                Thêm
              </button>
            </div>
          )}
        </div>

        <DataTable
          noDataComponent={"Không có dữ liệu."}
          sortServer
          progressPending={isLoading}
          columns={columns}
          data={bangCapList}
          customStyles={customStyles}
          pagination
          highlightOnHover
          pointerOnHover
          paginationServer
          paginationTotalRows={totalRows}
          onChangeRowsPerPage={handlePerRowsChange}
          onChangePage={handlePageChange}
          paginationComponentOptions={paginationOptions}
          progressComponent={
            <div
              style={{
                padding: "24px",
              }}
            >
              <Oval
                arialLabel="loading-indicator"
                color="#007bff"
                height={40}
              />
            </div>
          }
        />

        {/* Infor Modal */}
        <Modal
          size="lg"
          show={visibleInstDelete}
          onHide={() => {
            closeModal();
          }}
        >
          <Modal.Body>
            <div className="row mt-3 mb-5">
              <div className="col-12 text-center">
                <span className="fs-1 fw-bold">
                  {isAdd ? "Điền thông tin bằng cấp" : "Cập nhật thông tin bằng cấp"}
                </span>
              </div>
            </div>
            <div className="row">
              <div className="col-4 mb-4">
                <span className="required fw-bold">Văn bằng chuyên môn</span>
                <Select
                  key={"id"}
                  className="mt-2"
                  options={vanBangList}
                  style={{ height: "44px" }}
                  placeholder="Chọn văn bằng chuyên môn"
                  onChange={(e) => {
                    setValueVanBang(e);
                    setField("van_bang_chuyen_mon_id", e.value);
                  }}
                  value={valueVanBang}
                />
                <span className="text-danger">
                  {errors?.van_bang_chuyen_mon_id ? (
                    <span>{errors?.van_bang_chuyen_mon_id}</span>
                  ) : (
                    ""
                  )}
                </span>
              </div>
              <div className="col-4 mb-4">
                <span className="required fw-bold">Nơi cấp</span>
                <Select
                  key={"id"}
                  className="mt-2"
                  options={noiTotNghiepList}
                  style={{ height: "44px" }}
                  placeholder="Chọn nơi tốt nghiệp"
                  onChange={(e) => {
                    setValueNoiTotNghiep(e);
                    setField("noi_tot_nghiep_id", e.value);
                  }}
                  value={valueNoiTotNghiep}
                />
                <span className="text-danger">
                  {errors?.noi_tot_nghiep_id ? (
                    <span>{errors?.noi_tot_nghiep_id}</span>
                  ) : (
                    ""
                  )}
                </span>
              </div>
              <div className="col-4 mb-4">
                <span className="required fw-bold d-block mb-2">Ngày cấp</span>
                <DesktopDatePicker
                  label=" "
                  inputFormat="dd/MM/yyyy"
                  value={ngayCap}
                  onChange={handleNgayCap}
                  maxDate={new Date()}
                  renderInput={(params) => <TextField {...params} />}
                  isInvalid={!!errors?.ngay_cap}
                />
                <Form.Control.Feedback type="invalid">
                  {errors?.ngay_cap}
                </Form.Control.Feedback>
              </div>
              <div className="col-4 mb-4">
                <span className="required fw-bold">Số hiệu</span>
                <Form.Control
                  type="text"
                  className="mt-2"
                  placeholder="Nhập tên mã nơi tốt nghiệp"
                  style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                  onChange={(e) => {
                    setField("so_hieu", e.target.value);
                  }}
                  value={form?.so_hieu != null ? form?.so_hieu : ""}
                  isInvalid={!!errors?.so_hieu}
                />
                <Form.Control.Feedback type="invalid">
                  {errors?.so_hieu}
                </Form.Control.Feedback>
              </div>
              <div className="col-4 mb-4">
                <span className="required fw-bold">Hình thức đào tạo</span>
                <Select
                  key={"id"}
                  className="mt-2"
                  options={hinhthucdaotao}
                  style={{ height: "44px" }}
                  placeholder="Chọn hình thức đào tạo"
                  onChange={(e) => {
                    setValueHinhThuc(e);
                    setField("hinh_thuc_dao_tao", e.value);
                  }}
                  value={valueHinhThuc}
                  isInvalid={!!errors?.hinh_thuc_dao_tao}
                />
                <span className="text-danger">
                  {errors?.hinh_thuc_dao_tao ? (
                    <span>{errors?.hinh_thuc_dao_tao}</span>
                  ) : (
                    ""
                  )}
                </span>
              </div>
              <div className="col-4 mb-4">
                <span className="fw-bold">Xếp loại tốt nghiệp</span>
                <Select
                  key={"id"}
                  className="mt-2"
                  options={xeploai}
                  style={{ height: "44px" }}
                  placeholder="Chọn xếp loại tốt nghiệp"
                  onChange={(e) => {
                    setValueXepLoai(e);
                    setField("xep_hang", e.value);
                  }}
                  value={valueXepLoai ? valueXepLoai : null}
                />
              </div>
              <div className="col-12 mb-4">
                <span className="fw-bold">Ghi chú</span>
                <Form.Control
                  className="mt-2"
                  as="textarea"
                  rows={3}
                  style={{ fontWeight: 400 }}
                  placeholder="Nhập ghi chú"
                  onChange={(e) => {
                    setField("ghi_chu", e.target.value);
                  }}
                />
              </div>
            </div>
          </Modal.Body>
          <Modal.Footer style={{ width: "100%" }}>
            <div className="row w-100">
              <div className="col-12">
                <span className="required fs-2 fw-bold">
                  Danh sách chứng từ đính kèm:
                </span>

                {renderChooseFile({
                  arrayFileMap: fileDinhKem,
                  setFile: (value) => {
                    setFileDinhKem(value);
                  },
                })}
                <span className="text-danger">
                  {errors?.fileDinhKem
                    ? "Vui lòng chọn chứng từ đính kèm!"
                    : ""}
                </span>
              </div>
              <div className="col-12 text-center mt-5">
                <Button
                  variant="secondary"
                  style={{ marginRight: 10 }}
                  onClick={() => {
                    closeModal();
                  }}
                >
                  QUAY LẠI
                </Button>
                <Button
                  variant="primary"
                  onClick={(e) => {
                    onSubmit(e);
                    // closeModal()
                  }}
                >
                  Đồng ý
                </Button>
              </div>
            </div>
          </Modal.Footer>
        </Modal>

        {/* Delete Modal */}
        <Modal
          centered
          show={visibleDelete}
          onHide={() => {
            closeModalDelete();
          }}
        >
          <Modal.Header className="bg-blue2">
            <Modal.Title className="text-white" style={{ fontSize: "22px" }}>
              Bạn có chắc chắn muốn xóa bằng cấp này ?
            </Modal.Title>
          </Modal.Header>
          <Modal.Body
            className="row d-flex"
            style={{ justifyContent: "center" }}
          >
            <img
              width={350}
              height={175}
              className="mb-5"
              src="/media/assets/deleteTrash.gif"
              alt=""
            />
            <div className="col-12 text-center mt-5">
              <Button
                variant="secondary"
                style={{ marginRight: 10 }}
                onClick={() => {
                  closeModalDelete();
                }}
              >
                QUAY LẠI
              </Button>
              <Button
                variant="primary"
                onClick={() => {
                  deleteBangCap();
                  closeModalDelete();
                }}
              >
                Đồng ý
              </Button>
            </div>
          </Modal.Body>
          {/* <Modal.Footer>

                </Modal.Footer> */}
        </Modal>
      </div>
    </LocalizationProvider>
  );
}
