import { enAU } from "date-fns/locale";
import { LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";

import { Form, Modal } from "react-bootstrap";
import { useState } from "react";
import LoadingIndicator from "../../../components/LoadingIndicator";

export function PopupCapNhatYeuCauLienKetTC (props) {

    const [isLoading, setIsLoading] = useState(false);




    return (
        <Modal
            backdrop="static"
            keyboard={false}
            size="xl"
            show={props?.show}
            onHide={() => {
                props?.hide();
            }}
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
                <Modal.Header className="px-4" closeButton>
                    <Modal.Title className=" ms-auto ">
                        <h2 className=" text-uppercase text-center text-primary">
                            YÊU CẦU LIÊN KẾT DÀNH CHO DƯỢC SĨ ĐÃ CÓ CCHND
                        </h2>
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {/* THÔNG TIN HÀNH CHÍNH */}
                    <>
                        {isLoading && <LoadingIndicator />}
                        {!isLoading && (
                            <>
                                <div className="row card-box" style={{ pointerEvents: "none" }}>
                                    <div className="col-12 text-title pb-3 fw-bolder">
                                        THÔNG TIN CƠ SỞ/ĐỊA ĐIỂM KINH DOANH
                                    </div>
                                    <div className="col-4 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            Họ đệm
                                        </span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                setField("ho", e.target.value);
                                            }}
                                            value={formTTHC?.ho != null ? formTTHC?.ho : ""}
                                            isInvalid={!!errors?.ho}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {errors?.ho}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-2 mb-4">
                                        <span className="required fw-bolder text-primary">Tên</span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                setField("ten", e.target.value);
                                            }}
                                            value={formTTHC?.ten != null ? formTTHC?.ten : ""}
                                            isInvalid={!!errors?.ten}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {errors?.ten}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-3 mb-4">
                                        <span className="required fw-bolder d-block mb-2 text-primary">
                                            Ngày sinh
                                        </span>
                                        <DesktopDatePicker
                                            label=" "
                                            inputFormat="dd/MM/yyyy"
                                            value={formTTHC?.ngay_sinh}
                                            onChange={(e) => {
                                                setField("ngay_sinh", e);
                                            }}
                                            renderInput={(params) => <TextField {...params} />}
                                            isInvalid={!!errors?.ngay_sinh}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {errors?.ngay_sinh}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-3 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            Giới tính
                                        </span>
                                        <Select
                                            key={"id"}
                                            className="mt-2"
                                            options={GioiTinh}
                                            style={{ height: "44px" }}
                                            onChange={(e) => {
                                                setSelectedGioiTinh(e);
                                            }}
                                            value={selectedGioiTinh}
                                        />
                                        <span className="text-danger">
                                            {!selectedGioiTinh ? " không được bỏ trống!" : ""}
                                        </span>
                                    </div>

                                    <div className="col-lg-3 col-sm-6 col-md-6 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            CMND/CCCD
                                        </span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                setField("ma_cong_dan", e.target.value);
                                            }}
                                            value={
                                                formTTHC?.ma_cong_dan != null
                                                    ? formTTHC?.ma_cong_dan
                                                    : ""
                                            }
                                            isInvalid={!!errors?.so_hieu}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {errors?.so_hieu}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-lg-3 col-sm-6 col-md-6 mb-4">
                                        <span className="required fw-bolder d-block mb-2 text-primary">
                                            Ngày cấp CMND/CCCD
                                        </span>
                                        <DesktopDatePicker
                                            label=" "
                                            inputFormat="dd/MM/yyyy"
                                            value={formTTHC?.ngay_cap}
                                            onChange={(e) => {
                                                setField("ngay_cap", e.target.value);
                                            }}
                                            renderInput={(params) => <TextField {...params} />}
                                        // isInvalid={!!errors?.ngay_cap}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.ngay_cap} */}
                                        </Form.Control.Feedback>
                                    </div>
                                    <div className="col-lg-6 col-sm-12 col-md-12 mb-4">
                                        <span className="required fw-bolder d-block mb-2 text-primary">
                                            Nơi cấp CMND/CCCD
                                        </span>
                                        <Select
                                            key={"id"}
                                            className="mt-2"
                                            options={listNoiCapCMND}
                                            style={{ height: "44px" }}
                                            onChange={(e) => {
                                                setSelectedNoiCapCMND(e);
                                            }}
                                            value={selectedNoiCapCMND}
                                        />
                                        <span className="text-danger">
                                            {errors?.noi_tot_nghiep_id
                                                ? "Nơi tốt nghiệp không được bỏ trống!"
                                                : ""}
                                        </span>
                                    </div>
                                </div>

                                {/* THÔNG TIN CHỨNG CHỈ HÀNH NGHỀ DƯỢC */}
                                <div className="row card-box">
                                    <div className="col-12 text-title pb-3 fw-bolder">
                                        THÔNG TIN CHỨNG CHỈ HÀNH NGHỀ DƯỢC
                                    </div>
                                    <div className="col-6 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            Số giấy phép
                                        </span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                setFieldCCHND("so_giay_phep", e.target.value);
                                            }}
                                            value={
                                                formCCHND?.so_giay_phep != null
                                                    ? formCCHND?.so_giay_phep
                                                    : ""
                                            }
                                            isInvalid={!!errorsCCHND?.so_giay_phep}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {errorsCCHND?.so_giay_phep}
                                        </Form.Control.Feedback>
                                    </div>

                                    <div className="col-6 mb-4">
                                        <span className="required fw-bolder d-block mb-2 text-primary">
                                            Ngày cấp chứng chỉ
                                        </span>
                                        <DesktopDatePicker
                                            label=" "
                                            inputFormat="dd/MM/yyyy"
                                            value={formCCHND?.ngay_cap || null}
                                            onChange={(e) => {
                                                setFieldCCHND("ngay_cap", e);
                                            }}
                                            renderInput={(params) => <TextField {...params} />}
                                        // isInvalid={!!errors?.ngay_cap}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.ngay_cap} */}
                                        </Form.Control.Feedback>
                                    </div>

                                    <div className="col-6 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            Văn bằng chuyên môn
                                        </span>
                                        <Select
                                            key={"id"}
                                            className="mt-2"
                                            options={listVanBangChuyenMon}
                                            style={{ height: "44px" }}
                                            onChange={(e) => {
                                                setSelectedVanBang(e);
                                            }}
                                            value={selectedVanBang}
                                        />
                                        <span className="text-danger"></span>
                                    </div>

                                    <div className="col-6 mb-4">
                                        <span className=" fw-bolder text-primary">Nơi cấp</span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                setFieldCCHND("co_quan_cap", e.target.value);
                                            }}
                                            value={
                                                formCCHND?.co_quan_cap != null
                                                    ? formCCHND?.co_quan_cap
                                                    : ""
                                            }

                                        // isInvalid={!!errors?.so_hieu}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.so_hieu} */}
                                        </Form.Control.Feedback>
                                    </div>

                                    <div className="col-6 mb-4">
                                        <span className="fw-bolder text-primary">
                                            Phụ cấp chuyên môn tại
                                        </span>
                                        <Form.Control
                                            type="text"
                                            className="mt-2"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => {
                                                setFieldCCHND("phu_trach_chuyen_mon", e.target.value);
                                            }}
                                            value={
                                                formCCHND?.phu_trach_chuyen_mon != null
                                                    ? formCCHND?.phu_trach_chuyen_mon
                                                    : ""
                                            }

                                        // isInvalid={!!errors?.so_hieu}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {/* {errors?.so_hieu} */}
                                        </Form.Control.Feedback>
                                    </div>

                                    <div className="col-6 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            Hình thức
                                        </span>
                                        <Select
                                            key={"id"}
                                            className="mt-2"
                                            options={HinhThuc}
                                            style={{ height: "44px" }}
                                            onChange={(e) => {
                                                setSelectedHinhThuc(e);
                                            }}
                                            value={selectedHinhThuc}
                                        />
                                        <span className="text-danger">
                                            {/* {errors?.noi_tot_nghiep_id
                  ? "Nơi tốt nghiệp không được bỏ trống!"
                  : ""} */}
                                        </span>
                                    </div>
                                    <div className="col-12 mb-4">
                                        <span className="required fw-bolder text-primary">
                                            Phạm vi chuyên môn
                                        </span>
                                        <div className="row">
                                            {ListPVCM && ListPVCM.length > 0
                                                ? ListPVCM.map((e) => {
                                                    return (
                                                        <div className="col-6">
                                                            <FormControlLabel
                                                                key={e.value}
                                                                control={
                                                                    <Checkbox
                                                                        key={`chilđren${e.value}`}
                                                                        checked={ListIdCheckedPVCM.includes(
                                                                            String(e.value)
                                                                        )}
                                                                        onChange={() => {
                                                                            let checkExist =
                                                                                ListIdCheckedPVCM.includes(
                                                                                    String(e.value)
                                                                                );
                                                                            let listTemp = ListIdCheckedPVCM;
                                                                            if (checkExist) {
                                                                                let x1 = listTemp.filter(
                                                                                    (x) => x !== String(e.value)
                                                                                );
                                                                                dispatch(
                                                                                    commonActions.setListIdCheckedPVCM(
                                                                                        x1
                                                                                    )
                                                                                );
                                                                            } else {
                                                                                let x2 = [...listTemp, e.value];
                                                                                dispatch(
                                                                                    commonActions.setListIdCheckedPVCM(
                                                                                        x2
                                                                                    )
                                                                                );
                                                                            }
                                                                        }}
                                                                        value={e?.id || ""}
                                                                    />
                                                                }
                                                                label={e?.label}
                                                            />
                                                        </div>
                                                    );
                                                })
                                                : null}
                                        </div>
                                    </div>
                                    <div className="col-12 mb-4">
                                        <span className="required fw-bolder text-primary mb-2">
                                            Vị trí hành nghề
                                        </span>
                                        <div className="row">
                                            {ListVTHN && ListVTHN.length > 0
                                                ? ListVTHN?.map((e, i) => {
                                                    return <>{renderCheckbox(e)}</>;
                                                })
                                                : null}
                                        </div>
                                    </div>
                                </div>

                                {/* Đính kèm */}
                                <div className="row card-box">
                                    <div className="col-12 text-title pb-3 fw-bolder">
                                        Đính kèm
                                    </div>
                                    {renderChooseFile({
                                        title: "1. Hình chụp CCHND",
                                        name: "hinhchupcchnd",
                                        arrayFileMap: FileHinhChupCCHND,
                                        setFile: (value) => {
                                            setFileHinhChupCCHND(value);
                                        },
                                        arrayDelFile: FileDelHinhChupCCHND,
                                        setFileDelete: (value) => {
                                            setFileDelHinhChupCCHND(value);
                                        },
                                    })}
                                    {renderChooseFile({
                                        title: "2. Ảnh chân dung",
                                        name: "anhchandung",
                                        arrayFileMap: FileAnhChanDung,

                                        setFile: (value) => {
                                            setFileAnhChanDung(value);
                                        },
                                        arrayDelFile: FileDelAnhChanDung,
                                        setFileDelete: (value) => {
                                            setFileDelAnhChanDung(value);
                                        },
                                    })}
                                    {renderChooseFile({
                                        title: "3. CMND/CCCD",
                                        name: "filecmndcccd",
                                        arrayFileMap: FileCMNDCCCD,
                                        setFile: (value) => {
                                            setFileCMNDCCCD(value);
                                        },
                                        arrayDelFile: FileDelCMNDCCCD,

                                        setFileDelete: (value) => {
                                            setFileDelCMNDCCCD(value);
                                        },
                                    })}
                                    {renderChooseFile({
                                        title: "4. Giấy tờ chứng minh khác",
                                        name: "giaytokhac",
                                        arrayFileMap: FileGiayToKhac,
                                        setFile: (value) => {
                                            setFileGiayToKhac(value);
                                        },
                                        arrayDelFile: FileDelGiayToKhac,
                                        setFileDelete: (value) => {
                                            setFileDelGiayToKhac(value);
                                        },
                                    })}
                                </div>
                            </>
                        )}
                    </>
                </Modal.Body>
            </LocalizationProvider>
            <Modal.Footer style={{ width: "100%" }}>
                <div className="row w-100">
                    <div className="col-12 text-center mt-5">
                        {LoadingBtn ? (
                            <Button variant="secondary" style={{ marginRight: 10 }}>
                                <Spinner animation="border" role="status">
                                    <span className="visually-hidden">Loading...</span>
                                </Spinner>
                            </Button>
                        ) : (
                            <>
                                <Button
                                    variant="secondary"
                                    style={{ marginRight: 10 }}
                                    onClick={() => {
                                        props?.hide();
                                        resetState();
                                    }}
                                >
                                    Đóng
                                </Button>
                                {formCCHND?.trang_thai_ho_so !== "2" ? (
                                    <>
                                        <Button
                                            variant="success"
                                            style={{ marginRight: 10 }}
                                            onClick={(e) => {
                                                handleSubmit(e, 1);
                                            }}
                                        >
                                            Lưu
                                        </Button>
                                        <Button
                                            variant="primary"
                                            onClick={(e) => {
                                                handleSubmit(e, 2);
                                            }}
                                        >
                                            Gửi yêu cầu
                                        </Button>
                                    </>
                                ) : null}
                            </>
                        )}
                    </div>
                </div>
            </Modal.Footer>
        </Modal>
    )
}
