import { useAuth } from "../../../../../app/modules/apps/auth";

import axios from "axios";
import moment from "moment";
import { toast } from "react-toastify";
import api from "../../../../configs/api";
import { Oval } from "react-loader-spinner";
import { Modal, Button } from "react-bootstrap";
import React, { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import TextField from "@mui/material/TextField";
import { DesktopDatePicker } from "@mui/x-date-pickers/DesktopDatePicker";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";

import Select from "react-select";
import { Form, Dropdown } from "react-bootstrap";
import { enAU } from "date-fns/locale";
import { In } from "../../In/In";

export function LichSuThucHanh(props) {
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});

  const [selectedTT, setSelectedTT] = useState();
  const [selectedQH, setSelectedQH] = useState();
  const [selectedPX, setSelectedPX] = useState();

  const [tinhThanhList, setTinhThanhList] = useState();
  const [quanHuyenList, setQuanHuyenList] = useState();
  const [phuongXaList, setPhuongXaList] = useState();
  const [coSoThucHanhList, setCoSoThucHanhList] = useState({});
  const [noiDungThucHanhList, setNoiDungThucHanhList] = useState();

  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(10);
  const [totalRows, setTotalRows] = useState(0);
  const [isAdd, setIsAdd] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [idSelected, setIdSelected] = useState("");

  const [fileDinhKem, setFileDinhKem] = useState([]);
  const [deleteDinhKem, setDeleteDinhKem] = useState([]);

  const [visibleDelete, setvisibleDelete] = useState(false);
  const [visibleInstDelete, setvisibleIsntDelete] = useState(false);
  const closeModalDelete = () => setvisibleDelete(false);
  const showModalDelete = () => setvisibleDelete(true);
  const closeModal = () => setvisibleIsntDelete(false);
  const showModal = () => setvisibleIsntDelete(true);

  const [onHide, setOnHide] = useState(false);
  const empty_json = {};
  const json = {
    nhan_vien_id: props?.userId,
  };

  const initRow = {
    id: 1,
    noi_dung_thuc_hanh: null,
    tu_ngay: new Date(),
    den_ngay: new Date(),
  };

  const [noiDungThucHanh, setNoiDungThucHanh] = useState([]);

  useEffect(() => {
    if (props?.userId) {
      getCoSoThucHanhList({});
    }
  }, [props?.userId]);

  useEffect(() => {
    setOnHide(props?.onHide);
    if (visibleInstDelete) {
      getListTinhThanh();
      getListNoiDungThucHanh();
    }
  }, [visibleInstDelete]);

  const handlePageChange = (page) => {
    setPage(page);
    getCoSoThucHanhList({ page_number: page });
  };
  const inHoSo = async (id) => {

    let file_type = "XacNhanThucHanh";
    let file_name = "HoSoXacNhanThucHanh";

    if (file_type !== "") {
      await setIsLoading(true);
      axios
        .post(api.API_YEU_CAU, {
          file_type,
          file_name,
          url:
            "http://10.14.141.29:7791/yeucau" +
            "/xacnhanthoigianthuchanh/" +
            id,
        })
        .then(async ({ data }) => {
          if (data?.path[0]) {
            const url = window.location.origin + "/in-ho-so"
            localStorage.setItem("url", data?.path[0])
            window.open(url, "_blank");
          }
        })
        .catch((error) => {
          toast.error(error?.response?.data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    } else {
      toast.error("Lỗi thông tin hồ sơ", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "error",
      });
    }
  };
  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(api.API_DS_BANG_CAP + `?page=${page}&per_page=${newPerPage}`, {
        nhan_vien_id: props?.userId,
      })
      .then(({ data }) => {
        if (data?.results) {
          getCoSoThucHanhList(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => { })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const addRow = async () => {
    const content =
      noiDungThucHanh[noiDungThucHanh.length - 1]?.noi_dung_thuc_hanh;
    if (content == null && typeof content == "object") {
      toast.error("Vui lòng nhập đầy đủ thông tin thực hành.", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "error",
      });
    } else {
      let newRow = { ...initRow };
      var id = 0;
      if (noiDungThucHanh.length == 0) {
        id = 1;
      } else {
        id = noiDungThucHanh[noiDungThucHanh.length - 1].id + 1;
      }
      newRow.id = id;
      await setNoiDungThucHanh(noiDungThucHanh.concat(newRow));
    }
  };
  const setRowValue = async (e, ndth) => {
    const dataRow = {
      ...ndth,
      noi_dung_thuc_hanh: e,
    };
    const updatedData = noiDungThucHanh.map((x) =>
      x.id === ndth.id ? { ...x, ...dataRow } : x
    );
    await setNoiDungThucHanh(updatedData);
  };
  const setRowDateValue = async (e, ndth, type) => {
    const dataRow = {
      ...ndth,
      [type]: e,
    };
    const updatedData = noiDungThucHanh.map((x) =>
      x.id === ndth.id ? { ...x, ...dataRow } : x
    );
    await setNoiDungThucHanh(updatedData);
  };
  const removeRow = (id) => {
    const formArray = noiDungThucHanh.filter((row) => row.id != id);
    setNoiDungThucHanh(formArray);
  };

  const getCoSoThucHanhList = ({ page_number = page, size = perPage }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_DS_CO_SO_THUC_HANH + `?page=${page_number}&per_page=${size}`,
        {
          nhan_vien_id: props?.userId,
        }
      )
      .then(async ({ data }) => {
        setCoSoThucHanhList(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getListNoiDungThucHanh = () => {
    axios
      .post(api.API_DS_NOI_DUNG_THUC_HANH, empty_json)
      .then(async ({ data }) => {
        const result = data?.results;
        result.forEach((e) => {
          e.value = e.id;
          e.label = e.ten;
        });
        setNoiDungThucHanhList(result);
      });
  };

  const getListTinhThanh = () => {
    axios
      .get(api.API_TINH_THANH)
      .then(async ({ data }) => {
        const result = data?.results;
        result.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setTinhThanhList(result);
      })
      .catch((error) => {
        toast.error("Không thể lấy thông tin danh sách tỉnh thành", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      });
  };
  const getListQuanHuyen = (id) => {
    axios
      .get(api.API_QUAN_HUYEN + "/" + id)
      .then(async ({ data }) => {
        const result = data?.results;
        result.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setQuanHuyenList(result);
      })
      .catch((error) => {
        toast.error("Không thể lấy thông tin danh sách quận huyện", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      });
  };
  const getListPhuongXa = (id) => {
    axios
      .get(api.API_XA_PHUONG + "/" + id)
      .then(async ({ data }) => {
        const result = data?.results;
        result.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setPhuongXaList(result);
      })
      .catch((error) => {
        toast.error("Không thể lấy thông tin danh sách phường xã", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      });
  };

  const resetState = async () => {
    closeModal();
    setForm({
      tu_ngay: new Date(new Date()).getTime(),
      den_ngay: new Date(new Date()).getTime(),
    });
    setErrors({});
    setFileDinhKem([]);
    setSelectedTT(null);
    setSelectedQH(null);
    setSelectedPX(null);
    setNoiDungThucHanh([]);
  };

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const handleTuNgay = (value) => {
    setField("tu_ngay", new Date(value).getTime());
  };

  const handleDenNgay = (value) => {
    setField("den_ngay", new Date(value).getTime());
  };

  const findFormErrors = () => {
    const { ten_co_so, giay_chung_nhan, tu_ngay, den_ngay } = form;
    const newErrors = {};

    if (!ten_co_so || ten_co_so === "")
      newErrors.ten_co_so = "Tên cơ sở không được bỏ trống!";
    if (!giay_chung_nhan || giay_chung_nhan === "")
      newErrors.giay_chung_nhan = "Số giấy chứng nhận không được bỏ trống!";
    if (!tu_ngay || tu_ngay === "")
      newErrors.tu_ngay = "Ngày thực hành không được bỏ trống!";
    if (!den_ngay || den_ngay === "")
      newErrors.den_ngay = "Ngày thực hành không được bỏ trống!";
    if (tu_ngay > den_ngay)
      newErrors.soSanhNgay =
        "Ngày bắt đầu thực hành không được lớn hơn ngày kết thúc!";
    if (fileDinhKem.length < 1)
      newErrors.fileDinhKem = "Vui lòng chọn chứng từ đính kèm !";

    if (noiDungThucHanh.length > 0) {
      for (let x = 0; x < noiDungThucHanh.length; x++) {
        if (noiDungThucHanh[x].tu_ngay > noiDungThucHanh[x].den_ngay) {
          newErrors.soSanhNgay =
            "Ngày bắt đầu thực hành không được lớn hơn ngày kết thúc!";
        }
      }
    }

    return newErrors;
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else if (isAdd === true) {
      await setIsLoading(true);

      const {
        giay_chung_nhan,
        ten_co_so,
        tu_ngay,
        den_ngay,
        tinh_thanh_id,
        quan_huyen_id,
        xa_phuong_id,
        so_nha,
      } = form;

      const data = new FormData();

      data.append("nhan_vien_id", props?.userId);
      data.append("giay_chung_nhan", giay_chung_nhan);
      data.append("ten_co_so", ten_co_so);
      data.append("tu_ngay", tu_ngay);
      data.append("den_ngay", den_ngay);
      if (tinh_thanh_id) {
        data.append("tinh_thanh_id", tinh_thanh_id);
      }
      if (quan_huyen_id) {
        data.append("quan_huyen_id", quan_huyen_id);
      }
      if (xa_phuong_id) {
        data.append("xa_phuong_id", xa_phuong_id);
      }
      if (so_nha) {
        data.append("so_nha", so_nha);
      }
      data.append("noi_dung_thuc_hanh", JSON.stringify(noiDungThucHanh));
      for (let x = 0; x < fileDinhKem.length; x++) {
        await data.append("chung_tu_dinh_kem[]", fileDinhKem[x]);
      }

      await axios
        .post(api.API_CO_SO_THUC_HANH + "_add", data, {
          headers: {
            "content-type": "multipart/form-data",
          },
        })
        .then(async ({ data }) => {
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getCoSoThucHanhList({ page, perPage });
          setIsLoading(false);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    } else if (isAdd === false) {
      await setIsLoading(true);

      const {
        giay_chung_nhan,
        ten_co_so,
        tu_ngay,
        den_ngay,
        tinh_thanh_id,
        quan_huyen_id,
        xa_phuong_id,
        so_nha,
      } = form;

      const data = new FormData();

      data.append("nhan_vien_id", props?.userId);
      data.append("giay_chung_nhan", giay_chung_nhan);
      data.append("ten_co_so", ten_co_so);
      data.append("tu_ngay", tu_ngay);
      data.append("den_ngay", den_ngay);
      if (tinh_thanh_id) {
        data.append("tinh_thanh_id", tinh_thanh_id);
      }
      if (quan_huyen_id) {
        data.append("quan_huyen_id", quan_huyen_id);
      }
      if (xa_phuong_id) {
        data.append("xa_phuong_id", xa_phuong_id);
      }
      if (so_nha) {
        data.append("so_nha", so_nha);
      }
      data.append("noi_dung_thuc_hanh", noiDungThucHanh);

      for (let x = 0; x < fileDinhKem.length; x++) {
        await data.append("chung_tu_dinh_kem[]", fileDinhKem[x]);
      }

      if (deleteDinhKem.length > 0) {
        for (let d = 0; d < deleteDinhKem.length; d++) {
          await data.append(
            "delete_dinh_kem_anh_chan_dung[]",
            deleteDinhKem[d]
          );
        }
      }

      await axios
        .put(api.API_CO_SO_THUC_HANH + "_by_id/" + idSelected, data, {
          headers: {
            "content-type": "multipart/form-data",
          },
        })
        .then(async ({ data }) => {
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getCoSoThucHanhList({ page, perPage });
          setDeleteDinhKem([]);
          setIsLoading(false);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const deleteThucHanh = () => {
    axios
      .delete(api.API_CO_SO_THUC_HANH + "_by_id/" + idSelected)
      .then(async ({ data }) => {
        toast.success("Xóa thông tin thực hành thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        closeModalDelete();
        resetState();
        if (page == 1) {
          getCoSoThucHanhList({ page_number: 1 });
        } else if (coSoThucHanhList?.length == 1) {
          getCoSoThucHanhList({ page_number: page - 1 });
        } else {
          getCoSoThucHanhList({ page_number: page });
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getDetailThucHanh = async (row) => {
    setForm({ ...row });
    setSelectedTT({ value: row.tinh_thanh_id, label: row?.tinh_thanh });
    setSelectedQH({ value: row?.quan_huyen_id, label: row?.quan_huyen });
    setSelectedPX({ value: row?.xa_phuong_id, label: row?.xa_phuong });
    setFileDinhKem(row?.chung_tu_dinh_kem);
    setIdSelected(row?.id);
  };

  const columns = [
    {
      name: "Tên cơ sở",
      selector: (row) => <span>{row?.ten_co_so}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Từ ngày",
      selector: (row) => (
        <>
          {row?.tu_ngay ? (
            <span>{moment(row?.tu_ngay).format("DD/MM/YYYY")}</span>
          ) : (
            <span></span>
          )}
        </>
      ),
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Đến ngày",
      selector: (row) => (
        <>
          {row?.den_ngay ? (
            <span>{moment(row?.den_ngay).format("DD/MM/YYYY")}</span>
          ) : (
            <span></span>
          )}
        </>
      ),
      grow: props?.onHide ? 2 : 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Nội dung thực hành",
      // selector: (row) => <span>{row?.noi_dung_thuc_hanh}</span>,
      selector: (row) => (
        <>
          {row.noi_dung_thuc_hanh ? (
            <>
              {row?.noi_dung_thuc_hanh.length > 0 ? (
                <>
                  {row?.noi_dung_thuc_hanh.map((item, index) => (
                    <div key={index}>
                      {row.noi_dung_thuc_hanh.length == 1 ? (
                        <span key={index}>{item?.noi_dung_thuc_hanh}</span>
                      ) : (
                        <span key={index}>

                          {item?.noi_dung_thuc_hanh},
                        </span>
                      )}
                    </div>
                  ))}
                </>
              ) : (
                <span></span>
              )}
            </>
          ) : (
            <span></span>
          )}
        </>
      ),
      grow: 4,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Đính kèm",
      selector: (row) => (
        <>
          {row.noi_dung_thuc_hanh ? (
            <>
              {row?.chung_tu_dinh_kem.length > 0 ? (
                <>
                  {row?.chung_tu_dinh_kem.map((item, index) => (
                    <div key={index}>
                      <a
                        key={index}
                        target="_blank"
                        rel="noreferrer"
                        href={api.API_URL + item?.url}
                        className="mt-1"
                        style={{ display: "inline-block" }}
                      >
                        {item?.name}
                      </a>
                      <br />
                    </div>
                  ))}
                </>
              ) : (
                <span> </span>
              )}
            </>
          ) : (
            <span></span>
          )}
        </>
      ),
      grow: 5,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: " ",
      grow: 1,
      cell: (row) =>
        onHide ? null : (
          <div className="container my-3">
            <Dropdown>
              <Dropdown.Toggle variant="link" style={{ float: "right" }}>
                <i
                  className="fas fa-bars"
                  style={{ marginLeft: "3px", marginRight: "-3px" }}
                ></i>
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Item
                  onClick={() => {
                    setIsAdd(false);
                    showModal();
                    getDetailThucHanh(row);
                    console.log(selectedTT);
                  }}
                >
                  <i className="fas fa-eye text-primary me-3"></i>Xem chi tiết
                </Dropdown.Item>
                <Dropdown.Item
                  onClick={() => {
                    showModalDelete();
                    setIdSelected(row.id);
                  }}
                >
                  <i className="fas fa-trash text-danger me-3"></i>Xóa
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </div>
        ),
    },
  ];

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };

  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "14px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };

  const renderTable = (arrayTest) => {
    return (
      <>
        {noiDungThucHanh.length > 0 ? (
          <table className="table mt-4">
            <thead>
              <tr>
                <th className="fw-bolder px-3" style={{ width: "30%" }}>
                  Nội dung thực hành
                </th>
                <th className="fw-bolder px-3">Thực hành từ ngày</th>
                <th className="fw-bolder px-3">Đến ngày</th>
                <th className="fw-bolder px-3"></th>
              </tr>
            </thead>
            <tbody>
              {arrayTest.map((ndth, index) => (
                <tr key={ndth.id}>
                  <td className="px-3 py-0">
                    <Select
                      key={"id"}
                      style={{ height: "44px" }}
                      options={noiDungThucHanhList}
                      placeholder="Chọn nội dung thực hành"
                      onChange={(e) => {
                        setRowValue(e, ndth);
                      }}
                      value={ndth?.noi_dung_thuc_hanh}
                    />
                    <span className="text-danger">
                      {errors?.van_bang_chuyen_mon_id ? (
                        <span>{errors?.van_bang_chuyen_mon_id}</span>
                      ) : (
                        ""
                      )}
                    </span>
                  </td>
                  <td className="px-3 py-0">
                    <DesktopDatePicker
                      label=" "
                      name="tu_ngay"
                      maxDate={new Date()}
                      inputFormat="dd/MM/yyyy"
                      value={ndth.tu_ngay}
                      onChange={(e) => {
                        setRowDateValue(e, ndth, "tu_ngay");
                      }}
                      renderInput={(params) => <TextField {...params} />}
                    />
                  </td>
                  <td className="px-3 py-0">
                    <DesktopDatePicker
                      label=" "
                      name="den_ngay"
                      maxDate={new Date()}
                      inputFormat="dd/MM/yyyy"
                      value={ndth?.den_ngay}
                      onChange={(e) => {
                        setRowDateValue(e, ndth, "den_ngay");
                      }}
                      renderInput={(params) => <TextField {...params} />}
                    />
                  </td>
                  <td
                    className="px-3 py-0"
                    style={{ verticalAlign: "middle", textAlign: "center" }}
                  >
                    <button
                      className="btn btn-danger px-0 py-0 mb-4"
                      style={{
                        height: "38px",
                        width: "38px",
                        fontSize: "12px",
                      }}
                      onClick={() => {
                        removeRow(ndth.id);
                      }}
                    >
                      X
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : null}
      </>
    );
  };

  const renderChooseFile = ({ title, name, arrayFileMap, setFile }) => {
    return (
      <div className="col-12">
        <div className="row">
          <div className="col-12">
            <button className="btn btn-success btn-sm my-3">
              <UploadControl name={name}>Thêm chứng từ đính kèm</UploadControl>
            </button>
          </div>
          <div className="col-12">
            {arrayFileMap?.length > 0
              ? arrayFileMap.map((e, i) => {
                return (
                  <button
                    key={i}
                    className="btn ml-2 mb-2"
                    style={{
                      backgroundColor: "#e8e8e8",
                      padding: "12px 10px 12px 16px",
                      marginRight: "12px",
                    }}
                  >
                    <span>{e?.name}</span>
                    <i
                      style={{
                        display: "inline",
                        color: "red",
                        marginLeft: "8px",
                      }}
                      onClick={() => {
                        let arrFile = arrayFileMap;
                        arrFile = arrFile.filter(
                          (item) => item.name !== e?.name
                        );
                        setFile(arrFile);
                        deleteDinhKem.push(e?.url);
                      }}
                      className="fas fa-times-circle"
                    ></i>
                  </button>
                );
              })
              : null}
          </div>
        </div>
      </div>
    );
  };

  const UploadControl = ({
    children,
    value,
    onChange,
    disabled,
    accept,
    name,
  }) => {
    return (
      <label htmlFor={`contained-button-${name}`} className="m-0 w-100">
        <input
          disabled={disabled}
          style={{ display: "none" }}
          id={`contained-button-${name}`}
          // multiple
          type="file"
          accept="audio/*|video/*|image/*|MIME_type"
          onChange={async (event) => {
            await setFileDinhKem([...fileDinhKem, event.target.files[0]]);
            findFormErrors();
          }}
        />

        {children}
      </label>
    );
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
      <div className="bg-white card-box py-6 px-6 mx-3 my-3">
        <div className="row pb-5">
          <div className="col-md-6 col-sm-6">
            <label className="fw-bold fs-5">THỰC HÀNH TẠI CƠ SỞ</label>
          </div>
          {props?.onHide ? null : (
            <div
              className="col-md-6 col-sm-6 d-flex justify-content-end"
              data-kt-user-table-toolbar="base"
            >
              <button
                type="button"
                className="btn btn-sm btn-primary d-flex"
                style={{ alignItems: "center" }}
                onClick={async () => {
                  resetState();
                  setIsAdd(true);
                  showModal();
                }}
              >
                <i className="fas fa-plus me-1"></i>
                Thêm
              </button>
            </div>
          )}
        </div>

        <DataTable
          noDataComponent={"Không có dữ liệu."}
          sortServer
          progressPending={isLoading}
          columns={columns}
          data={coSoThucHanhList}
          customStyles={customStyles}
          pagination
          highlightOnHover
          pointerOnHover
          paginationServer
          paginationTotalRows={totalRows}
          onChangeRowsPerPage={handlePerRowsChange}
          onChangePage={handlePageChange}
          paginationComponentOptions={paginationOptions}
          progressComponent={
            <div
              style={{
                padding: "24px",
              }}
            >
              <Oval
                arialLabel="loading-indicator"
                color="#007bff"
                height={40}
              />
            </div>
          }
        />
      </div>

      {/* Infor Modal */}
      <Modal
        size="lg"
        show={visibleInstDelete}
        onHide={() => {
          closeModal();
        }}
      >
        <Modal.Body>
          <div className="row mt-3 mb-5">
            <div className="col-12 text-center">
              <span className="fs-1 fw-bold">
                {isAdd
                  ? "Thêm mới cơ sở thực hành"
                  : "Thông tin cơ sở thực hành"}
              </span>
            </div>
          </div>
          <div className="row">
            <div className="col-6 mb-4">
              <span className="required fw-bold">Giấy CNĐĐKKDD</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập số giấy CNĐĐKKDD"
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("giay_chung_nhan", e.target.value);
                }}
                value={
                  form?.giay_chung_nhan != null ? form?.giay_chung_nhan : ""
                }
                isInvalid={!!errors?.giay_chung_nhan}
              />
              {errors?.giay_chung_nhan ? (
                <span className="text-danger">{errors?.giay_chung_nhan}</span>
              ) : (
                ""
              )}
            </div>
            <div className="col-6 mb-4">
              <span className="required fw-bold d-block mb-2">
                Thực hành từ ngày
              </span>
              <DesktopDatePicker
                label=" "
                maxDate={new Date()}
                inputFormat="dd/MM/yyyy"
                value={form.tu_ngay ? form.tu_ngay : null}
                toolbarPlaceholder="dd/MM/yyyy"
                onChange={handleTuNgay}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    inputProps={{
                      ...params.inputProps,
                      placeholder: "dd/mm/aaaa",
                    }}
                  />
                )}
                isInvalid={!!errors?.tu_ngay}
              />
              {errors?.tu_ngay ? (
                <span className="text-danger">{errors?.tu_ngay}</span>
              ) : (
                ""
              )}
            </div>
            <div className="col-6 mb-4">
              <span className="required fw-bold">Tên cơ sở</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập tên cơ sở"
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("ten_co_so", e.target.value);
                }}
                value={form?.ten_co_so != null ? form?.ten_co_so : ""}
                isInvalid={!!errors?.ten_co_so}
              />
              {errors?.ten_co_so ? (
                <span className="text-danger">{errors?.ten_co_so}</span>
              ) : (
                ""
              )}
            </div>
            <div className="col-6 mb-4">
              <span className="required fw-bold d-block mb-2">Đến ngày</span>
              <DesktopDatePicker
                label=" "
                maxDate={new Date()}
                inputFormat="dd/MM/yyyy"
                value={form.den_ngay ? form.den_ngay : null}
                onChange={handleDenNgay}
                renderInput={(params) => <TextField {...params} />}
                isInvalid={!!errors?.den_ngay}
              />
              {errors?.den_ngay ? (
                <span className="text-danger">{errors?.den_ngay}</span>
              ) : (
                ""
              )}
            </div>
            <div className="col-3 mb-4">
              <span className="fw-bold">Tỉnh/TP</span>
              <Select
                key={"id"}
                className="mt-2"
                value={selectedTT}
                options={tinhThanhList}
                style={{ height: "44px" }}
                placeholder="Chọn thành phố"
                onChange={(e) => {
                  setSelectedTT(e);
                  setSelectedQH(null);
                  setSelectedPX(null);
                  setField("tinh_thanh_id", e.value);
                  getListQuanHuyen(e.value);
                }}
              />
            </div>
            <div className="col-3 mb-4">
              <span className="fw-bold">Quận/Huyện</span>
              <Select
                key={"id"}
                className="mt-2"
                value={selectedQH}
                options={quanHuyenList}
                style={{ height: "44px" }}
                placeholder="Chọn quận huyện"
                onChange={(e) => {
                  setSelectedQH(e);
                  setSelectedPX(null);
                  setField("quan_huyen_id", e.value);
                  getListPhuongXa(e.value);
                }}
                isDisabled={selectedTT ? false : true}
              />
            </div>
            <div className="col-3 mb-4">
              <span className="fw-bold">Phường/Xã</span>
              <Select
                key={"id"}
                className="mt-2"
                value={selectedPX}
                options={phuongXaList}
                style={{ height: "44px" }}
                placeholder="Chọn phường xã"
                onChange={(e) => {
                  setSelectedPX(e);
                  setField("xa_phuong_id", e.value);
                }}
                isDisabled={selectedQH ? false : true}
              />
            </div>
            <div className="col-3 mb-4">
              <span className="fw-bold">Địa chỉ</span>
              <Form.Control
                type="text"
                className="mt-2"
                placeholder="Nhập số nhà"
                style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                onChange={(e) => {
                  setField("so_nha", e.target.value);
                }}
                value={form?.so_nha != null ? form?.so_nha : ""}
              />
            </div>

            <button
              className="btn btn-primary ms-3 px-1 py-2 fw-normal"
              style={{ width: "120px" }}
              onClick={() => {
                addRow();
              }}
            >
              THÊM MỚI
            </button>
            {renderTable(noiDungThucHanh)}
          </div>
        </Modal.Body>
        <Modal.Footer style={{ width: "100%" }}>
          <div className="row w-100">
            <div className="col-12">
              <span className="required fs-2 fw-bold">
                Danh sách chứng từ đính kèm:
              </span>

              {renderChooseFile({
                arrayFileMap: fileDinhKem,
                setFile: (value) => {
                  setFileDinhKem(value);
                },
              })}
              <span className="text-danger">
                {errors?.fileDinhKem ? "Vui lòng chọn chứng từ đính kèm!" : ""}
              </span>
            </div>
            <div className="col-12 text-center mt-5">
              <Button
                variant="secondary"
                style={{ marginRight: 10 }}
                onClick={() => {
                  closeModal();
                }}
              >
                Quay lại
              </Button>
              <Button
                variant="primary"
                style={{ marginRight: 10 }}
                onClick={() => {
                  inHoSo(idSelected);
                }}
              >
                In giấy xác nhận
              </Button>

              <Button
                variant="success"
                onClick={(e) => {
                  onSubmit(e);
                }}
              >
                Đồng ý
              </Button>
            </div>
          </div>
        </Modal.Footer>
      </Modal>

      {/* Delete Modal */}
      <Modal
        centered
        show={visibleDelete}
        onHide={() => {
          closeModalDelete();
        }}
      >
        <Modal.Header className="bg-blue2">
          <Modal.Title className="text-white" style={{ fontSize: "22px" }}>
            Bạn có chắc chắn muốn xóa thông tin thực hành này ?
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="row d-flex" style={{ justifyContent: "center" }}>
          <img
            width={350}
            height={175}
            className="mb-5"
            src="/media/assets/deleteTrash.gif"
            alt=""
          />
          <div className="col-12 text-center mt-5">
            <Button
              variant="secondary"
              style={{ marginRight: 10 }}
              onClick={() => {
                closeModalDelete();
              }}
            >
              QUAY LẠI
            </Button>
            <Button
              variant="primary"
              onClick={() => {
                deleteThucHanh();
                closeModalDelete();
              }}
            >
              Đồng ý
            </Button>
          </div>
        </Modal.Body>
        {/* <Modal.Footer>

                </Modal.Footer> */}
      </Modal>
    </LocalizationProvider>
  );
}
