import { call, put, takeEvery } from "redux-saga/effects";
import { locationActions, tinhThanhActions } from "./locationSlice";
import { getListTinhThanh } from "./serivice";

function* handleTinhThanh({ payload }) {
    try {
        const resultsTinhThanh = yield call(getListTinhThanh, payload);

        if (resultsTinhThanh) {
            yield put({
                type: locationActions.getLocationSuccess.type,
                payload: resultsTinhThanh,
            });
        }
    } catch (e) {
        yield put({
            type: locationActions.getLocationFailed.type,
        });
    }
}

export default function* locationSaga() {
    //   yield fork(watchLoginFlow);
    yield takeEvery(tinhThanhActions.getTinhThanh.type, handleTinhThanh);
}
