import { LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { enAU } from "date-fns/locale";

import { useEffect, useState } from "react";
import { Form, Modal } from "react-bootstrap";
import { PhamViVaViTriChuyenMon } from "../danh-sach-ho-so-thu-ly-duoc-si/components/PhamViChuyenMon";
// import ThongTinCaNhanCapNhat from "../danh-sach-ho-so-thu-ly-duoc-si/components/ThongTinCaNhanCapNhat";
import { ThongTinCapCCHN } from "../danh-sach-ho-so-thu-ly-duoc-si/components/ThongTinCapCCHN";

export function PopupThongTinHoSoDuocSi(props) {
  const data = props?.data;

  const initRow = {
    id: 1,
    noi_dung_thuc_hanh: null,
    tu_ngay: new Date(),
    den_ngay: new Date(),
  };

  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});

  const [noiDungThucHanh, setNoiDungThucHanh] = useState([]);
  const [noiDungThucHanhList, setNoiDungThucHanhList] = useState();

  // useEffect(() => {
  //   console.log("data", data)
  // },[props?.data])

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const addRow = async () => {
    const content =
      noiDungThucHanh[noiDungThucHanh.length - 1]?.noi_dung_thuc_hanh;
    // if (content == null && typeof content == "object") {
    //   toast.error("Vui lòng nhập đầy đủ thông tin thực hành.", {
    //     position: "top-right",
    //     autoClose: 2000,
    //     hideProgressBar: false,
    //     closeOnClick: true,
    //     pauseOnHover: true,
    //     draggable: true,
    //     progress: undefined,
    //     toastId: "error",
    //   });
    // } else {
    let newRow = { ...initRow };
    var id = 0;
    if (noiDungThucHanh.length == 0) {
      id = 1;
    } else {
      id = noiDungThucHanh[noiDungThucHanh.length - 1].id + 1;
    }
    newRow.id = id;
    await setNoiDungThucHanh(noiDungThucHanh.concat(newRow));
    // }
  };
  const setRowValue = async (e, ndth) => {
    const dataRow = {
      ...ndth,
      noi_dung_thuc_hanh: e,
    };
    const updatedData = noiDungThucHanh.map((x) =>
      x.id === ndth.id ? { ...x, ...dataRow } : x
    );
    await setNoiDungThucHanh(updatedData);
  };
  const setRowDateValue = async (e, ndth, type) => {
    const dataRow = {
      ...ndth,
      [type]: e,
    };
    const updatedData = noiDungThucHanh.map((x) =>
      x.id === ndth.id ? { ...x, ...dataRow } : x
    );
    await setNoiDungThucHanh(updatedData);
  };
  const removeRow = (id) => {
    const formArray = noiDungThucHanh.filter((row) => row.id != id);
    setNoiDungThucHanh(formArray);
  };

  const renderTable = (arrayTest) => {
    return (
      <>
        {noiDungThucHanh.length > 0 ? (
          <table className="table mt-4">
            <thead>
              <tr>
                <th className="fw-bolder px-3" style={{ width: "4%" }}>
                  STT
                </th>
                <th className="fw-bolder px-3" style={{ width: "25%" }}>
                  Họ tên
                </th>
                <th className="fw-bolder px-3" style={{ width: "45%" }}>
                  Nội dung ý kiến
                </th>
                <th className="fw-bolder px-3">Thời gian</th>
                <th className="fw-bolder px-3"></th>
              </tr>
            </thead>
            <tbody>
              {arrayTest.map((ndth, index) => (
                <tr key={ndth.id}>
                  <td
                    className="px-3 py-0 text-center"
                    style={{ verticalAlign: "middle" }}
                  >
                    <span className="fw-bold">{ndth.id}</span>
                  </td>
                  <td className="px-3 py-0">
                    <Form.Control
                      type="text"
                      placeholder="Nhập họ tên"
                      style={{
                        fontSize: 14,
                        fontWeight: 400,
                        height: "38px",
                        margin: "10px 0 10px 0",
                      }}
                      onChange={(e) => {
                        setField("so_nha", e.target.value);
                      }}
                      value={form?.so_nha != null ? form?.so_nha : ""}
                    />
                  </td>
                  <td className="px-3 py-0">
                    <Form.Control
                      type="text"
                      placeholder="Nhập nội dung ý kiến"
                      style={{
                        fontSize: 14,
                        fontWeight: 400,
                        height: "38px",
                        margin: "10px 0 10px 0",
                      }}
                      onChange={(e) => {
                        setField("so_nha", e.target.value);
                      }}
                      value={form?.so_nha != null ? form?.so_nha : ""}
                    />
                  </td>
                  <td className="px-3 py-0" style={{ verticalAlign: "middle" }}>
                    <span>Thời gian ngày giờ</span>
                  </td>
                  <td
                    className="px-3 py-0"
                    style={{ verticalAlign: "middle", textAlign: "center" }}
                  >
                    <button
                      className="btn btn-danger px-0 py-0 mb-4"
                      style={{
                        width: "38px",
                        height: "38px",
                        fontSize: "12px",
                        margin: "10px 0 10px 0",
                      }}
                      onClick={() => {
                        removeRow(ndth.id);
                      }}
                    >
                      X
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : null}
      </>
    );
  };

  return (
    <Modal
      show={props?.show}
      backdrop="static"
      keyboard={false}
      size="xl"
      onHide={() => props?.hide()}
      centered
    >
      <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
        <Modal.Header className="px-4" closeButton>
          <Modal.Title className=" ms-auto ">
            <h2 className=" text-uppercase text-center text-primary">
              Thông tin hồ sơ
            </h2>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="row justify-content-center">
            {/* <ThongTinCaNhanCapNhat data={data} /> */}
            <div className="card py-6 px-6 mx-3 my-3">
              <PhamViVaViTriChuyenMon id={data?.id} hideGhiChu={true} />
            </div>
            <ThongTinCapCCHN />

            <div className="card py-6 px-6 mx-3 my-3">
              <div className="row pb-2">
                <div className="col-md-6 col-sm-6">
                  <label className="fw-bold fs-5">KẾT QUẢ THẨM XÉT</label>
                </div>
              </div>
              <div className="row px-2 py-2">
                <div className="col-6">
                  <span className="fw-bolder">1. Nội dung thẩm xét, lý do</span>
                  <Form>
                    <Form.Control as={"textarea"} rows={3} />
                  </Form>
                </div>
                <div className="col-6">
                  <span className="fw-bolder">2. Nội dung đề nghị</span>
                  <Form>
                    <Form.Control as={"textarea"} rows={3} />
                  </Form>
                </div>
              </div>
            </div>

            <div className="card py-6 px-6 mx-3 my-3">
              <div className="row pb-2">
                <div className="col-md-6 col-sm-6">
                  <label className="fw-bold fs-5">
                    Ý KIẾN CỦA LÃNH ĐẠO PHÒNG
                  </label>
                </div>
                <div className="col-md-6 col-sm-6 text-end">
                  <button
                    className="btn btn-primary ms-3 px-1 py-2 fw-normal"
                    style={{ width: "120px" }}
                    onClick={() => {
                      addRow();
                    }}
                  >
                    Thêm mới
                  </button>
                </div>
              </div>
              {renderTable(noiDungThucHanh)}
            </div>
            <div className="text-center">
              <button
                className="mx-1 btn btn-secondary"
                onClick={() => props?.hide()}
              >
                {" "}
                Đóng
              </button>
              <button className="mx-1 btn btn-primary">Lưu</button>
              <button className="mx-1 btn btn-success">Duyệt</button>
              <button
                className="mx-1 btn btn-info"
                style={{
                  backgroundColor: "#0dcaf0",
                  borderColor: "#0dcaf0",
                }}
              >
                Bổ sung
              </button>
              <button className="mx-1 btn btn-danger">Từ chối</button>
            </div>
          </div>
        </Modal.Body>
      </LocalizationProvider>
    </Modal>
  );
}
