import axios from "axios";
import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";
import LoadingOverlay from "react-loading-overlay";
import { useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import api from "../../../../configs/api";

LoadingOverlay.propTypes = undefined;
export default function ChiTietYeuCauLienKetDuocSi() {
  const navigate = useNavigate();
  const { id } = useParams();

  const [FileAnhChanDung, setFileAnhChanDung] = useState([]);
  const [FileCMNDCCCD, setFileCMNDCCCD] = useState([]);
  const [FileGiayToKhac, setFileGiayToKhac] = useState([]);
  const [FileHinhChupCCHND, setFileHinhChupCCHND] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [DataThongTinChungChi, setDataThongTinChungChi] = useState({});
  const [DataThongTinCungCap, setDataThongTinCungCap] = useState({});
  const [diff, setDiff] = useState([]);

  useEffect(() => {
    if (id) {
      getThongTinYCLK(id);
    }
  }, []);

  const duyetLienKet = async (e) => {
    e.preventDefault();
    await setIsLoading(true);
    axios
      .put(api.API_DUYET_LIEN_KET + "/" + id)
      .then(async ({ data }) => {
        navigate(-1);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const tuChoiLienKet = async (e) => {
    e.preventDefault();
    await setIsLoading(true);
    axios
      .put(api.API_TU_CHOI_LIEN_KET + "/" + id)
      .then(async ({ data }) => {
        navigate(-1);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getThongTinYCLK = async (id) => {
    await setIsLoading(true);
    axios
      .post(api.API_THONG_TIN_CHUNG_CHI_VA_SO_SANH + "/" + id)
      .then(async ({ data }) => {
        const { results } = data;
        setDataThongTinChungChi(results?.thong_tin_chung_chi);
        setDataThongTinCungCap(results?.thong_tin_cung_cap);
        setDiff(results?.diff);
        setFileAnhChanDung(
          results?.thong_tin_cung_cap?.dinh_kem_anh_chan_dung || []
        );
        setFileCMNDCCCD(
          results?.thong_tin_cung_cap?.dinh_kem_xac_nhan_cong_dan || []
        );
        setFileHinhChupCCHND(
          results?.thong_tin_cung_cap?.dinh_kem_chung_chi || []
        );
        setFileGiayToKhac(
          results?.thong_tin_cung_cap?.dinh_kem_xac_nhan_khac || []
        );
      })
      .catch((error) => {
        toast.error("Lấy thông tin chứng chỉ không thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        navigate("/error/404");
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const renderChooseFile = ({ title, name, arrayFileMap, setFile }) => {
    return (
      <div className="col-6 pb-5">
        <div className="row">
          <div className="col-12">{title}</div>
          <div className="col-12">
            {arrayFileMap?.length > 0
              ? arrayFileMap.map((e, i) => {
                  return (
                    <button
                      key={`chooseFile${i}`}
                      className="btn ml-2 mb-2"
                      style={{
                        backgroundColor: "#e8e8e8",
                        padding: "12px 10px 12px 16px",
                        marginRight: "12px",
                      }}
                    >
                      <span>{e?.name}</span>
                    </button>
                  );
                })
              : null}
          </div>
        </div>
      </div>
    );
  };

  const UploadControl = ({
    children,
    value,
    onChange,
    disabled,
    accept,
    name,
  }) => {
    return (
      <label htmlFor={`contained-button-${name}`} className="m-0 w-100">
        <input
          disabled={disabled}
          style={{ display: "none" }}
          id={`contained-button-${name}`}
          multiple
          type="file"
          accept="audio/*|video/*|image/*|MIME_type"
          onChange={(event) => {
            switch (name) {
              case "denghicaplai":
                setFileHinhChupCCHND(Object.values(event.target.files));
                break;
              case "banchinhhoacbansau":
                setFileCMNDCCCD(Object.values(event.target.files));
                break;
              case "anhchandung":
                setFileAnhChanDung(Object.values(event.target.files));
                break;
              case "banchinhdaduoccap":
                setFileGiayToKhac(Object.values(event.target.files));
                break;
              default:
                break;
            }
          }}
        />
        {children}
      </label>
    );
  };

  return (
    <div>
      <LoadingOverlay spinner active={isLoading} text="Đang tải dữ liệu ...">
        <div className="card-box row px-3 py-3 mx-3 my-3">
          <div className="row">
            <div className="col-6">
              <div className="title">
                <h3 className="text-primary">Thông tin cung cấp</h3>
                <h4>
                  Số CC: <span>{DataThongTinCungCap?.so_giay_phep || ""}</span>
                </h4>
              </div>
            </div>
            <div className="col-6">
              <div className="title">
                <h3 className="text-primary">Thông tin chứng chỉ</h3>
                <h4>
                  Số CC:{" "}
                  <span
                    className={
                      diff.includes("so_giay_phep") ? "text-danger" : ""
                    }
                  >
                    {DataThongTinChungChi?.so_giay_phep || ""}
                  </span>
                </h4>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Họ tên: </div>
                <div className="col-9">
                  <span data-bind="">{DataThongTinCungCap?.ten || ""}</span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Họ tên: </div>
                <div className="col-9">
                  <span className={diff.includes("ten") ? "text-danger" : ""}>
                    {DataThongTinChungChi?.ten || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Ngày sinh: </div>
                <div className="col-9">
                  <span className="">
                    {DataThongTinCungCap?.ngay_sinh || ""}
                  </span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Ngày sinh: </div>
                <div className="col-9">
                  <span
                    className={diff.includes("ngay_sinh") ? "text-danger" : ""}
                  >
                    {DataThongTinChungChi?.ngay_sinh || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Giới tính: </div>
                <div className="col-9">
                  <span className="">
                    {DataThongTinCungCap?.gioi_tinh || ""}
                  </span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Giới tính: </div>
                <div className="col-9">
                  <span
                    className={diff.includes("gioi_tinh") ? "text-danger" : ""}
                  >
                    {DataThongTinChungChi?.gioi_tinh || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">CMND/CCCD: </div>
                <div className="col-9">
                  <span>{DataThongTinCungCap?.ma_cong_dan || ""}</span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">CMND/CCCD: </div>
                <div className="col-9">
                  <span
                    className={
                      diff.includes("ma_cong_dan") ? "text-danger" : ""
                    }
                  >
                    {DataThongTinChungChi?.ma_cong_dan || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Ngày cấp: </div>
                <div className="col-9">
                  <span className="">
                    {DataThongTinCungCap?.ngay_cap || ""}
                  </span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Ngày cấp: </div>
                <div className="col-9">
                  <span
                    className={diff.includes("ngay_cap") ? "text-danger" : ""}
                  >
                    {DataThongTinChungChi?.ngay_cap || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Nơi cấp: </div>
                <div className="col-9">
                  <span className="">{DataThongTinCungCap?.noi_cap || ""}</span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Nơi cấp: </div>
                <div className="col-9">
                  <span
                    className={diff.includes("noi_cap") ? "text-danger" : ""}
                  >
                    {DataThongTinChungChi?.noi_cap || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Địa chỉ: </div>
                <div className="col-9">
                  <span className="">{DataThongTinCungCap?.dia_chi || ""}</span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Địa chỉ: </div>
                <div className="col-9">
                  <span
                    className={diff.includes("dia_chi") ? "text-danger" : ""}
                  >
                    {DataThongTinChungChi?.dia_chi || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Văn bằng: </div>
                <div className="col-9">
                  <span className="">
                    {DataThongTinCungCap?.van_bang || ""}
                  </span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Văn bằng: </div>
                <div className="col-9">
                  <span
                    className={diff.includes("van_bang") ? "text-danger" : ""}
                  >
                    {DataThongTinChungChi?.van_bang || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Năm cấp: </div>
                <div className="col-9">
                  <span className="">{DataThongTinCungCap?.nam_cap || ""}</span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Năm cấp: </div>
                <div className="col-9">
                  <span
                    className={diff.includes("nam_cap") ? "text-danger" : ""}
                  >
                    {DataThongTinChungChi?.nam_cap || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">PVHD chuyên môn: </div>
                <div className="col-9">
                  {DataThongTinCungCap?.pvhd_chuyen_mon?.length > 0 &&
                    DataThongTinCungCap?.pvhd_chuyen_mon.map((e, i) => {
                      return (
                        <div key={`pvhd${i}`}>
                          <span className="">{e}</span>
                          <br />
                        </div>
                      );
                    })}
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">PVHD chuyên môn: </div>
                <div className="col-9">
                  {DataThongTinChungChi?.pvhd_chuyen_mon?.length > 0 &&
                    DataThongTinChungChi?.pvhd_chuyen_mon.map((e, i) => {
                      return (
                        <div key={`pvhdCC${i}`}>
                          <span className="">{e}</span>
                          <br />
                        </div>
                      );
                    })}
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Vị trí hành nghề: </div>
                <div className="col-9">
                  {DataThongTinCungCap?.vi_tri_hanh_nghe?.length > 0 &&
                    DataThongTinCungCap?.vi_tri_hanh_nghe.map((e, i) => {
                      return (
                        <div key={`vthn${i}`}>
                          <span className="">{e}</span>
                          <br />
                        </div>
                      );
                    })}
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Vị trí hành nghề: </div>
                <div className="col-9">
                  {DataThongTinChungChi?.vi_tri_hanh_nghe?.length > 0 &&
                    DataThongTinChungChi?.vi_tri_hanh_nghe.map((e, i) => {
                      return (
                        <div key={`vthnCC${i}`}>
                          <span className="">{e}</span>
                          <br />
                        </div>
                      );
                    })}
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Hiệu lực từ: </div>
                <div className="col-9">
                  <span className="">
                    {DataThongTinCungCap?.ngay_hieu_luc || ""}
                  </span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Hiệu lực từ: </div>
                <div className="col-9">
                  <span
                    className={
                      diff.includes("ngay_hieu_luc") ? "text-danger" : ""
                    }
                  >
                    {DataThongTinChungChi?.ngay_hieu_luc || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Cơ quan cấp: </div>
                <div className="col-9">
                  <span className="">
                    {DataThongTinCungCap?.co_quan_cap || ""}
                  </span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Cơ quan cấp: </div>
                <div className="col-9">
                  <span
                    className={
                      diff.includes("co_quan_cap") ? "text-danger" : ""
                    }
                  >
                    {DataThongTinChungChi?.co_quan_cap || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Hình thức: </div>
                <div className="col-9">
                  <span className="">
                    {DataThongTinCungCap?.hinh_thuc_thi || ""}
                  </span>
                </div>
              </div>
            </div>
            <div className="col-6">
              <div className="row">
                <div className="col-3 font-weight-bold">Hình thức: </div>
                <div className="col-9">
                  <span
                    className={
                      diff.includes("hinh_thuc_thi") ? "text-danger" : ""
                    }
                  >
                    {DataThongTinChungChi?.hinh_thuc_thi || ""}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* ĐÍNH KÈM */}
        <div className="row card-box px-3 py-3 mx-3 my-3">
          <div className="col-12 text-title pb-3 fw-bolder">Đính kèm</div>
          {renderChooseFile({
            title: "1. Hình chụp CCHND",
            name: "hinhchupcchnd",
            arrayFileMap: FileHinhChupCCHND,
            setFile: (value) => {
              setFileHinhChupCCHND(value);
            },
          })}
          {renderChooseFile({
            title: "2. Ảnh chân dung",
            name: "anhchandung",
            arrayFileMap: FileAnhChanDung,
            setFile: (value) => {
              setFileAnhChanDung(value);
            },
          })}
          {renderChooseFile({
            title: "3. CMND/CCCD",
            name: "filecmndcccd",
            arrayFileMap: FileCMNDCCCD,
            setFile: (value) => {
              setFileCMNDCCCD(value);
            },
          })}
          {renderChooseFile({
            title: "4. Giấy tờ chứng minh khác",
            name: "giaytokhac",
            arrayFileMap: FileGiayToKhac,
            setFile: (value) => {
              setFileGiayToKhac(value);
            },
          })}
        </div>

        <div className="row w-100">
          <div className="col-12 mt-4 mb-5 text-center">
            <div
              onClick={() => {
                navigate(-1);
              }}
              className="btn btn-secondary text-white px-4 py-2 mx-2 font-weight-bold"
            >
              ĐÓNG
            </div>
            <div
              onClick={(e) => {
                duyetLienKet(e);
              }}
              className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold "
            >
              DUYỆT LIÊN KẾT
            </div>
            <div
              onClick={(e) => {
                tuChoiLienKet(e);
              }}
              className="btn btn-danger text-white px-4 py-2 mx-2 font-weight-bold "
            >
              TỪ CHỐI LIÊN KẾT
            </div>
          </div>
        </div>
      </LoadingOverlay>
    </div>
  );
}
