import axios from "axios";
import { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { conditionalRowStyles, customStyles, paginationOptions } from "../../../../../_metronic/assets/custom/table";
import api from "../../../../configs/api";


export function QuanLyNguoiDung () {

    const [isLoading, setIsLoading] = useState(false);
    const [totalRows, setTotalRows] = useState(0);

    const [nguoiDungList, setNguoiDungList] = useState({});


    useEffect(() => {
        getList();
    }, [])

    const handlePageChange = async (page) => {
        // await setPage(page);
        // getList({ page_number: page });
    };

    const getList = () => {
        axios
            .post(api.API_QUAN_LY_NGUOI_DUNG, {})
            .then(({data}) => {
                if (data) {
                    setNguoiDungList(data?.results)
                    console.log(data?.results);
                }
            })
    }


    const columns = [
        {
            name: "Tên người dùng",
            selector: (row) => <span>{row.ho_ten}</span>,
            grow: 8,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Giới tính",
            selector: (row) => <span>{row.gioi_tinh}</span>,
            grow: 3,
        },
        {
            name: "Số điện thoại",
            selector: (row) => <span>{row.dien_thoai}</span>,
            grow: 2,
        },
        {
            name: "Email",
            selector: (row) => <span>{row.email}</span>,
            grow: 2,
        },
        {
            name: "Trạng thái",
            selector: (row) => <span>{row.active}</span>,
            grow: 2,
        },
    ];

    return (
        <div className="py-3">
            <div className="card-box" style={{padding: "16px"}}>
                <div className="row">
                    <div className="col-12 mb-4">
                        <h4 className="fw-bolder">QUẢN LÝ NGƯỜI DÙNG</h4>
                    </div>

                    <div className="col-12">

                        <DataTable
                            noDataComponent={"Không có dữ liệu ..."}
                            sortServer
                            progressPending={isLoading}
                            columns={columns}
                            data={nguoiDungList}
                            customStyles={customStyles}
                            pagination
                            highlightOnHover
                            pointerOnHover
                            paginationServer
                            paginationTotalRows={totalRows}
                            // onChangeRowsPerPage={handlePerRowsChange}
                            onChangePage={handlePageChange}
                            paginationComponentOptions={paginationOptions}
                            conditionalRowStyles={conditionalRowStyles}
                            // onRowClicked={(data) => {
                            //   detail(data);
                            //   handleRowClicked(data);
                            // }}
                            progressComponent={
                                <div
                                    style={{
                                        padding: "24px",
                                    }}
                                >
                                    <Oval
                                        arialLabel="loading-indicator"
                                        color="#007bff"
                                        height={40}
                                    />
                                </div>
                            }
                        />
                    </div>
                </div>
            </div>
        </div>
    )
}
