import axios from "axios";
import moment from "moment";

import { vi } from "date-fns/locale";
import { useEffect, useState } from "react";

import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { DesktopDatePicker } from "@mui/x-date-pickers/DesktopDatePicker";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { toast } from "react-toastify";
import { KTCard } from "../../../../../_metronic/helpers";
import api from "../../../../configs/api";

export function LichSuChungChi(props) {
  const [isLoading, setIsLoading] = useState(false);

  const [chungChiList, setChungChiList] = useState([]);

  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(10);
  const [totalRows, setTotalRows] = useState(0);

  const empty_json = {};
  const json = {
    nhan_vien_id: props?.userId,
  };

  useEffect(() => {
    getChungChiList({});
  }, []);

  const handlePageChange = (page) => {
    setPage(page);
    getChungChiList({ page_number: page });
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(api.API_DS_CHUNG_CHI + `?page=${page}&per_page=${newPerPage}`, json)
      .then(({ data }) => {
        if (data?.results) {
          setChungChiList(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getChungChiList = ({ page_number = page, size = perPage }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_DS_CHUNG_CHI + `?page=${page_number}&per_page=${size}`,
        json
      )
      .then(async ({ data }) => {
        setChungChiList(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const columns = [
    {
      name: "Ngày thay đổi",
      selector: (row) => (
        <>
          {row?.ngay_thay_doi ? (
            <span>{moment(row?.ngay_thay_doi).format("DD/MM/YYYY")}</span>
          ) : (
            <span></span>
          )}
        </>
      ),
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Số CCHND",
      selector: (row) => <span>{row?.chung_chi_moi}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Hoạt động",
      selector: (row) => <span>{row?.loai_thay_doi}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Nội dung",
      selector: (row) => <span>{row?.noi_dung_thay_doi}</span>,
      grow: 4,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Chuyên viên xử lý",
      selector: (row) => <span>{row?.noi_dung_thay_doi}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
  ];

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };

  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "15px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
      <KTCard className="bg-white py-5 px-5 my-5">
        <div className="row" style={{ padding: "8px 16px 8px 16px" }}>
          <div className="col-md-6 col-sm-6">
            <label className="fw-bold fs-3">LỊCH SỬ CHỨNG CHỈ</label>
          </div>
        </div>

        <DataTable
          noDataComponent={"Không có dữ liệu."}
          sortServer
          progressPending={isLoading}
          columns={columns}
          data={chungChiList}
          customStyles={customStyles}
          pagination
          highlightOnHover
          pointerOnHover
          paginationServer
          paginationTotalRows={totalRows}
          onChangeRowsPerPage={handlePerRowsChange}
          onChangePage={handlePageChange}
          paginationComponentOptions={paginationOptions}
          progressComponent={
            <div
              style={{
                padding: "24px",
              }}
            >
              <Oval
                arialLabel="loading-indicator"
                color="#007bff"
                height={40}
              />
            </div>
          }
        />
      </KTCard>
    </LocalizationProvider>
  );
}
