/* eslint-disable jsx-a11y/anchor-is-valid */
import { FC, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import {
  authActions,
  selectCurrentUser,
  selectRoleUser,
} from "../../../../app/redux-module/auth/authSlice";
import { toAbsoluteUrl } from "../../../helpers";
import * as authHelper from "../../../../app/modules/apps/auth/core/AuthHelpers";
import axios from "axios";
import api from "../../../../app/configs/api";
import { optionsDoiTuong } from "../../../../app/data";

const HeaderUserMenu: FC = () => {
  const dispatch = useDispatch();

  const roleUser = useSelector(selectRoleUser);
  const currentUser = useSelector(selectCurrentUser);

  const [roles, setRoles] = useState<any | undefined>();

  useEffect(() => {
    if (roleUser) {
      setRoles(roleUser);
    }
  }, [roleUser]);

  const unregisterNoti = () => {
    axios
      .post(
        api.API_UNREGISTER_NOTIFICATION,
        {},
        {
          headers: {
            fcm_token: localStorage.getItem("tokenFirebase"),
          },
        }
      )
      .then(async ({ data }) => {
        console.log("data data unregister success", data);
      });
  };

  const getChucVu = (chuc_vu: any) => {
    let chucVu = optionsDoiTuong.find((e: any) => e.value === String(chuc_vu));
    return chucVu?.label;
  };

  return (
    <div
      className="menu menu-sub menu-sub-dropdown menu-column menu-rounded menu-gray-600 menu-state-bg menu-state-primary fw-bold py-4 fs-6 w-300px"
      data-kt-menu="true"
    >
      <div className="menu-item px-3">
        {roles &&
          (roles[0]?.ten_en === "duocsi" ||
            roles[0]?.ten_en === "admin" ||
            roles[0]?.ten_en === "chuyenvien" ||
            roles[0]?.ten_en === "lanhdao" ||
            roles[0]?.ten_en === "chuyenvienhoidong" ||
            roles[0]?.ten_en === "vanthu") ? (
          <div>
            <Link
              to={"/thong-tin-duoc-si"}
              className="menu-content d-flex align-items-center px-3"
            >
              <div className="symbol symbol-50px me-5">
                <img
                  alt="Logo"
                  src={toAbsoluteUrl("/media/avatars/blank.png")}
                />
              </div>

              <div className="d-flex flex-column">
                <div className="fw-bolder d-flex align-items-center fs-5">
                  {currentUser?.ho} {currentUser?.ten}
                </div>
                <span className="fw-bold text-muted text-hover-primary fs-7">
                  {currentUser?.email}
                </span>
              </div>
            </Link>
            <div className="separator my-2"></div>
          </div>
        ) : null}
        {roles && roles[0]?.ten_en === "tochuc" ? (
          <div>
            <Link
              to={"/thong-tin-to-chuc"}
              className="menu-content d-flex align-items-center px-3"
            >
              <div className="symbol symbol-50px me-5">
                <img
                  alt="Logo"
                  src={toAbsoluteUrl("/media/avatars/blank.png")}
                />
              </div>

              <div className="d-flex flex-column">
                <div className="fw-bolder d-flex align-items-center fs-5">
                  {currentUser?.ho} {currentUser?.ten}
                </div>
                <a
                  href="#"
                  className="fw-bold text-muted text-hover-primary fs-7"
                >
                  {currentUser?.email}
                </a>
              </div>
            </Link>
            <div className="separator my-2"></div>
          </div>
        ) : null}
      </div>
      {currentUser?.chuc_vu ? (<div className="menu-item px-5">
        <Link to={""} className="menu-link px-5" onClick={(e) => e.preventDefault()}>
          {/* Vai trò : {currentUser?.assigned_role[0]?.ten} */}
          Chức vụ : {getChucVu(currentUser?.chuc_vu)}
        </Link>
      </div>) : null}


      {/* <div className="menu-item px-5">
        <a href="#" className="menu-link px-5">
          <span className="menu-text">My Projects</span>
          <span className="menu-badge">
            <span className="badge badge-light-danger badge-circle fw-bolder fs-7">
              3
            </span>
          </span>
        </a>
      </div> */}

      {/* <div
        className="menu-item px-5"
        data-kt-menu-trigger="hover"
        data-kt-menu-placement="left-start"
        data-kt-menu-flip="bottom"
      >
        <a href="#" className="menu-link px-5">
          <span className="menu-title">My Subscription</span>
          <span className="menu-arrow"></span>
        </a>

        <div className="menu-sub menu-sub-dropdown w-175px py-4">
          <div className="menu-item px-3">
            <a href="#" className="menu-link px-5">
              Referrals
            </a>
          </div>

          <div className="menu-item px-3">
            <a href="#" className="menu-link px-5">
              Billing
            </a>
          </div>

          <div className="menu-item px-3">
            <a href="#" className="menu-link px-5">
              Payments
            </a>
          </div>

          <div className="menu-item px-3">
            <a href="#" className="menu-link d-flex flex-stack px-5">
              Statements
              <i
                className="fas fa-exclamation-circle ms-2 fs-7"
                data-bs-toggle="tooltip"
                title="View your statements"
              ></i>
            </a>
          </div>

          <div className="separator my-2"></div>

          <div className="menu-item px-3">
            <div className="menu-content px-3">
              <label className="form-check form-switch form-check-custom form-check-solid">
                <input
                  className="form-check-input w-30px h-20px"
                  type="checkbox"
                  value="1"
                  defaultChecked={true}
                  name="notifications"
                />
                <span className="form-check-label text-muted fs-7">
                  Notifications
                </span>
              </label>
            </div>
          </div>
        </div>
      </div> */}

      {/* <div className="menu-item px-5">
        <a href="#" className="menu-link px-5">
          My Statements
        </a>
      </div> */}

      <div className="separator my-2"></div>

      {/* <Languages /> */}
      {roles &&
        (roles[0]?.ten_en === "lanhdao" || roles[0]?.ten_en === "admin") ? (
        <div className="menu-item px-5 my-1">
          <Link to="/quan-ly-nguoi-dung" className="menu-link px-5">
            Quản lý người dùng
          </Link>
        </div>
      ) : null}

      <div className="menu-item px-5 my-1">
        <Link to="/doi-mat-khau" className="menu-link px-5">
          Đổi mật khẩu
        </Link>
      </div>

      <div className="menu-item px-5">
        <a
          onClick={() => {
            unregisterNoti();
            dispatch(authActions.logout());
            // localStorage.clear();
          }}
          className="menu-link px-5"
        >
          Đăng xuất
        </a>
      </div>
    </div>
  );
};

export { HeaderUserMenu };
