import axios from "axios";
import React, { useEffect, useState } from "react";
import { Dropdown, Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { useDispatch } from "react-redux";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { TextField } from "@mui/material";
import { vi } from "date-fns/locale";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { toast } from "react-toastify";
import { KTCard, useDebounce } from "../../../../_metronic/helpers";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";
import { TrangThaiHoSo } from "../../../data";
import { useSelector } from "react-redux";
import { selectRoleUser } from "../../../redux-module/auth/authSlice";
import { enAU } from "date-fns/locale";
import { PopupThongTinHoSoDuocSi } from "../popup/PopupThongTinHoSoDuocSi";
import { arrayIncludes } from "@mui/x-date-pickers/internals/utils/utils";

export function DanhSachHoSoChoIn() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const roleUser = useSelector(selectRoleUser);

  const [form, setForm] = useState({});

  const [listHoSoThuLy, setListHoSoThuLy] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [roles, setRoles] = useState({});

  //open modal chi tiet
  const [chitiet, setChiTiet] = useState(false);
  const showModalChiTiet = () => setChiTiet(true);
  const closeModalChiTiet = () => setChiTiet(false);

  const [rowData, setRowData] = useState();
  const [searchMaHoSo, setSearchMaHoSo] = useState("");
  const [searchHoTen, setSearchHoTen] = useState("");
  const [dateHanDen, setDateHanDen] = useState(new Date());
  const [dateHanTu, setDateHanTu] = useState(new Date());
  const [dateNopDen, setDateNopDen] = useState(new Date());
  const [dateNopTu, setDateNopTu] = useState(new Date());
  const [ListDMHetHan, setListDMHetHan] = useState([]);
  const [ListDMThuTuc, setListDMThuTuc] = useState([]);
  const [valueThuTuc, setValueThuTuc] = useState({
    label: "Tất cả",
    value: "",
  });

  const [valueTrangThai, setValueTrangThai] = useState({
    label: "Đang Trình HĐTV Duyệt",
    value: "9",
  });
  // const [valueTrangThai, setValueTrangThai] = useState({
  //   label: "Tất cả",
  //   value: "",
  // });
  const [valueTrangThaiHetHan, setValueTrangThaiHetHan] = useState({
    label: "Tất cả",
    value: "",
  });
  const [ListDMTrangThaiHoSo, setListDMTrangThaiHoSo] = useState([]);
  const [selectedRows, setSelectedRows] = useState([]);
  // const [selectedRows, setSelectedRows] = React.useState(false);
  const [toggledClearRows, setToggleClearRows] = React.useState(false);

  const handleChange = ({ selectedRows }) => {
    setSelectedRows(selectedRows);
    console.log("selectedRows", selectedRows);
  };

  // Toggle the state so React Data Table changes to clearSelectedRows are triggered
  const handleClearRows = () => {
    setToggleClearRows(!toggledClearRows);
  };

  const debouncedSearchMaHoSo = useDebounce(searchMaHoSo, 1000);
  const debouncedSearchHoTen = useDebounce(searchHoTen, 1000);

  useEffect(
    () => {
      getListDM();
    },
    [] // Only call effect if debounced search term changes
  );
  useEffect(() => {
    if (roleUser) {
      setRoles(roleUser[0]?.ten_en);
    }
  }, [roleUser]);

  useEffect(
    () => {
      if (
        (debouncedSearchMaHoSo !== undefined && searchMaHoSo !== undefined) ||
        (debouncedSearchHoTen !== undefined && searchMaHoSo !== undefined)
      ) {
        getList({
          page_number: 1,
          ma_ho_so: debouncedSearchMaHoSo,
          ho_ten: debouncedSearchHoTen,
        });
      }
    },
    [debouncedSearchMaHoSo, debouncedSearchHoTen] // Only call effect if debounced search term changes
  );
  // console.log(roles);
  // useEffect(() => {
  //   switch (roles) {
  //     case "chuyenvienhoidong":
  //       setValueTrangThai({
  //         label: "Đang Trình HĐTV Duyệt",
  //         value: "9",
  //       });
  //       getList({ trang_thai: "9" });
  //       break;
  //     case "lanhdao":
  //       setValueTrangThai({
  //         label: "Chờ Lãnh Đạo Phê Duyệt",
  //         value: "6",
  //       });
  //       getList({ trang_thai: "6" });
  //       break;
  //     case "vanthu":
  //       setValueTrangThai({
  //         label: "Đang Chờ Cấp Số",
  //         value: "10",
  //       });
  //       getList({ trang_thai: "10" });
  //       break;

  //     default:
  //       break;
  //   }
  // }, []);

  const getListDM = () => {
    axios
      .get(api.API_DANH_MUC_HO_SO_THU_LY_DUOC_SI)
      .then(async ({ data }) => {
        let { het_hans } = data?.results;
        let { thu_tuc } = data?.results;
        let { trang_thai_ho_so } = data?.results;
        het_hans.unshift({
          label: "Tất cả",
          value: "",
        });
        thu_tuc.unshift({
          label: "Tất cả",
          value: "",
        });
        trang_thai_ho_so.unshift({
          label: "Tất cả",
          value: "",
        });
        setListDMHetHan(het_hans);
        setListDMThuTuc(thu_tuc);
        setListDMTrangThaiHoSo(trang_thai_ho_so);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const getList = ({
    page_number = page,
    size = perPage,
    ma_ho_so = searchMaHoSo,
    ho_ten = searchHoTen,
    // nop_tu = String(new Date(dateNopTu).getTime()),
    // nop_den = String(new Date(dateNopDen).getTime()),
    // han_tu = String(new Date(dateHanTu).getTime()),
    // han_den = String(new Date(dateHanDen).getTime()),
    nop_tu = "",
    nop_den = "",
    han_tu = "",
    han_den = "",
    thu_tuc_id = valueThuTuc?.value || "",
    trang_thai = valueTrangThai?.value || "",
    trang_thai_het_han = valueTrangThaiHetHan?.value || "",
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_LIST_HO_SO_THU_LY_DUOC_SI +
          `?page=${page_number}&per_page=${size}`,
        {
          ma_ho_so,
          ho_ten,
          nop_tu,
          nop_den,
          han_tu,
          han_den,
          thu_tuc_id,
          trang_thai,
          trang_thai_het_han,
        }
      )
      .then(async ({ data }) => {
        setListHoSoThuLy(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  // handle api

  const resetState = async () => {
    await setSearchHoTen("");
    await setSearchMaHoSo("");
    await setDateHanDen(null);
    await setDateHanTu(null);
    await setDateNopDen(null);
    await setDateNopTu(null);
    await setValueThuTuc({
      label: "Tất cả",
      value: "",
    });
    await setValueTrangThai({
      label: "Tất cả",
      value: "",
    });
    await setValueTrangThaiHetHan({
      label: "Tất cả",
      value: "",
    });
  };

  // DETAIL
  // const detail = async (data) => {
  //   setTrang_thai(data?.trang_thai);
  //   setShowUpdate(true);
  // };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${page}&per_page=${newPerPage}`,
        {}
      )
      .then(({ data }) => {
        if (data?.results) {
          setListHoSoThuLy(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const changeStatus = async (valuebuton) => {
    setIsLoading(true);
    const {
      ids,
      trang_thai_ho_so,
      ly_do_tu_choi,
      so_quyet_dinh,
      ngay_quyet_dinh,
    } = form;
    console.log("selectedRows selectedRows", selectedRows);
    const arrayIdChecked = selectedRows.map((e) => e.id);
    console.log("arrayIdChecked", arrayIdChecked);
    const json = {
      ids: arrayIdChecked,
      trang_thai_ho_so: valuebuton,
      ly_do_tu_choi: "",
      so_quyet_dinh: "",
      ngay_quyet_dinh: "",
    };
    if (arrayIdChecked.length === 0) {
      await toast.error("Chưa có hồ sơ nào được chọn", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "success",
      });
      setIsLoading(false);
      return;
    }
    await axios
      .put(api.API_DUYET_MULTI_YEU_CAU_CHUNG_CHI, json)
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({});
        setIsLoading(false);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setSelectedRows([]);
        setTimeout(() => {
          setIsLoading(false);
        }, 1000);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  const handleRowClicked = (row) => {};

  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );

  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const btnDuyet = {
    fontWeight: "500",
    backgroundColor: "#1abc9c",
    color: "white",
    borderRadius: "3px",
  };
  const btnBoSung = {
    fontWeight: "500",
    backgroundColor: "#f5a51a",
    color: "white",
    borderRadius: "3px",
  };
  const btnTuChoi = {
    fontWeight: "500",
    backgroundColor: "#1ab6d0",
    color: "white",
    borderRadius: "3px",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Mã hồ sơ",
      selector: (row) => <span>{row.ma_ho_so}</span>,
      grow: 1,
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Họ tên",
      selector: (row) => <span>{row.ten_nguoi_yeu_cau}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Số điện thoại",
      selector: (row) => <span>{row?.so_dien_thoai}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Thủ tục",
      selector: (row) => <span>{row.ten_thu_tuc}</span>,
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Ngày nộp",
      selector: (row) => <span>{row.thoi_gian_nop_ho_so}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Ngày hết hạn",
      selector: (row) => <span>{row.thoi_gian_nop_ho_so}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trạng thái",
      selector: (row) => <span>{checkStatus(row?.trang_thai_ho_so)}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      grow: 1.5,
      cell: (row) => (
        <div className="container my-3">
          <button
            onClick={() => {
              console.log("row danh sách cấp số", row);
              // showModalChiTiet();
              // setRowData(row);
              // dispatch(commonActions.setDoiTuongAll(row?.doi_tuong));
              if (
                roles === "chuyenvienhoidong" ||
                roles === "lanhdao" ||
                roles === "vanthu"
              ) {
                navigate(
                  `/admin/danhsach-hoso-thuly-duocsi/duthaocapphep/${row?.id}`
                );
