import axios from "axios";
import { useState } from "react";
import { Form } from "react-bootstrap";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import { Link, useNavigate } from "react-router-dom";

export function DangKy() {
  const navigate = useNavigate();

  const initialState = {
    dien_thoai: "",
    tai_khoan: "",
    mat_khau: "",
    retype: "",
  };

  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const [activeToChuc, setActiveToChuc] = useState(false);
  const [resetfield, setResetfield] = useState(initialState);
  const [vaitro, setVaitro] = useState("96e2a4d8-6e15-4309-a91d-17ab26533552");

  const toggleClassds = () => {
    setActiveToChuc(false);
    setVaitro("96e2a4d8-6e15-4309-a91d-17ab26533552");
  };
  const toggleClasstc = () => {
    setActiveToChuc(true);
    setVaitro("f9e88007-a940-483c-bb64-4e60379d25fb");
  };


  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {
    const { tai_khoan, mat_khau, dien_thoai, retype } = form;
    const newErrors = {};
    // name errors
    if (!tai_khoan || tai_khoan === "")
      newErrors.tai_khoan = "Tài khoản không được bỏ trống!";
    if (!mat_khau || mat_khau === "")
      newErrors.mat_khau = "Mật khẩu được bỏ trống!";
    if (!dien_thoai || dien_thoai === "")
      newErrors.dien_thoai = "Điện thoại không được bỏ trống!";
    if (!retype || retype === "")
      newErrors.retype = "Xác nhận mật khẩu không được bỏ trống!";
    if (mat_khau !== retype) newErrors.retype = "Mật khẩu không trùng";

    return newErrors;
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      setErrors(newErrors);
    } else {
      register(e);
    }
  };
  const register = async () => {
    const { tai_khoan, mat_khau, dien_thoai } = form;
    const json = {
      tai_khoan,
      mat_khau,
      dien_thoai,
      vai_tro_id: vaitro,
    };
    await axios
      .post(api.API_REGISTER, json)
      .then(async ({ data }) => {
        toast.success("Tạo tài khoản thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        setResetfield(initialState);
        navigate(`/dang-nhap`);
      })
      .catch((error) => {
        console.log("error", error?.response);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
      });
  };
  return (
    <>
      <div id="contaner-template">
        <div className="pt-10 pb-5 min-vh-100 pagee">
          <div className="row justify-content-center">
            <div className="box-login-wrapper col-sm-7 col-lg-6 col-xl-4">
              <div className="box-login-wrapper card border-0 position-relative">
                <Link to={"/dang-nhap"}>
                  <i className="fas fa-times-circle position-absolute icon-close"></i>
                </Link>
                <div className="card-body w-100 w-md-75 p-3 m-auto ">
                  <div className="title-text text-center">
                    ĐĂNG KÝ TÀI KHOẢN
                  </div>
                  <Form action="#" className="box-login-wrapper">
                    <div className="form-group my-2">
                      <div className="text-type-select text-center mb-2">
                        Chọn loại tài khoản
                      </div>
                      <div className="type-list d-flex justify-content-around">
                        <div
                          className={
                            activeToChuc
                              ? "type-item text-center type-no-select"
                              : "type-item text-center type-selected"
                          }
                          onClick={toggleClassds}
                        >
                          <label>
                            <i className="fas fa-user-nurse"></i>
                            <span className="text-type-item pt-2">Cá nhân</span>
                          </label>
                          <input type="radio" name="role" />
                        </div>
                        <div
                          className={
                            !activeToChuc
                              ? "type-item text-center type-no-select"
                              : "type-item text-center type-selected"
                          }
                          onClick={toggleClasstc}
                        >
                          <label>
                            <i className="fas fa-hospital-alt "></i>
                            <span className="text-type-item pt-2">Tổ chức</span>
                          </label>
                          <input type="radio" name="role" />
                        </div>
                      </div>
                    </div>
                    <div className="form-group mt-10 mb-6 input-icon">
                      <label className="label-input fw-bold">
                        Nhập số điện thoại
                      </label>
                      <Form.Control
                        type="text"
                        placeholder="Nhập số điện thoại"
                        className="input-register input-login"
                        style={{ fontSize: 14, fontWeight: 400 }}
                        value={form.dien_thoai === null ? "" : form.dien_thoai}
                        onChange={(e) => {
                          setField("dien_thoai", e.target.value);
                        }}
                        isInvalid={!!errors.dien_thoai}
                      />
                      {/* <i className="fa fa-user"></i> */}

                      <Form.Control.Feedback type="invalid">
                        {errors.dien_thoai}
                      </Form.Control.Feedback>
                    </div>
                    <div className="form-group mb-6">
                      <label className="label-input fw-bold">Tài Khoản</label>
                      <div className="input-group input-icon">
                        <Form.Control
                          type="text"
                          placeholder="Nhập tài khoản"
                          className="input-register"
                          style={{ fontSize: 14, fontWeight: 400 }}
                          value={form.tai_khoan === null ? "" : form.tai_khoan}
                          onChange={(e) => {
                            setField("tai_khoan", e.target.value);
                          }}
                          isInvalid={!!errors.tai_khoan}
                        />
                        {/* <i className="fa fa-user"></i> */}
                        <Form.Control.Feedback type="invalid">
                          {errors.tai_khoan}
                        </Form.Control.Feedback>
                      </div>
                    </div>

                    <div className="form-group mb-6">
                      <label className="label-input fw-bold">Mật khẩu</label>
                      <div className="input-group input-icon">
                        <Form.Control
                          type="password"
                          placeholder="Nhập mật khẩu"
                          className="input-register"
                          style={{ fontSize: 14, fontWeight: 400 }}
                          value={form.mat_khau === null ? "" : form.mat_khau}
                          onChange={(e) => {
                            setField("mat_khau", e.target.value);
                          }}
                          isInvalid={!!errors.mat_khau}
                        />
                        {/* <i className="fa fa-key"></i> */}

                        <Form.Control.Feedback type="invalid">
                          {errors.mat_khau}
                        </Form.Control.Feedback>
                      </div>
                    </div>

                    <div className="form-group mb-10">
                      <label className="label-input fw-bold">
                        Xác nhận mật khẩu
                      </label>
                      <div className="input-group input-icon">
                        <Form.Control
                          type="password"
                          placeholder="Nhập lại mật khẩu"
                          className="input-register"
                          style={{ fontSize: 14, fontWeight: 400 }}
                          onChange={(e) => {
                            setField("retype", e.target.value);
                          }}
                          isInvalid={!!errors.retype}
                        />
                        {/* <i className="fa fa-key"></i> */}

                        <Form.Control.Feedback type="invalid">
                          {errors.retype}
                        </Form.Control.Feedback>
                      </div>
                    </div>
                    <div className="form-group my-2 text-center">
                      <button
                        className="btn btn-primary btn-block btn-login w-100 mb-8"
                        type="button"
                        id="btn-register"
                        onClick={async (e) => {
                          handleSubmit(e);
                        }}
                      >
                        <i className="fas fa-chevron-circle-right"></i> Đăng ký
                      </button>
                    </div>
                  </Form>
                </div>
              </div>
            </div>
          </div>
        </div>
        <footer className="footer footer-alt text-footer">
          SỞ Y TẾ TP HỒ CHÍ MINH
        </footer>
      </div>
    </>
  );
}
