import { enAU } from "date-fns/locale";
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";

import axios from "axios";
import { useEffect, useState } from "react";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../../configs/api";
import { TextField } from "@mui/material";
import { Oval } from "react-loader-spinner";
import DataTable from "react-data-table-component";
import { Button, Dropdown, Form, Modal } from "react-bootstrap";

export function ChungNhanThucHanh(props) {

    const [isAdd, setIsAdd] = useState(true);
    const [chungNhanList, setChungNhanList] = useState([]);

    const [page, setPage] = useState(1);
    const [perPage, setPerPage] = useState(10);
    const [totalRows, setTotalRows] = useState(0);
    const [isLoading, setIsLoading] = useState(false);
    const [idSelected, setIdSelected] = useState("");
    const [idDelete, setIdDelete] = useState("");


    const [form, setForm] = useState({});
    const [errors, setErrors] = useState({});

    const [selectedTT, setSelectedTT] = useState();
    const [selectedQH, setSelectedQH] = useState();
    const [selectedPX, setSelectedPX] = useState();

    const [tinhThanhList, setTinhThanhList] = useState();
    const [quanHuyenList, setQuanHuyenList] = useState();
    const [phuongXaList, setPhuongXaList] = useState();
    const [coSoThucHanhList, setCoSoThucHanhList] = useState({});
    const [noiDungThucHanhList, setNoiDungThucHanhList] = useState();

    const [fileDinhKem, setFileDinhKem] = useState([]);
    const [deleteDinhKem, setDeleteDinhKem] = useState([]);

    const [visibleDelete, setvisibleDelete] = useState(false);
    const [visibleInstDelete, setvisibleIsntDelete] = useState(false);
    const showModal = () => setvisibleIsntDelete(true);
    const closeModal = () => setvisibleIsntDelete(false);
    const showModalDelete = () => setvisibleDelete(true);
    const closeModalDelete = () => setvisibleDelete(false);

    const [onHide, setOnHide] = useState(false);

    useEffect(() => {
        getListTinhThanh();
    }, [])

    const resetState = async () => {
        setForm({});
        setErrors({});
        closeModal();
    };

    const getListTinhThanh = () => {
        axios
            .get(api.API_TINH_THANH)
            .then(async ({ data }) => {
                const result = data?.results;
                result.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setTinhThanhList(result);
            })
            .catch((error) => {
                toast.error("Không thể lấy thông tin danh sách tỉnh thành", {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "error",
                });
            });
    };
    const getListQuanHuyen = (id) => {
        axios
            .get(api.API_QUAN_HUYEN + "/" + id)
            .then(async ({ data }) => {
                const result = data?.results;
                result.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setQuanHuyenList(result);
            })
            .catch((error) => {
                toast.error("Không thể lấy thông tin danh sách quận huyện", {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "error",
                });
            });
    };
    const getListPhuongXa = (id) => {
        axios
            .get(api.API_XA_PHUONG + "/" + id)
            .then(async ({ data }) => {
                const result = data?.results;
                result.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setPhuongXaList(result);
            })
            .catch((error) => {
                toast.error("Không thể lấy thông tin danh sách phường xã", {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "error",
                });
            });
    };

    const handlePageChange = (page) => {
        setPage(page);
        // getChungNhanList({ page_number: page });
    };

    const handlePerRowsChange = async (newPerPage, page) => {
        await setIsLoading(true);
        axios
            .get(api.API_DS_BANG_CAP + `?page=${page}&per_page=${newPerPage}`)
            .then(({ data }) => {
                if (data?.results) {
                    setChungNhanList(data?.results);
                    setPerPage(newPerPage);
                }
            })
            .catch(() => { })
            .finally(() => {
                setIsLoading(false);
            });
    };

    const renderChooseFile = ({ title, name, arrayFileMap, setFile }) => {
        return (
            <div className="col-12">
                <div className="row">
                    <div className="col-12">
                        <button className="btn btn-success btn-sm my-3">
                            <UploadControl name={name}>Thêm chứng từ đính kèm</UploadControl>
                        </button>
                    </div>
                    <div className="col-12">
                        {arrayFileMap?.length > 0
                            ? arrayFileMap.map((e, i) => {
                                return (
                                    <button
                                        key={i}
                                        className="btn ml-2 mb-2"
                                        style={{
                                            backgroundColor: "#e8e8e8",
                                            padding: "12px 10px 12px 16px",
                                            marginRight: "12px",
                                        }}
                                    >
                                        <span>{e?.name}</span>
                                        <i
                                            style={{
                                                display: "inline",
                                                color: "red",
                                                marginLeft: "8px",
                                            }}
                                            onClick={() => {
                                                let arrFile = arrayFileMap;
                                                arrFile = arrFile.filter(
                                                    (item) => item.name !== e?.name
                                                );
                                                setFile(arrFile);
                                                deleteDinhKem.push(e?.url);
                                            }}
                                            className="fas fa-times-circle"
                                        ></i>
                                    </button>
                                );
                            })
                            : null}
                    </div>
                </div>
            </div>
        );
    };

    const UploadControl = ({
        children,
        value,
        onChange,
        disabled,
        accept,
        name,
    }) => {
        return (
            <label htmlFor={`contained-button-${name}`} className="m-0 w-100">
                <input
                    disabled={disabled}
                    style={{ display: "none" }}
                    id={`contained-button-${name}`}
                    // multiple
                    type="file"
                    accept="audio/*|video/*|image/*|MIME_type"
                    onChange={async (event) => {
                        await setFileDinhKem([...fileDinhKem, event.target.files[0]]);
                        findFormErrors();
                    }}
                />

                {children}
            </label>
        );
    };

    const setField = (field, value) => {
        setForm({
            ...form,
            [field]: value,
        });
        if (!!errors[field])
            setErrors({
                ...errors,
                [field]: null,
            });
    };

    const findFormErrors = () => {

    }






    const paginationOptions = {
        rowsPerPageText: "Dòng hiển thị",
        rangeSeparatorText: "trên",
    };

    const customStyles = {
        headRow: {
            style: {
                borderTopStyle: "solid",
                borderTopWidth: "1px",
                borderTopColor: "#e0e0e0",
                borderBottomColor: "#e0e0e0",
            },
        },
        headCells: {
            style: {
                color: "#202124",
                fontSize: "14px",
            },
        },
        rows: {
            highlightOnHoverStyle: {
                backgroundColor: "rgb(230, 244, 244)",
                borderBottomColor: "#FFFFFF",
                outline: "1px solid #FFFFFF",
            },
        },
        pagination: {
            style: {
                border: "none",
            },
        },
    };

    const columns = [
        {
            name: "Nơi tốt nghiệp",
            selector: (row) => <span>{row?.noi_tot_nghiep}</span>,
            grow: 4,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Văn bằng chuyên môn",
            selector: (row) => <span>{row?.van_bang_chuyen_mon}</span>,
            grow: 3,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Số hiệu",
            selector: (row) => <span>{row?.so_hieu}</span>,
            grow: onHide ? 3 : 2,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: "Đính kèm",
            selector: (row) => (
                <>
                    {row?.chung_tu_dinh_kem.length > 0 ? (
                        <>
                            {row?.chung_tu_dinh_kem.map((item, index) => (
                                <div key={index}>
                                    <a
                                        key={index}
                                        target="_blank"
                                        rel="noreferrer"
                                        href={api.API_URL + item?.url}
                                        className="mt-1"
                                        style={{ display: "inline-block" }}
                                    >
                                        {item?.name}
                                    </a>
                                    <br />
                                </div>
                            ))}
                        </>
                    ) : (
                        <span> </span>
                    )}
                </>
            ),
            grow: 5,
            style: {
                cursor: "pointer",
                color: "#202124",
            },
        },
        {
            name: " ",
            grow: onHide ? 1 : 0,
            cell: (row) =>
                onHide ? null : (
                    <div className="container my-3">
                        <Dropdown>
                            <Dropdown.Toggle variant="link" style={{ float: "right" }}>
                                <i
                                    className="fas fa-eye"
                                    style={{ marginLeft: "3px", marginRight: "-3px" }}
                                ></i>
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                                <Dropdown.Item
                                    onClick={() => {
                                        setIsAdd(false);
                                        showModal();
                                        // getDetailBangCap(row);
                                    }}
                                >
                                    <i className="fas fa-chevron-right text-primary me-3"></i>Xem
                                    chi tiết
                                </Dropdown.Item>
                                <Dropdown.Item
                                    onClick={() => {
                                        showModalDelete();
                                        setIdDelete(row.id);
                                    }}
                                >
                                    <i className="fas fa-trash text-danger me-3"></i>Xóa
                                </Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </div>
                ),
        },
    ];


    return (
        <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
            <div className="bg-white card-box py-6 px-6 mx-3 my-3">
                <div className="row pb-5">
                    <div className="col-md-6 col-sm-6">
                        <label className="fw-bold fs-5">THÔNG TIN GIẤY CHỨNG NHẬN</label>
                    </div>
                    {props?.onHide ? null : (
                        <div
                            className="col-md-6 col-sm-6 d-flex justify-content-end"
                            data-kt-user-table-toolbar="base"
                        >
                            <button
                                type="button"
                                className="btn btn-sm btn-primary d-flex"
                                style={{ alignItems: "center" }}
                                onClick={async () => {
                                    resetState();
                                    setIsAdd(true);
                                    showModal();
                                }}
                            >
                                <i className="fas fa-plus me-1"></i>
                                Thêm
                            </button>
                        </div>
                    )}
                </div>

                <DataTable
                    noDataComponent={"Không có dữ liệu."}
                    sortServer
                    progressPending={isLoading}
                    columns={columns}
                    // data={bangCapList}
                    customStyles={customStyles}
                    pagination
                    highlightOnHover
                    pointerOnHover
                    paginationServer
                    paginationTotalRows={totalRows}
                    onChangeRowsPerPage={handlePerRowsChange}
                    onChangePage={handlePageChange}
                    paginationComponentOptions={paginationOptions}
                    progressComponent={
                        <div
                            style={{
                                padding: "24px",
                            }}
                        >
                            <Oval
                                arialLabel="loading-indicator"
                                color="#007bff"
                                height={40}
                            />
                        </div>
                    }
                />

                {/* Infor Modal */}
                <Modal
                    size="lg"
                    show={visibleInstDelete}
                    onHide={() => {
                        closeModal();
                    }}
                >
                    <Modal.Body>
                        <div className="row mt-3 mb-5">
                            <div className="col-12 text-center">
                                <span className="fs-1 fw-bold">
                                    {/* {isAdd ? "Thêm mới bằng cấp" : "Thông tin bằng cấp"} */}
                                    Thông tin giấy chứng nhận
                                </span>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-6 mb-4">
                                <span className="required fw-bold">Số chứng nhận</span>
                                <Form.Control
                                    type="text"
                                    className="mt-2"
                                    placeholder="Nhập số chứng nhận"
                                    style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                    onChange={(e) => {
                                        setField("so_chung_nhan", e.target.value);
                                    }}
                                    value={form?.so_chung_nhan != null ? form?.so_chung_nhan : ""}
                                />
                                <span className="text-danger">
                                    {errors?.so_chung_nhan ? (
                                        <span>{errors?.so_chung_nhan}</span>
                                    ) : (
                                        ""
                                    )}
                                </span>
                            </div>
                            <div className="col-6 mb-4">
                                <span className="fw-bold">Ngày kiểm tra</span>
                                <DesktopDatePicker
                                    label=" "
                                    inputFormat="dd/MM/yyyy"
                                    // value={ngayCap}
                                    // onChange={handleNgayCap}
                                    maxDate={new Date()}
                                    renderInput={(params) => <TextField {...params} />}
                                    isInvalid={!!errors?.ngay_cap}
                                />
                                <span className="text-danger">
                                    {errors?.noi_tot_nghiep_id ? (
                                        <span>{errors?.noi_tot_nghiep_id}</span>
                                    ) : (
                                        ""
                                    )}
                                </span>
                            </div>
                            <div className="col-4 mb-4">
                                <span className="fw-bold">Ngày cấp</span>
                                <DesktopDatePicker
                                    label=" "
                                    inputFormat="dd/MM/yyyy"
                                    // value={ngayCap}
                                    // onChange={handleNgayCap}
                                    maxDate={new Date()}
                                    renderInput={(params) => <TextField {...params} />}
                                    isInvalid={!!errors?.ngay_cap}
                                />
                                <span className="text-danger">
                                    {errors?.noi_tot_nghiep_id ? (
                                        <span>{errors?.noi_tot_nghiep_id}</span>
                                    ) : (
                                        ""
                                    )}
                                </span>
                            </div>
                            <div className="col-4 mb-4">
                                <span className="fw-bold">Ngày hiệu lực</span>
                                <DesktopDatePicker
                                    label=" "
                                    inputFormat="dd/MM/yyyy"
                                    // value={ngayCap}
                                    // onChange={handleNgayCap}
                                    maxDate={new Date()}
                                    renderInput={(params) => <TextField {...params} />}
                                    isInvalid={!!errors?.ngay_cap}
                                />
                                <span className="text-danger">
                                    {errors?.noi_tot_nghiep_id ? (
                                        <span>{errors?.noi_tot_nghiep_id}</span>
                                    ) : (
                                        ""
                                    )}
                                </span>
                            </div>
                            <div className="col-4 mb-4">
                                <span className="fw-bold">Ngày hết hạn</span>
                                <DesktopDatePicker
                                    label=" "
                                    inputFormat="dd/MM/yyyy"
                                    // value={ngayCap}
                                    // onChange={handleNgayCap}
                                    maxDate={new Date()}
                                    renderInput={(params) => <TextField {...params} />}
                                    isInvalid={!!errors?.ngay_cap}
                                />
                                <span className="text-danger">
                                    {errors?.noi_tot_nghiep_id ? (
                                        <span>{errors?.noi_tot_nghiep_id}</span>
                                    ) : (
                                        ""
                                    )}
                                </span>
                            </div>

                            <div className="col-3 mb-4">
                                <span className="fw-bold">Tỉnh/TP</span>
                                <Select
                                    key={"id"}
                                    className="mt-2"
                                    value={selectedTT}
                                    options={tinhThanhList}
                                    style={{ height: "44px" }}
                                    placeholder="Chọn thành phố"
                                    onChange={(e) => {
                                        setSelectedTT(e);
                                        setSelectedQH(null);
                                        setSelectedPX(null);
                                        setField("tinh_thanh_id", e.value);
                                        getListQuanHuyen(e.value);
                                    }}
                                />
                            </div>
                            <div className="col-3 mb-4">
                                <span className="fw-bold">Quận/Huyện</span>
                                <Select
                                    key={"id"}
                                    className="mt-2"
                                    value={selectedQH}
                                    options={quanHuyenList}
                                    style={{ height: "44px" }}
                                    placeholder="Chọn quận huyện"
                                    onChange={(e) => {
                                        setSelectedQH(e);
                                        setSelectedPX(null);
                                        setField("quan_huyen_id", e.value);
                                        getListPhuongXa(e.value);
                                    }}
                                    isDisabled={selectedTT ? false : true}
                                />
                            </div>
                            <div className="col-3 mb-4">
                                <span className="fw-bold">Phường/Xã</span>
                                <Select
                                    key={"id"}
                                    className="mt-2"
                                    value={selectedPX}
                                    options={phuongXaList}
                                    style={{ height: "44px" }}
                                    placeholder="Chọn phường xã"
                                    onChange={(e) => {
                                        setSelectedPX(e);
                                        setField("xa_phuong_id", e.value);
                                    }}
                                    isDisabled={selectedQH ? false : true}
                                />
                            </div>
                            <div className="col-3 mb-4">
                                <span className="fw-bold">Địa chỉ</span>
                                <Form.Control
                                    type="text"
                                    className="mt-2"
                                    placeholder="Nhập số nhà"
                                    style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                    onChange={(e) => {
                                        setField("so_nha", e.target.value);
                                    }}
                                    value={form?.so_nha != null ? form?.so_nha : ""}
                                />
                            </div>
                        </div>
                    </Modal.Body>
                    <Modal.Footer style={{ width: "100%" }}>
                        <div className="row w-100">
                            <div className="col-12">
                                <span className="required fs-2 fw-bold">
                                    Danh sách giấy chứng nhận:
                                </span>

                                {renderChooseFile({
                                    arrayFileMap: fileDinhKem,
                                    setFile: (value) => {
                                        setFileDinhKem(value);
                                    },
                                })}
                                <span className="text-danger">
                                    {errors?.fileDinhKem
                                        ? "Vui lòng chọn chứng từ đính kèm!"
                                        : ""}
                                </span>
                            </div>
                            <div className="col-12 text-center mt-5">
                                <Button
                                    variant="secondary"
                                    style={{ marginRight: 10 }}
                                    onClick={() => {
                                        closeModal();
                                    }}
                                >
                                    Quay lại
                                </Button>
                                <Button
                                    variant="primary"
                                    onClick={(e) => {
                                        // onSubmit(e);
                                        // closeModal()
                                    }}
                                >
                                    Đồng ý
                                </Button>
                            </div>
                        </div>
                    </Modal.Footer>
                </Modal>

                {/* Delete Modal */}
                <Modal
                    centered
                    show={visibleDelete}
                    onHide={() => {
                        closeModalDelete();
                    }}
                >
                    <Modal.Header className="bg-blue2">
                        <Modal.Title className="text-white" style={{ fontSize: "22px" }}>
                            Bạn có chắc chắn muốn xóa bằng cấp này ?
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body
                        className="row d-flex"
                        style={{ justifyContent: "center" }}
                    >
                        <img
                            width={350}
                            height={175}
                            className="mb-5"
                            src="/media/assets/deleteTrash.gif"
                            alt=""
                        />
                        <div className="col-12 text-center mt-5">
                            <Button
                                variant="secondary"
                                style={{ marginRight: 10 }}
                                onClick={() => {
                                    closeModalDelete();
                                }}
                            >
                                Quay lại
                            </Button>
                            <Button
                                variant="primary"
                                onClick={() => {
                                    // deleteBangCap();
                                    closeModalDelete();
                                }}
                            >
                                Đồng ý
                            </Button>
                        </div>
                    </Modal.Body>
                    {/* <Modal.Footer>

                </Modal.Footer> */}
                </Modal>
            </div>
        </LocalizationProvider>
    );
}
