import { vi } from "date-fns/locale";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import axios from "axios";
import moment from "moment";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { TextField } from "@mui/material";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { toast } from "react-toastify";
import { KTCard, useDebounce } from "../../../../_metronic/helpers";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";
import { TrangThaiHoSo } from "../../../data";
import PopupCancel from "../popup/PopupCancel";
import { useSelector } from "react-redux";
import { selectRoleUser } from "../../../redux-module/auth/authSlice";

export function DanhSachHoSoThuLyDuocSi() {
  const navigate = useNavigate();
  const roleUser = useSelector(selectRoleUser);

  const [listHoSoThuLy, setListHoSoThuLy] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [trang_thai, setTrang_thai] = useState(true);

  //open modal Cancel
  const [isvisibleCancel, setIsvisibleCancel] = useState(false);
  const closeModalCancel = () => setIsvisibleCancel(false);
  const showModalCancel = () => setIsvisibleCancel(true);

  const [roles, setRoles] = useState({});
  const [ItemTuChoi, setItemTuChoi] = useState({});
  const [ItemXacNhan, setItemXacNhan] = useState({});
  const [searchMaHoSo, setSearchMaHoSo] = useState("");
  const [searchHoTen, setSearchHoTen] = useState("");
  const [dateHanDen, setDateHanDen] = useState(new Date());
  const [dateHanTu, setDateHanTu] = useState(new Date());
  const [dateNopDen, setDateNopDen] = useState(new Date());
  const [dateNopTu, setDateNopTu] = useState(new Date());
  const [ListDMHetHan, setListDMHetHan] = useState([]);
  const [ListDMThuTuc, setListDMThuTuc] = useState([]);
  const [valueThuTuc, setValueThuTuc] = useState({
    label: "Tất cả",
    value: "",
  });
  const [valueTrangThai, setValueTrangThai] = useState({
    label: "Tất cả",
    value: "",
  });
  const [valueTrangThaiHetHan, setValueTrangThaiHetHan] = useState({
    label: "Tất cả",
    value: "",
  });
  const [ListDMTrangThaiHoSo, setListDMTrangThaiHoSo] = useState([]);

  const debouncedSearchMaHoSo = useDebounce(searchMaHoSo, 1000);
  const debouncedSearchHoTen = useDebounce(searchHoTen, 1000);
  // filter
  const filters = JSON.parse(localStorage.getItem("filters"));
  // const filterHoTen = localStorage.getItem("filterHoTen");

  const filterStatusThuLy = JSON.parse(
    localStorage.getItem("filterTrangThaiThuLy")
  );
  // const filterMaHoSo = JSON.parse(localStorage.getItem("filterMaHoSo"));

  useEffect(
    () => {
      getListDM();
    },
    [] // Only call effect if debounced search term changes
  );

  useEffect(() => {
    if (filters) {
      // setSearchMaHoSo(filterMaHoSo);
      // setSearchHoTen(filterHoTen);
      setValueTrangThaiHetHan(filterStatusThuLy);
      setValueTrangThai(filters);
      getList({
        page_number: 1,
        trang_thai: filters?.value,
        // ho_ten: searchHoTen,
        // ma_ho_so: searchMaHoSo,
        trang_thai_het_han: filterStatusThuLy?.value,
      });

      // localStorage.removeItem("filterMaHoSo");
      // localStorage.removeItem("filterHoTen");
      localStorage.removeItem("filterTrangThaiThuLy");
      localStorage.removeItem("filters");
    }
  }, []);

  useEffect(() => {
    if (roleUser) {
      setRoles(roleUser[0]?.ten_en);
    }
  }, [roleUser]);

  useEffect(
    () => {
      if (!filters || !filterStatusThuLy) {
        if (
          (debouncedSearchMaHoSo !== undefined && searchMaHoSo !== undefined) ||
          (debouncedSearchHoTen !== undefined && searchMaHoSo !== undefined)
        ) {
          getList({
            page_number: 1,
            ma_ho_so: debouncedSearchMaHoSo,
            ho_ten: debouncedSearchHoTen,
          });
        }
      }
    },
    [debouncedSearchMaHoSo, debouncedSearchHoTen] // Only call effect if debounced search term changes
  );

  const getListDM = () => {
    axios
      .get(api.API_DANH_MUC_HO_SO_THU_LY_DUOC_SI)
      .then(async ({ data }) => {
        console.log(data?.results)
        let { het_hans } = data?.results;
        let { thu_tuc } = data?.results;
        let { trang_thai_ho_so } = data?.results;

        let temp_TrangThai = []

        trang_thai_ho_so.forEach((e) => {
          if (e.label !== "Hồ Sơ Mới") {
            temp_TrangThai.push(e)
          }
        });

        het_hans.unshift({
          label: "Tất cả",
          value: "",
        });
        thu_tuc.unshift({
          label: "Tất cả",
          value: "",
        });
        temp_TrangThai.unshift({
          label: "Tất cả",
          value: "",
        });
        setListDMHetHan(het_hans);
        setListDMThuTuc(thu_tuc);
        setListDMTrangThaiHoSo(temp_TrangThai);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const getList = ({
    page_number = page,
    size = perPage,
    ma_ho_so = searchMaHoSo,
    ho_ten = searchHoTen,
    // nop_tu = String(new Date(dateNopTu).getTime()),
    // nop_den = String(new Date(dateNopDen).getTime()),
    // han_tu = String(new Date(dateHanTu).getTime()),
    // han_den = String(new Date(dateHanDen).getTime()),
    nop_tu = "",
    nop_den = "",
    han_tu = "",
    han_den = "",
    thu_tuc_id = valueThuTuc?.value || "",
    trang_thai = valueTrangThai?.value || "",
    trang_thai_het_han = valueTrangThaiHetHan?.value || "",
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_LIST_HO_SO_THU_LY_DUOC_SI +
          `?page=${page_number}&per_page=${size}`,
        {
          ma_ho_so,
          ho_ten,
          nop_tu,
          nop_den,
          han_tu,
          han_den,
          thu_tuc_id,
          trang_thai,
          trang_thai_het_han,
        }
      )
      .then(async ({ data }) => {
        setListHoSoThuLy(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const updateHoSo = (id) => {
    const jsonUpdate = {
      id: id,
      trang_thai_ho_so: "4",
    };
    axios
      .post(api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/thu_ly", jsonUpdate)
      .then(async ({ data }) => {
        toast.success("Cập nhật thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({});
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  // handle api

  const resetState = async () => {
    await setSearchHoTen("");
    await setSearchMaHoSo("");
    await setDateHanDen(null);
    await setDateHanTu(null);
    await setDateNopDen(null);
    await setDateNopTu(null);
    await setValueThuTuc({
      label: "Tất cả",
      value: "",
    });
    await setValueTrangThai({
      label: "Tất cả",
      value: "",
    });
    await setValueTrangThaiHetHan({
      label: "Tất cả",
      value: "",
    });
  };

  // DETAIL
  const detail = async (data) => {
    setTrang_thai(data?.trang_thai);
    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${page}&per_page=${newPerPage}`,
        {}
      )
      .then(({ data }) => {
        if (data?.results) {
          setListHoSoThuLy(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  const handleRowClicked = (row) => {};

  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );

  const checkStatus = (id) => {
    let status = TrangThaiHoSo.find((e) => e.id === id);
    return status.name;
  };

  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };

  const getDetail = (id) => {
    axios
      .get(api.API_DETAIL_HO_SO_THU_LY_DUOC_SI + "/" + id)
      .then(({ data }) => {
        console.log("log data", data);
      })
      .catch(() => {})
      .finally(() => {});
  };

  const columns = [
    {
      name: "Mã hồ sơ",
      selector: (row) => (
        <span
          onClick={() => {
            console.log("row danh sách hồ sơ", row);
            if (
              roles === "chuyenvienhoidong" ||
              roles === "lanhdao" ||
              roles === "vanthu"
            ) {
              navigate(`duthaocapphep/${row?.id}`);
            }
            if (roles === "chuyenvien") {
              navigate(`capmoi/${row?.id}`);
            }
            // localStorage.setItem("filterMaHoSo", searchMaHoSo);
            // localStorage.setItem("filterHoTen", searchHoTen);
            localStorage.setItem(
              "filterTrangThaiThuLy",
              JSON.stringify({ ...valueTrangThaiHetHan })
            );
            localStorage.setItem(
              "filters",
              JSON.stringify({ ...valueTrangThai })
            );
          }}
        >
          {row.ma_ho_so}
        </span>
      ),
      grow: 1,
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Tên thủ tục",
      selector: (row) => <span>{row.ten_thu_tuc}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Tên người hành nghề",
      selector: (row) => <span>{row.ten_nguoi_yeu_cau}</span>,
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Ngày nộp",
      selector: (row) => (
        <span>
          {moment(Number(row.thoi_gian_nop_ho_so)).format("DD/MM/YYYY")}
        </span>
      ),
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trạng thái",
      selector: (row) => <span>{checkStatus(row?.trang_thai_ho_so)}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      grow: 2,
      cell: (row) =>
        row?.trang_thai_ho_so === "2" ? (
          <div className="container my-3">
            <button
              onClick={() => {
                updateHoSo(row?.id);
              }}
              className="btn btn-bg-success btn-color-white btn-sm"
              style={{ marginRight: 5 }}
            >
              Xác nhận thụ lý
            </button>
            <button
              onClick={() => {
                setItemTuChoi(row);
                showModalCancel();
