import { enAU } from "date-fns/locale";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";

import axios from "axios";
import moment from "moment";
import { In } from "../../In/In";
import Select from "react-select";
import { toast } from "react-toastify";
//
//
import { Viewer, Worker } from "@react-pdf-viewer/core";
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout';

//


import { Form, Modal } from "react-bootstrap";
import api from "../../../../configs/api";
import { GioiTinh, HinhThuc } from "../../../../data";
import LoadingOverlay from "react-loading-overlay";
import ImageUploading from "react-images-uploading";
import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { BangCap } from "../../duoc-si/bang-cap/BangCap";
import { useNavigate, useParams } from "react-router-dom";
import { LichSuDaoTao } from "../../duoc-si/lich-su-dao-tao/LichSuDaoTao";
import { LichSuThucHanh } from "../../duoc-si/lich-su-thuc-hanh/LichSuThucHanh";
import LyDoHeader from "../../danh-sach-ho-so-thu-ly-duoc-si/components/LyDoHeader";
import { selectRoleUser } from "../../../../redux-module/auth/authSlice";
import {
  Checkbox,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
  TextField,
} from "@mui/material";
import ThongTinCaNhan from "../../danh-sach-ho-so-thu-ly-duoc-si/components/thong-tin-ca-nhan/ThongTinCaNhan";
import {
  locationActions,
  selectListNoiCap,
  selectListQuanHuyen,
  selectListTinhThanh,
  selectListXaPhuong,
} from "../../../../redux-module/locations/locationSlice";
import {
  commonActions,
  selectListIdChecked,
} from "../../../../redux-module/common/commonSlice";
import LoadingIndicator from "../../../../components/LoadingIndicator";

LoadingOverlay.propTypes = undefined;
export function CapNhatHoSo() {
  const navigate = useNavigate();
  const params = useParams();
  const currentRole = useSelector(selectRoleUser);

  const LoaiChungChi = useRef(null);
  const LoaiHinhThuc = useRef(null);
  const PhamViHNCM = useRef(null);

  const scrollToLoaiChungChi = () =>
    LoaiChungChi.current.scrollIntoView({
      behavior: "smooth",
      block: "center",
    });
  const scrollToLoaiHinhThuc = () =>
    LoaiHinhThuc.current.scrollIntoView({
      behavior: "smooth",
      block: "center",
    });
  const scrollToPhamViHNCM = () =>
    PhamViHNCM.current.scrollIntoView({ behavior: "smooth", block: "center" });

  const dispatch = useDispatch();
  const [currentId, setCurrentId] = useState("");

  const [form, setForm] = useState({});
  const [errors, setErrors] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [soChungChi, setSoChungChi] = useState("");
  const [thongTinHoSo, setThongTinHoSo] = useState({});
  const [thongTinDoiTuong, setThongTinDoiTuong] = useState({});
  const [thongTinChungChi, setThongTinChungChi] = useState({});
  const [isUserCreated, setIsUserCreated] = useState(false);
  const [isCCSuccess, setIsCCSuccess] = useState(false);

  const locationType = useSelector((state) => state.location);
  const listTinhThanh = useSelector(selectListTinhThanh);
  const listQuanHuyen = useSelector(selectListQuanHuyen);
  const listXaPhuong = useSelector(selectListXaPhuong);
  const listNoiCap = useSelector(selectListNoiCap);

  const [listQuanHuyenTT, setListQuanHuyenTT] = useState([]);
  const [listXaPhuongTT, setListXaPhuongTT] = useState([]);
  const [listQuanHuyenHN, setListQuanHuyenHN] = useState([]);
  const [listXaPhuongHN, setListXaPhuongHN] = useState([]);

  const [selectedTTTT, setSelectedTTTT] = useState();
  const [selectedQHTT, setSelectedQHTT] = useState();
  const [selectedXPTT, setSelectedXPTT] = useState();

  const [selectedTTHN, setSelectedTTHN] = useState();
  const [selectedQHHN, setSelectedQHHN] = useState();
  const [selectedXPHN, setSelectedXPHN] = useState();

  const [ccHinhThuc, setCCHinhThuc] = useState("");
  const [ccViTriHanhNghe, setCCViTriHanhNghe] = useState("");
  const [ccPhamViChuyenMon, setCCPhamViChuyenMon] = useState("");

  const [selectedNoiCapCMND, setSelectedNoiCapCMND] = useState();
  const [selectedGioiTinh, setSelectedGioiTinh] = useState(null);
  const [DeNghiHinhThuc, setDeNghiHinhThuc] = useState(null);
  const [DeNghiLoaiDeNghi, setDeNghiLoaiDeNghi] = useState(null);
  const [DeNghiDMPVHN, setDeNghiDMPVHN] = useState(null);
  const [valueDeNghiLoai, setValueDeNghiLoai] = useState(null);
  const [valueDeNghiHinhThuc, setValueDeNghiHinhThuc] = useState(null);
  const [FileDeNghiCapLai, setFileDeNghiCapLai] = useState([]);
  const [FileAnhChanDung, setFileAnhChanDung] = useState([]);
  const [FileBanChinhHoacBanSau, setFileBanChinhHoacBanSau] = useState([]);
  const [FileBanChinhDaDuocCap, setFileBanChinhDaDuocCap] = useState([]);
  const [FileKhac, setFileKhac] = useState([]);
  const [FileVanBangChuyenMon, setFileVanBangChuyenMon] = useState([]);
  const [FileXacNhanSucKhoe, setFileXacNhanSucKhoe] = useState([]);
  const [FileXacNhanThucHanh, setFileXacNhanThucHanh] = useState([]);
  const [FileXacNhanDaoTao, setFileXacNhanDaoTao] = useState([]);
  const [FileXacNhanCongDan, setFileXacNhanCongDan] = useState([]);
  const [FileXacNhanLyLich, setFileXacNhanLyLich] = useState([]);
  const [FileXacNhanLePhi, setFileXacNhanLePhi] = useState([]);
  const [checkedCamKet, setCheckedCamKet] = useState(true);
  const [ValueNote, setValueNote] = useState("");
  const [checkSave, setCheckSave] = useState(false);
  const [valueTrangThaiHoSo, setValueTrangThaiHoSo] = useState("");
  const ListIdChecked = useSelector(selectListIdChecked);



  const [file_url, setFileUrl] = useState("");
  const [isvisibleIn, setIsvisibleIn] = useState(false);
  const closeModalIn = () => setIsvisibleIn(false);
  const showModalIn = () => {
    setIsvisibleIn(true);
    inHoSo(thongTinHoSo?.yeu_cau_cchnd?.id);
  };

  const defaultLayoutPluginInstance = defaultLayoutPlugin();


  useEffect(() => {
    if (params?.id) {
      getThongTinHoSo(params?.id);
    }

    const soGiayPhep = localStorage.getItem("soChungChi", soChungChi);
    if (soGiayPhep !== null && soGiayPhep !== undefined) {
      setSoChungChi(soGiayPhep);
      getThongTinChungChi(soGiayPhep);
    }

    dispatch(locationActions.getTinhThanh());
    dispatch(locationActions.getNoiCap());
  }, []);

  useEffect(() => {
    setSelectedNoiCapCMND(listNoiCap.find((e) => e.label === form?.noi_cap));
  }, [listNoiCap, form?.noi_cap]);

  useEffect(() => {
    if (locationType.loai === "thuongtru") {
      setListQuanHuyenTT(listQuanHuyen);
    } else if (locationType.loai === "hiennay") {
      setListQuanHuyenHN(listQuanHuyen);
    }
  }, [listQuanHuyen, locationType]);
  useEffect(() => {
    if (locationType.loai === "thuongtru") {
      setListXaPhuongTT(listXaPhuong);
    } else if (locationType.loai === "hiennay") {
      setListXaPhuongHN(listXaPhuong);
    }
  }, [listXaPhuong, locationType]);

  useEffect(() => {
    if (
      listTinhThanh !== null &&
      listTinhThanh !== undefined &&
      listTinhThanh.length !== 0
    ) {
      onChangeTinhThanh(
        listTinhThanh.find((e) => e.value === form?.tinh_thanh_id)
      );
    }
  }, [listTinhThanh, form.tinh_thanh_id]);
  useEffect(() => {
    onChangeTinhThanhCOHN(
      listTinhThanh.find((e) => e.value === form?.tinh_thanh_hien_nay_id)
    );
  }, [listTinhThanh, form.tinh_thanh_hien_nay_id]);

  useEffect(() => {
    const handleQuanHuyenTT = () => {
      onChangeQuanHuyen(
        listQuanHuyenTT.find((e) => e.value === form?.quan_huyen_id)
      );
    };
    setTimeout(() => {
      handleQuanHuyenTT();
    }, 100);
  }, [listQuanHuyenTT, form.quan_huyen_id]);
  useEffect(() => {
    const handleQuanHuyenHN = () => {
      onChangeQuanHuyenCOHN(
        listQuanHuyenHN.find((e) => e.value === form?.quan_huyen_hien_nay_id)
      );
    };
    setTimeout(() => {
      handleQuanHuyenHN();
    }, 100);
  }, [listQuanHuyenHN, form.quan_huyen_hien_nay_id]);

  useEffect(() => {
    const handleXaPhuongTT = () => {
      onChangeXaPhuong(
        listXaPhuongTT.find((e) => e.value === form?.xa_phuong_id)
      );
    };
    setTimeout(() => {
      handleXaPhuongTT();
    }, 500);
  }, [listXaPhuongTT, form.xa_phuong_id]);
  useEffect(() => {
    const handleXaPhuongHN = () => {
      onChangeXaPhuongCOHN(
        listXaPhuongHN.find((e) => e.value === form?.xa_phuong_hien_nay_id)
      );
    };
    setTimeout(() => {
      handleXaPhuongHN();
    }, 500);
  }, [listXaPhuongHN, form.xa_phuong_hien_nay_id]);

  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const selectedNS = (date) => {
    setField("ngay_sinh", new Date(date).getTime());
  };
  const handleGioiTinh = (e) => {
    setField("gioi_tinh", e);
    // if (hoSoChuyenVien?.doi_tuong?.gioi_tinh) {
    //   setSelectedGioiTinh(GioiTinh.find((e) => e.value === hoSoChuyenVien?.doi_tuong?.gioi_tinh))
    // }
  };
  const handleNoiCap = (e) => {
    setField("noi_cap", e);
    if (form?.noi_cap) {
      setSelectedNoiCapCMND(listNoiCap.find((e) => e.value === form.noi_cap));
    }
  };
  const handleNgayCap = (date) => {
    setField("ngay_cap", new Date(date).getTime());
  };
  const handleChangeDeNghiLoai = (event) => {
    setValueDeNghiLoai(event.target.value);
  };
  const handleChangeDeNghiHinhThuc = (event) => {
    setValueDeNghiHinhThuc(event.target.value);
  };

  // ONCHANGE LOCATIONs
  const onChangeTinhThanh = (values) => {
    if (values) {
      setField("tinh_thanh_id", values?.id);
      dispatch(
        locationActions.getQuanHuyen({ id: values.id, loai: "thuongtru" })
      );
      setSelectedTTTT(values);
    }
    setSelectedQHTT(null);
    setSelectedXPTT(null);
  };
  const onChangeQuanHuyen = (values) => {
    if (values) {
      setField("quan_huyen_id", values.id);
      dispatch(
        locationActions.getXaPhuong({ id: values.id, loai: "thuongtru" })
      );
      setSelectedQHTT(values);
      setSelectedXPTT(null);
    }
  };
  const onChangeXaPhuong = (values) => {
    if (values) {
      setField("xa_phuong_id", values.id);
      setSelectedXPTT(values);
    }
  };

  const onChangeTinhThanhCOHN = (values) => {
    if (values) {
      setField("tinh_thanh_hien_nay_id", values?.id);
      dispatch(
        locationActions.getQuanHuyen({ id: values.id, loai: "hiennay" })
      );
      setSelectedTTHN(values);
    }
    setSelectedQHHN(null);
    setSelectedXPHN(null);
  };
  const onChangeQuanHuyenCOHN = (values) => {
    if (values) {
      setField("quan_huyen_hien_nay_id", values.id);
      dispatch(locationActions.getXaPhuong({ id: values.id, loai: "hiennay" }));
      setSelectedQHHN(values);
    }
    setSelectedXPHN(null);
  };
  const onChangeXaPhuongCOHN = (values) => {
    if (values) {
      setField("xa_phuong_hien_nay_id", values.id);
      setSelectedXPHN(values);
    }
  };

  // GET THONG TIN FUNCTIONs
  const getThongTinHoSo = async (id) => {
    await setIsLoading(true);
    axios
      .get(api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/" + id)
      .then(async ({ data }) => {
        const { result } = data;
        setValueNote(result?.noi_dung_dieu_chinh || result?.ly_do_tu_choi);
        if (result) {
          if (result?.yeu_cau_cchnd?.doi_tuong?.id !== result?.user?.id) {
            const temp_Form = result?.yeu_cau_cchnd?.doi_tuong;
            setCurrentId(temp_Form.id);
            setIsUserCreated(true);
            setForm(temp_Form);
            setSelectedGioiTinh(
              GioiTinh.find((e) => e.value === temp_Form.gioi_tinh)
            );
          } else {
            setCurrentId(result?.user?.id);
          }

          setThongTinHoSo(data?.result);
          setCCHinhThuc(
            HinhThuc.find(
              (e) => e.id === data?.result?.chung_chi_hanh_nghe?.hinh_thuc_thi
            )
          );

          let viTriHanhNghe =
            data?.result?.chung_chi_hanh_nghe?.vi_tri_hanh_nghe;
          let phamViChuyenMon =
            data?.result?.chung_chi_hanh_nghe?.pham_vi_chuyen_mon;

          if (viTriHanhNghe && viTriHanhNghe.length > 0) {
            let a = [];
            for (let x = 0; x < viTriHanhNghe.length; x++) {
              a.push(viTriHanhNghe[x].ten);
            }
            a = a.join("  ");
            setCCViTriHanhNghe(a);
          }

          if (phamViChuyenMon && phamViChuyenMon.length > 0) {
            let b = [];
            for (let x = 0; x < phamViChuyenMon.length; x++) {
              b.push(phamViChuyenMon[x].ten);
            }
            b = b.join(";  ");
            setCCPhamViChuyenMon(b);
          }

          if (
            result?.yeu_cau_cchnd?.trang_thai_ho_so === "0" ||
            result?.yeu_cau_cchnd?.trang_thai_ho_so === "1"
          ) {
            setCheckedCamKet(false);
          }
          if (result?.thu_tuc?.loai === "1") {
            setDeNghiHinhThuc(
              Object.entries(result?.de_nghi?.hinh_thuc_thi).map(
                ([key, value]) => ({ value: key, label: value })
              )
            );
            setDeNghiLoaiDeNghi(
              Object.entries(result?.de_nghi?.loai_de_nghi).map(
                ([key, value]) => ({ value: key, label: value })
              )
            );
            setDeNghiDMPVHN(result?.de_nghi?.danh_muc_pham_vi_hn);
            setValueDeNghiLoai(result?.checked_denghi?.loai_de_nghi);
            setValueDeNghiHinhThuc(result?.checked_denghi?.hinh_thuc_thi);
            setValueTrangThaiHoSo(result?.yeu_cau_cchnd?.trang_thai_ho_so);

            dispatch(
              commonActions.setListIdChecked(
                result?.checked_denghi?.danh_muc_pham_vi_hn?.id || []
              )
            );
          }
        }
      })
      .catch((error) => {
        toast.error("Lấy thông tin chứng chỉ không thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        navigate("/error/404");
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getThongTinChungChi = (value) => {
    if (value && value !== "" && value !== "[object Object]") {
      const json = {
        so_giay_phep: value,
      };
      axios
        .post(api.API_THONG_TIN_LIEN_KET_DUOC_SI, json)
        .then(({ data }) => {
          if (data?.results?.doi_tuong?.gioi_tinh) {
            data.results.doi_tuong.gioi_tinh = GioiTinh.find(
              (e) => e.value === data?.results?.doi_tuong?.gioi_tinh
            );
          }
          localStorage.setItem("soChungChi", value);
          setThongTinChungChi(data?.results);
          setThongTinDoiTuong(data?.results?.doi_tuong);
          updateHoSoYeuCau(data?.results?.doi_tuong);
          getThongTinHoSo(params?.id);
          setCurrentId(data?.results?.doi_tuong?.id);
          setIsCCSuccess(true);
        })
        .catch((error) => {
          toast.error(errors?.response?.data, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {});
    } else {
      toast.error("Nhập vào số chứng chỉ cần tra cứu", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "error",
      });
    }
  };

  const validateFormSubmit = async () => {
    const numCheck = /^\d+$/;
    const { ho, ten, ngay_sinh, ma_cong_dan, ngay_cap, dien_thoai, email } =
      form;
    const newErrors = {};

    if (!ho || ho === "") newErrors.ho = "Họ không được bỏ trống!";
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";
    if (!ma_cong_dan || ma_cong_dan === "")
      newErrors.ma_cong_dan = "CMND/CCCD không được bỏ trống!";

    if (dien_thoai) {
      if (numCheck.test(dien_thoai) === false || dien_thoai.length !== 10) {
        newErrors.dien_thoai = "Sai định dạng số điện thoại!";
      }
    }

    if (ma_cong_dan) {
      if (
        numCheck.test(ma_cong_dan) === false ||
        (ma_cong_dan.length !== 8 && ma_cong_dan.length !== 12)
      ) {
        newErrors.ma_cong_dan = "Sai định dạng CMND/CCCD!";
      }
    }

    if (!dien_thoai || dien_thoai === "")
      newErrors.dien_thoai = "Số điện thoại không được bỏ trống!";

    if (!email || email === "") newErrors.email = "Email không được bỏ trống!";

    if (!ngay_sinh) newErrors.ngay_sinh = "Ngày sinh không được bỏ trống!";
    else if (ngay_sinh > new Date().getTime())
      newErrors.ngay_sinh = "Ngày sinh không hợp lệ!";
    if (!ngay_cap)
      newErrors.ngay_cap = "Ngày cấp CMND/CCCD không được bỏ trống!";
    else if (ngay_cap > new Date().getTime())
      newErrors.ngay_cap = "Ngày cấp CMND/CCCD không hợp lệ!";

    if (!selectedGioiTinh) {
      newErrors.gioi_tinh = "Giới tính không được bỏ trống!";
    }
    if (!selectedNoiCapCMND) {
      newErrors.noi_cap = "Nơi cấp CMND/CCCD không được bỏ trống!";
    }

    if (!selectedTTTT) {
      newErrors.selectedTTTT = "Tỉnh thành thường trú không được bỏ trống!";
    }
    if (!selectedQHTT) {
      newErrors.selectedQHTT = "Quận huyện thường trú không được bỏ trống!";
    }
    if (!selectedXPTT) {
      newErrors.selectedXPTT = "Xã phường thường trú không được bỏ trống!";
    }
    if (!selectedTTHN) {
      newErrors.selectedTTHN = "Tỉnh thành hiện nay không được bỏ trống!";
    }
