import axios from "axios";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import { useDispatch, useSelector } from "react-redux";
import { Form, Modal } from "react-bootstrap";
import { NavLink, useNavigate } from "react-router-dom";
import {
  authActions,
  selectCurrentUser,
} from "../../../redux-module/auth/authSlice";
import { notiActions } from "../../../redux-module/noti/notiSlice";
import { KTSVG } from "../../../../_metronic/helpers";

export function Login() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [data, setData] = useState();
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const [isDaCap, setIsDaCap] = useState(false);
  const [currentRole, setCurrentRole] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const currentUser = useSelector(selectCurrentUser);

  const [type, setType] = useState("password");

  const [icon, setIcon] = useState("fas fa-eye text-primary mx-auto");

  function showOrHide(e) {
    e.preventDefault()
    type === "password" ? setType("text") : setType("password");


    if (type === "password") {
      setType("text");
      setIcon("fas fa-eye-slash text-primary mx-auto")
    } else if (type === "text") {
      setType("password");
      setIcon("fas fa-eye text-primary mx-auto");
    }
  }

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  useEffect(() => {
    if (currentUser) {
      navigate("/dashboard");
    }
  }, []);

  const findFormErrors = () => {
    const { username, password } = form;
    const newErrors = {};
    // name errors
    if (!username || username === "")
      newErrors.username = "Tên đăng nhập không được bỏ trống!";
    if (!password || password === "")
      newErrors.password = "Mật khẩu không được bỏ trống!";
    return newErrors;
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setIsLoading(false);
    } else {
      onChoosen();
    }
  };

  const onNavigate = (status) => {
    if (data && data?.userInfo?.assigned_role[0]?.ten_en === "duocsi") {
      if (status === "dacap") {
        navigate("/admin/chung-chi-hanh-nghe");
      } else if (status === "chuacap") {
        navigate("/thong-tin-duoc-si");
      }
    } else if (data && data?.userInfo?.assigned_role[0]?.ten_en === "tochuc") {
      if (status === "dacap") {
        navigate("/admin/giay-phep-kinh-doanh");
      } else if (status === "chuacap") {
      }
    }
    toast.success("Đăng nhập thành công", {
      position: "top-right",
      autoClose: 1000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      toastId: "success",
    });
  };

  const onChoosen = () => {
    const { username, password } = form;
    const json = { username, password };
    axios
      .post(api.API_LOGIN, json)
      .then(async ({ data }) => {
        setData(data);
        dispatch(authActions.loginSuccess(data));
        dispatch(notiActions.getNoti(data?.userInfo?.id));
        setCurrentRole(data?.userInfo?.assigned_role[0]?.ten_en);

        if (data?.userInfo?.last_login == null) {
          if (
            data?.userInfo?.assigned_role[0]?.ten_en === "duocsi" ||
            data?.userInfo?.assigned_role[0]?.ten_en === "tochuc"
          ) {
            setIsDaCap(true);
          } else {
            navigate("/dashboard");
          }
        } else {
          switch (data?.userInfo?.assigned_role[0]?.ten_en) {
            case "duocsi":
              navigate("/admin/chung-chi-hanh-nghe");
              break;
            case "tochuc":
              navigate("/admin/giay-phep-kinh-doanh");
              break;
            default:
              navigate("/dashboard");
              break;
          }
        }

        setIsLoading(false);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  return (
    <div className="pagee">
      <div className="row justify-content-center">
        <div className="col-sm-8 col-lg-5 col-xl-4">
          <div className="text-center w-100 m-auto">
            <div className="auth-logo">
              <NavLink to="/" className="logo logo-dark text-center">
                <span className="logo">
                  <img
                    className="img-logo"
                    src="media/logos/Logo_BoYTe.png"
                    alt=""
                    height="90"
                    width="90"
                  />
                </span>
              </NavLink>
            </div>
            <div className="auth-name my-6" style={{ fontSize: "1.3rem" }}>
              HỆ THỐNG QUẢN LÝ HÀNH NGHỀ DƯỢC
            </div>
          </div>
          <div className="box-login-wrapper card border-0">
            <div className="card-body w-100 w-md-75 py-6 px-2 m-auto">
              <Form onSubmit={onSubmit}>
                <Form.Group>
                  <div className="row mb-8">
                    <span
                      className="mb-3"
                      style={{ fontSize: "1rem", fontWeight: 600 }}
                    >
                      Tên đăng nhập
                    </span>
                    <Form.Control
                      type="text"
                      placeholder="Tên đăng nhập"
                      style={{ fontSize: 14, fontWeight: 400 }}
                      onChange={(e) => {
                        setField("username", e.target.value);
                      }}
                      isInvalid={!!errors.username}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errors.username}
                    </Form.Control.Feedback>
                  </div>
                  <div className="row mb-9">


                    <span
                      className="mb-3"
                      style={{ fontSize: "1rem", fontWeight: 600 }}
                    >
                      Mật khẩu
                    </span>
                    <div className="input-group px-0" id="show_hide_password">
                      <Form.Control
                        className="inputPassword"
                        type={type}
                        placeholder="Mật khẩu"
                        style={{ fontSize: 14, fontWeight: 400 }}
                        onChange={(e) => {
                          setField("password", e.target.value);
                        }}
                        autoComplete="off"
                        isInvalid={!!errors.password}
                      />
                      <div className="input-group-addon">
                        <button type="button" className="btn btn-light showHidePassword ps-4 pe-3" onClick={showOrHide}>
                          <i className={icon}></i>
                        </button>
                      </div>
                    </div>

                    <Form.Control.Feedback type="invalid">
                      {errors.password}
                    </Form.Control.Feedback>
                  </div>
                </Form.Group>

                <div className="form-group mb-1 text-center">
                  <button
                    className="btn btn-primary btn-block btn-login"
                    style={{ width: "100%" }}
                    type="submit"
                    onClick={(e) => {
                      onSubmit(e);
                    }}
                  >
                    {isLoading ? (
                      <span
                        className="indicator-progress"
                        style={{ display: "block" }}
                      >
                        Vui lòng chờ...
                        <span className="spinner-border spinner-border-sm align-middle ms-2"></span>
                      </span>
                    ) : (
                      <span>
                        <i className="fas fa-chevron-circle-right"></i>
                        Đăng nhập
                      </span>
                    )}
                  </button>
                </div>
              </Form>
              <div className="form-group text-center mb-3">
                <NavLink to="/dang-ky">Bạn chưa có tài khoản?</NavLink>
              </div>

              <div className="form-group float-start">
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    value=""
                    id="flexCheckDefault"
                    defaultChecked
                  />
                  <label
                    className="form-check-label"
                    htmlFor="flexCheckDefault"
                  >
                    Lưu mật khẩu
                  </label>
                </div>
              </div>

              <div className="form-group float-end">
                <NavLink to="/forgot-password" className="forgot-password">
                  Quên mật khẩu
                </NavLink>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="footer footer-alt text-footer">
        SỞ Y TẾ TP HỒ CHÍ MINH
      </div>
      <Modal
        className="d-flex"
        size="lg"
        style={{ justifyContent: "center", top: "20vh" }}
        show={isDaCap}
        onHide={() => setIsDaCap(false)}
      >
        <Modal.Header className="px-5 py-5">
          <div className="row w-100">
            <div className="col-12 px-0 text-end">
              <button
                className="btn btn-link py-0"
                onClick={() => {
                  setIsDaCap(false);
                }}
              >
                <i className="fas fa-times"></i>
              </button>
            </div>
          </div>
        </Modal.Header>
        <Modal.Body>
          <div
            className="row justify-content-center text-center"
            style={{ width: "40vw" }}
          >
            <div className="col-11">
              {currentRole === "duocsi" ? (
                <h3 style={{ textTransform: "uppercase" }}>
                  ông/bà đã đươc sở y tế tp. hồ chí minh cấp chứng chỉ hành nghề
                  dược hay chưa ?
                </h3>
              ) : (
                <h3 style={{ textTransform: "uppercase" }}>
                  tổ chức/đơn vị đã được sở y tế tp.hồ chí minh cấp chứng chỉ
                  hành nghề dược hay chưa ?
                </h3>
              )}
            </div>
            <div className="col-6 mt-7 mb-4 text-end">
              <button
                className="btn btn-primary"
                onClick={() => {
                  onNavigate("chuacap");
                }}
              >
                {" "}
                Chưa được cấp{" "}
              </button>
            </div>
            <div className="col-6 mt-7 mb-4 text-start">
              <button
                className="btn btn-warning"
                onClick={() => {
                  onNavigate("dacap");
                }}
              > Đã được cấp
              </button>
            </div>
          </div>
        </Modal.Body>
      </Modal>
    </div>
  );
}
