export function ChiTietHoSoThuLyCapMoi(props) {
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    if (id) {
      getThongTinHoSo(id);
    }
  }, []);

  const [isLoading, setIsLoading] = useState(true);
  const [thongTinHoSo, setThongTinHoSo] = useState({});
  const [FileDeNghiCapLai, setFileDeNghiCapLai] = useState([]);
  const [FileAnhChanDung, setFileAnhChanDung] = useState([]);
  const [FileKhac, setFileKhac] = useState([]);
  const [FileVanBangChuyenMon, setFileVanBangChuyenMon] = useState([]);
  const [FileXacNhanSucKhoe, setFileXacNhanSucKhoe] = useState([]);
  const [FileXacNhanThucHanh, setFileXacNhanThucHanh] = useState([]);
  const [FileXacNhanDaoTao, setFileXacNhanDaoTao] = useState([]);
  const [FileXacNhanCongDan, setFileXacNhanCongDan] = useState([]);
  const [FileXacNhanLyLich, setFileXacNhanLyLich] = useState([]);
  const [FileXacNhanLePhi, setFileXacNhanLePhi] = useState([]);
  const [TypeHoSo, setTypeHoSo] = useState("");
  const [DeNghi, setDeNghi] = useState("");
  const [NoiDung, setNoiDung] = useState("");
  const getThongTinHoSo = async (id) => {
    await setIsLoading(true);
    axios
      .get(api.API_DETAIL_HO_SO_THU_LY_DUOC_SI + "/" + id)
      .then(async ({ data }) => {
        const { result } = data;
        if (result) {
          setTypeHoSo(data?.result?.trang_thai_ho_so);
          setDeNghi(data?.result?.de_nghi);
          setThongTinHoSo(data?.result);

          console.log(data?.result)
          if (data?.result?.trang_thai_ho_so === "Điều chỉnh nội dung Chứng chỉ hành nghề dược") {
            setNoiDung(data?.result?.noi_dung_xin_dieu_chinh);
          } else if (data?.result?.thu_tuc?.ten === "Cấp lại chứng chỉ hành nghề dược") {
            setNoiDung(data?.result?.ly_do_mat_hong);
          }
        }
      })
      .catch((error) => {
        toast.error("Lấy thông tin chứng chỉ không thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        navigate("/error/404");
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const renderChooseFile = ({ title, name, arrayFileMap, setFile }) => {
    return (
      <div className="col-6">
        <div className="row">
          <div className="col-12">{title}</div>
          <div className="col-12">
            {arrayFileMap?.length > 0
              ? arrayFileMap.map((e, i) => {
                  return (
                    <div key={i} className="pb-2">
                      <i
                        style={{ display: "inline", color: "red" }}
                        onClick={() => {
                          let arrFile = arrayFileMap;
                          arrFile = arrFile.filter(
                            (item) => item.name !== e?.name
                          );
                          setFile(arrFile);
                        }}
                        className="fas fa-times-circle"
                      ></i>
                      <Button className="ml-2">
                        <span>{e?.name}</span>
                      </Button>
                    </div>
                  );
                })
              : null}
          </div>
        </div>
      </div>
    );
  };

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];

  const renderButtonsBottom = () => {
    switch (TypeHoSo) {
      case "2":
        return (
          <div
            onClick={() => {
              updateHoSo();
            }}
            className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold"
          >
            XÁC NHẬN THỤ LÝ
          </div>
        );
      case "4":
      case "5":
        return (
          <>
            <div
              onClick={() => {
                navigate(
                  `/admin/danhsach-hoso-thuly-duocsi/duthaocapphep/${id}`
                );
              }}
              className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold"
            >
              DỰ THẢO TRÌNH CẤP
            </div>

            <div
              onClick={() => {
                navigate(
                  `/admin/danhsach-hoso-thuly-duocsi/yeucaubosung/${id}`
                );
              }}
              className="btn btn-warning text-white px-4 py-2 mx-2 font-weight-bold"
            >
              YÊU CẦU BỔ SUNG
            </div>
          </>
        );
      case "10":
      case "11":
        return (
          <>
            <div className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold">
              THẨM ĐỊNH
            </div>
            <div className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold">
              IN VĂN BẢN TỪ CHỐI
            </div>
          </>
        );
      default:
        break;
    }
  };

  const updateHoSo = async () => {
    await setIsLoading(true);
    const jsonUpdate = {
      id: id,
      trang_thai_ho_so: "4",
    };
    axios
      .post(api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/thu_ly", jsonUpdate)
      .then(async ({ data }) => {
        toast.success("Cập nhật thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        setTimeout(() => {
          navigate(0);
        }, 1000);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  return (
    <div>
      <PageTitle breadcrumbs={usersBreadcrumbs}>Chi tiết hồ sơ</PageTitle>
      <LoadingOverlay spinner active={isLoading} text="Đang tải dữ liệu ...">
        <LyDoHeader thongTinHoSo={thongTinHoSo} />

        {/* Thủ tục */}
        <div className="card py-6 px-6 mx-3 my-3">
          <label className="fw-bold fs-5">THỦ TỤC</label>
          <div className="text py-2">
            {thongTinHoSo?.thu_tuc ? thongTinHoSo?.thu_tuc?.ten : ""}
          </div>
        </div>

        {/* THÔNG TIN CÁ NHÂN */}
        <ThongTinCaNhan thongTinHoSo={thongTinHoSo} />

        {/* BẰNG CẤP  */}
        <BangCap userId={thongTinHoSo?.user?.id} />
        {/* THỰC HÀNH TẠI CƠ SỞ DƯỢC */}
        <LichSuThucHanh userId={thongTinHoSo?.user?.id} />
        {/* ĐÀO TẠO LIÊN TỤC */}
        <LichSuDaoTao userId={thongTinHoSo?.user?.id} />

        {/* ĐỀ NGHỊ */}
        <DeNghiView DeNghi={DeNghi} NoiDung={NoiDung} ThuTuc={thongTinHoSo?.thu_tuc} />
        {/* FILE ĐÍNH KÈM */}
        <div className="card-box py-6 px-6 mx-3 my-3">
          <div className="fw-bold fs-5">DANH SÁCH HỒ SƠ ĐÍNH KÈM</div>
          <div className="row pt-2">
            {renderChooseFile({
              title:
                "1. Đơn đề nghị cấp lại Chứng chỉ hành nghề dược(theo mẫu)",
              name: "denghicaplai",
              arrayFileMap: FileDeNghiCapLai,
              setFile: (value) => {
                setFileDeNghiCapLai(value);
              },
            })}
            {renderChooseFile({
              title:
                "2. 02 ảnh chân dung 4 cm x 6 cm chụp trên nền trắng không quá 06 tháng;",
              name: "anhchandung",
              arrayFileMap: FileAnhChanDung,
              setFile: (value) => {
                setFileAnhChanDung(value);
              },
            })}
          </div>

          <div className="row pt-2">
            {renderChooseFile({
              title: "3. Bản sao có chứng thực văn bằng chuyên môn",
              name: "vanbangchuyenmon",
              arrayFileMap: FileVanBangChuyenMon,
              setFile: (value) => {
                setFileVanBangChuyenMon(value);
              },
            })}
            {renderChooseFile({
              title:
                "4. Giấy chứng nhận đủ sức khỏe đề hành nghề được do cơ sở y tế có thẩm quyền cấp",
              name: "xacnhansuckhoe",
              arrayFileMap: FileXacNhanSucKhoe,
              setFile: (value) => {
                setFileXacNhanSucKhoe(value);
              },
            })}
          </div>
          <div className="row pt-2">
            {renderChooseFile({
              title:
                "5. Giấy xác nhận về thời gian thực hành chuyên môn do người đứng đầu cơ sở nơi người đó thực hành cấp (Giấy xác nhận thời gian thực hành tại cơ sở thực hành chuyên môn về dược)",
              name: "xacnhanthuchanh",
              arrayFileMap: FileXacNhanThucHanh,
              setFile: (value) => {
                setFileXacNhanThucHanh(value);
              },
            })}
            {renderChooseFile({
              title:
                "6. Giấy xác nhận hoàn thành chương trình đào tạo, cập nhật kiến thức chuyên môn về dược đối với trường hợp đã bị thu hồi CCHND theo quy định tại khoản 9 điều 28 của Luật dược.",
              name: "xacnhandaotao",
              arrayFileMap: FileXacNhanDaoTao,
              setFile: (value) => {
                setFileXacNhanDaoTao(value);
              },
            })}
          </div>
          <div className="row pt-2">
            {renderChooseFile({
              title:
                "7. Bản sao có chứng thưc căn cước công dân, giấy chứng minh nhân dân hoặc hộ chiếu",
              name: "xacnhancongdan",
              arrayFileMap: FileXacNhanCongDan,
              setFile: (value) => {
                setFileXacNhanCongDan(value);
              },
            })}
            {renderChooseFile({
              title: "8. Phiếu lý lịch tư pháp",
              name: "xacnhanlylich",
              arrayFileMap: FileXacNhanLyLich,
              setFile: (value) => {
                setFileXacNhanLyLich(value);
              },
            })}
          </div>
          <div className="row pt-2">
            {renderChooseFile({
              title:
                "9. Biên lai, chứng từ xác nhận đã nộp phí tài khoản ngân hàng của Sở Y tế",
              name: "xacnhanlephi",
              arrayFileMap: FileXacNhanLePhi,
              setFile: (value) => {
                setFileXacNhanLePhi(value);
              },
            })}
            {renderChooseFile({
              title: "10. Khác",
              name: "khac",
              arrayFileMap: FileKhac,
              setFile: (value) => {
                setFileKhac(value);
              },
            })}
          </div>
        </div>

        <div className="row w-100">
          <div className="col-12 mt-4 mb-5 text-center">
            <div
              onClick={() => {
                navigate(-1);
              }}
              className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
            >
              QUAY LẠI
            </div>
            {renderButtonsBottom()}
            {TypeHoSo === "2" ||
            TypeHoSo === "3" ||
            TypeHoSo === "4" ||
            TypeHoSo === "5" ? (
              <div
                onClick={() => {
                  navigate(`/admin/danhsach-hoso-thuly-duocsi/tuchoi/${id}`);
                }}
                className="btn btn-danger text-white px-4 py-2 mx-2 font-weight-bold "
              >
                TỪ CHỐI
              </div>
            ) : null}
          </div>
        </div>
      </LoadingOverlay>
    </div>
  );
}
