import axios from "axios";
import clsx from "clsx";
import { useEffect, useState } from "react";
import BlockUi from "react-block-ui";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { KTCard, useDebounce } from "../../../../_metronic/helpers";
import Status from "../../../components/Status";
import api from "../../../configs/api";
import { PopupDelete } from "../popup/PopupDelete";
// import { vi } from "date-fns/locale";
import ReactTooltip from "react-tooltip";
import { PageTitle } from "../../../../_metronic/layout/core";
import PopupQuanLyNguoiDung from "../popup/PopupQuanLyNguoiDung";

export function QuanLyNguoiDung() {
  const navigate = useNavigate();
  const [NgayHieuLuc, setNgayHieuLuc] = useState(new Date());
  const [listNguoiDung, setListNguoiDung] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [trang_thai, setTrang_thai] = useState(true);
  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);
  //modal
  const [showAdd, setShowAdd] = useState(false);
  const [edit, setEdit] = useState(false);
  //modal thêm danh mục
  const closeModal = () => setShowAdd(false);
  const showModal = () => setShowAdd(true);
  const [searchTerm, setSearchTerm] = useState("");
  // location
  const [ListTinhThanh, setListTinhThanh] = useState([]);
  const [ListQuanHuyen, setListQuanHuyen] = useState([]);
  const [ListXaPhuong, setListXaPhuong] = useState([]);

  const [disQuanHuyen, setDisQuanHuyen] = useState(true);
  const [disXaPhuong, setDisXaPhuong] = useState(true);

  const [quanHuyenValue, setQuanHuyenValue] = useState(null);
  const [xaPhuongValue, setXaPhuongValue] = useState(null);
  // location Chỗ ở hiện nay
  const [ListTinhThanhCOHN, setListTinhThanhCOHN] = useState([]);
  const [ListQuanHuyenCOHN, setListQuanHuyenCOHN] = useState([]);
  const [ListXaPhuongCOHN, setListXaPhuongCOHN] = useState([]);

  const [disQuanHuyenCOHN, setDisQuanHuyenCOHN] = useState(true);
  const [disXaPhuongCOHN, setDisXaPhuongCOHN] = useState(true);

  const [quanHuyenValueCOHN, setQuanHuyenValueCOHN] = useState(null);
  const [xaPhuongValueCOHN, setXaPhuongValueCOHN] = useState(null);
  // cccd
  const [selectedNoiCapCMND, setSelectedNoiCapCMND] = useState(null);
  const [listNoiCapCMND, setListNoiCapCMND] = useState([]);
  // van bang chuyen mon
  const [selectedVBCN, setSelectedVBCN] = useState(null);
  const [listVanBangChuyenMon, setListVanBangChuyenMon] = useState([]);
  // noi tốt nghiệp
  const [selectedNoiTotNghiep, setSelectedNoiTotNghiep] = useState(null);
  const [listNoiTotNghiep, setListNoiTotNghiep] = useState([]);
  // get detail
  const [ngaySinh, setNgaySinh] = useState(null);
  const [ngayCapCmnd, setNgayCapCmnd] = useState(null);
  // ---
  const [idNoiCapCmnd, setIdNoiCapCmnd] = useState(null);
  const [showModalQLND, setShowModalQLND] = useState(false);
  const closeModalQuanLyNguoiDung = () => setShowModalQLND(false);
  const showModalQuanLyNguoiDung = () => setShowModalQLND(true);

  const [Item, setItem] = useState({});

  const [selectedDoiTuong, setSelectedDoiTuong] = useState({
    label: "Dược sĩ",
    value: 1,
    id: 1,
  });
  const optionsDoiTuong = [
    {
      label: "Dược sĩ",
      value: 1,
      id: 1,
    },
    {
      label: "Tổ chức",
      value: 2,
      id: 2,
    },
  ];
  const [selectedgioitinh, setSelectedgioitinh] = useState({
    label: "Nam",
    value: 1,
    id: 1,
  });
  const optionsGioiTinh = [
    {
      label: "Nam",
      value: 1,
      id: 1,
    },
    {
      label: "Nữ",
      value: 2,
      id: 2,
    },
    {
      label: "Khác",
      value: 3,
      id: 3,
    },
  ];

  const handleNgayHieuLuc = (newValue) => {
    console.log("log new value", new Date(newValue).getTime());
    setNgayHieuLuc(newValue);
  };

  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList(1, perPage, debouncedSearchTerm);
      }
    },
    [debouncedSearchTerm]
  );

  const getList = ({
    page_number = page,
    size = perPage,
    search_ten = searchTerm,
  }) => {
    // setIsLoading(true);
    axios
      .post(api.API_QL_NGUOI_DUNG + `?page=${page_number}&per_page=${size}`, {
        search_ten: search_ten,
      })
      .then(async ({ data }) => {
        setListNguoiDung(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };
  // GET LOCATIONs
  const getTinhThanh = () => {
    axios
      .get(api.API_TINH_THANH)
      .then(({ data }) => {
        let resultsTinhThanh = data?.results;
        resultsTinhThanh.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListTinhThanh(resultsTinhThanh);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getQuanHuyen = (value) => {
    axios
      .get(`${api.API_QUAN_HUYEN}/${value}?per_page=100`)
      .then(({ data }) => {
        let resultsQuanHuyen = data?.results;
        resultsQuanHuyen.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListQuanHuyen(resultsQuanHuyen);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getXaPhuong = (value) => {
    axios
      .get(`${api.API_XA_PHUONG}/${value}?per_page=100`)
      .then(({ data }) => {
        let resultsXaPhuong = data?.results;
        resultsXaPhuong.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListXaPhuong(resultsXaPhuong);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const onChangeTinhThanh = async (values) => {
    setField("tinh_thanh_id", values.id);
    getQuanHuyen(values.id);
    setDisQuanHuyen(false);
    setDisXaPhuong(true);
    setQuanHuyenValue(null);
    setXaPhuongValue(null);
  };
  const onChangeQuanHuyen = (values) => {
    setField("quan_huyen_id", values.id);
    getXaPhuong(values.id);
    setDisXaPhuong(false);
    setQuanHuyenValue(values);
    setXaPhuongValue(null);
  };
  const onChangeXaPhuong = (values) => {
    setField("phuong_xa_id", values.id);
    setXaPhuongValue(values);
  };
  // GET LOCATIONs COHN
  const getTinhThanhCOHN = () => {
    axios
      .get(api.API_TINH_THANH)
      .then(({ data }) => {
        let resultsTinhThanh = data?.results;
        resultsTinhThanh.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListTinhThanhCOHN(resultsTinhThanh);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getQuanHuyenCOHN = (value) => {
    axios
      .get(`${api.API_QUAN_HUYEN}/${value}?per_page=100`)
      .then(({ data }) => {
        let resultsQuanHuyen = data?.results;
        resultsQuanHuyen.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListQuanHuyenCOHN(resultsQuanHuyen);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getXaPhuongCOHN = (value) => {
    axios
      .get(`${api.API_XA_PHUONG}/${value}?per_page=100`)
      .then(({ data }) => {
        let resultsXaPhuong = data?.results;
        resultsXaPhuong.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListXaPhuongCOHN(resultsXaPhuong);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const onChangeTinhThanhCOHN = async (values) => {
    setField("tinh_thanh_id", values.id);
    getQuanHuyenCOHN(values.id);
    setDisQuanHuyenCOHN(false);
    setDisXaPhuongCOHN(true);
    setQuanHuyenValueCOHN(null);
    setXaPhuongValueCOHN(null);
  };
  const onChangeQuanHuyenCOHN = (values) => {
    setField("quan_huyen_id", values.id);
    getXaPhuongCOHN(values.id);
    setDisXaPhuongCOHN(false);
    setQuanHuyenValueCOHN(values);
    setXaPhuongValueCOHN(null);
  };
  const onChangeXaPhuongCOHN = (values) => {
    setField("phuong_xa_id", values.id);
    setXaPhuongValueCOHN(values);
  };
  // cccd/cmnd

  const getCATinhThanh = () => {
    axios
      .get(api.API_CA_TINH_THANH)
      .then(async ({ data }) => {
        if (data) {
          let resultsCATinhThanh = data?.results;
          resultsCATinhThanh.forEach((e) => {
            e.label = e.ten;
            e.value = e.id;
          });
          setListNoiCapCMND(resultsCATinhThanh);
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => { });
  };
  // van bang chuyen mon
  const getVanBangChuyenMon = () => {
    axios
      .post(api.API_LIST_VAN_BANG_CHUYEN_MON, { search_ten: "" })
      .then(async ({ data }) => {
        if (data) {
          let resultsVBCM = data?.results;
          resultsVBCM.forEach((e) => {
            e.label = e.ten;
            e.value = e.id;
          });
          setListVanBangChuyenMon(resultsVBCM);
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => { });
  };
  const getNoiTotNghiep = () => {
    axios
      .post(api.API_LIST_NOI_TOT_NGHIEP_GET_LIST, {})
      .then(async ({ data }) => {
        let resultsNoiTotnghiep = data.results;
        resultsNoiTotnghiep.forEach((data) => {
          data.label = data.ten;
          data.value = data.id;
        });
        setListNoiTotNghiep(data.results);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => { });
  };

  // selected ngay
  const selectedNS = async (date) => {
    await setField("ngay_sinh", date);
  };
  const selectedNCCMND = async (date) => {
    await setField("ngay_cap", date);
  };

  //validate thêm thủ tục
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const resetState = () => {
    setForm({});
    setErrors({});
    setShowUpdate(false);
    const updatedData = listNguoiDung?.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setData(updatedData);
  };

  // DETAIL
  const detail = async (data) => {
    setForm({
      ...data,
      ngay_sinh: data?.ngay_sinh * 1000,
      ngay_cap: data?.ngay_cap * 1000,
    });

    let dataSelectedCMND = listNoiCapCMND.find(
      (e, i) => e.ten === data?.noi_cap
    );
    console.log("log dataSelectedCMND", data?.noi_cap);
    console.log("log listNoiCapCMND", listNoiCapCMND);

    setSelectedNoiCapCMND(dataSelectedCMND);
    setTrang_thai(data?.active);
    setShowUpdate(true);
    console.log(data);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    // await setIsLoading(true);
    axios
      .post(api.API_LIST_THU_TUC + `?page=${page}&per_page=${newPerPage}`, {})
      .then(({ data }) => {
        if (data?.results) {
          setListNguoiDung(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => { })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  //đổi màu khi selected
  const [data, setData] = useState(listNguoiDung);
  useEffect(() => {
    setData(listNguoiDung);
  }, [listNguoiDung]);

  const handleRowClicked = (row) => {
    setErrors({});
    const updatedData = listNguoiDung?.map((item) => {
      if (row.id !== item.id) {
        return item;
      }
      return {
        ...item,
        toggleSelected: !item.toggleSelected,
      };
    });
    setData(updatedData);
    showModal();
    setEdit(true);
  };

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Họ và tên",
      grow: 3,
      selector: (row) => (
        <div
          onClick={() => {
            resetState();
            detail(row);
            setItem(row);
            handleRowClicked(row);
            setShowAdd(true);
            showModalQuanLyNguoiDung();
          }}
        >
          <span>{row.ho_ten}</span>
          <ReactTooltip place="top" type="dark" effect="float" />
        </div>
      ),
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Email",
      selector: (row) => <span>{row.email}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Điện Thoại",
      selector: (row) => <span>{row.dien_thoai}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Vai Trò",
      selector: (row) => <span>{row.chuc_vu}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trang thái",
      selector: (row) => <span>{row.active}</span>,
      grow: 3,
      center: true,
      cell: (row) => (
        <td>
          <Status status={row.active} />
        </td>
      ),
    },
  ];
  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];
  return (
    <>
      {/* <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}> */}
      <PageTitle breadcrumbs={usersBreadcrumbs}>Danh sách người dùng</PageTitle>
      <KTCard>
        <div className="page">
          <div className="container-fluid">
            <div className="fv-row mb-7 mt-6">
              {/* begin::Label */}
              <div className="col-md-12 col-sm-12 text-center">
                <label className="fw-bold fs-6 mb-2">
                  DANH SÁCH NGƯỜI DÙNG
                </label>
              </div>
              {/* end::Label */}

              {/* begin::Input */}

              <div className="row">
                <div className="col-md-6 col-sm-6">
                  <input
                    placeholder="Nhập tên
                    "
                    type="text"
                    name="name"
                    className={clsx(
                      "form-control form-control-solid mb-3 mb-lg-0"
                    )}
                    autoComplete="off"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>

                <div
                  className="col-md-6 col-sm-6 d-flex justify-content-start"
                  data-kt-user-table-toolbar="base"
                >
                  {/* <button type="button" className="btn btn-light-primary me-3">
                  <KTSVG
                    path="/media/icons/duotune/arrows/arr078.svg"
                    className="svg-icon-2"
                  />
                  Export
                </button> */}
                </div>
              </div>

              {/* end::Input */}
            </div>
            <BlockUi
              tag="div"
              // blocking={isLoading}
              centerY={true}
              centerX={true}
            >
              <div className="row">
                <div className="col">
                  <div className="" style={cardBox}>
                    <DataTable
                      noDataComponent={"Không có dữ liệu."}
                      sortServer
                      progressPending={isLoading}
                      columns={columns}
                      data={data}
                      customStyles={customStyles}
                      pagination
                      highlightOnHover
                      pointerOnHover
                      paginationServer
                      paginationTotalRows={totalRows}
                      onChangeRowsPerPage={handlePerRowsChange}
                      onChangePage={handlePageChange}
                      paginationComponentOptions={paginationOptions}
                      conditionalRowStyles={conditionalRowStyles}
                      // onRowClicked={(data) => {
                      //   detail(data);
                      //   handleRowClicked(data);
                      //   setShowAdd(true);
                      // }}
                      progressComponent={
                        <div
                          style={{
                            padding: "24px",
                          }}
                        >
                          <Oval
                            arialLabel="loading-indicator"
                            color="#007bff"
                            height={40}
                          />
                        </div>
                      }
                    />
                  </div>
                </div>
              </div>

              <PopupQuanLyNguoiDung
                getList={() => {
                  getList({});
                }}
                data={Item}
                show={showModalQLND}
                hide={closeModalQuanLyNguoiDung}
                edit={edit}
              />
              <PopupDelete
                title="Bạn có đồng ý xóa thủ tục này không?"
                show={isvisibleDelete}
                onHide={closeModalDelete}
              // onDetele={() => {
              //   deleteTT();
              // }}
              />
            </BlockUi>
          </div>
        </div>
      </KTCard>
      {/* </LocalizationProvider> */}
    </>
  );
}
