import axios from "axios";
import { useEffect, useState } from "react";
import BlockUi from "react-block-ui";
import { Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
// import { useDispatch } from "react-redux";
import clsx from "clsx";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { KTCard, KTSVG, useDebounce } from "../../../../_metronic/helpers";
import { PageTitle } from "../../../../_metronic/layout/core";
import Status from "../../../components/Status";
import api from "../../../configs/api";
import { PopupDelete } from "../popup/PopupDelete";

export function LoaiMaChungChi() {
  const navigate = useNavigate();
  const [listLoaiMaChungChi, setListLoaiMaChungChi] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [stt, setStt] = useState(0);
  const [trang_thai, setTrang_thai] = useState(true);

  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);
  const [searchTerm, setSearchTerm] = useState("");

  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList(1, perPage, debouncedSearchTerm);
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = ({
    page_number = page,
    size = perPage,
    search_key = searchTerm,
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${page_number}&per_page=${size}`,
        { ten: search_key }
      )
      .then(async ({ data }) => {
        setListLoaiMaChungChi(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  //validate thêm loại mã chứng chỉ
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const findFormErrors = () => {
    const { ten, ma_noi_tot_nghiep, chi_tiet, loai, stt, ten_in } = form;
    const newErrors = {};
    // name errors
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";
    if (!ma_noi_tot_nghiep || ma_noi_tot_nghiep === "")
      newErrors.ma_noi_tot_nghiep = "Mã không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
    } else {
      // No errors! Put any logic here for the form submission!
      showUpdate ? updateLoaiMaChungChi() : addLoaiMaChungChi();
    }
  };

  // handle api
  const addLoaiMaChungChi = async () => {
    setIsLoading(true);
    const { ten, ma_noi_tot_nghiep } = form;
    const json = {
      ten,
      ma_noi_tot_nghiep,
      trang_thai: trang_thai,
    };
    await axios
      .post(api.API_NOI_TOT_NGHIEP, json)
      .then(async ({ data }) => {
        resetState();
        toast.success("Tạo loại mã chứng chỉ thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({});
        setIsLoading(false);
      })
      .catch((error) => {
        console.log("error", error?.response);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const updateLoaiMaChungChi = async () => {
    setIsLoading(true);
    const { ten, ma_noi_tot_nghiep, id } = form;
    const json = {
      ten,
      ma_noi_tot_nghiep,
      trang_thai: trang_thai,
    };
    await axios
      .put(api.API_NOI_TOT_NGHIEP + "/" + id, json)
      .then(async ({ data }) => {
        resetState();
        toast.success("Cập nhật loại mã chứng chỉ thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({});
      })
      .catch((error) => {
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const resetState = () => {
    setForm({
      ma_noi_tot_nghiep: "",
      ten: "",
    });
    setErrors({});
    setShowUpdate(false);
    const updatedData = listLoaiMaChungChi?.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setData(updatedData);
  };

  // DETAIL
  const detail = async (data) => {
    setForm({
      ...data,
    });
    setTrang_thai(data?.trang_thai);
    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${page}&per_page=${newPerPage}`,
        {}
      )
      .then(({ data }) => {
        if (data?.results) {
          setListLoaiMaChungChi(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteNTN = () => {
    axios
      .delete(api.API_NOI_TOT_NGHIEP + "/" + idDelete)
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        closeModalDelete();
        resetState();
        if (page == 1) {
          getList({ page_number: 1 });
        } else if (listLoaiMaChungChi?.length == 1) {
          getList({ page_number: page - 1 });
        } else {
          getList({ page_number: page });
        }
      })
      .catch((error) => {
        console.log("error", error);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };
  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  //đổi màu khi selected
  const [data, setData] = useState(listLoaiMaChungChi);
  useEffect(() => {
    setData(listLoaiMaChungChi);
  }, [listLoaiMaChungChi]);

  const handleRowClicked = (row) => {
    setErrors({});
    const updatedData = listLoaiMaChungChi?.map((item) => {
      if (row.id !== item.id) {
        return item;
      }
      return {
        ...item,
        toggleSelected: !item.toggleSelected,
      };
    });
    setData(updatedData);
  };
  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );
  const handleChangeLocation = (evt) => {
    const temp = evt.target.validity.valid ? evt.target.value : stt;
    setStt(temp);
  };
  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Mã",
      selector: (row) => <span>{row.ma_noi_tot_nghiep}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Tên",
      selector: (row) => <span>{row.ten}</span>,
      grow: 8,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trang thái",
      selector: (row) => <span>{row.trang_thai}</span>,
      grow: 3,
      cell: (row) => (
        <td>
          <Status status={row.trang_thai} />
        </td>
      ),
    },
    {
      name: "Thao tác",
      grow: 2,
      cell: (row) => (
        <div className="container my-3">
          <button
            onClick={async () => {
              await setIdDelete(row?.id);
              showModalDelete();
            }}
            className="btn btn-icon btn-bg-light btn-color-danger btn-sm"
          >
            <KTSVG
              path="/media/icons/duotune/general/gen027.svg"
              className="svg-icon-3"
            />
          </button>
        </div>
      ),
    },
  ];
  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];

  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];

  return (
    <KTCard>
      <PageTitle breadcrumbs={usersBreadcrumbs}>Loại mã chứng chỉ</PageTitle>
      <div className="page">
        <div className="container-fluid">
          <div className="fv-row mb-7 mt-6">
            {/* begin::Label */}
            <label className="required fw-bold fs-6 mb-2">
              Tên loại mã chứng chỉ
            </label>
            {/* end::Label */}

            {/* begin::Input */}
            <div className="col-md-6">
              <input
                placeholder="Nhập tên tìm kiếm..."
                type="text"
                name="name"
                className={clsx("form-control form-control-solid mb-3 mb-lg-0")}
                autoComplete="off"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* end::Input */}
          </div>
          <BlockUi tag="div" blocking={isLoading} centerY={true} centerX={true}>
            <div className="row">
              <div className="col-md-8">
                <div className="" style={cardBox}>
                  <DataTable
                    noDataComponent={"Không có dữ liệu."}
                    sortServer
                    progressPending={isLoading}
                    columns={columns}
                    data={data}
                    customStyles={customStyles}
                    pagination
                    highlightOnHover
                    pointerOnHover
                    paginationServer
                    paginationTotalRows={totalRows}
                    onChangeRowsPerPage={handlePerRowsChange}
                    onChangePage={handlePageChange}
                    paginationComponentOptions={paginationOptions}
                    conditionalRowStyles={conditionalRowStyles}
                    onRowClicked={(data) => {
                      detail(data);
                      handleRowClicked(data);
                    }}
                    progressComponent={
                      <div
                        style={{
                          padding: "24px",
                        }}
                      >
                        <Oval
                          arialLabel="loading-indicator"
                          color="#007bff"
                          height={40}
                        />
                      </div>
                    }
                  />
                </div>
              </div>
              <div className="col-md-4">
                <div className="" style={cardBox}>
                  <div className="">
                    <p className="font-weight-bold">
                      {showUpdate
                        ? "Cập nhật loại mã chứng chỉ"
                        : "Tạo mới loại mã chứng chỉ"}
                    </p>
                  </div>

                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">
                        Mã<span className="text-danger">*</span>
                      </p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        type="text"
                        placeholder="Nhập tên mã loại mã chứng chỉ"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={
                          form.ma_noi_tot_nghiep === null
                            ? ""
                            : form.ma_noi_tot_nghiep
                        }
                        onChange={async (e) => {
                          await setField("ma_noi_tot_nghiep", e.target.value);
                        }}
                        isInvalid={!!errors.ma_noi_tot_nghiep}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.ma_noi_tot_nghiep}
                      </Form.Control.Feedback>
                    </div>
                  </div>

                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">
                        Tên<span className="text-danger">*</span>
                      </p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Nhập tên mã loại mã chứng chỉ"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.ten === null ? "" : form.ten}
                        onChange={(e) => {
                          setField("ten", e.target.value);
                        }}
                        isInvalid={!!errors.ten}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.ten}
                      </Form.Control.Feedback>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                    </div>
                    <div className="col-8">
                      <div className="form-check form-check-solid form-switch fv-row">
                        <input
                          className="form-check-input w-45px h-30px"
                          type="checkbox"
                          id="trang_thai"
                          checked={trang_thai}
                          onChange={(e) => {
                            setTrang_thai(!trang_thai);
                          }}
                        />
                        <label className="form-check-label"></label>
                      </div>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      {showUpdate ? (
                        <button
                          className="btn text-white bg-blue2"
                          style={{ fontSize: ".9rem" }}
                          onClick={() => resetState()}
                        >
                          <i className="fas fa-sync-alt text-white"></i> Làm mới
                        </button>
                      ) : null}
                    </div>

                    <div className="col-8">
                      <button
                        className="btn text-white bg-blue2 mr-3"
                        style={{ fontSize: ".9rem" }}
                        onClick={(e) => {
                          handleSubmit(e);
                        }}
                      >
                        {showUpdate ? (
                          <i className="fa fa-pencil-alt text-white"></i>
                        ) : (
                          <i className="fas fa-save text-white"></i>
                        )}{" "}
                        {showUpdate ? "Cập nhật" : "Tạo loại mã chứng chỉ"}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <PopupDelete
              title="Bạn có đồng ý xóa loại mã chứng chỉ này không?"
              show={isvisibleDelete}
              onHide={closeModalDelete}
              onDetele={() => {
                deleteNTN();
              }}
            />
          </BlockUi>
        </div>
      </div>
    </KTCard>
  );
}
