import axios from "axios";
import moment from "moment";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";
import api from "../../../../../configs/api";
import { GioiTinh } from "../../../../../data";

export default function ThongTinCaNhan(props) {
  const { yeu_cau_cchnd } = props?.thongTinHoSo;

  const [isLoading, setIsLoading] = useState(false);

  const [vanBang, setVanBang] = useState({});
  const [noiTotNghiep, setNoiTotNghiep] = useState({});

  const empty_json = { }

  useEffect(() => {
    getVanBangList();
    getNoiTotNghiepList();
  }, [])

  const getVanBangList = () => {
    axios
      .post(api.API_LIST_VAN_BANG + `?page=${1}&per_page=${100}`, empty_json)
      .then(async ({ data }) => {
        if (yeu_cau_cchnd?.doi_tuong?.van_bang_chuyen_mon_id) {
          data?.results.forEach((e) => {
            if (e.id === yeu_cau_cchnd?.doi_tuong?.van_bang_chuyen_mon_id) {
              setVanBang(e)
              console.log("vanBang", e)
            }
          });
        }
      })
      .catch((error) => {
        toast.error("Không thể lấy danh sách văn bằng chuyên môn", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const getNoiTotNghiepList = () => {
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${1}&per_page=${100}`,
        empty_json
      )
      .then(async ({ data }) => {
        if (yeu_cau_cchnd?.doi_tuong?.noi_tot_nghiep_id) {
          data?.results.forEach((e) => {
            if (e.id === yeu_cau_cchnd?.doi_tuong?.noi_tot_nghiep_id) {
              setNoiTotNghiep(e)
              console.log("noiTotNghiep", e)
            }
          });
        }
      })
      .catch((error) => {
        toast.error("Không thể lấy danh sách nơi tốt nghiệp", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const selectGioiTinh = (id) => {
    let tempGioiTinh = GioiTinh.find((e) => e.value === id);
    return tempGioiTinh.label;
  };

  return (
    <div className="card-box py-6 px-6 mx-3 my-3">
      <div className="fw-bold fs-5 mb-1">THÔNG TIN CÁ NHÂN</div>
      <div className="row">
        <div className="col-3 pr-0">
          <span className="text-fix pr-1">Họ tên: </span>
          <span className="text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.ho || ""}{" "}
            {yeu_cau_cchnd?.doi_tuong?.ten || ""}
          </span>
        </div>
        <div className="col-2">
          <span className="text-fix pr-1">Ngày sinh: </span>
          <span className=" text-user-info">
            {moment(Number(yeu_cau_cchnd?.doi_tuong?.ngay_sinh)).format(
              "DD/MM/YYYY"
            ) || ""}
          </span>
        </div>
        <div className="col-2">
          <span className="text-fix pr-1">Giới tính: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.gioi_tinh
              ? selectGioiTinh(yeu_cau_cchnd?.doi_tuong?.gioi_tinh)
              : ""}
          </span>
        </div>
        <div className="col-3">
          <span className="text-fix pr-1">Email: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.email || ""}
          </span>
