import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import TextField from "@mui/material/TextField";
import { vi } from "date-fns/locale";
import { useState, useEffect } from "react";
import ImageUploading from "react-images-uploading";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { selectCurrentUser } from "../../../redux-module/auth/authSlice";
import { useSelector } from "react-redux";
import { Form } from "react-bootstrap";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import axios from "axios";
import { PageTitle } from "../../../../_metronic/layout/core";
import LoadingOverlay from "react-loading-overlay";
// import Switch from "react-switch";

export default function ThongTinToChuc() {
  let navigate = useNavigate();
  const thongTinToChuc = useSelector(selectCurrentUser);

  const [form, setForm] = useState({});
  const [errors, setErrors] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [disabledChuQuan, setdisabledChuQuan] = useState(true);

  const [listTinhThanhCS, setListTinhThanhCS] = useState([]);
  const [listQuanHuyenCS, setListQuanHuyenCS] = useState([]);
  const [listXaPhuongCS, setListXaPhuongCS] = useState([]);
  const [listTinhThanhTT, setListTinhThanhTT] = useState([]);
  const [listQuanHuyenTT, setListQuanHuyenTT] = useState([]);
  const [listXaPhuongTT, setListXaPhuongTT] = useState([]);

  const [selectedTTCS, setSelectedTTCS] = useState(null);
  const [selectedQHCS, setSelectedQHCS] = useState(null);
  const [selectedXPCS, setSelectedXPCS] = useState(null);

  const [selectedTTTT, setSelectedTTTT] = useState(null);
  const [selectedQHTT, setSelectedQHTT] = useState(null);
  const [selectedXPTT, setSelectedXPTT] = useState(null);

  // loai hinh kinh doanh
  const [selectedLoaiHinhKinhDoanh, setSelectedLoaiHinhKinhDoanh] = useState(
    []
  );
  const [listLoaiHinhKinhDoanh, setListLoaiHinhKinhDoanh] = useState([]);

  useEffect(() => {
    setForm(thongTinToChuc);
    getLoaiHinhKinhDoanh();
    getTinhThanh();
    console.log("thong tin to chuc", thongTinToChuc);
  }, []);

  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  const usersBreadcrumbs = [
    {
      title: "Trang chá»§",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];
  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const getTinhThanh = () => {
    axios
      .get(api.API_TINH_THANH)
      .then(({ data }) => {
        let resultsTinhThanh = data?.results;
        resultsTinhThanh.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });

        if (thongTinToChuc.tinh_thanh_id) {
          const ttcs = resultsTinhThanh.find(
            (e) => e.id === thongTinToChuc.tinh_thanh_id
          );
          setSelectedTTCS(ttcs);
          getQuanHuyen(ttcs.id, "coso");
        }
        if (thongTinToChuc.tinh_thanh_hien_nay_id) {
          const tttt = resultsTinhThanh.find(
            (e) => e.id === thongTinToChuc.tinh_thanh_hien_nay_id
          );
          setSelectedTTTT(tttt);
          getQuanHuyen(tttt.id, "tructhuoc");
        }

        setListTinhThanhCS(resultsTinhThanh);
        setListTinhThanhTT(resultsTinhThanh);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getQuanHuyen = (id, type) => {
    axios
      .get(`${api.API_QUAN_HUYEN}/${id}?per_page=100`)
      .then(({ data }) => {
        let resultsQuanHuyen = data?.results;
        resultsQuanHuyen.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });

        if (thongTinToChuc.quan_huyen_id === id) {
          const qhcs = resultsQuanHuyen.find(
            (e) => e.id === thongTinToChuc.xa_phuong_id
          );
          setSelectedQHCS(qhcs);
          getXaPhuong(qhcs.id, "coso");
        }
        if (thongTinToChuc.quan_huyen_hien_nay_id === id) {
          const qhtt = resultsQuanHuyen.find(
            (e) => e.id === thongTinToChuc.xa_phuong_hien_nay_id
          );
          setSelectedQHTT(qhtt);
          getXaPhuong(qhtt.id, "tructhuoc");
        }

        if (type === "coso") {
          setListQuanHuyenCS(resultsQuanHuyen);
        } else if (type === "tructhuoc") {
          setListQuanHuyenTT(resultsQuanHuyen);
        }
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getXaPhuong = (id, type) => {
    axios
      .get(`${api.API_XA_PHUONG}/${id}?per_page=100`)
      .then(({ data }) => {
        let resultsXaPhuong = data?.results;
        resultsXaPhuong.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });

        if (thongTinToChuc.quan_huyen_id === id) {
          const xpcs = resultsXaPhuong.find(
            (e) => e.id === thongTinToChuc.xa_phuong_id
          );
          setSelectedXPCS(xpcs);
        }
        if (thongTinToChuc.quan_huyen_hien_nay_id === id) {
          const xptt = resultsXaPhuong.find(
            (e) => e.id === thongTinToChuc.xa_phuong_hien_nay_id
          );
          setSelectedXPTT(xptt);
        }

        if (type === "coso") {
          setListXaPhuongCS(resultsXaPhuong);
        } else if (type === "tructhuoc") {
          setListXaPhuongTT(resultsXaPhuong);
        }
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getLoaiHinhKinhDoanh = () => {
    axios
      .post(api.API_LIST_LOAI_HINH_KINH_DOANH, {
        search_ten: "",
      })
      .then(async ({ data }) => {
        if (data) {
          let resultsLoaiHinhKinhDoanh = data?.results;
          resultsLoaiHinhKinhDoanh.forEach((e) => {
            e.label = e.ten;
            e.value = e.id;
          });
          setListLoaiHinhKinhDoanh(resultsLoaiHinhKinhDoanh);
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  // HANDLE LOCATIONs
  const onChangeTinhThanhCS = (values) => {
    setField("tinh_thanh_coso_id", values.id);
    getQuanHuyen(values.id, "coso");
    setSelectedTTCS(values);
    setSelectedQHCS(null);
    setSelectedXPCS(null);
  };
  const onChangeQuanHuyenCS = (values) => {
    setField("quan_huyen_coso_id", values.id);
    getXaPhuong(values.id, "coso");
    setSelectedQHCS(values);
    setSelectedXPCS(null);
  };
  const onChangeXaPhuongCS = (values) => {
    setField("xa_phuong_coso_id", values.id);
    setSelectedXPCS(values);
  };

  const onChangeTinhThanhTT = (values) => {
    setField("tinh_thanh_tructhuoc_id", values.id);
    getQuanHuyen(values.id, "tructhuoc");
    setSelectedTTTT(values);
    setSelectedQHTT(null);
    setSelectedXPTT(null);
  };
  const onChangeQuanHuyenTT = (values) => {
    setField("quan_huyen_tructhuoc_id", values.id);
    getXaPhuong(values.id, "tructhuoc");
    setSelectedQHTT(values);
    setSelectedXPTT(null);
  };
  const onChangeXaPhuongTT = (values) => {
    setField("xa_phuong_tructhuoc_id", values.id);
    setSelectedXPTT(values);
  };

  const handleNgayCap = (newValue) => {
    setField("ngay_cap", new Date(newValue).getTime());
  };

  const findFormErrors = () => {
    const newErrors = {};

    return newErrors;
  };

  const onSubmit = async (e) => {
    await setIsLoading(true);
    e.preventDefault();

    const newErrors = findFormErrors();

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setIsLoading(false);
    } else {
      const configs = {
        headers: {
          "content-type": "multipart/form-data",
        },
      };

      const {
        ten_coso,
        website_co_so,
        ma_coso,
        ngay_cap,
        email_coso,
        dienthoai_coso,
        fax_coso,
        diachi_coso,
        ten_nguoi_lien_he,
        chuc_danh_nguoi_lienhe,
        email_nguoi_lienhe,
        dien_thoai_nguoi_lienhe,
        dia_chi_nguoi_lien_he,
        ten_truc_thuoc,
        ma_tructhuoc,
        diachi_tructhuoc,
      } = form;

      const json = new FormData();

      await json.append("ten_coso", ten_coso);
      await json.append(
        "loai_hinh_kinh_doanh_id",
        selectedLoaiHinhKinhDoanh ? selectedLoaiHinhKinhDoanh : null
      );
      await json.append("website_co_so", website_co_so);
      await json.append("ma_coso", ma_coso);
      await json.append("ngay_cap", ngay_cap ? ngay_cap : null);
      await json.append("email_coso", email_coso);
      await json.append("dienthoai_coso", dienthoai_coso);
      await json.append("fax_coso", fax_coso);
      await json.append(
        "tinh_thanh_coso_id",
        selectedTTCS ? selectedTTCS?.value : null
      );
      await json.append(
        "quan_huyen_coso_id",
        selectedQHCS ? selectedQHCS?.value : null
      );
      await json.append(
        "xa_phuong_coso_id",
        selectedXPCS ? selectedXPCS?.value : null
      );
      await json.append("diachi_coso", diachi_coso);
      await json.append("ten_nguoi_lien_he", ten_nguoi_lien_he);
      await json.append("chuc_danh_nguoi_lienhe", chuc_danh_nguoi_lienhe);
      await json.append("email_nguoi_lienhe", email_nguoi_lienhe);
      await json.append("dien_thoai_nguoi_lienhe", dien_thoai_nguoi_lienhe);
      await json.append("dia_chi_nguoi_lien_he", dia_chi_nguoi_lien_he);
      await json.append("ten_truc_thuoc", ten_truc_thuoc);
      await json.append("ma_tructhuoc", ma_tructhuoc);
      await json.append(
        "tinh_thanh_tructhuoc_id",
        selectedTTTT ? selectedTTTT?.value : null
      );
      await json.append(
        "quan_huyen_tructhuoc_id",
        selectedQHTT ? selectedQHTT?.value : null
      );
      await json.append(
        "xa_phuong_tructhuoc_id",
        selectedXPTT ? selectedXPTT?.value : null
      );
      await json.append("diachi_tructhuoc", diachi_tructhuoc);

      // const json = {
      //     ten_coso,
      //     // loai_hinh_kinh_doanh_id: selectedLoaiHinhKinhDoanh,
      //     website_co_so,
      //     ma_coso,
      //     ngay_cap,
      //     email_coso,
      //     dienthoai_coso,
      //     fax_coso,
      //     tinh_thanh_coso_id: selectedTTCS?.value,
      //     quan_huyen_coso_id: selectedQHCS?.value,
      //     xa_phuong_coso_id: selectedXPCS?.value,
      //     diachi_coso,
      //     ten_nguoi_lien_he,
      //     chuc_danh_nguoi_lienhe,
      //     email_nguoi_lienhe,
      //     dien_thoai_nguoi_lienhe,
      //     dia_chi_nguoi_lien_he,
      //     ten_truc_thuoc,
      //     ma_tructhuoc,
      //     tinh_thanh_tructhuoc_id: selectedTTTT?.value,
      //     quan_huyen_tructhuoc_id: selectedQHTT?.value,
      //     xa_phuong_tructhuoc_id: selectedXPTT?.value,
      //     diachi_tructhuoc
      // }

      axios
        .put(api.API_CO_SO_KINH_DOANH + "/" + thongTinToChuc.id, json, configs)
        .then(({ data }) => {
          console.log(data);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  return (
    <div>
      <LoadingOverlay spinner active={isLoading} text="Äang táº£i dá»¯ liá»u ...">
        <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
          <PageTitle breadcrumbs={usersBreadcrumbs}>
            ThÃ´ng tin tÃ i khoáº£n
          </PageTitle>
          <div className="card">
            <div className="card-body">
              <div className="row">
                <div className="col-md-6">
                  <div style={cardBox}>
                    <ImageUploading maxNumber={1} dataURLKey="data_url">
                      {({
                        imageList,
                        onImageUpload,
                        onImageRemove,
                        isDragging,
                        dragProps,
                      }) => (
                        <div className="form-group">
                          {imageList.length >= 1 ? (
                            imageList.map((image, index) => (
                              <div key={index} className="form-group">
                                <button
                                  style={{ fontSize: "14px" }}
                                  className="btn btn-danger"
                                  onClick={() => onImageRemove(index)}
                                >
                                  XÃ³a
                                </button>
                                <div>
                                  <img
                                    className="mr-2 my-2"
                                    src={image.data_url}
                                    alt=""
                                    width="140"
                                  />
                                </div>
                              </div>
                            ))
                          ) : (
                            <div>
                              <div className="avatar-img">
                                <i
                                  className="fas fa-camera btn-change-avatar"
                                  // onClick={onImageUpload}
                                ></i>
                                <img
                                  src="/media/avatars/default-forum-user.png"
                                  alt=""
                                  className="rounded-circle border p-1"
                                  id="avatar-img"
                                  width="150"
                                  height="150"
                                  backgroundColor="red"
                                />
                              </div>
                            </div>
                          )}
                        </div>
                      )}
                    </ImageUploading>
                    <div className="form-group">
                      <label className="fw-bold fs-6 mb-1">
                        
                        TÃªn cÆ¡ sá»/Äá»a Äiá»m kinh doanh
                      </label>
                      <Form.Control
                        type="text"
                        placeholder="Nháº­p há» tÃªn"
                        style={{
                          fontSize: 14,
                          fontWeight: 400,
                          height: "38px",
                        }}
                        onChange={(e) => {
                          setField("ten_coso", e.target.value);
                        }}
                        value={form?.ten_coso != null ? form?.ten_coso : ""}
                      />
                    </div>
                    <div className="form-group mt-2">
                      <label className="fw-bold fs-6 mb-1">
                        
                        Loáº¡i hÃ¬nh kinh doanh
                      </label>
                      <Select
                        value={selectedLoaiHinhKinhDoanh}
                        key={"id"}
                        options={listLoaiHinhKinhDoanh}
                        placeholder="Chá»n loáº¡i hÃ¬nh kinh doanh"
                        formatGroupLabel={formatGroupLabel}
                        onChange={(e) => {
                          setSelectedLoaiHinhKinhDoanh(
                            ...selectedLoaiHinhKinhDoanh,
                            e.id
                          );
                        }}
                      />
                    </div>
                    <div className="form-group mt-2">
                      <label className="fw-bold fs-6 mb-1"> Website </label>
                      <Form.Control
                        type="text"
                        placeholder="Website"
                        style={{
                          fontSize: 14,
                          fontWeight: 400,
                          height: "38px",
                        }}
                        onChange={(e) => {
                          setField("website_co_so", e.target.value);
                        }}
                        value={
                          form?.website_co_so != null ? form?.website_co_so : ""
                        }
                      />
                    </div>
                    <div className="form-group mt-2">
                      <label className="fw-bold fs-6 mb-1">
                        
                        MÃ£ sá» Doanh nghiá»p/Äá»a Äiá»m kinh doanh
                      </label>
                      <Form.Control
                        type="text"
                        placeholder="MÃ£ sá» Doanh nghiá»p/Äá»a Äiá»m kinh doanh"
                        style={{
                          fontSize: 14,
                          fontWeight: 400,
                          height: "38px",
                        }}
                        onChange={(e) => {
                          setField("ma_coso", e.target.value);
                        }}
                        value={form?.ma_coso != null ? form?.ma_coso : ""}
                      />
                    </div>
                    <div className="form-group mt-2">
                      <label className="fw-bold fs-6 mb-1">
                        
                        NgÃ y cáº¥p GCN DKKD
                      </label>

                      <DesktopDatePicker
                        label=" "
                        inputFormat="dd/MM/yyyy"
                        value={form?.ngay_cap ? form?.ngay_cap : null}
                        onChange={handleNgayCap}
                        maxDate={new Date()}
                        renderInput={(params) => (
                          <TextField size="small" {...params} />
                        )}
                      />
                    </div>
                    <div className="form-group mt-2">
                      <label className="fw-bold fs-6 mb-1"> Email </label>
                      <Form.Control
                        type="text"
                        placeholder="Email"
                        style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                        onChange={(e) => {
                          setField("email_coso", e.target.value);
                        }}
                        value={form?.email_coso != null ? form?.email_coso : ""}
                      />
                    </div>
                    <div className="form-group mt-2">
                      <label className="fw-bold fs-6 mb-1">
                        
                        Sá» Äiá»n thoáº¡i
                      </label>
                      <Form.Control
                        type="text"
                        placeholder="Nháº­p sá» Äiá»n thoáº¡i"
                        style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                        onChange={(e) => {
                          setField("dienthoai_coso", e.target.value);
                        }}
                        value={
                          form?.dienthoai_coso != null
                            ? form?.dienthoai_coso
                            : ""
                        }
                      />
