import axios from "axios";
import React, { useEffect, useState } from "react";
import { Dropdown, Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
// import { useDispatch } from "react-redux";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { TextField } from "@mui/material";
import { vi } from "date-fns/locale";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { toast } from "react-toastify";
import { KTCard, useDebounce } from "../../../../_metronic/helpers";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";
import { TrangThaiHoSo } from "../../../data";

export function DanhSachHoSoChoInToChuc() {
  const navigate = useNavigate();
  const [listHoSoThuLy, setListHoSoThuLy] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [stt, setStt] = useState(0);
  const [trang_thai, setTrang_thai] = useState(true);

  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);
  const [searchMaHoSo, setSearchMaHoSo] = useState("");
  const [searchHoTen, setSearchHoTen] = useState("");
  const [dateHanDen, setDateHanDen] = useState(new Date());
  const [dateHanTu, setDateHanTu] = useState(new Date());
  const [dateNopDen, setDateNopDen] = useState(new Date());
  const [dateNopTu, setDateNopTu] = useState(new Date());
  const [ListDMHetHan, setListDMHetHan] = useState([]);
  const [ListDMThuTuc, setListDMThuTuc] = useState([]);
  const [valueThuTuc, setValueThuTuc] = useState({
    label: "Tất cả",
    value: "",
  });
  const [valueTrangThai, setValueTrangThai] = useState({
    label: "Tất cả",
    value: "",
  });
  const [valueTrangThaiHetHan, setValueTrangThaiHetHan] = useState({
    label: "Tất cả",
    value: "",
  });
  const [ListDMTrangThaiHoSo, setListDMTrangThaiHoSo] = useState([]);
  const [selectedRows, setSelectedRows] = React.useState(false);
  const [toggledClearRows, setToggleClearRows] = React.useState(false);

  const handleChange = ({ selectedRows }) => {
    setSelectedRows(selectedRows);
  };

  // Toggle the state so React Data Table changes to clearSelectedRows are triggered
  const handleClearRows = () => {
    setToggleClearRows(!toggledClearRows);
  };

  const debouncedSearchMaHoSo = useDebounce(searchMaHoSo, 1000);
  const debouncedSearchHoTen = useDebounce(searchHoTen, 1000);

  useEffect(
    () => {
      getListDM();
    },
    [] // Only call effect if debounced search term changes
  );

  useEffect(
    () => {
      if (
        (debouncedSearchMaHoSo !== undefined && searchMaHoSo !== undefined) ||
        (debouncedSearchHoTen !== undefined && searchMaHoSo !== undefined)
      ) {
        getList({
          page_number: 1,
          ma_ho_so: debouncedSearchMaHoSo,
          ho_ten: debouncedSearchHoTen,
        });
      }
    },
    [debouncedSearchMaHoSo, debouncedSearchHoTen] // Only call effect if debounced search term changes
  );
  const getListDM = () => {
    axios
      .get(api.API_DANH_MUC_HO_SO_THU_LY_DUOC_SI)
      .then(async ({ data }) => {
        let { het_hans } = data?.results;
        let { thu_tuc } = data?.results;
        let { trang_thai_ho_so } = data?.results;
        het_hans.unshift({
          label: "Tất cả",
          value: "",
        });
        thu_tuc.unshift({
          label: "Tất cả",
          value: "",
        });
        trang_thai_ho_so.unshift({
          label: "Tất cả",
          value: "",
        });
        setListDMHetHan(het_hans);
        setListDMThuTuc(thu_tuc);
        setListDMTrangThaiHoSo(trang_thai_ho_so);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const getList = ({
    page_number = page,
    size = perPage,
    ma_ho_so = searchMaHoSo,
    ho_ten = searchHoTen,
    // nop_tu = String(new Date(dateNopTu).getTime()),
    // nop_den = String(new Date(dateNopDen).getTime()),
    // han_tu = String(new Date(dateHanTu).getTime()),
    // han_den = String(new Date(dateHanDen).getTime()),
    nop_tu = "",
    nop_den = "",
    han_tu = "",
    han_den = "",
    thu_tuc_id = valueThuTuc?.value || "",
    trang_thai = valueTrangThai?.value || "",
    trang_thai_het_han = valueTrangThaiHetHan?.value || "",
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_LIST_HO_SO_THU_LY_DUOC_SI +
          `?page=${page_number}&per_page=${size}`,
        {
          ma_ho_so,
          ho_ten,
          nop_tu,
          nop_den,
          han_tu,
          han_den,
          thu_tuc_id,
          trang_thai,
          trang_thai_het_han,
        }
      )
      .then(async ({ data }) => {
        setListHoSoThuLy(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  // handle api

  const resetState = async () => {
    await setSearchHoTen("");
    await setSearchMaHoSo("");
    await setDateHanDen(null);
    await setDateHanTu(null);
    await setDateNopDen(null);
    await setDateNopTu(null);
    await setValueThuTuc({
      label: "Tất cả",
      value: "",
    });
    await setValueTrangThai({
      label: "Tất cả",
      value: "",
    });
    await setValueTrangThaiHetHan({
      label: "Tất cả",
      value: "",
    });
  };

  // DETAIL
  const detail = async (data) => {
    setTrang_thai(data?.trang_thai);
    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${page}&per_page=${newPerPage}`,
        {}
      )
      .then(({ data }) => {
        if (data?.results) {
          setListHoSoThuLy(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  const handleRowClicked = (row) => {};

  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );

  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };

  const columns = [
    {
      name: "Mã hồ sơ",
      selector: (row) => <span>{row.ma_ho_so}</span>,
      grow: 2,
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Họ tên",
      selector: (row) => <span>{row.ten_nguoi_yeu_cau}</span>,
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Số điện thoại",
      selector: (row) => <span>{row?.so_dien_thoai}</span>,
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Thủ tục",
      selector: (row) => <span>{row.ten_thu_tuc}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Ngày nộp",
      selector: (row) => <span>{row.thoi_gian_nop_ho_so}</span>,
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trạng thái",
      selector: (row) => <span>{checkStatus(row?.trang_thai_ho_so)}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
  ];

  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];

  const checkStatus = (id) => {
    let status = TrangThaiHoSo.find((e) => e.id === id);
    return status.name;
  };
  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];

  return (
    <KTCard>
      {/* <div className="page"> */}
      <div className="container-fluid" style={{ marginTop: " 30px" }}>
        <PageTitle breadcrumbs={usersBreadcrumbs}>
          Danh sách hồ sơ chờ in
        </PageTitle>
        <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
          <div className="card-box">
            <div className="row mb-7">
              <div className="row col-12 mb-2">
                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Mã hồ sơ</label>
                  <Form.Control
                    type="text"
                    placeholder="Nhập mã hồ sơ..."
                    onChange={(e) => {
                      setSearchMaHoSo(e.target.value);
                    }}
                    style={{ height: 38 }}
                    value={searchMaHoSo}
                  />
                </div>
                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Họ tên</label>
                  <Form.Control
                    type="text"
                    placeholder="Nhập họ tên..."
                    onChange={(e) => {
                      setSearchHoTen(e.target.value);
                    }}
                    style={{ height: 38 }}
                    value={searchHoTen}
                    className="text-uppercase"
                  />
                </div>

                <div className="row col-6">
                  <div className="col-3">
                    <label className="fw-bold fs-6 mb-2">Nộp từ</label>
                    <DesktopDatePicker
                      inputFormat="dd/MM/yyyy"
                      label=" "
                      value={dateNopTu}
                      onChange={(value) => {
                        setDateNopTu(value);
                        getList({
                          nop_tu: String(new Date(value).getTime()),
                        });
                        console.log("log value", value);
                      }}
                      maxDate={new Date()}
                      renderInput={(params) => (
                        <TextField size="small" {...params} />
                      )}
                    />
                  </div>
                  <div className="col-3">
                    <label className="fw-bold fs-6 mb-2">Nộp đến</label>
                    <DesktopDatePicker
                      inputFormat="dd/MM/yyyy"
                      label=" "
                      value={dateNopDen}
                      onChange={(value) => {
                        setDateNopDen(value);
                        getList({
                          nop_den: String(new Date(value).getTime()),
                        });
                        console.log("log value", value);
                      }}
                      maxDate={new Date()}
                      renderInput={(params) => (
                        <TextField size="small" {...params} />
                      )}
                    />
                  </div>
                  <div className="col-3">
                    <label className="fw-bold fs-6 mb-2">Hạn từ</label>
                    <DesktopDatePicker
                      inputFormat="dd/MM/yyyy"
                      label=" "
                      value={dateHanTu}
                      onChange={(value) => {
                        setDateHanTu(value);
                        getList({
                          han_tu: String(new Date(value).getTime()),
                        });
                        console.log("log value", value);
                      }}
                      maxDate={new Date()}
                      renderInput={(params) => (
                        <TextField size="small" {...params} />
                      )}
                    />
                  </div>
                  <div className="col-3">
                    <label className="fw-bold fs-6 mb-2">Hạn đến</label>
                    <DesktopDatePicker
                      inputFormat="dd/MM/yyyy"
                      label=" "
                      value={dateHanDen}
                      onChange={(value) => {
                        setDateHanDen(value);
                        getList({
                          han_den: String(new Date(value).getTime()),
                        });
                        console.log("log value", value);
                      }}
                      maxDate={new Date()}
                      renderInput={(params) => (
                        <TextField size="small" {...params} />
                      )}
                    />
                  </div>
                </div>
              </div>

              <div className="row col-12">
                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Thủ tục</label>
                  <Select
                    value={valueThuTuc}
                    key={"id"}
                    options={ListDMThuTuc}
                    formatGroupLabel={formatGroupLabel}
                    onChange={(data) => {
                      setValueThuTuc(data);
                      getList({ thu_tuc_id: data?.value });
                    }}
                  />
                </div>

                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                  <Select
                    value={valueTrangThai}
                    key={"id"}
                    options={ListDMTrangThaiHoSo}
                    formatGroupLabel={formatGroupLabel}
                    onChange={(data) => {
                      setValueTrangThai(data);
                      getList({ trang_thai: data?.value });
                    }}
                  />
                </div>

                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Trạng thái thụ lý</label>
                  <Select
                    value={valueTrangThaiHetHan}
                    key={"id"}
                    options={ListDMHetHan}
                    formatGroupLabel={formatGroupLabel}
                    onChange={(data) => {
                      setValueTrangThaiHetHan(data);
                      getList({ trang_thai_het_han: data?.value });
                    }}
                  />
                </div>

                <div className="row col-3">
                  <div className="col-6 pt-8">
                    <button
                      type="button"
                      className="btn btn-sm btn-trolai mb-5"
                      onClick={() => {
                        resetState();
                        getList({
                          page_number: 1,
                          han_den: "",
                          han_tu: "",
                          nop_den: "",
                          nop_tu: "",
                          trang_thai: "",
                          trang_thai_het_han: "",
                          thu_tuc_id: "",
                          ho_ten: "",
                          ma_ho_so: "",
                        });
                      }}
                    >
                      Xóa bộ lọc
                    </button>
                  </div>
                  <div className="col-6 pt-8">
                    <Dropdown>
                      <Dropdown.Toggle
                        size="sm"
                        variant="primary"
                        id="dropdown-basic"
                      >
                        Tùy chọn
                      </Dropdown.Toggle>

                      <Dropdown.Menu>
                        <Dropdown.Item href="#/action-1">
                          Nhập số QĐ
                        </Dropdown.Item>
                        <Dropdown.Item href="#/action-2">In</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">
                          Chuyển CVTL
                        </Dropdown.Item>
                        <Dropdown.Item href="#/action-3">
                          Chuyển VTCS
                        </Dropdown.Item>
                      </Dropdown.Menu>
                    </Dropdown>
                  </div>
                  {/* <div className="col-4 pt-8"></div> */}
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-md-12">
                <DataTable
                  noDataComponent={"Không có dữ liệu."}
                  sortServer
                  progressPending={isLoading}
                  columns={columns}
                  data={listHoSoThuLy}
                  customStyles={customStyles}
                  pagination
                  highlightOnHover
                  pointerOnHover
                  paginationServer
                  paginationTotalRows={totalRows}
                  onChangeRowsPerPage={handlePerRowsChange}
                  onChangePage={handlePageChange}
                  selectableRows
                  onSelectedRowsChange={handleChange}
                  clearSelectedRows={toggledClearRows}
                  paginationComponentOptions={paginationOptions}
                  conditionalRowStyles={conditionalRowStyles}
                  onRowClicked={(data) => {
                    detail(data);
                    handleRowClicked(data);
                  }}
                  progressComponent={
                    <div
                      style={{
                        padding: "24px",
                      }}
                    >
                      <Oval
                        arialLabel="loading-indicator"
                        color="#007bff"
                        height={40}
                      />
                    </div>
                  }
                />
              </div>
            </div>
          </div>
        </LocalizationProvider>
      </div>
      {/* </div> */}
    </KTCard>
  );
}
