import axios from "axios";
import { useEffect, useState } from "react";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";
import { TrangThaiLienKetDuocSi } from "../../../data";
import { PopupCapNhatYeuCauLienKetTC } from "../popup/PopupCapNhatYeuCauLienKetTC";


export function GiayPhepKinhDoanhDuoc () {

    const [isLoading, setIsLoading] = useState(false);

    const [ThongTinGiayPhep, setThongTinGiayPhep] = useState(null);

    const [showModalYCLK, setShowModalYCLK] = useState(false);

    const showModalCapNhatYeuCauLK = () => setShowModalYCLK(true);
    const closeModalCapNhatYeuCauLK = () => setShowModalYCLK(false);

    useEffect(() => {
        getGiayPhepUser();
    },[])

    const getGiayPhepUser = async () => {
        await setIsLoading(true);
        axios
            .get(api.API_THONG_TIN_LIEN_KET_TO_CHUC)
            .then(({data}) => {
                setThongTinGiayPhep(data?.results);
            })
            .catch(() => {})
            .finally(() => {
                setIsLoading(false);
            });
    }

    const usersBreadcrumbs = [
        {
            title: "Trang chủ",
            path: "/",
            isSeparator: false,
            isActive: false,
        },
        {
            title: "",
            path: "",
            isSeparator: true,
            isActive: false,
        },
    ];

    const checkStatus = (id) => {
        let status = TrangThaiLienKetDuocSi.find((e) => e.id === id);
        return <span className={`text-${status.color}`}>{status.name}</span>;
    };

    return (
        <>
            <div className="row gy-5 g-xl-8 w-100">
                <PopupCapNhatYeuCauLienKetTC
                    show={showModalYCLK}
                    hide={closeModalCapNhatYeuCauLK}
                    getGiayPhepUserUser={getGiayPhepUser}
                />
                <PageTitle breadcrumbs={usersBreadcrumbs}>
                    Chứng chỉ hành nghề
                </PageTitle>
            </div>
        </>
    )

}
