import moment from "moment";
import React, { useEffect } from "react";
import { GioiTinh } from "../../../../../data";

export default function ThongTinCaNhan(props) {
  const { yeu_cau_cchnd } = props?.thongTinHoSo;
  useEffect(() => {
    console.log("log ThongTinCaNhan", props);
  }, []);

  const selectGioiTinh = (id) => {
    let tempGioiTinh = GioiTinh.find((e) => e.value === id);
    return tempGioiTinh.label;
  };

  return (
    <div className="card-box py-6 px-6 mx-3 my-3">
      <div className="fw-bold fs-5 mb-1">THÔNG TIN CÁ NHÂN</div>
      <div className="row">
        <div className="col-3 pr-0">
          <span className="text-fix pr-1">Họ tên: </span>
          <span className="text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.ho || ""}{" "}
            {yeu_cau_cchnd?.doi_tuong?.ten || ""}
          </span>
        </div>
        <div className="col-2">
          <span className="text-fix pr-1">Ngày sinh: </span>
          <span className=" text-user-info">
            {moment(Number(yeu_cau_cchnd?.doi_tuong?.ngay_sinh)).format(
              "DD/MM/YYYY"
            ) || ""}
          </span>
        </div>
        <div className="col-2">
          <span className="text-fix pr-1">Giới tính: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.gioi_tinh
              ? selectGioiTinh(yeu_cau_cchnd?.doi_tuong?.gioi_tinh)
              : ""}
          </span>
        </div>
        <div className="col-3">
          <span className="text-fix pr-1">Email: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.email || ""}
          </span>
        </div>
        <div className="col-2">
          <span className="text-fix pr-1">SĐT: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.dien_thoai || ""}
          </span>
        </div>
        <div className="col-12">
          <span className="text-fix pr-1">Hộ khẩu thường trú: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.dia_chi_thuong_tru || ""}
          </span>
        </div>
        <div className="col-3">
          <span className="text-fix pr-1">CMND/CCCD: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.ma_cong_dan || ""}
          </span>
        </div>
        <div className="col-2">
          <span className="text-fix pr-1">Ngày cấp: </span>
          <span className=" text-user-info">
            {moment(Number(yeu_cau_cchnd?.doi_tuong?.ngay_cap)).format(
              "DD/MM/YYYY"
            ) || ""}
          </span>
        </div>
        <div className="col-7">
          <span className="text-fix pr-1">Nơi cấp: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.noi_cap || ""}
          </span>
        </div>

        <div className="col-5">
          <span className="text-fix pr-1">Văn bằng chuyên môn:</span>{" "}
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.gioi_tinh || ""}
          </span>
        </div>
        <div className="col-5">
          <span className="text-fix pr-1">Tốt nghiệp tại: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.gioi_tinh || ""}
          </span>
        </div>
        <div className="col-2">
          <span className="text-fix pr-1">Năm tốt nghiệp: </span>
          <span className=" text-user-info">
            {yeu_cau_cchnd?.doi_tuong?.gioi_tinh || ""}
          </span>
        </div>
      </div>
    </div>
  );
}
