import React from "react";
import { HinhThuc, LoaiDeNghi } from "../../../../data";

export default function DeNghi(props) {
  const getLoaiDeNghi = (value) => {
    let data = LoaiDeNghi.find((e) => e?.id === value);
    if (data) {
      return data.name;
    }
  };

  const getHinhThuc = (value) => {
    let data = HinhThuc.find((e) => e?.id === value);
    if (data) {
      return data.name;
    }
  };

  const renderNoiDung = () => {
    let type = props?.ThuTuc?.loai;
    switch (type) {
      case "2":
        return (
          <>
            <span className="text-fix">Nội dung xin điều chỉnh: </span>
            <span></span>
          </>
        );
      case "3":
        return (
          <>
            <span className="text-fix">Lý do hỏng/mất: </span>
            <span></span>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div className="card-box py-6 px-6 mx-3 my-3">
      <div className="fw-bold fs-5">ĐỀ NGHỊ</div>

      <div>
        <span className="text-fix py-2">
          Người đề nghị cấp chứng chỉ hành nghề dược thuộc trường hợp sau:{" "}
        </span>{" "}
        <span>{getLoaiDeNghi(props?.DeNghi?.loai_de_nghi)}</span>
      </div>
      <div>
        <span className="text-fix py-2">
          Đề nghị cấp chứng chỉ hành nghề dược theo hình thức:{" "}
        </span>{" "}
        <span> {getHinhThuc(props?.DeNghi?.hinh_thuc_thi)}</span>
      </div>
      <div className="text-fix pt-2">
        Đăng ký phạm vi hành nghề chuyên môn sau:
      </div>
      <div className="row">
        {props?.DeNghi?.danh_muc_pham_vi_hn &&
        props?.DeNghi?.danh_muc_pham_vi_hn.length > 0
          ? props?.DeNghi?.danh_muc_pham_vi_hn.map((e, i) => {
              if (e?.value) {
                return (
                  <div key={i} className="col-12">
                    <b>{e?.ten}:</b>
                    <div className="ml-1">{e?.value}</div>{" "}
                  </div>
                );
              }
            })
          : null}
      </div>

      <div className="col-12 px-0 pt-2">{renderNoiDung()}</div>
    </div>
  );
}
