import axios from "axios";
import clsx from "clsx";
import { useEffect, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import Select from "react-select";
import { toast } from "react-toastify";
import { KTCard, KTSVG, useDebounce } from "../../../../../_metronic/helpers";
import Status from "../../../../components/Status";
import api from "../../../../configs/api";
import { PopupDelete } from "../../popup/PopupDelete";

export function ThuTuc() {
  const [listThuTuc, setListThuTuc] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [trang_thai, setTrang_thai] = useState(true);
  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);
  //modal
  const [showAdd, setShowAdd] = useState(false);
  const [edit, setEdit] = useState(false);
  //modal thêm danh mục
  const closeModal = () => setShowAdd(false);
  const showModal = () => setShowAdd(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDoiTuong, setSelectedDoiTuong] = useState({
    label: "Dược sĩ",
    value: 1,
    id: 1,
  });
  const optionsDoiTuong = [
    {
      label: "Dược sĩ",
      value: 1,
      id: 1,
    },
    {
      label: "Tổ chức",
      value: 2,
      id: 2,
    },
  ];

  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList(1, perPage, debouncedSearchTerm);
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = ({
    page_number = page,
    size = perPage,
    search_key = searchTerm,
  }) => {
    setIsLoading(true);
    axios
      .post(api.API_LIST_THU_TUC + `?page=${page_number}&per_page=${size}`, {
        search_ten: search_key,
      })
      .then(async ({ data }) => {
        setListThuTuc(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  //validate thêm thủ tục
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const findFormErrors = () => {
    const {
      ten,
      linh_vuc,
      muc_do,
      doi_tuong,
      co_quan_thuc_hien,
      thoi_gian_giai_quyet,
      ket_qua_thuc_hien,
      le_phi_text,
    } = form;
    const newErrors = {};
    // name errors
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";
    if (!linh_vuc || linh_vuc === "")
      newErrors.linh_vuc = "Lĩnh vực không được bỏ trống!";
    if (!muc_do || muc_do === "")
      newErrors.muc_do = "Mức độ không được bỏ trống!";
    if (!co_quan_thuc_hien || co_quan_thuc_hien === "")
      newErrors.co_quan_thuc_hien = "Cơ quan thực hiện không được bỏ trống!";
    if (!thoi_gian_giai_quyet || thoi_gian_giai_quyet === "")
      newErrors.thoi_gian_giai_quyet =
        "Thời gian giải quyết không được bỏ trống!";
    if (!ket_qua_thuc_hien || ket_qua_thuc_hien === "")
      newErrors.ket_qua_thuc_hien = "Kết quả thực hiện không được bỏ trống!";
    if (!le_phi_text || le_phi_text === "")
      newErrors.le_phi_text = "Lệ phí text không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
    } else {
      // No errors! Put any logic here for the form submission!
      edit ? updateThuTuc(e) : addThuTuc(e);
    }
  };

  // handle api
  const addThuTuc = async () => {
    setIsLoading(true);
    const {
      ten,
      linh_vuc,
      muc_do,
      doi_tuong,
      co_quan_thuc_hien,
      thoi_gian_giai_quyet,
      le_phi_text,
      ket_qua_thuc_hien,
    } = form;
    const json = {
      ten,
      trang_thai: trang_thai,
      linh_vuc,
      muc_do,
      doi_tuong: String(selectedDoiTuong.value),
      co_quan_thuc_hien: "Sở Y tế",
      thoi_gian_giai_quyet,
      le_phi_text,
      ket_qua_thuc_hien,
    };
    await axios
      .post(api.API_THU_TUC + "_add", json)
      .then(async ({ data }) => {
        closeModal();
        resetState();
        toast.success("Tạo thủ tục thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({});
        setIsLoading(false);
      })
      .catch((error) => {
        console.log("error", error?.response);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const updateThuTuc = async () => {
    setIsLoading(true);
    const {
      ten,
      linh_vuc,
      muc_do,
      doi_tuong,
      co_quan_thuc_hien,
      thoi_gian_giai_quyet,
      le_phi_text,
      id,
      ket_qua_thuc_hien,
    } = form;
    const json = {
      ten,
      trang_thai: trang_thai,
      linh_vuc,
      muc_do,
      doi_tuong: String(selectedDoiTuong.value),
      co_quan_thuc_hien: "Sở Y tế",
      thoi_gian_giai_quyet,
      le_phi_text,
      ket_qua_thuc_hien,
    };
    await axios
      .put(api.API_THU_TUC + "_by_id/" + id, json)
      .then(async ({ data }) => {
        closeModal();
        resetState();
        toast.success("Cập nhật thủ tục thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({ page_number: 1 });
      })
      .catch((error) => {
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const resetState = () => {
    setForm({});
    setErrors({});
    setShowUpdate(false);
    const updatedData = listThuTuc?.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setData(updatedData);
  };

  // DETAIL
  const detail = async (data) => {
    setForm({
      ...data,
    });
    setTrang_thai(data?.trang_thai);
    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(api.API_LIST_THU_TUC + `?page=${page}&per_page=${newPerPage}`, {})
      .then(({ data }) => {
        if (data?.results) {
          setListThuTuc(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteTT = () => {
    axios
      .delete(api.API_THU_TUC + "_by_id/" + idDelete, {})
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        closeModal();
        closeModalDelete();
        resetState();
        if (page == 1) {
          getList({ page_number: 1 });
        } else if (listThuTuc?.length == 1) {
          getList({ page_number: page - 1 });
        } else {
          getList({ page_number: page });
        }
      })
      .catch((error) => {
        console.log("error", error);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };
  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  //đổi màu khi selected
  const [data, setData] = useState(listThuTuc);
  useEffect(() => {
    setData(listThuTuc);
  }, [listThuTuc]);

  const handleRowClicked = (row) => {
    setErrors({});
    const updatedData = listThuTuc?.map((item) => {
      if (row.id !== item.id) {
        return item;
      }
      return {
        ...item,
        toggleSelected: !item.toggleSelected,
      };
    });
    setData(updatedData);
    showModal();
    setEdit(true);
  };
  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );

  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Mã",
      grow: 3,
      selector: (row) => (
        <div
          onClick={() => {
            detail(row);
            handleRowClicked(row);
            setShowAdd(true);
          }}
        >
          <span>{row.ma_thu_tuc}</span>
        </div>
      ),
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Tên",
      selector: (row) => <span>{row.ten}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Đối tượng",
      selector: (row) => <span>{row.doi_tuong}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trạng thái",
      selector: (row) => <span>{row.trang_thai}</span>,
      grow: 3,
      center: true,
      cell: (row) => <Status status={row.trang_thai} />,
    },
    {
      name: "Thao tác",
      grow: 1,
      cell: (row) => (
        <div className="container my-3">
          <button
            onClick={async () => {
              await setIdDelete(row?.id);
              showModalDelete();
            }}
            className="btn btn-icon btn-bg-light btn-color-danger btn-sm"
          >
            <KTSVG
              path="/media/icons/duotune/general/gen027.svg"
              className="svg-icon-3"
            />
          </button>
        </div>
      ),
    },
  ];
  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  return (
    <KTCard>
      <div className="page">
        <div className="container-fluid">
          <div className="fv-row mb-7 mt-6">
            {/* begin::Label */}
            <label className="fw-bold fs-6 mb-2">Tên thủ tục</label>
            {/* end::Label */}

            {/* begin::Input */}

            <div className="row">
              <div className="col-md-6 col-sm-6">
                <input
                  placeholder="Nhập tên thủ tục tìm kiếm..."
                  type="text"
                  name="name"
                  className={clsx(
                    "form-control form-control-solid mb-3 mb-lg-0"
                  )}
                  autoComplete="off"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              <div
                className="col-md-6 col-sm-6 d-flex justify-content-end"
                data-kt-user-table-toolbar="base"
              >
                <button type="button" className="btn btn-light-primary me-3">
                  <KTSVG
                    path="/media/icons/duotune/arrows/arr078.svg"
                    className="svg-icon-2"
                  />
                  Export
                </button>

                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={() => {
                    showModal();
                    resetState();
                    setEdit(false);
                  }}
                >
                  <KTSVG
                    path="/media/icons/duotune/arrows/arr075.svg"
                    className="svg-icon-2"
                  />
                  Thêm
                </button>
              </div>
            </div>

            {/* end::Input */}
          </div>

          <div className="row">
            <div className="col">
              <div className="" style={cardBox}>
                <DataTable
                  noDataComponent={"Không có dữ liệu."}
                  sortServer
                  progressPending={isLoading}
                  columns={columns}
                  data={data}
                  customStyles={customStyles}
                  pagination
                  highlightOnHover
                  pointerOnHover
                  paginationServer
                  paginationTotalRows={totalRows}
                  onChangeRowsPerPage={handlePerRowsChange}
                  onChangePage={handlePageChange}
                  paginationComponentOptions={paginationOptions}
                  conditionalRowStyles={conditionalRowStyles}
                  // onRowClicked={(data) => {
                  //   detail(data);
                  //   handleRowClicked(data);
                  //   setShowAdd(true);
                  // }}
                  progressComponent={
                    <div
                      style={{
                        padding: "24px",
                      }}
                    >
                      <Oval
                        arialLabel="loading-indicator"
                        color="#007bff"
                        height={40}
                      />
                    </div>
                  }
                />
              </div>
            </div>
          </div>
          <Modal
            backdrop="static"
            keyboard={false}
            show={showAdd}
            onHide={() => {
              closeModal();
            }}
            centered
            size="lg"
          >
            <Modal.Header className="bg-blue2" closeButton>
              <Modal.Title className="text-white">
                {!edit ? "Thêm" : "Cập nhật"} thủ tục
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <div className="row">
                {showUpdate && (
                  <div className="col-6 form-group mb-5">
                    <label className="required fw-bold fs-6 mb-2">
                      Mã thủ tục
                    </label>
                    <Form.Control
                      disabled={true}
                      type="text"
                      placeholder="Nhập mã thủ tục"
                      style={{ fontSize: 14, fontWeight: 500 }}
                      value={form.ma_thu_tuc === null ? "" : form.ma_thu_tuc}
                    />
                  </div>
                )}

                <div className="col-6 form-group mb-5">
                  <label className="required fw-bold fs-6 mb-2">
                    Tên thủ tục
                  </label>
                  <Form.Control
                    placeholder="Nhập địa chỉ"
                    style={{ fontSize: 14, fontWeight: 500 }}
                    value={form.ten === null ? "" : form.ten}
                    onChange={(e) => {
                      setField("ten", e.target.value);
                    }}
                    isInvalid={!!errors.ten}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.ten}
                  </Form.Control.Feedback>
                </div>
                <div className="col-6 form-group mb-5">
                  <label className="required fw-bold fs-6 mb-2">
                    Tên lĩnh vực
                  </label>
                  <Form.Control
                    placeholder="Nhập lĩnh vực"
                    style={{ fontSize: 14, fontWeight: 500 }}
                    value={form.linh_vuc === null ? "" : form.linh_vuc}
                    onChange={(e) => {
                      setField("linh_vuc", e.target.value);
                    }}
                    isInvalid={!!errors.linh_vuc}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.linh_vuc}
                  </Form.Control.Feedback>
                </div>
                <div className="col-6 form-group mb-5">
                  <label className="required fw-bold fs-6 mb-2">Mức độ</label>
                  <Form.Control
                    placeholder="Nhập mức độ"
                    style={{ fontSize: 14, fontWeight: 500 }}
                    value={form.muc_do === null ? "" : form.muc_do}
                    onChange={(e) => {
                      setField("muc_do", e.target.value);
                    }}
                    isInvalid={!!errors.muc_do}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.muc_do}
                  </Form.Control.Feedback>
                </div>
                <div className="col-6 form-group mb-5">
                  <label className="required fw-bold fs-6 mb-2">
                    Đối tượng
                  </label>
                  <Select
                    value={selectedDoiTuong}
                    key={"id"}
                    options={optionsDoiTuong}
                    formatGroupLabel={formatGroupLabel}
                    onChange={async (data) => {
                      setField("doi_tuong", data?.value);
                      setSelectedDoiTuong(data);
                    }}
                  />
                </div>
                <div className="col-6 form-group mb-5">
                  <label className="required fw-bold fs-6 mb-2">
                    Cơ quan thực hiện
                  </label>
                  <Form.Control
                    placeholder="Nhập cơ quan thực hiện"
                    style={{ fontSize: 14, fontWeight: 500 }}
                    value={
                      form.co_quan_thuc_hien === null
                        ? ""
                        : form.co_quan_thuc_hien
                    }
                    onChange={(e) => {
                      setField("co_quan_thuc_hien", e.target.value);
                    }}
                    isInvalid={!!errors.co_quan_thuc_hien}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.co_quan_thuc_hien}
                  </Form.Control.Feedback>
                </div>
                <div className="col-6 form-group mb-5">
                  <label className="required fw-bold fs-6 mb-2">
                    Thời gian giải quyết
                  </label>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    placeholder="Nhập đối tượng"
                    style={{ fontSize: 14, fontWeight: 500 }}
                    value={
                      form.thoi_gian_giai_quyet === null
                        ? ""
                        : form.thoi_gian_giai_quyet
                    }
                    onChange={(e) => {
                      setField("thoi_gian_giai_quyet", e.target.value);
                    }}
                    isInvalid={!!errors.thoi_gian_giai_quyet}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.thoi_gian_giai_quyet}
                  </Form.Control.Feedback>
                </div>
                <div className="col-6 form-group mb-5">
                  <label className="required fw-bold fs-6 mb-2">
                    Kết quả thực hiện
                  </label>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    placeholder="Nhập kết quả thực hiện"
                    style={{ fontSize: 14, fontWeight: 500 }}
                    value={
                      form.ket_qua_thuc_hien === null
                        ? ""
                        : form.ket_qua_thuc_hien
                    }
                    onChange={(e) => {
                      setField("ket_qua_thuc_hien", e.target.value);
                    }}
                    isInvalid={!!errors.ket_qua_thuc_hien}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.ket_qua_thuc_hien}
                  </Form.Control.Feedback>
                </div>
                <div className="col-6 form-group mb-5">
                  <label className="required fw-bold fs-6 mb-2">
                    Lệ phí text
                  </label>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    placeholder="Nhập lệ phí text"
                    style={{ fontSize: 14, fontWeight: 500 }}
                    value={form.le_phi_text === null ? "" : form.le_phi_text}
                    onChange={(e) => {
                      setField("le_phi_text", e.target.value);
                    }}
                    isInvalid={!!errors.le_phi_text}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.le_phi_text}
                  </Form.Control.Feedback>
                </div>
                <div className="col-6 form-group mb-2">
                  <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                  <div className="form-check form-check-solid form-switch fv-row">
                    <input
                      className="form-check-input w-45px h-30px"
                      type="checkbox"
                      value={trang_thai}
                      checked={trang_thai}
                      onChange={(e) => {
                        setTrang_thai(!trang_thai);
                      }}
                    />
                    <label className="form-check-label"></label>
                  </div>
                </div>
              </div>
            </Modal.Body>
            <Modal.Footer>
              <Button
                variant="secondary"
                onClick={() => {
                  closeModal();
                }}
              >
                <i className="fas fa-times"></i> Huỷ
              </Button>
              <Button
                variant="btn text-white bg-blue2"
                onClick={async (e) => {
                  handleSubmit(e);
                }}
              >
                {edit ? (
                  <i className="fa fa-pencil-alt text-white"></i>
                ) : (
                  <i className="fas fa-save text-white"></i>
                )}{" "}
                {edit ? "Cập nhật" : "Thêm"}
              </Button>
            </Modal.Footer>
          </Modal>

          <PopupDelete
            title="Bạn có đồng ý xóa thủ tục này không?"
            show={isvisibleDelete}
            onHide={closeModalDelete}
            onDetele={() => {
              deleteTT();
            }}
          />
        </div>
      </div>
    </KTCard>
  );
}
