import axios from "axios";
import React, { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { useSelector } from "react-redux";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import { selectRoleUser } from "../../../redux-module/auth/authSlice";

export default function PopupBoSung(props) {
  //validate thêm vị trí hành nghề
  const [form, setForm] = useState({});
  console.log("props", props);
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const roleUser = useSelector(selectRoleUser)[0].ten_en;
  const roleUserten = useSelector(selectRoleUser)[0].ten;

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {
    const { noi_dung_yeu_cau_bo_sung } = form;
    const newErrors = {};
    // name errors
    if (!noi_dung_yeu_cau_bo_sung || noi_dung_yeu_cau_bo_sung === "")
      newErrors.noi_dung_yeu_cau_bo_sung = "Lý do bổ sung không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
      for (const property in newErrors) {
        toast.error(newErrors[property], {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        return;
      }
    } else {
      if (props?.isMulti) {
        handleSubmitDuyet();
      }

      // No errors! Put any logic here for the form submission!
    }
  };

  const handleSubmitDuyet = () => {
    const { noi_dung_yeu_cau_bo_sung } = form;

    const arrayTemp = props?.selectedRow?.map((e) => e.id);
    console.log("log arrayTemp", arrayTemp);

    const json = {
      ids: arrayTemp,
      trang_thai_ho_so: "8",
      noi_dung_yeu_cau_bo_sung,
    };
    if (arrayTemp.length === 0) {
      toast.error("Chưa có hồ sơ nào được chọn", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "success",
      });
      setIsLoading(false);
      return;
    }
    axios
      .put(api.API_DUYET_MULTI_YEU_CAU_CHUNG_CHI, json)
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        props?.getList();
        setIsLoading(false);
        resetState();
        props?.onHide();
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const resetState = () => {
    setField("noi_dung_yeu_cau_bo_sung", "");
  };

  return (
    <Modal
      centered
      show={props?.show}
      onHide={() => {
        props?.onHide();
        setErrors({});
      }}
    >
      <Modal.Header className="bg-blue2">
        <Modal.Title className="text-white" style={{ fontSize: "22px" }}>
          {props?.title}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body style={{ justifyContent: "center" }}>
        {props.isMulti ? (
          <span className="fw-bold fs-6 mb-2">Họ tên : {roleUserten}</span>
        ) : null}
        <br />
        <br />
        <span className="fw-bold fs-6 mb-2">Yêu cầu bổ sung</span>

        <Form.Control
          type="text"
          placeholder="Nhập lý do..."
          style={{ height: 38 }}
          value={
            form.noi_dung_yeu_cau_bo_sung === null
              ? ""
              : form.noi_dung_yeu_cau_bo_sung
          }
          onChange={(e) => {
            setField("noi_dung_yeu_cau_bo_sung", e.target.value);
          }}
          isInvalid={!!errors.noi_dung_yeu_cau_bo_sung}
        />
      </Modal.Body>
      <Modal.Footer>
        <div className="d-flex justify-content-center">
          <Button
            className="btn btn-secondary"
            style={{ marginRight: 10 }}
            onClick={() => {
              props?.onHide();
              resetState();
              setErrors({});
            }}
          >
            Quay lại
          </Button>
          <Button
            className="btn btn-primary"
            onClick={(e) => {
              handleSubmit(e);
            }}
          >
            Đồng ý
          </Button>
        </div>
      </Modal.Footer>
    </Modal>
  );
}
