import axios from "axios";
import moment from "moment";
import { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { KTCard, KTSVG, useDebounce } from "../../../../../_metronic/helpers";
import api from "../../../../configs/api";
import { TrangThaiHoSo } from "../../../../data";
import { useAuth } from "../../auth";

export function DanhSachHoSo() {
  const { roleUser, currentUser } = useAuth();
  const navigate = useNavigate();
  const [listThuTuc, setListThuTuc] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [stt, setStt] = useState(0);
  const [trang_thai, setTrang_thai] = useState(true);
  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);
  //modal
  const [showAdd, setShowAdd] = useState(false);
  const [edit, setEdit] = useState(false);
  //modal thêm danh mục
  const closeModal = () => setShowAdd(false);
  const showModal = () => setShowAdd(true);
  const [searchTerm, setSearchTerm] = useState("");

  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList(1, perPage, debouncedSearchTerm);
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = ({
    page_number = page,
    size = perPage,
    search_key = searchTerm,
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_LIST_YEU_CAU_CHUNG_CHI_HANH_NGHE +
          `?page=${page_number}&per_page=${size}`,
        { nhan_vien_id: currentUser?.id }
      )
      .then(async ({ data }) => {
        setListThuTuc(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  //validate thêm thủ tục
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});

  const resetState = () => {
    setForm({});
    setErrors({});
    setShowUpdate(false);
    const updatedData = listThuTuc?.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setData(updatedData);
  };

  // DETAIL

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_LIST_YEU_CAU_CHUNG_CHI_HANH_NGHE +
          `?page=${page}&per_page=${newPerPage}`,
        {}
      )
      .then(({ data }) => {
        if (data?.results) {
          setListThuTuc(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  //đổi màu khi selected
  const [data, setData] = useState(listThuTuc);
  useEffect(() => {
    setData(listThuTuc);
  }, [listThuTuc]);

  const handleRowClicked = (row) => {
    navigate(`/admin/nop-ho-so/dieuchinh/${row?.id}`);
  };

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Mã hồ sơ",
      selector: (row) => (
        <span
          onClick={() => {
            handleRowClicked(row);
          }}
        >
          {row.ma_ho_so}
        </span>
      ),
      grow: 1,
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Tên thủ tục",
      selector: (row) => <span>{row.ten_thu_tuc}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Tên người yêu cầu",
      selector: (row) => <span>{row.ten_nguoi_yeu_cau}</span>,
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Ngày nộp",
      selector: (row) => (
        <span>{moment(row.created_at).format("DD/MM/YYYY")}</span>
      ),
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trạng thái",
      selector: (row) => <span>{checkStatus(row.trang_thai_ho_so)}</span>,
      grow: 1,
    },
  ];

  const checkStatus = (value) => {
    return TrangThaiHoSo.find((e) => e.id == value).name;
  };

  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];

  return (
    <KTCard>
      <div id="danh_sach_ho_so">
        <div className="container-fluid ">
          <div className="row">
            <div className="col">
              <h2 className="title_ho_so text-blue2">
                LỊCH SỬ CÁC HỒ SƠ ĐÃ NỘP
              </h2>
              <button
                type="button"
                className="btn btn-sm btn-success mb-5"
                onClick={() => {
                  navigate("/admin/nop-ho-so/create");
                }}
              >
                <i className="fas fa-plus me-1"></i>
                Nộp hồ sơ mới
              </button>
              <DataTable
                noDataComponent={
                  <img
                    style={{ width: "30%", height: "auto" }}
                    src="/media/nodata-found.svg"
                  />
                }
                sortServer
                progressPending={isLoading}
                columns={columns}
                data={data}
                customStyles={customStyles}
                pagination
                highlightOnHover
                pointerOnHover
                paginationServer
                paginationTotalRows={totalRows}
                onChangeRowsPerPage={handlePerRowsChange}
                onChangePage={handlePageChange}
                paginationComponentOptions={paginationOptions}
                conditionalRowStyles={conditionalRowStyles}
                // onRowClicked={(data) => {
                //   handleRowClicked(data);
                // }}
                progressComponent={
                  <div
                    style={{
                      padding: "24px",
                    }}
                  >
                    <Oval
                      arialLabel="loading-indicator"
                      color="#007bff"
                      height={40}
                    />
                  </div>
                }
              />
            </div>
          </div>
        </div>
      </div>
    </KTCard>
  );
}
