import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import TextField from "@mui/material/TextField";
import axios from "axios";
import clsx from "clsx";
import { vi } from "date-fns/locale";
import moment from "moment";
import { useEffect, useState } from "react";
import BlockUi from "react-block-ui";
import { Button, Dropdown, Form, Modal } from "react-bootstrap";
import DataTable from "react-data-table-component";
import ImageUploading from "react-images-uploading";
import { Oval, TailSpin } from "react-loader-spinner";
import { toast } from "react-toastify";
import ReactTooltip from "react-tooltip";
import { KTCard, KTSVG, useDebounce } from "../../../../../_metronic/helpers";
import api from "../../../../configs/api";
import { PopupDelete } from "../../popup/PopupDelete";
import { AiTwotoneEdit } from "react-icons/ai";
import { IoRemoveCircle } from "react-icons/io5";

export function ChungNhanThucHanhCoSo() {
  const [listChungNhan, setListChungNhan] = useState([]);
  const [perPage, setPerPage] = useState(10);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [showUpdate, setShowUpdate] = useState(false);
  const [id, setId] = useState("");
  const [page, setPage] = useState(1);

  const [trang_thai, setTrang_thai] = useState(true);
  const [userInfo, setUserInfo] = useState({});

  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);

  //modal
  const [showAdd, setShowAdd] = useState(false);
  const [edit, setEdit] = useState(false);

  //modal thêm danh mục
  const closeModal = () => setShowAdd(false);
  const showModal = () => setShowAdd(true);

  const [images, setImages] = useState([]);

  const [ngayCap, setNgayCap] = useState(null);
  const [ngayKiemTra, setNgayKiemTra] = useState(null);
  const [ngayHieuLuc, setNgayHieuLuc] = useState(null);
  const [ngayHetHan, setNgayHetHan] = useState(null);

  //search
  const [searchTerm, setSearchTerm] = useState("");
  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  const getAuth = async () => {
    let auth = JSON.parse(await localStorage.getItem("kt-auth-react-v"));
    await setUserInfo(auth);
  };

  useEffect(
    () => {
      getAuth();
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList({ page_number: 1, search_key: debouncedSearchTerm });
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = ({ page_number = page, size = perPage, search_key = "" }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_GET_CHUNG_NHAN_THUC_HANH_CO_SO +
          "_get_list" +
          `?page=${page_number}&per_page=${size}`,
        {
          search_ten: search_key,
        }
      )
      .then(async ({ data }) => {
        setListChungNhan(data?.results);
        setTotalRows(data?.total);

        setIsLoading(false);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  //validate thêm chỉ định chuyên khoa
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  // SET IMG DATA
  const uploadImageChange = async (imageList) => {
    await setImages(imageList);
  };

  const findFormErrors = () => {
    const {
      diachi,
      so_giaychungnhan,
      ngay_cap,
      ngay_het_han,
      ngay_hieu_luc,
      ngay_kiemtra,
    } = form;
    const newErrors = {};
    // name errors
    if (!diachi || diachi === "")
      newErrors.diachi = "Địa chỉ không được bỏ trống!";
    if (!so_giaychungnhan || so_giaychungnhan === "")
      newErrors.so_giaychungnhan = "Số giấy chứng nhận không được bỏ trống!";

    if (!ngay_cap || ngay_cap === "")
      newErrors.ngay_cap = "Ngày cấp không được bỏ trống!";
    if (!ngay_het_han || ngay_het_han === "")
      newErrors.ngay_het_han = "Ngày hết hạn không được bỏ trống!";
    if (!ngay_hieu_luc || ngay_hieu_luc === "")
      newErrors.ngay_hieu_luc = "Ngày hiệu lực không được bỏ trống!";
    if (!ngay_kiemtra || ngay_kiemtra === "")
      newErrors.ngay_kiemtra = "Ngày kiểm tra không được bỏ trống!";

    return newErrors;
  };

  // handle api
  const addChungNhan = async (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true);

      const configs = {
        headers: {
          "content-type": "multipart/form-data",
        },
      };

      const {
        so_giaychungnhan,
        diachi,
        ngay_cap,
        ngay_het_han,
        ngay_hieu_luc,
        ngay_kiemtra,
      } = form;
      const json = {
        so_giaychungnhan,
        diachi,
        trang_thai,
        ngay_cap,
        ngay_het_han,
        ngay_hieu_luc,
        ngay_kiemtra,
      };
      const data = new FormData();

      // await setField("thong_tin_dieu_tri_va_tiep_nhan", jsonTTDV);
      await data.append("so_giaychungnhan", so_giaychungnhan);
      await data.append("trang_thai", trang_thai);
      await data.append("diachi", diachi);
      await data.append("ngay_cap", ngay_cap);
      await data.append("ngay_het_han", ngay_het_han);
      await data.append("ngay_hieu_luc", ngay_hieu_luc);
      await data.append("ngay_kiemtra", ngay_kiemtra);
      await data.append(
        "coso_kinhdoanh_id",
        "79698fe2-d166-4377-9da6-d6ddefab8eed"
      );
      await data.append("dinhkem_giaychungnhan", images[0]?.file);

      axios
        .post(api.API_GET_CHUNG_NHAN_THUC_HANH_CO_SO + "_add", data, configs)
        .then(async ({ data }) => {
          resetState();
          closeModal();

          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getList(1, perPage, "");
          setIsLoading(false);
          // navigate(0);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const updateChungNhan = async (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true);
      const configs = {
        headers: {
          "content-type": "multipart/form-data",
        },
      };

      const {
        so_giaychungnhan,
        diachi,
        ngay_cap,
        ngay_het_han,
        ngay_hieu_luc,
        ngay_kiemtra,
      } = form;

      const data = new FormData();

      await data.append("so_giaychungnhan", so_giaychungnhan);
      await data.append("trang_thai", trang_thai);
      await data.append("diachi", diachi);
      await data.append("ngay_cap", ngay_cap);
      await data.append("ngay_het_han", ngay_het_han);
      await data.append("ngay_hieu_luc", ngay_hieu_luc);
      await data.append("ngay_kiemtra", ngay_kiemtra);
      //   await data.append("coso_kinhdoanh_id", userInfo?.userInfo?.id);
      await data.append(
        "coso_kinhdoanh_id",
        "d68b1270-84cf-4f74-9a37-355d61a1194b"
      );
      await data.append("dinhkem_giaychungnhan", images[0]?.file);

      await axios
        .put(
          api.API_GET_CHUNG_NHAN_THUC_HANH_CO_SO + "_update/" + id,
          data,
          configs
        )
        .then(async ({ data }) => {
          resetState();
          closeModal();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getList(1, perPage, "");
          setIsLoading(false);
          // navigate(0);
        })
        .catch((error) => {
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const resetState = async () => {
    await setPerPage(10);
    await setTotalRows(0);
    await setForm({
      diachi: "",
      dinhkem_giaychungnhan: "",
      ngay_cap: null,
      ngay_het_han: null,
      ngay_hieu_luc: null,
      ngay_kiemtra: null,
      so_giaychungnhan: "",
    });
    await setTrang_thai(true);
    await setErrors({});
    await setId("");
    await setShowUpdate(false);
    await setEdit(false);
    await setImages(false);

    await setNgayCap(null);
    await setNgayKiemTra(null);
    await setNgayHieuLuc(null);
    await setNgayHetHan(null);

    // getNhomChuyenKhoa();
  };
  // DETAIL
  const detail = async (data) => {
    setForm({
      ...data,
    });
    setEdit(true);
    setId(data.id);
    setTrang_thai(data.trang_thai);
    setImages(data?.dinhkem_giaychungnhan);
    setShowUpdate(true);
    // showModal();
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_GET_CHUNG_NHAN_THUC_HANH_CO_SO +
          "_get_list" +
          `?page=${page}&per_page=${newPerPage}`,
        {
          so_giaychungnhan: searchTerm,
        }
      )
      .then(({ data }) => {
        if (data?.results) {
          setListChungNhan(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteCK = () => {
    axios
      .delete(
        api.API_GET_CHUNG_NHAN_THUC_HANH_CO_SO + "_update/" + idDelete,
        {}
      )
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        closeModalDelete();
        resetState();

        if (page === 1) {
          getList({ page_number: 1 });
        } else if (listChungNhan?.length == 1) {
          getList({ page_number: page - 1 });
        } else {
          getList({ page_number: page });
        }
      })
      .catch((error) => {
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };
  const handlePageChange = async (page) => {
    await setPage(page);
    getList({ page_number: page });
  };

  //selected date
  const selectedNC = async (date) => {
    await setNgayCap(date);
    await setField("ngay_cap", moment(date).format("DD/MM/YYYY"));
  };
  const selectedNKT = async (date) => {
    await setNgayKiemTra(date);
    await setField("ngay_kiemtra", moment(date).format("DD/MM/YYYY"));
  };
  const selectedHL = async (date) => {
    await setNgayHieuLuc(date);
    await setField("ngay_hieu_luc", moment(date).format("DD/MM/YYYY"));
  };
  const selectedHH = async (date) => {
    await setNgayHetHan(date);
    await setField("ngay_het_han", moment(date).format("DD/MM/YYYY"));
  };

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Số giấy chứng nhận",
      selector: (row) => (
        <span
          onClick={() => {
            detail(row);
            showModal();
          }}
        >
          {row.so_giaychungnhan}
        </span>
      ),
      grow: 2,
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Địa chỉ",
      selector: (row) => (
        <span data-tip={row?.diachi}>
          {row.diachi}
          <ReactTooltip place="top" type="dark" effect="float" />
        </span>
      ),
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },

    {
      name: "Hình ảnh",
      grow: 2,

      selector: (row) => (
        <div className="text-center">
          {row?.dinhkem_giaychungnhan ? (
            <img
              className="mt-1 mb-2"
              style={{ width: "140px" }}
              src={"https://cdn-dev.yte360.com/" + row?.dinhkem_giaychungnhan}
              width="120"
              // onClick={() => {
              //   openPopupImage();
              //   setImageChoose(row);
              // }}
            />
          ) : (
            <img
              className="my-2"
              style={{ width: "140px" }}
              src="/media/assets/noPicture.jpeg"
            />
          )}
        </div>
      ),
      style: {
        color: "#202124",
        fontSize: "14px",
        fontWeight: 500,
      },
    },

    {
      name: "Ngày hiệu lực",
      selector: (row) => (
        <span data-tip={row?.ngay_hieu_luc}>
          {row.ngay_hieu_luc}
          <ReactTooltip place="top" type="dark" effect="float" />
        </span>
      ),
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },

    {
      name: "Ngày hết hạn",
      selector: (row) => (
        <span data-tip={row?.ngay_het_han}>
          {row.ngay_het_han}
          <ReactTooltip place="top" type="dark" effect="float" />
        </span>
      ),
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Ngày kiểm tra",
      selector: (row) => (
        <span data-tip={row?.ngay_kiemtra}>
          {row.ngay_kiemtra}
          <ReactTooltip place="top" type="dark" effect="float" />
        </span>
      ),
      grow: 2,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },

    {
      name: "Trang thái",
      selector: (row) => <span>{row.trang_thai}</span>,
      grow: 3,
      cell: (row) => (
        <td>
          {row.trang_thai ? (
            <span className="badge badge-light-success fs-7 fw-bold">
              Hoạt động
            </span>
          ) : (
            <span className="badge badge-light-dark fs-7 fw-bold">
              Không hoạt động
            </span>
          )}
        </td>
      ),
    },

    {
      name: "Thao tác",
      grow: 1,

      cell: (row) => (
        <Dropdown style={{ width: "100%", textAlign: "center" }}>
          <Dropdown.Toggle variant="link">
            <i
              className="fas fa-bars text-primary"
              style={{ marginLeft: "3px", marginRight: "-3px" }}
            ></i>
          </Dropdown.Toggle>

          <Dropdown.Menu style={{ position: "absolute", overflow: "auto" }}>
            <Dropdown.Item
              className="py-5"
              onClick={async () => {
                await detail(row);
                await showModal();
              }}
            >
              <AiTwotoneEdit style={{ marginRight: 5 }} color="blue" />
              Cập nhật tin
            </Dropdown.Item>
            <Dropdown.Item
              className="py-5"
              onClick={async () => {
                await setIdDelete(row?.id);
                showModalDelete();
              }}
            >
              <IoRemoveCircle style={{ marginRight: 5 }} color="red" />
              Xoá tin
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      ),
    },
  ];
  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  return (
    <KTCard>
      <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
        <div className="page">
          <div className="container-fluid">
            <div className="fv-row mb-7 mt-6">
              {/* begin::Label */}
              <label className="fw-bold fs-6 mb-2">Số giấy chứng nhận</label>
              {/* end::Label */}

              {/* begin::Input */}

              <div className="row">
                <div className="col-md-6 col-sm-6">
                  <input
                    placeholder="Nhập số giấy chứng nhận tìm kiếm..."
                    type="text"
                    name="name"
                    className={clsx(
                      "form-control form-control-solid mb-3 mb-lg-0"
                    )}
                    autoComplete="off"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>

                <div
                  className="col-md-6 col-sm-6 d-flex justify-content-end"
                  data-kt-user-table-toolbar="base"
                >
                  <button type="button" className="btn btn-light-primary me-3">
                    <KTSVG
                      path="/media/icons/duotune/arrows/arr078.svg"
                      className="svg-icon-2"
                    />
                    Export
                  </button>

                  <button
                    type="button"
                    className="btn btn-primary"
                    onClick={async () => {
                      //   resetState();

                      await setForm({
                        diachi: "",
                        dinhkem_giaychungnhan: "",
                        ngay_cap: null,
                        ngay_het_han: null,
                        ngay_hieu_luc: null,
                        ngay_kiemtra: null,
                        so_giaychungnhan: "",
                      });
                      await setTrang_thai(true);
                      await setErrors({});
                      await setId("");
                      await setShowUpdate(false);
                      await setEdit(false);
                      await setImages(false);

                      await setNgayCap(null);
                      await setNgayKiemTra(null);
                      await setNgayHieuLuc(null);
                      await setNgayHetHan(null);

                      showModal();
                    }}
                  >
                    <KTSVG
                      path="/media/icons/duotune/arrows/arr075.svg"
                      className="svg-icon-2"
                    />
                    Thêm
                  </button>
                </div>
              </div>

              {/* end::Input */}
            </div>
            <BlockUi
              tag="div"
              // blocking={!isLoading}
              loader={
                <TailSpin
                  arialLabel="loading-indicator"
                  height={60}
                  width={60}
                  strokeWidth={2}
                  strokeWidthSecondary={1}
                  color="blue"
                  secondaryColor="blue"
                  wrapperclassName={"d-inline-flex"}
                />
              }
            >
              <div className="row">
                <div className="col-md-12">
                  <div className="" style={cardBox}>
                    <DataTable
                      noDataComponent={"Không có dữ liệu."}
                      sortServer
                      progressPending={isLoading}
                      columns={columns}
                      data={listChungNhan}
                      customStyles={customStyles}
                      pagination
                      highlightOnHover
                      pointerOnHover
                      paginationServer
                      paginationTotalRows={totalRows}
                      onChangeRowsPerPage={handlePerRowsChange}
                      onChangePage={handlePageChange}
                      paginationComponentOptions={paginationOptions}
                      conditionalRowStyles={conditionalRowStyles}
                      progressComponent={
                        <div
                          style={{
                            padding: "24px",
                          }}
                        >
                          <Oval
                            arialLabel="loading-indicator"
                            color="#007bff"
                            height={40}
                          />
                        </div>
                      }
                    />
                  </div>
                </div>
              </div>

              {/* Modal add question */}

              <Modal
                backdrop="static"
                keyboard={false}
                show={showAdd}
                onHide={closeModal}
                centered
                size="lg"
              >
                <Modal.Header closeButton className="bg-blue2">
                  <Modal.Title className="text-white">
                    {!edit ? "Thêm" : "Cập nhật"} chứng nhận thực hành cơ sở
                  </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <div className="form-group mb-5">
                    <label className="required fw-bold fs-6 mb-2">
                      Số giấy chứng nhận
                    </label>
                    <Form.Control
                      type="text"
                      placeholder="Nhập số giấy chứng nhận"
                      style={{ fontSize: 14, fontWeight: 500 }}
                      value={
                        form.so_giaychungnhan === null
                          ? ""
                          : form.so_giaychungnhan
                      }
                      onChange={async (e) => {
                        await setField("so_giaychungnhan", e.target.value);
                      }}
                      isInvalid={!!errors.so_giaychungnhan}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errors.so_giaychungnhan}
                    </Form.Control.Feedback>
                  </div>

                  <div className="form-group mb-5">
                    <label className="required fw-bold fs-6 mb-2">
                      Địa chỉ
                    </label>
                    <Form.Control
                      as="textarea"
                      rows={2}
                      placeholder="Nhập địa chỉ"
                      style={{ fontSize: 14, fontWeight: 500 }}
                      value={form.diachi === null ? "" : form.diachi}
                      onChange={(e) => {
                        setField("diachi", e.target.value);
                      }}
                      isInvalid={!!errors.diachi}
                    />
                    <Form.Control.Feedback type="invalid">
                      {errors.diachi}
                    </Form.Control.Feedback>
                  </div>

                  <div className="form-group mb-5 row">
                    <div className="col-md-6 row">
                      <label className="required fw-bold fs-6">Ngày cấp</label>

                      <div className="row">
                        <div>
                          <DesktopDatePicker
                            inputFormat="dd/MM/yyyy"
                            value={ngayCap}
                            onChange={selectedNC}
                            maxDate={new Date()}
                            renderInput={(params) => (
                              <TextField variant="standard" {...params} />
                            )}
                          />
                        </div>

                        {errors?.ngay_cap !== null ? (
                          <span
                            className="text-danger mt-2"
                            style={{ fontSize: 12 }}
                          >
                            {errors?.ngay_cap}
                          </span>
                        ) : null}
                      </div>
                    </div>
                    <div className="col-md-6 row">
                      <label className="required fw-bold fs-6">
                        Ngày kiểm tra
                      </label>
                      <div className="row">
                        <div>
                          <DesktopDatePicker
                            inputFormat="dd/MM/yyyy"
                            value={ngayKiemTra}
                            onChange={selectedNKT}
                            maxDate={new Date()}
                            renderInput={(params) => (
                              <TextField variant="standard" {...params} />
                            )}
                          />
                        </div>

                        {errors?.ngay_kiemtra !== null ? (
                          <span
                            className="text-danger mt-2"
                            style={{ fontSize: 12 }}
                          >
                            {errors?.ngay_kiemtra}
                          </span>
                        ) : null}
                      </div>
                    </div>
                  </div>

                  <div className="form-group mb-5 row">
                    <div className="col-md-6 row">
                      <label className="required fw-bold fs-6">
                        Ngày hiệu lực
                      </label>
                      <div className="row">
                        <div>
                          <DesktopDatePicker
                            inputFormat="dd/MM/yyyy"
                            value={ngayHieuLuc}
                            onChange={selectedHL}
                            maxDate={new Date()}
                            renderInput={(params) => (
                              <TextField variant="standard" {...params} />
                            )}
                          />
                        </div>

                        {errors?.ngay_hieu_luc !== null ? (
                          <span
                            className="text-danger mt-2"
                            style={{ fontSize: 12 }}
                          >
                            {errors?.ngay_hieu_luc}
                          </span>
                        ) : null}
                      </div>
                    </div>
                    <div className="col-md-6 row">
                      <label className="required fw-bold fs-6">
                        Ngày hết hạn
                      </label>
                      <div className="row">
                        <div>
                          <DesktopDatePicker
                            inputFormat="dd/MM/yyyy"
                            value={ngayHetHan}
                            onChange={selectedHH}
                            maxDate={new Date()}
                            renderInput={(params) => (
                              <TextField variant="standard" {...params} />
                            )}
                          />
                        </div>
                        {errors?.ngay_het_han !== null ? (
                          <span
                            className="text-danger mt-2"
                            style={{ fontSize: 12 }}
                          >
                            {errors?.ngay_het_han}
                          </span>
                        ) : null}
                      </div>
                    </div>
                  </div>

                  <ImageUploading
                    value={images}
                    onChange={uploadImageChange}
                    maxNumber={1}
                    dataURLKey="data_url"
                  >
                    {({
                      imageList,
                      onImageUpload,
                      onImageRemove,
                      isDragging,
                      dragProps,
                    }) => (
                      <div className="form-group mb-2">
                        <label className="required fw-bold fs-6 mb-2">
                          Đính kèm giấy chứng nhận
                        </label>
                        <div>
                          {edit ? (
                            form?.dinhkem_giaychungnhan !== null ? (
                              <div className="form-group">
                                <button
                                  style={{ fontSize: "14px" }}
                                  className="btn btn-danger"
                                  onClick={() => {
                                    setField("dinhkem_giaychungnhan", null);
                                    setImages([]);
                                  }}
                                >
                                  Xóa
                                </button>
                                <div>
                                  <img
                                    className="mr-2 my-2"
                                    src={
                                      "https://cdn-dev.yte360.com/" +
                                      form?.dinhkem_giaychungnhan
                                    }
                                    alt=""
                                    width="140"
                                  />
                                </div>
                              </div>
                            ) : (
                              <div>
                                <button
                                  className="btn bg-blue2 text-white"
                                  style={
                                    (isDragging ? { color: "red" } : null,
                                    { fontSize: "14px" })
                                  }
                                  onClick={onImageUpload}
                                  {...dragProps}
                                >
                                  Kéo và thả ở đây
                                </button>
                                <div>
                                  <img
                                    src="/media/assets/noPicture.jpeg"
                                    className="my-2"
                                    style={{ width: "140px" }}
                                    alt=""
                                  />
                                </div>
                              </div>
                            )
                          ) : (
                            <div>
                              {imageList.length >= 1 || images?.length >= 1 ? (
                                imageList.map((image, index) => (
                                  <div key={index} className="form-group">
                                    <button
                                      style={{ fontSize: "14px" }}
                                      className="btn btn-danger"
                                      onClick={() => onImageRemove(index)}
                                    >
                                      Xóa
                                    </button>
                                    <div>
                                      <img
                                        className="mr-2 my-2"
                                        src={image.data_url}
                                        alt=""
                                        width="140"
                                      />
                                    </div>
                                  </div>
                                ))
                              ) : (
                                <div>
                                  <button
                                    className="btn bg-blue2 text-white"
                                    style={
                                      (isDragging ? { color: "red" } : null,
                                      { fontSize: "14px" })
                                    }
                                    onClick={onImageUpload}
                                    {...dragProps}
                                  >
                                    Kéo và thả ở đây
                                  </button>
                                  <div>
                                    <img
                                      src="/media/assets/noPicture.jpeg"
                                      className="my-2"
                                      style={{ width: "140px" }}
                                      alt=""
                                    />
                                  </div>
                                </div>
                              )}
                            </div>
                          )}
                        </div>
                      </div>
                    )}
                  </ImageUploading>

                  <div className="form-group mb-2">
                    <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                    <div className="form-check form-check-solid form-switch fv-row">
                      <input
                        className="form-check-input w-45px h-30px"
                        type="checkbox"
                        id="trang_thai"
                        // value={trang_thai}
                        checked={trang_thai}
                        onChange={(e) => {
                          setTrang_thai(!trang_thai);
                        }}
                      />
                      <label className="form-check-label"></label>
                    </div>
                  </div>
                </Modal.Body>
                <Modal.Footer>
                  <Button variant="secondary" onClick={closeModal}>
                    <i className="fas fa-times"></i> Huỷ
                  </Button>
                  <Button
                    variant="btn text-white bg-blue2"
                    onClick={async (e) => {
                      !edit ? addChungNhan(e) : updateChungNhan(e);
                    }}
                  >
                    {edit ? (
                      <i className="fa fa-pencil-alt text-white"></i>
                    ) : (
                      <i className="fas fa-save text-white"></i>
                    )}{" "}
                    {edit ? "Cập nhật" : "Thêm"}
                  </Button>
                </Modal.Footer>
              </Modal>

              <PopupDelete
                title="Bạn có đồng ý xóa hội đồng này không?"
                show={isvisibleDelete}
                onHide={closeModalDelete}
                onDetele={() => {
                  deleteCK();
                }}
              />
            </BlockUi>
          </div>
        </div>
      </LocalizationProvider>
    </KTCard>
  );
}
