import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { vi } from "date-fns/locale";
import { useState, useEffect, useRef } from "react";
import { Form } from "react-bootstrap";
import { GioiTinh, HinhThuc } from "../../../../app/data";
import Select from "react-select";
import { useNavigate, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { PageTitle } from "../../../../_metronic/layout/core";
import { PhamViVaViTriChuyenMon } from "../danh-sach-ho-so-thu-ly-duoc-si/components/PhamViChuyenMon";
import { ThongTinHanhChinhCapNhat } from "../danh-sach-ho-so-thu-ly-duoc-si/components/thong-tin-hanh-chinh/ThongTinHanhChinhCapNhat";
import { BangCap } from "../duoc-si/bang-cap/BangCap";
import { LichSuChungChi } from "../duoc-si/lich-su-chung-chi/LichSuChungChi";
import { LichSuDaoTao } from "../duoc-si/lich-su-dao-tao/LichSuDaoTao";
import { LichSuThucHanh } from "../duoc-si/lich-su-thuc-hanh/LichSuThucHanh";
import api from "../../../configs/api";
import axios from "axios";
import { toast } from "react-toastify";
import ThongTinCaNhanCapNhat from "../danh-sach-ho-so-thu-ly-duoc-si/components/thong-tin-ca-nhan/ThongTinCaNhanCapNhat";
import ThongTinHanhChinh from "./ThongTinHanhChinh";
import ThongTinChungChiHanhNgheDuoc from "./ThongTinChungChiHanhNghieDuoc";
import {
  commonActions,
  selectListIdChecked,
} from "../../../redux-module/common/commonSlice";
import {
  Checkbox,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
  TextField,
} from "@mui/material";

export function ChiTietChungChi() {
  const [thongTinHoSo, setThongTinHoSo] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [validateCapNhatTT, setValidateCapNhatTT] = useState(false);
  const [validateVTHNvsPVCM, setValidateVTHNvsPVCM] = useState(false);
  const [valueDeNghiLoai, setValueDeNghiLoai] = useState(null);
  const [checkSave, setCheckSave] = useState(false);
  // -----------------------------------
  const [ccViTriHanhNghe, setCCViTriHanhNghe] = useState("");
  const [ccPhamViChuyenMon, setCCPhamViChuyenMon] = useState("");
  const [DeNghiHinhThuc, setDeNghiHinhThuc] = useState(null);
  const [DeNghiLoaiDeNghi, setDeNghiLoaiDeNghi] = useState(null);
  const [valueDeNghiHinhThuc, setValueDeNghiHinhThuc] = useState(null);
  const [DeNghiDMPVHN, setDeNghiDMPVHN] = useState(null);
  const [DeNghiPVCM, setDeNghiPVCM] = useState(null);
  const ListIdChecked = useSelector(selectListIdChecked);
  const LoaiChungChi = useRef(null);
  const LoaiHinhThuc = useRef(null);
  const PhamViHNCM = useRef(null);

  const scrollToLoaiChungChi = () =>
    LoaiChungChi.current.scrollIntoView({
      behavior: "smooth",
      block: "center",
    });
  const scrollToLoaiHinhThuc = () =>
    LoaiHinhThuc.current.scrollIntoView({
      behavior: "smooth",
      block: "center",
    });
  const scrollToPhamViHNCM = () =>
    PhamViHNCM.current.scrollIntoView({ behavior: "smooth", block: "center" });
  const handleChangeDeNghiLoai = (event) => {
    setValueDeNghiLoai(event.target.value);
  };
  const handleChangeDeNghiHinhThuc = (event) => {
    setValueDeNghiHinhThuc(event.target.value);
  };
  const renderCheckbox = (e) => {
    return (
      <div key={e?.id} className="row col-12">
        <span key={e?.id} className="font-weight-bold mb-1">
          {e?.ten}
        </span>
        {e.value && e.value.length > 0
          ? e.value.map((a) => {
              return (
                <FormControlLabel
                  key={a.value}
                  control={
                    <Checkbox
                      key={`pvhncm${a.value}`}
                      checked={ListIdChecked?.includes(String(a.value))}
                      onChange={async () => {
                        let checkExist = ListIdChecked?.includes(
                          String(a.value)
                        );
                        let listTemp = ListIdChecked;
                        if (checkExist) {
                          let x1 = listTemp.filter(
                            (x) => x !== String(a.value)
                          );
                          dispatch(commonActions.setListIdChecked(x1));
                        } else {
                          let x2 = [...listTemp, a.value];
                          dispatch(commonActions.setListIdChecked(x2));
                        }
                      }}
                      value={a?.value || ""}
                    />
                  }
                  label={a?.label}
                />
              );
            })
          : null}
      </div>
    );
  };
  // ---------------------------------
  const dispatch = useDispatch();
  const { id } = useParams();
  const navigate = useNavigate();
  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];
  useEffect(() => {
    if (id) {
      getThongTinHoSo(id);
    }
  }, []);

  const [selectedGioiTinh, setSelectedGioiTinh] = useState({});

  const callBackValidateTTCN = () => {
    setValidateCapNhatTT(false);
  };

  const callBackValidateVTHNvsPVCM = () => {
    setValidateVTHNvsPVCM(false);
  };

  const getThongTinHoSo = async (id) => {
    console.log("id nè", id);
    await setIsLoading(true);
    axios
      .get(api.API_CHI_TIET_CHUNG_CHI_HANH_NGHE + "/" + id)
      .then(async ({ data }) => {
        const { results } = data;
        if (data?.results) {
          setThongTinHoSo(data?.results);
        }
        console.log("thongtin hoSo", data?.results);
        // let viTriHanhNghe = data?.result?.chung_chi_hanh_nghe?.vi_tri_hanh_nghe;
        // let phamViChuyenMon =
        //   data?.result?.chung_chi_hanh_nghe?.pham_vi_chuyen_mon;

        // if (viTriHanhNghe && viTriHanhNghe.length > 0) {
        //   let a = [];
        //   for (let x = 0; x < viTriHanhNghe.length; x++) {
        //     a.push(viTriHanhNghe[x].ten);
        //   }
        //   a = a.join("  ");
        //   setCCViTriHanhNghe(a);
        // }
        setDeNghiDMPVHN(data?.results?.vi_tri_hanh_nghe?.danh_sach);
        setDeNghiPVCM(data?.results?.pham_vi_chuyen_mon?.danh_sach);

        dispatch(
          commonActions.setListIdChecked(
            data.results?.vi_tri_hanh_nghe?.checked || []
          )
        );
      })
      .catch((error) => {
        toast.error("Lấy thông tin chứng chỉ không thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        navigate("/error/404");
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  return (
    <div className="container-fluid">
      <PageTitle breadcrumbs={usersBreadcrumbs}>
        Chi tiết chứng chỉ hành nghề
      </PageTitle>
      <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
        <div className="mt-10">
          <ThongTinHanhChinh
            validate={validateCapNhatTT}
            changeValidate={callBackValidateTTCN}
            data={thongTinHoSo?.thong_tin_hanh_chinh}
          />
        </div>

        <BangCap userId={thongTinHoSo?.thong_tin_hanh_chinh?.id} />
        <LichSuThucHanh userId={thongTinHoSo?.thong_tin_hanh_chinh?.id} />
        <LichSuDaoTao userId={thongTinHoSo?.thong_tin_hanh_chinh?.id} />
        <LichSuChungChi />

        <ThongTinChungChiHanhNgheDuoc
