import axios from "axios";
import clsx from "clsx";
import { useEffect, useState } from "react";
import BlockUi from "react-block-ui";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { KTCard, KTSVG, useDebounce } from "../../../../_metronic/helpers";
import Status from "../../../components/Status";
import api from "../../../configs/api";
import { PopupDelete } from "../popup/PopupDelete";
// import { vi } from "date-fns/locale";
import ReactTooltip from "react-tooltip";
import { PageTitle } from "../../../../_metronic/layout/core";
import PopupQuanLyNguoiDung from "../popup/PopupQuanLyNguoiDung";

export function QuanLyNguoiDung() {
  const navigate = useNavigate();
  const [NgayHieuLuc, setNgayHieuLuc] = useState(new Date());
  const [listNguoiDung, setListNguoiDung] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [trang_thai, setTrang_thai] = useState(true);
  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);

  const [searchTerm, setSearchTerm] = useState("")

  const [edit, setEdit] = useState(false)

  const [idNoiCapCmnd, setIdNoiCapCmnd] = useState(null);
  const [showModalQLND, setShowModalQLND] = useState(false);
  const closeModalQuanLyNguoiDung = () => setShowModalQLND(false);
  const showModalQuanLyNguoiDung = () => setShowModalQLND(true);

  const [Item, setItem] = useState({});

  const [selectedDoiTuong, setSelectedDoiTuong] = useState({
    label: "Dược sĩ",
    value: 1,
    id: 1,
  });
  const optionsDoiTuong = [
    {
      label: "Dược sĩ",
      value: 1,
      id: 1,
    },
    {
      label: "Tổ chức",
      value: 2,
      id: 2,
    },
  ];
  const [selectedgioitinh, setSelectedgioitinh] = useState({
    label: "Nam",
    value: 1,
    id: 1,
  });
  const optionsGioiTinh = [
    {
      label: "Nam",
      value: 1,
      id: 1,
    },
    {
      label: "Nữ",
      value: 2,
      id: 2,
    },
    {
      label: "Khác",
      value: 3,
      id: 3,
    },
  ];
  // useEffect(() => {
  //   getTinhThanh();
  //   getTinhThanhCOHN();
  //   getCATinhThanh();
  //   getVanBangChuyenMon();
  //   getNoiTotNghiep();
  // }, []);

  const handleNgayHieuLuc = (newValue) => {
    console.log("log new value", new Date(newValue).getTime());
    setNgayHieuLuc(newValue);
  };

  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList(1, perPage, debouncedSearchTerm);
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = ({
    page_number = page,
    size = perPage,
    search_ten = searchTerm,
  }) => {
    // setIsLoading(true);
    axios
      .post(api.API_QL_NGUOI_DUNG + `?page=${page_number}&per_page=${size}`, {
        search_ten: search_ten,
      })
      .then(async ({ data }) => {
        setListNguoiDung(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };




  const resetState = () => {

    setShowUpdate(false);
    const updatedData = listNguoiDung?.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setData(updatedData);
  };


  const handlePerRowsChange = async (newPerPage, page) => {
    // await setIsLoading(true);
    axios
      .post(api.API_LIST_THU_TUC + `?page=${page}&per_page=${newPerPage}`, {})
      .then(({ data }) => {
        if (data?.results) {
          setListNguoiDung(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => { })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  //đổi màu khi selected
  const [data, setData] = useState(listNguoiDung);
  useEffect(() => {
    setData(listNguoiDung);
  }, [listNguoiDung]);

  const handleRowClicked = (row) => {

    const updatedData = listNguoiDung?.map((item) => {
      if (row.id !== item.id) {
        return item;
      }
      return {
        ...item,
        toggleSelected: !item.toggleSelected,
      };
    });
    setData(updatedData);
    setEdit(true);
  };

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Họ và tên",
      grow: 3,
      selector: (row) => (
        <div
          onClick={() => {
            resetState();
            setItem(row);
            setEdit(true);
            handleRowClicked(row);
            showModalQuanLyNguoiDung();
          }}
        >
          <span>{row.ho_ten}</span>
          <ReactTooltip place="top" type="dark" effect="float" />
        </div>
      ),
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Email",
      selector: (row) => <span>{row.email}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Điện Thoại",
      selector: (row) => <span>{row.dien_thoai}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Vai Trò",
      selector: (row) => <span>{row.chuc_vu}</span>,
      grow: 3,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trang thái",
      selector: (row) => <span>{row.active}</span>,
      grow: 3,
      center: true,
      cell: (row) => (
        <td>
          <Status status={row.active} />
        </td>
      ),
    },
  ];
  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];
  return (
    <>
      {/* <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}> */}
      <PageTitle breadcrumbs={usersBreadcrumbs}>Danh sách người dùng</PageTitle>
      <KTCard>
        <div className="page">
          <div className="container-fluid">
            <div className="fv-row mb-7 mt-6">
              {/* begin::Label */}
              <div className="col-md-12 col-sm-12 text-center">
                <label className="fw-bold fs-6 mb-2">
                  DANH SÁCH NGƯỜI DÙNG
                </label>
              </div>
              {/* end::Label */}

              {/* begin::Input */}

              <div className="row">
                <div className="col-md-6 col-sm-6">
                  <input
                    placeholder="Nhập tên
                    "
                    type="text"
                    name="name"
                    className={clsx(
                      "form-control form-control-solid mb-3 mb-lg-0"
                    )}
                    autoComplete="off"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>

                <div
                  className="col-md-6 col-sm-6 d-flex justify-content-start"
                  data-kt-user-table-toolbar="base"
                >
                  <button className="btn btn-primary me-3"
                    onClick={() => {
                      setEdit(false);
                      resetState();
                      showModalQuanLyNguoiDung();
                    }}
                  >
                    Tạo mới
                  </button>
                </div>
              </div>

              {/* end::Input */}
            </div>
            <BlockUi
              tag="div"
              // blocking={isLoading}
              centery="true"
              centerx="true"
            >
              <div className="row">
                <div className="col">
                  <div className="" style={cardBox}>
                    <DataTable
                      noDataComponent={"Không có dữ liệu."}
                      sortServer
                      progressPending={isLoading}
                      columns={columns}
                      data={data}
                      customStyles={customStyles}
                      pagination
                      highlightOnHover
                      pointerOnHover
                      paginationServer
                      paginationTotalRows={totalRows}
                      onChangeRowsPerPage={handlePerRowsChange}
                      onChangePage={handlePageChange}
                      paginationComponentOptions={paginationOptions}
                      conditionalRowStyles={conditionalRowStyles}
                      // onRowClicked={(data) => {
                      //   detail(data);
                      //   handleRowClicked(data);
                      //   setShowAdd(true);
                      // }}
                      progressComponent={
                        <div
                          style={{
                            padding: "24px",
                          }}
                        >
                          <Oval
                            arialLabel="loading-indicator"
                            color="#007bff"
                            height={40}
                          />
                        </div>
                      }
                    />
                  </div>
                </div>
              </div>

              <PopupQuanLyNguoiDung
                getList={() => {
                  getList({});
                }}
                data={Item}
                show={showModalQLND}
                hide={closeModalQuanLyNguoiDung}
                edit={edit}
              />
              <PopupDelete
                title="Bạn có đồng ý xóa thủ tục này không?"
                show={isvisibleDelete}
                onHide={closeModalDelete}
              // onDetele={() => {
              //   deleteTT();
              // }}
              />
            </BlockUi>
          </div>
        </div>
      </KTCard>
      {/* </LocalizationProvider> */}
    </>
  );
}
