// import Viewer, { Worker, SpecialZoomLevel } from "@phuocng/react-pdf-viewer";
import { useEffect, useState } from "react";
// Import the styles
// import "@phuocng/react-pdf-viewer/cjs/react-pdf-viewer.css";

import { Modal } from "react-bootstrap";
import { pdfjs } from "react-pdf";
import LoadingIndicator from "../../../components/LoadingIndicator";
// ---------------------------------------------------------------
import { Viewer, Worker } from "@react-pdf-viewer/core";
// Plugins
import { printPlugin } from '@react-pdf-viewer/print';
import { defaultLayoutPlugin } from "@react-pdf-viewer/default-layout";

// Import styles
import "@react-pdf-viewer/core/lib/styles/index.css";
import "@react-pdf-viewer/default-layout/lib/styles/index.css";

// ---------------------------------------------------------------
export function In(props) {
  const [numPages, setNumPages] = useState(null);

  const printPluginInstance = printPlugin();
  const { print } = printPluginInstance;

  useEffect(() => {
    if (props?.path) {
      console.log("propsssss pathpathpathpath", props);
    }
  }, [props?.show]);

  useEffect(() => {
    pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
  }, []);
  const defaultLayoutPluginInstance = defaultLayoutPlugin();

  function onDocumentLoadSuccess({ numPages: nextNumPages }) {
    setNumPages(nextNumPages);
  }
  const options = {
    cMapUrl: "cmaps/",
    cMapPacked: true,
  };

  return (
    <Modal
      centered
      show={props?.show}
      onHide={() => {
        props?.onHide();
      }}
      size="xl"
    >
      {/* <Modal.Header className="bg-blue2" closeButton>
        <Modal.Title className="text-white">test</Modal.Title>
      </Modal.Header> */}

      <Modal.Body>
        {!props?.path ? (
          <LoadingIndicator />
        ) : (
          <Worker workerUrl="https://unpkg.com/pdfjs-dist@2.12.313/build/pdf.worker.min.js">
            <div styles={{ marginTop: "0" }}>
                <button onClick={print}>Print</button>
              <Viewer
                // defaultScale={SpecialZoomLevel.PageFit}

                fileUrl={props?.path}
                plugins={[defaultLayoutPluginInstance]}
              />
            </div>
          </Worker>
        )}
      </Modal.Body>
    </Modal>
  );
}
