import axios from "axios";
// import { setTitle } from "../../saga-modules/common/actions";
// import { PopupDelete } from "../popup/PopupDelete";
import clsx from "clsx";
import { useEffect, useState } from "react";
import BlockUi from "react-block-ui";
import { Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval, TailSpin } from "react-loader-spinner";
import { toast } from "react-toastify";
import ReactTooltip from "react-tooltip";
import { KTCard, KTSVG, useDebounce } from "../../../../../_metronic/helpers";
import { PageTitle } from "../../../../../_metronic/layout/core";
import api from "../../../../configs/api";
import { PopupDelete } from "../../popup/PopupDelete";

export function HoiDong() {
  //   const dispatch = useDispatch();

  const [listHoiDong, setListHoiDong] = useState([]);
  const [perPage, setPerPage] = useState(10);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);

  const [showUpdate, setShowUpdate] = useState(false);
  const [id, setId] = useState("");
  const [page, setPage] = useState(1);

  const [trang_thai, setTrang_thai] = useState(true);

  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);

  const [searchTerm, setSearchTerm] = useState("");

  const debouncedSearchTerm = useDebounce(searchTerm, 300);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList({ page_number: 1, search_key: debouncedSearchTerm });
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = ({
    page_number = page,
    size = perPage,
    search_key = searchTerm,
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_GET_HOI_DONG_GET_LIST +
          `?page=${page_number}&per_page=${perPage}`,
        {
          search_ten: search_key,
        }
      )
      .then(async ({ data }) => {
        setListHoiDong(data?.results);
        setTotalRows(data?.total);

        setIsLoading(false);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  //validate thêm chỉ định chuyên khoa
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const findFormErrors = () => {
    const { ten_hoi_dong } = form;
    const newErrors = {};
    // name errors
    if (!ten_hoi_dong || ten_hoi_dong === "")
      newErrors.ten_hoi_dong = "Tên không được bỏ trống!";
    return newErrors;
  };

  // handle api
  const addHoiDong = async (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true);
      const { ten_hoi_dong } = form;
      const json = {
        ten_hoi_dong,
        trang_thai,
      };
      await axios
        .post(api.API_GET_HOI_DONG + "_add", json)
        .then(async ({ data }) => {
          console.log("data", data);
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getList({});
          setIsLoading(false);
          // navigate(0);
        })
        .catch((error) => {
          console.log("error", error?.response);
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const updateHoiDong = async (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true);
      const { ten_hoi_dong, id } = form;
      const json = {
        ten_hoi_dong,
        trang_thai,
      };
      await axios
        .put(api.API_GET_HOI_DONG + "/" + id, json)
        .then(async ({ data }) => {
          console.log("data", data);
          resetState();
          toast.success(data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          getList({});
          setIsLoading(false);
          // navigate(0);
        })
        .catch((error) => {
          console.log("error", error?.response);
          toast.error(error?.data?.errors, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const resetState = async () => {
    await setPerPage(10);
    await setTotalRows(0);
    await setForm({
      ten_hoi_dong: "",
    });
    await setTrang_thai(true);
    await setErrors({});
    await setId("");
    await setShowUpdate(false);
    // getNhomChuyenKhoa();
  };
  console.log("trang_thai", trang_thai);
  // DETAIL
  const detail = async (data) => {
    setForm({
      ...data,
    });

    setId(data.id);
    setTrang_thai(data.trang_thai);

    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_GET_HOI_DONG +
          "_get_list" +
          `?page=${page}&per_page=${newPerPage}`,
        {
          ten_hoi_dong: searchTerm,
        }
      )
      .then(({ data }) => {
        if (data?.results) {
          setListHoiDong(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteCK = () => {
    const json = {
      id: idDelete,
    };
    console.log("123213", json);
    axios
      .delete(api.API_GET_HOI_DONG + "/" + idDelete, {})
      .then(async ({ data }) => {
        console.log("data delete success", data);
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        closeModalDelete();
        resetState();
        if (page == 1) {
          getList({ page_number: 1 });
        } else if (listHoiDong?.length == 1) {
          getList({ page_number: page - 1 });
        } else {
          getList({ page_number: page });
        }
      })
      .catch((error) => {
        console.log("error", error);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  //đổi màu khi selected
  const [data, setData] = useState(listHoiDong);
  useEffect(() => {
    setData(listHoiDong);
  }, [listHoiDong]);
  const handleRowClicked = (row) => {
    const updatedData = listHoiDong?.map((item) => {
      if (row.id !== item.id) {
        return item;
      }
      return {
        ...item,
        toggleSelected: !item.toggleSelected,
      };
    });
    setData(updatedData);
  };

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Mã hội đồng",
      grow: 3,
      selector: (row) => (
        <div
          onClick={() => {
            detail(row);
            handleRowClicked(row);
          }}
        >
          <span>{row.ma_hoi_dong}</span>
        </div>
      ),
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Tên hội đồng",
      selector: (row) => (
        <span data-tip={row?.ten_hoi_dong}>
          {row.ten_hoi_dong}
          <ReactTooltip place="top" type="dark" effect="float" />
        </span>
      ),
      grow: 7,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },

    {
      name: "Trang thái",
      selector: (row) => <span>{row.trang_thai}</span>,
      grow: 3,
      cell: (row) => (
        <td>
          {row.trang_thai ? (
            <span className="badge badge-light-success fs-7 fw-bold">
              Hoạt động
            </span>
          ) : (
            <span className="badge badge-light-dark fs-7 fw-bold">
              Không hoạt động
            </span>
          )}
        </td>
      ),
    },

    {
      name: "Thao tác",
      grow: 2,

      cell: (row) => (
        <div className="container my-3">
          <a
            onClick={async () => {
              await setIdDelete(row?.id);
              showModalDelete();
            }}
            className="btn btn-icon btn-bg-light btn-color-danger btn-sm"
          >
            <KTSVG
              path="/media/icons/duotune/general/gen027.svg"
              className="svg-icon-3"
            />
          </a>
        </div>
      ),
    },
  ];

  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];

  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];

  return (
    <KTCard>
      <div className="page">
        <PageTitle breadcrumbs={usersBreadcrumbs}>Danh sách hội đồng</PageTitle>
        <div className="container-fluid">
          <div className="fv-row mb-7 mt-6">
            {/* begin::Label */}
            <label className="fw-bold fs-6 mb-2">Tên hội đồng</label>
            {/* end::Label */}

            {/* begin::Input */}
            <div className="col-md-6">
              <input
                placeholder="Nhập tên tìm kiếm..."
                type="text"
                name="name"
                className={clsx("form-control form-control-solid mb-3 mb-lg-0")}
                autoComplete="off"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* end::Input */}
          </div>
          <BlockUi
            tag="div"
            // blocking={!isLoading}
            loader={
              <TailSpin
                arialLabel="loading-indicator"
                height={60}
                width={60}
                strokeWidth={2}
                strokeWidthSecondary={1}
                color="blue"
                secondaryColor="blue"
                wrapperclassName={"d-inline-flex"}
              />
            }
          >
            <div className="row">
              <div className="col-md-8">
                <div className="" style={cardBox}>
                  <DataTable
                    noDataComponent={
                      <img
                        style={{ width: "30%", height: "auto" }}
                        src="/media/nodata-found.svg"
                      />
                    }
                    sortServer
                    progressPending={isLoading}
                    columns={columns}
                    data={data}
                    customStyles={customStyles}
                    pagination
                    highlightOnHover
                    pointerOnHover
                    paginationServer
                    paginationTotalRows={totalRows}
                    onChangeRowsPerPage={handlePerRowsChange}
                    onChangePage={handlePageChange}
                    paginationComponentOptions={paginationOptions}
                    conditionalRowStyles={conditionalRowStyles}
                    // onRowClicked={(data) => {
                    //   detail(data);
                    //   handleRowClicked(data);
                    // }}
                    progressComponent={
                      <div
                        style={{
                          padding: "24px",
                        }}
                      >
                        <Oval
                          arialLabel="loading-indicator"
                          color="#007bff"
                          height={40}
                        />
                      </div>
                    }
                  />
                </div>
              </div>
              <div className="col-md-4">
                <div className="" style={cardBox}>
                  <div className="">
                    <label className="fw-bold fs-6 mb-2">
                      Tạo mới/ cập nhật hội đồng.
                    </label>
                  </div>

                  {showUpdate && (
                    <div className="row mt-3">
                      <div className="col-4">
                        <label className="required fw-bold fs-6 mb-2">
                          Mã hội đồng
                        </label>
                      </div>
                      <div className="col-8">
                        <Form.Control
                          disabled={true}
                          type="text"
                          placeholder="Nhập mã hội đồng"
                          style={{ fontSize: 14, fontWeight: 500 }}
                          value={
                            form.ma_hoi_dong === null ? "" : form.ma_hoi_dong
                          }
                        />
                      </div>
                    </div>
                  )}

                  <div className="row mt-3">
                    <div className="col-4">
                      {/* <p className="font-weight-bold">
                        Tên hội đồng<span className="text-danger">*</span>
                      </p> */}
                      <label className="required fw-bold fs-6 mb-2">
                        Tên hội đồng
                      </label>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Nhập tên hội đồng"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={
                          form.ten_hoi_dong === null ? "" : form.ten_hoi_dong
                        }
                        onChange={(e) => {
                          setField("ten_hoi_dong", e.target.value);
                        }}
                        isInvalid={!!errors.ten_hoi_dong}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.ten_hoi_dong}
                      </Form.Control.Feedback>
                    </div>
                  </div>

                  <div className="row mt-3">
                    <div className="col-4">
                      {/* <p className="font-weight-bold">
                        Tên hội đồng<span className="text-danger">*</span>
                      </p> */}
                      <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                    </div>
                    <div className="col-8">
                      <div className="form-check form-check-solid form-switch fv-row">
                        <input
                          className="form-check-input w-45px h-30px"
                          type="checkbox"
                          id="trang_thai"
                          // value={trang_thai}
                          checked={trang_thai}
                          onChange={(e) => {
                            setTrang_thai(!trang_thai);
                          }}
                        />
                        <label className="form-check-label"></label>
                      </div>
                    </div>
                  </div>

                  <div className="row mt-5">
                    <div className="col-4">
                      {showUpdate ? (
                        <button
                          className="btn text-white bg-blue2"
                          style={{ fontSize: ".9rem" }}
                          onClick={(e) => {
                            resetState();
                            getList({ page_number: 1, search_key: "" });
                          }}
                        >
                          <i className="fas fa-sync-alt text-white"></i> Làm mới
                        </button>
                      ) : null}
                    </div>

                    <div className="col-8">
                      <button
                        className="btn text-white bg-blue2 mr-3"
                        style={{ fontSize: ".9rem" }}
                        onClick={(e) => {
                          showUpdate ? updateHoiDong(e) : addHoiDong(e);
                        }}
                      >
                        {showUpdate ? (
                          <i className="fa fa-pencil-alt text-white"></i>
                        ) : (
                          <i className="fas fa-save text-white"></i>
                        )}{" "}
                        {showUpdate ? "Cập nhật" : "Lưu hội đồng"}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <PopupDelete
              title="Bạn có đồng ý xóa hội đồng này không?"
              show={isvisibleDelete}
              onHide={closeModalDelete}
              onDetele={() => {
                deleteCK();
              }}
