import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import axios from "axios";
import React, { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { useSelector } from "react-redux";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import { selectRoleUser } from "../../../redux-module/auth/authSlice";
import TextField from "@mui/material/TextField";
import { enAU } from "date-fns/locale";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import moment from "moment";

export default function PopupSoQuyetDinh(props) {
  //validate thêm vị trí hành nghề
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const roleUser = useSelector(selectRoleUser)[0].ten_en;
  const roleUserten = useSelector(selectRoleUser)[0].ten;
  const [ngayQuyetDinh, setNgayQuyetDinh] = useState(new Date());

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const handleNgayQuyetDinh = (newValue) => {
    console.log("log newValue ", newValue);
    setNgayQuyetDinh(newValue);
    // await setField("ngay_quyet_dinh", moment(newValue).format("DD/MM/YYYY"));
    setField("ngay_quyet_dinh", new Date(newValue).getTime());
  };

  const findFormErrors = () => {
    const { so_quyet_dinh } = form;
    const newErrors = {};
    // name errors
    if (!so_quyet_dinh || so_quyet_dinh === "")
      newErrors.so_quyet_dinh = "Số quyết định không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
      for (const property in newErrors) {
        toast.error(newErrors[property], {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        return;
      }
    } else {
      if (props?.isMulti) {
        handleSubmitDuyet();
      }

      // No errors! Put any logic here for the form submission!
    }
  };

  const handleSubmitDuyet = () => {
    const { so_quyet_dinh, ngay_quyet_dinh } = form;

    const arrayTemp = props?.selectedRow?.map((e) => e.id);

    const json = {
      ids: arrayTemp,
      trang_thai_ho_so: "11",
      so_quyet_dinh,
      ngay_quyet_dinh,
    };
    if (arrayTemp.length === 0) {
      toast.error("Chưa có hồ sơ nào được chọn", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "success",
      });
      setIsLoading(false);
      return;
    }
    axios
      .put(api.API_DUYET_MULTI_YEU_CAU_CHUNG_CHI, json)
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        props?.getList();
        setIsLoading(false);
        resetState();
        props?.onHide();
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const resetState = () => {
    setField("so_quyet_dinh", "");
    setForm({
      ngay_quyet_dinh: new Date(new Date()).getTime(),
    });
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
      <Modal
        centered
        show={props?.show}
        onHide={() => {
          props?.onHide();
          setErrors({});
        }}
      >
        <Modal.Header className="bg-blue2">
          <Modal.Title className="text-white" style={{ fontSize: "22px" }}>
            {props?.title}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body style={{ justifyContent: "center" }}>
          {props.isMulti ? (
            <span className="fw-bold fs-6 mb-2">Họ tên : {roleUserten}</span>
          ) : null}
          <br />
          <br />
          <span className="fw-bold fs-6 mb-2">Số quyết định</span>

          <Form.Control
            type="text"
            placeholder="Nhập số quyết định..."
            style={{ height: 38 }}
            value={form.so_quyet_dinh === null ? "" : form.so_quyet_dinh}
            onChange={(e) => {
              setField("so_quyet_dinh", e.target.value);
            }}
            isInvalid={!!errors.so_quyet_dinh}
          />

          <div>
            <span className="fw-bold d-block mb-2">Ngày QĐ</span>
            <DesktopDatePicker
              label=" "
              inputFormat="dd/MM/yyyy"
              value={ngayQuyetDinh}
              onChange={(e) => handleNgayQuyetDinh(e)}
              renderInput={(params) => <TextField {...params} />}
            />
            {/* <span className="text-danger">
              {errors?.ngay_quyet_dinh
                ? "Ngày quyết định không được bỏ trống"
                : ""}
            </span> */}
          </div>
        </Modal.Body>
        <Modal.Footer>
          <div className="d-flex justify-content-center">
            <Button
              className="btn btn-secondary"
              style={{ marginRight: 10 }}
              onClick={() => {
                props?.onHide();
                resetState();
                setErrors({});
              }}
            >
              QUAY LẠI
            </Button>
            <Button
              className="btn btn-primary"
              onClick={(e) => {
                handleSubmit(e);
              }}
            >
              HOÀN THÀNH
            </Button>
          </div>
        </Modal.Footer>
      </Modal>
    </LocalizationProvider>
  );
}
