import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import TextField from "@mui/material/TextField";
import { vi } from "date-fns/locale";
import { useState, useEffect } from "react";
import ImageUploading from "react-images-uploading";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { selectCurrentUser } from "../../../redux-module/auth/authSlice";
import { useSelector } from "react-redux";
import { Form } from "react-bootstrap";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import axios from "axios";
import { PageTitle } from "../../../../_metronic/layout/core";
// import Switch from "react-switch";

export default function ThongTinToChuc() {

    let navigate = useNavigate();
    const thongTinToChuc = useSelector(selectCurrentUser);

    const [disabelChuQuan, setDisabelChuQuan] = useState(false);
    const [form, setForm] = useState({});
    const [errors, setErrors] = useState([]);
    // location
    const [ListTinhThanh, setListTinhThanh] = useState([]);
    const [ListQuanHuyen, setListQuanHuyen] = useState([]);
    const [ListXaPhuong, setListXaPhuong] = useState([]);

    const [disQuanHuyen, setDisQuanHuyen] = useState(true);
    const [disXaPhuong, setDisXaPhuong] = useState(true);

    const [quanHuyenValue, setQuanHuyenValue] = useState(null);
    const [xaPhuongValue, setXaPhuongValue] = useState(null);
    const [NgayHieuLuc, setNgayHieuLuc] = useState(new Date());
    const [ngayCapCmnd, setNgayCapCmnd] = useState(new Date());

    // loai hinh kinh doanh
    const [selectedLoaiHinhKinhDoanh, setSelectedLoaiHinhKinhDoanh] = useState([]);
    const [listLoaiHinhKinhDoanh, setListLoaiHinhKinhDoanh] = useState([]);

    // location Chỗ ở hiện nay
    const [ListTinhThanhCOHN, setListTinhThanhCOHN] = useState([]);
    const [ListQuanHuyenCOHN, setListQuanHuyenCOHN] = useState([]);
    const [ListXaPhuongCOHN, setListXaPhuongCOHN] = useState([]);

    const [disQuanHuyenCOHN, setDisQuanHuyenCOHN] = useState(true);
    const [disXaPhuongCOHN, setDisXaPhuongCOHN] = useState(true);

    const [quanHuyenValueCOHN, setQuanHuyenValueCOHN] = useState(null);
    const [xaPhuongValueCOHN, setXaPhuongValueCOHN] = useState(null);

    console.log("thong tin to chuc", thongTinToChuc);

    useEffect(() => {
        getLoaiHinhKinhDoanh();
        getTinhThanh();
        getTinhThanhCOHN();
    }, []);

    const handleNgayHieuLuc = (newValue) => {
        console.log("log new value", new Date(newValue).getTime());
        setNgayHieuLuc(newValue);
    };

    const handleNgayCap = (newValue) => {
        console.log("log new value", new Date(newValue).getTime());
        setNgayCapCmnd(newValue);
    };

    // selected ngay
    const selectedNgayCapGCN = async (date) => {
        // await setNgaySinh(date);
        await setField("ngay_sinh", date);
    };


    const cardBox = {
        backgroundColor: "#fff",
        backgroundClip: "border-box",
        border: "1px solid #e7eaed",
        padding: "1.5rem",
        marginBottom: "24px",
        borderRadius: "0.5rem",
    };
    const usersBreadcrumbs = [
        {
            title: "Trang chủ",
            path: "/",
            isSeparator: false,
            isActive: false,
        },
        {
            title: "",
            path: "",
            isSeparator: true,
            isActive: false,
        },
    ];
    const getLoaiHinhKinhDoanh = () => {
        axios
            .post(api.API_LIST_LOAI_HINH_KINH_DOANH, {
                search_ten: "",
            })
            .then(async ({ data }) => {
                if (data) {
                    let resultsLoaiHinhKinhDoanh = data?.results;
                    resultsLoaiHinhKinhDoanh.forEach((e) => {
                        e.label = e.ten;
                        e.value = e.id;
                    });
                    setListLoaiHinhKinhDoanh(resultsLoaiHinhKinhDoanh);
                }
            })
            .catch((error) => {
                toast.error(error?.response?.data?.msg, {
                    position: "top-right",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    toastId: "error",
                });
            })
            .finally(() => { });
    };
    // GET LOCATIONs
    const getTinhThanh = () => {
        axios
            .get(api.API_TINH_THANH)
            .then(({ data }) => {
                let resultsTinhThanh = data?.results;
                resultsTinhThanh.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListTinhThanh(resultsTinhThanh);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getQuanHuyen = (value) => {
        axios
            .get(`${api.API_QUAN_HUYEN}/${value}?per_page=100`)
            .then(({ data }) => {
                let resultsQuanHuyen = data?.results;
                resultsQuanHuyen.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListQuanHuyen(resultsQuanHuyen);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getXaPhuong = (value) => {
        axios
            .get(`${api.API_XA_PHUONG}/${value}?per_page=100`)
            .then(({ data }) => {
                let resultsXaPhuong = data?.results;
                resultsXaPhuong.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListXaPhuong(resultsXaPhuong);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const onChangeTinhThanh = async (values) => {
        setField("tinh_thanh_id", values.id);
        getQuanHuyen(values.id);
        setDisQuanHuyen(false);
        setDisXaPhuong(true);
        setQuanHuyenValue(null);
        setXaPhuongValue(null);
    };
    const onChangeQuanHuyen = (values) => {
        setField("quan_huyen_id", values.id);
        getXaPhuong(values.id);
        setDisXaPhuong(false);
        setQuanHuyenValue(values);
        setXaPhuongValue(null);
    };
    const onChangeXaPhuong = (values) => {
        setField("phuong_xa_id", values.id);
        setXaPhuongValue(values);
    };
    // GET LOCATIONs COHN
    const getTinhThanhCOHN = () => {
        axios
            .get(api.API_TINH_THANH)
            .then(({ data }) => {
                let resultsTinhThanh = data?.results;
                resultsTinhThanh.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListTinhThanhCOHN(resultsTinhThanh);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getQuanHuyenCOHN = (value) => {
        axios
            .get(`${api.API_QUAN_HUYEN}/${value}?per_page=100`)
            .then(({ data }) => {
                let resultsQuanHuyen = data?.results;
                resultsQuanHuyen.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListQuanHuyenCOHN(resultsQuanHuyen);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getXaPhuongCOHN = (value) => {
        axios
            .get(`${api.API_XA_PHUONG}/${value}?per_page=100`)
            .then(({ data }) => {
                let resultsXaPhuong = data?.results;
                resultsXaPhuong.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });
                setListXaPhuongCOHN(resultsXaPhuong);
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const onChangeTinhThanhCOHN = async (values) => {
        setField("tinh_thanh_id", values.id);
        getQuanHuyenCOHN(values.id);
        setDisQuanHuyenCOHN(false);
        setDisXaPhuongCOHN(true);
        setQuanHuyenValueCOHN(null);
        setXaPhuongValueCOHN(null);
    };
    const onChangeQuanHuyenCOHN = (values) => {
        setField("quan_huyen_id", values.id);
        getXaPhuongCOHN(values.id);
        setDisXaPhuongCOHN(false);
        setQuanHuyenValueCOHN(values);
        setXaPhuongValueCOHN(null);
    };
    const onChangeXaPhuongCOHN = (values) => {
        setField("phuong_xa_id", values.id);
        setXaPhuongValueCOHN(values);
    };
    // SET FIELDs DATA
    const setField = (field, value) => {
        setForm({
            ...form,
            [field]: value,
        });
        // Check and see if errors exist, and remove them from the error object:
        if (!!errors[field])
            setErrors({
                ...errors,
                [field]: null,
            });
    };
    const formatGroupLabel = (listTemp) => (
        <div style={groupStyles}>
            <span>{listTemp.ten}</span>
        </div>
    );
    const groupStyles = {
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
    };
    return (
        <div>
            <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
                <PageTitle breadcrumbs={usersBreadcrumbs}>
                    Thông tin tài khoản
                </PageTitle>
                <div className="card">
                    <div className="card-body">
                        <div className="row">

                            <div className="col-md-6">
                                <div style={cardBox}>
                                    <ImageUploading maxNumber={1} dataURLKey="data_url">
                                        {({
                                            imageList,
                                            onImageUpload,
                                            onImageRemove,
                                            isDragging,
                                            dragProps,
                                        }) => (
                                            <div className="form-group">
                                                {imageList.length >= 1 ? (
                                                    imageList.map((image, index) => (
                                                        <div key={index} className="form-group">
                                                            <button
                                                                style={{ fontSize: "14px" }}
                                                                className="btn btn-danger"
                                                                onClick={() => onImageRemove(index)}
                                                            >
                                                                Xóa
                                                            </button>
                                                            <div>
                                                                <img
                                                                    className="mr-2 my-2"
                                                                    src={image.data_url}
                                                                    alt=""
                                                                    width="140"
                                                                />
                                                            </div>
                                                        </div>
                                                    ))
                                                ) : (
                                                    <div>
                                                        <div class="avatar-img">
                                                            <i
                                                                class="fas fa-camera btn-change-avatar"
                                                            // onClick={onImageUpload}
                                                            ></i>
                                                            <img
                                                                src="/media/avatars/default-forum-user.png"
                                                                alt=""
                                                                class="rounded-circle border p-1"
                                                                id="avatar-img"
                                                                width="150"
                                                                height="150"
                                                                backgroundColor="red"
                                                            />
                                                        </div>
                                                    </div>
                                                )}
                                            </div>
                                        )}
                                    </ImageUploading>
                                    <div className="form-group">
                                        <label className="fw-bold required fs-6 mb-1"> Tên cơ sở/Địa điểm kinh doanh </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Nhập họ tên"
                                            style={{ fontSize: 14, fontWeight: 400, height: "38px" }}
                                            onChange={(e) => { setField("ten_coso", e.target.value)}}
                                            value={ thongTinToChuc?.ten_coso != null ? thongTinToChuc?.ten_coso : "" }
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1"> Loại hình kinh doanh </label>
                                        <Select
                                            value={selectedLoaiHinhKinhDoanh}
                                            key={"id"}
                                            options={listLoaiHinhKinhDoanh}
                                            placeholder="Chọn loại hình kinh doanh"
                                            formatGroupLabel={formatGroupLabel}
                                            onChange={(e) => { 
                                                console.log(e)
                                                setSelectedLoaiHinhKinhDoanh(...selectedLoaiHinhKinhDoanh, e); 
                                            }}
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1">
                                            Website
                                        </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Website"
                                            style={{
                                                fontSize: 14,
                                                fontWeight: 400,
                                                height: "38px",
                                            }}
                                            onChange={(e) => {
                                                setField("website_co_so", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.website_co_so != null
                                                    ? thongTinToChuc?.website_co_so
                                                    : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1">
                                            Mã số Doanh nghiệp/Địa điểm kinh doanh
                                        </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Mã số Doanh nghiệp/Địa điểm kinh doanh"
                                            style={{
                                                fontSize: 14,
                                                fontWeight: 400,
                                                height: "38px",
                                            }}
                                            onChange={(e) => {
                                                setField("ma_coso", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.ma_coso != null
                                                    ? thongTinToChuc?.ma_coso
                                                    : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1">
                                            Ngày cấp GCN DKKD
                                        </label>

                                        <DesktopDatePicker
                                            label=" "
                                            inputFormat="dd/MM/yyyy"
                                            value={form?.ngay_cap ? form?.ngay_cap : null}
                                            onChange={selectedNgayCapGCN}
                                            maxDate={new Date()}
                                            renderInput={(params) => (
                                                <TextField size="small" {...params} />
                                            )}
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1"> Email </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Email"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("email_coso", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.email_coso != null ? thongTinToChuc?.email_coso : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1">
                                            Số điện thoại
                                        </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Nhập số điện thoại"
                                            style={{ height: 38 }}
                                            onChange={(e) => {
                                                setField("dienthoai_coso", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.dienthoai_coso != null
                                                    ? thongTinToChuc?.dienthoai_coso
                                                    : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-1"> Fax </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Nhập số điện thoại"
                                            style={{ height: 38 }}
                                            onChange={(e) => {
                                                setField("fax_coso", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.fax_coso != null ? thongTinToChuc?.fax_coso : ""
                                            }
                                        />
                                    </div>
                                    <div className="mt-5">
                                        <label className="font-weight-bold text-uppercase">
                                            Địa chỉ kinh doanh: <span className="text-danger"> </span>
                                        </label>
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-2"> Tỉnh/Thành phố </label>
                                        <Select
                                            key={"id"}
                                            labelField={"ten"}
                                            placeholder="Chọn tỉnh thành"
                                            searchBy={"tenkhongdau"}
                                            options={ListTinhThanh}
                                            onChange={(values) => onChangeTinhThanh(values)}
                                            closeOnSelect={true}
                                        />
                                        {errors?.tinh_thanh !== null && !form.tinh_thanh_id ? (
                                            <span className="text-danger"> {errors?.tinh_thanh}</span>
                                        ) : null}
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-2"> Quận/Huyện </label>
                                        <Select
                                            key={"id"}
                                            className={"quan_huyen"}
                                            labelField={"ten"}
                                            placeholder="Chọn quận huyện"
                                            options={ListQuanHuyen}
                                            onChange={(values) => onChangeQuanHuyen(values)}
                                            closeOnSelect={true}
                                            multi={false}
                                            isDisabled={disQuanHuyen}
                                            value={quanHuyenValue}
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold required fs-6 mb-2"> Xã/Phường </label>
                                        <Select
                                            key={"id"}
                                            labelField={"ten"}
                                            placeholder="Chọn xã phường"
                                            options={ListXaPhuong}
                                            onChange={(values) => onChangeXaPhuong(values)}
                                            closeOnSelect={true}
                                            isDisabled={disXaPhuong}
                                            value={xaPhuongValue}
                                        />
                                    </div>
                                    <div className="form-group mt-2">
                                        <label className="fw-bold fs-6 mb-2"> Số nhà, tên đường </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Nhập số nhà tên đường"
                                            style={{ height: 38 }}
                                            value={form.diachi_coso === null ? "" : form.diachi_coso}
                                            onChange={(e) => {
                                                setField("diachi_coso", e.target.value);
                                            }}
                                            isInvalid={!!errors.diachi_coso}
                                        />
                                    </div>
                                </div>
                            </div>

                            <div className="col-md-6">
                                <div style={cardBox}>

                                    <div className="mt-7">
                                        <label className="font-weight-bold text-uppercase"> Thông tin người đứng đầu: </label>
                                    </div>

                                    <div className="form-group mt-5">
                                        <label className="fw-bold required fs-6 mb-2"> Tên người liên hệ </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Nhập tên người liên hệ"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("ten_nguoi_lien_he", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.ten_nguoi_lien_he != null
                                                    ? thongTinToChuc?.ten_nguoi_lien_he
                                                    : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold required fs-6 mb-2"> Chức danh </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Chức danh"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("chuc_danh_nguoi_lienhe", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.chuc_danh_nguoi_lienhe != null
                                                    ? thongTinToChuc?.chuc_danh_nguoi_lienhe
                                                    : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold required fs-6 mb-2"> Email </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Email"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("email_nguoi_lienhe", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.email_nguoi_lienhe != null ? thongTinToChuc?.email_nguoi_lienhe : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold required fs-6 mb-2"> Điện thoại </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Điện thoại"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("dien_thoai_nguoi_lienhe", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.dien_thoai_nguoi_lienhe != null ? thongTinToChuc?.dien_thoai_nguoi_lienhe : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold fs-6 mb-2">
                                            Địa chỉ liên lạc
                                            <span className="text-danger"> (*) </span>
                                        </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Địa chỉ liên lạc"
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("dia_chi_nguoi_lien_he", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.dia_chi_nguoi_lien_he != null
                                                    ? thongTinToChuc?.dia_chi_nguoi_lien_he
                                                    : ""
                                            }
                                        />
                                    </div>
                                    <div
                                        className="form-group mt-2 d-flex mt-14"
                                        style={{ alignSelf: "center" }}
                                    >
                                        <div>
                                            <label
                                                className="font-weight-bold text-uppercase"
                                                style={{ alignSelf: "center" }}
                                            >
                                                Thông tin doanh nghiệp/chi nhánh chủ quản:
                                                <span className="text-danger"> </span>
                                            </label>
                                        </div>
                                        <input
                                            style={{ alignSelf: "center" }}
                                            className="ml-4"
                                            type="checkbox"
                                            onChange={() => setDisabelChuQuan(!disabelChuQuan)}
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold required fs-6 mb-2">
                                            Tên doanh nghiệp/chi nhánh
                                        </label>

                                        <Form.Control
                                            type="text"
                                            placeholder="Tên doanh nghiệp/chi nhánh"
                                            disabled={disabelChuQuan ? false : true}
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("ten_truc_thuoc", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.ten_truc_thuoc != null
                                                    ? thongTinToChuc?.ten_truc_thuoc
                                                    : ""
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold fs-6 mb-2">
                                            Mã số Doanh nghiệp/chi nhánh
                                            <span className="text-danger"> (*) </span>
                                        </label>

                                        <Form.Control
                                            type="text"
                                            placeholder="Mã số Doanh nghiệp/chi nhánh"
                                            disabled={disabelChuQuan ? false : true}
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("ma_tructhuoc", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.ma_tructhuoc != null
                                                    ? thongTinToChuc?.ma_tructhuoc
                                                    : ""
                                            }
                                        />
                                    </div>

                                    <div className="form-group mt-5">
                                        <label className="fw-bold fs-6 mb-2">
                                            Tỉnh/Thành phố <span className="text-danger"> (*) </span>
                                        </label>
                                        <Select
                                            key={"id"}
                                            labelField={"ten"}
                                            placeholder="Chọn tỉnh thành"
                                            searchBy={"tenkhongdau"}
                                            options={ListTinhThanhCOHN}
                                            onChange={(values) => onChangeTinhThanhCOHN(values)}
                                            closeOnSelect={true}
                                            isDisabled={disabelChuQuan ? false : true}
                                        />
                                        {errors?.tinh_thanh !== null && !form.tinh_thanh_id ? (
                                            <span className="text-danger"> {errors?.tinh_thanh}</span>
                                        ) : null}
                                    </div>

                                    <div className="form-group mt-5">
                                        <label className="fw-bold fs-6 mb-2">
                                            Quận/Huyện <span className="text-danger"> (*) </span>
                                        </label>
                                        <Select
                                            key={"id"}
                                            className={"quan_huyen"}
                                            labelField={"ten"}
                                            placeholder="Chọn quận huyện"
                                            options={ListQuanHuyenCOHN}
                                            onChange={(values) => onChangeQuanHuyenCOHN(values)}
                                            closeOnSelect={true}
                                            multi={false}
                                            // isDisabled={disQuanHuyenCOHN}
                                            value={quanHuyenValueCOHN}
                                            isDisabled={
                                                disQuanHuyenCOHN || disabelChuQuan ? false : true
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold fs-6 mb-2">
                                            Xã/Phường <span className="text-danger"> (*) </span>
                                        </label>
                                        <Select
                                            key={"id"}
                                            labelField={"ten"}
                                            placeholder="Chọn xã phường"
                                            options={ListXaPhuongCOHN}
                                            onChange={(values) => onChangeXaPhuongCOHN(values)}
                                            closeOnSelect={true}
                                            // isDisabled={disXaPhuongCOHN}
                                            value={xaPhuongValueCOHN}
                                            isDisabled={
                                                disXaPhuongCOHN || disabelChuQuan ? false : true
                                            }
                                        />
                                    </div>
                                    <div className="form-group mt-5">
                                        <label className="fw-bold fs-6 mb-2">
                                            Số nhà, tên đường <span className="text-danger"> (*) </span>
                                        </label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Số nhà/tên đường"
                                            disabled={disabelChuQuan ? false : true}
                                            style={{ fontSize: 14, fontWeight: 400, height: 38 }}
                                            onChange={(e) => {
                                                setField("diachi_tructhuoc", e.target.value);
                                            }}
                                            value={
                                                thongTinToChuc?.diachi_tructhuoc != null
                                                    ? thongTinToChuc?.diachi_tructhuoc
                                                    : ""
                                            }
                                        />
                                    </div>
                                </div>
                            </div>

                            <div className="col-12 text-center mt-5">
                                <button
                                    to={"/dashboard"}
                                    className="btn btn-secondary"
                                    onClick={() => navigate(-1)}
                                >
                                    Trở về
                                </button>

                                <button
                                    className="btn btn-primary"
                                    style={{ marginLeft: "10px" }}
                                >
                                    Lưu thông tin
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </LocalizationProvider>
        </div>
    );
}
