import { LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import axios from "axios";
// import { vi } from "date-fns/locale";
import { enAU } from "date-fns/locale";
import { useEffect, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import { async } from "@firebase/util";
// import { GioiTinh, HinhThuc } from "../../../data";
// import moment from "moment";

export default function PopupQuanLyNguoiDung(props) {
  const [showUpdate, setShowUpdate] = useState(false);
  const [form, setForm] = useState({});
  const [display_chuc_vu, setDisplay_chuc_vu] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (props?.show) {
      // resetState();
      console.log("props.edit", props.edit);
      if (props.edit == false) {
        setForm({ ho_ten: "", tai_khoan: "",nhap_lai_mat_khau: "", mat_khau: "" });
        setSelectedDoiTuong({
          label: props?.data?.chuc_vu,
          value: props?.data?.chuc_vu,
        });
      } else {
        setForm({
          ...props?.data,
        }); setSelectedDoiTuong({
          label: props?.data?.chuc_vu,
          value: props?.data?.chuc_vu,
        });
      }


    }
    console.log("propsssss", props);
  }, [props?.show, props?.edit]);

  const [selectedDoiTuong, setSelectedDoiTuong] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );
  const findFormErrors = () => {
    const { mat_khau, nhap_lai_mat_khau } = form;
    const newErrors = {};
    // name errors
    if (!mat_khau || mat_khau === "")
      newErrors.mat_khau = "Vui lòng nhập mật khẩu hiện tại!";
    if (!nhap_lai_mat_khau || nhap_lai_mat_khau === "")
      newErrors.nhap_lai_mat_khau = "Vui lòng nhập xác nhận mật khẩu!";

    return newErrors;
  };
  const addNguoiDung = async (e)=>{
    e.preventDefault();
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      setIsLoading(true);
      const json = {
        chuc_vu: selectedDoiTuong?.value,
        mat_khau: form?.mat_khau,
        ho_ten: form?.ho_ten,
        tai_khoan: form?.tai_khoan
      };
      await axios
        .post(api.API_QL_TAI_KHOAN_NGUOI_DUNG_ADD , json)
        .then(async ({ data }) => {
          toast.success("Cập nhật tài khoản thành công", {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          props?.getList();
          props?.hide();
        })
        .catch((error) => {
          toast.error(error?.response?.data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  }
  const handleSubmitUpdate = async (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      console.log(newErrors);
    } else {
      setIsLoading(true);
      const json = {
        chuc_vu: selectedDoiTuong?.value,
        mat_khau: form?.mat_khau,
        ho_ten: form?.ho_ten,
      };
      await axios
        .put(api.API_QL_TAI_KHOAN_NGUOI_DUNG_ID + "/" + props?.data?.id, json)
        .then(async ({ data }) => {
          toast.success("Cập nhật tài khoản thành công", {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
          props?.getList();
          props?.hide();
        })
        .catch((error) => {
          toast.error(error?.response?.data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };

  const optionsDoiTuong = [
    {
      label: "Chuyên viên thụ lý",
      value: 4,
      id: "Chuyên viên thụ lý",
    },
    {
      label: "Chuyên viên hội đồng",
      value: 5,
      id: "Chuyên viên hội đồng",
    },
    {
      label: "Phó trưởng phòng",
      value: 3,
      id: "Phó trưởng phòng",
    },
    {
      label: "Trưởng phòng",
      value: 2,
      id: "Trưởng phòng",
    },
    {
      label: "Giám đốc",
      value: 0,
      id: "Giám đốc",
    },
    {
      label: "Phó giám đốc",
      value: 1,
      id: "Phó giám đốc",
    },
    {
      label: "Văn thư",
      value: 6,
      id: "Văn thư",
    },
  ];

  // GET LOCATIONs

  const handleSubmit = (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
    } else {
      // No errors! Put any logic here for the form submission!
      edit ? handleSubmitUpdate(e) : addNguoiDung(e);
    }
  };


  // cccd/cmnd
  const [datareset, setDatareset] = useState(props?.data);
  useEffect(() => {
    setDatareset(props?.data);
  }, [props?.data]);
  const resetState = () => {
    setForm({});
    setErrors({});
    setShowUpdate(false);
    const updatedData = props.data.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setDatareset(updatedData);
  };

  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  return (
    <Modal
      backdrop="static"
      keyboard={false}
      size="xl"
      show={props?.show}
      onHide={() => {
        props?.hide();
      }}
    >
      <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
        <Modal.Header className="bg-blue2" closeButton>
          <Modal.Title className="text-white">
            {!props.edit ? "Thêm" : "Cập nhật"} người dùng
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <div className="row" style={cardBox}>
              <div className=" col-12 form-group">
                <div className=" form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Họ và tên
                  </label>
                  <Form.Control
                    type="text"
                    placeholder="Nhập họ và tên đệm"
                    style={{ height: 38 }}
                    value={form.ho_ten === null ? "" : form.ho_ten}
                    onChange={(e) => {
                      setField("ho_ten", e.target.value);
                    }}
                    isInvalid={!!errors.ho_ten}
                  />
                </div>
                <div className=" form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Tên tài khoản
                  </label>
                  <Form.Control
                    type="text"
                    placeholder="Tên tài khoản"
                    style={{ height: 38 }}
                    value={form.tai_khoan === null ? "" : form.tai_khoan}
                    onChange={(e) => {
                      setField("tai_khoan", e.target.value);
                    }}
                    isInvalid={!!errors.tai_khoan}
                  />
                </div>
                <div className="form-group mt-2 mb-5">
                  <label className="required fw-bold fs-6 mb-2">Vai trò</label>
                  <Select
                    value={selectedDoiTuong}
                    key={"id"}
                    options={optionsDoiTuong}
                    formatGroupLabel={formatGroupLabel}
                    onChange={async (data) => {
                      setSelectedDoiTuong(data);
                    }}
                  />
                </div>
                {
                  props.edit ? (<div className="form-group mb-2">
                    <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                    <div className="form-check form-check-solid form-switch fv-row">
                      <input
                        className="form-check-input w-45px h-30px"
                        type="checkbox"
                        id="display_chuc_vu"
                      checked={display_chuc_vu}
                      onChange={(e) => {
                        setDisplay_chuc_vu(!display_chuc_vu);
                      }}
                      />
                      <label className="form-check-label ml-3">Hiện vào bản in</label>
                    </div>
                  </div>) : null
                }

                <div className="form-group mt-2 mb-5">
                  <label className="label-input fw-bold">Mật khẩu</label>
                  <div className="input-group input-icon">
                    <Form.Control
                      placeholder="Nhập mật khẩu mới"
                      className="input-register"
                      style={{ fontSize: 14, fontWeight: 400 }}
                      onChange={(e) => {
                        setField("mat_khau", e.target.value);
                      }}
                    />
                    {/* <i className="fa fa-key"></i> */}

                    <Form.Control.Feedback type="invalid">
                      {errors.mat_khau}
                    </Form.Control.Feedback>
                  </div>
                </div>

                <div className="form-group mb-10">
                  <label className="label-input fw-bold">
                    Xác nhận mật khẩu
                  </label>
                  <div className="input-group input-icon">
                    <Form.Control
                      placeholder="Nhập lại mật khẩu mới"
                      className="input-register"
                      style={{ fontSize: 14, fontWeight: 400 }}
                      onChange={(e) => {
                        setField("nhap_lai_mat_khau", e.target.value);
                      }}
                    />
                    {/* <i className="fa fa-key"></i> */}

                    <Form.Control.Feedback type="invalid">
                      {errors.nhap_lai_mat_khau}
                    </Form.Control.Feedback>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              props?.hide();
            }}
          >
            <i className="fas fa-times"></i> Hủy
          </Button>
          <Button
            variant="btn text-white bg-blue2"
            onClick={async (e) => {
              handleSubmit(e);
            }}
          >
            {props?.edit ? (
              <i className="fa fa-pencil-alt text-white"></i>
            ) : (
              <i className="fas fa-save text-white"></i>
            )}{" "}
            {props?.edit ? "CẬP NHẬT" : "THÊM"}
          </Button>
        </Modal.Footer>
      </LocalizationProvider>
    </Modal>
  );
}
