import { initializeApp } from "firebase/app";
import { getMessaging, getToken, onMessage } from "firebase/messaging";

var firebaseConfig = {
  apiKey: "AIzaSyBAyZD8XWJo4nl4KcIO-c2KOCdLKchlLb4",
  authDomain: "testnotiappcchndv2.firebaseapp.com",
  projectId: "testnotiappcchndv2",
  storageBucket: "testnotiappcchndv2.appspot.com",
  messagingSenderId: "568106658592",
  appId: "1:568106658592:web:97ae7bbddbf26121c04545",
  measurementId: "G-SE8ET9ZK0K",
};

const firebaseApp = initializeApp(firebaseConfig);
const messaging = getMessaging(firebaseApp);

export const fetchToken = (setTokenFound) => {
  return getToken(messaging, {
    vapidKey:
      "BG4vg0Hec0WeyoWxYYFa6HakkPrmySbA1fcushhMNxF8RBUJd9Scl011sB_bhTSDCNDKkS0QjVhLT8thlcA-ohc",
  })
    .then((currentToken) => {
      console.log("log current token: ", currentToken);
      if (currentToken) {
        localStorage.setItem("tokenFirebase", currentToken);
        // Track the token -> client mapping, by sending to backend server
        // show on the UI that permission is secured
        return currentToken;
      } else {
        console.log(
          "No registration token available. Request permission to generate one."
        );

        localStorage.setItem("tokenFirebase", null);
        // shows on the UI that permission is required
      }
    })
    .catch((err) => {
      // catch error while creating client token
    });
};

export const onMessageListener = () =>
  new Promise((resolve) => {
    onMessage(messaging, (payload) => {
      console.log("log payload", payload);
      resolve(payload);
    });
  });
