import axios from "axios";

import moment from "moment";
import { useEffect, useState } from "react";
import { Modal } from "react-bootstrap";
import InfiniteScroll from "react-infinite-scroll-component";
import LoadingOverlay from "react-loading-overlay";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";
import { useAuth } from "../../../../app/modules/apps/auth";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";
import { selectCurrentUser } from "../../../redux-module/auth/authSlice";
import {
  notiActions,
  selectCountUnreadNoti,
  selectListNoti,
  selectLoading,
} from "../../../redux-module/noti/notiSlice";

LoadingOverlay.propTypes = undefined;
export function ThongBao() {
  const [listThongBao, setListThongBao] = useState([]);
  const [page, setPage] = useState(1);
  const dispatch = useDispatch();

  //modal
  const [showDetail, setShowDetail] = useState(false);

  const [detailNoti, setDetailNoti] = useState([]);
  const currentUser = useSelector(selectCurrentUser);
  const isLoading = useSelector(selectLoading);
  const ListNoti = useSelector(selectListNoti);
  const CountUnreadNoti = useSelector(selectCountUnreadNoti);

  const showModal = () => setShowDetail(true);
  const closeModal = () => setShowDetail(false);

  useEffect(() => {
    console.log("currentUser?.id", currentUser?.id);
    dispatch(notiActions.getNoti(currentUser?.id));
    // getList({ page_number: 1 });
  }, []);

  // const getList = async ({ page_number = page }) => {
  //   await setIsLoading(true);
  //   axios
  //     .get(
  //       api.API_GET_THONG_BAO +
  //         "/" +
  //         currentUser.id +
  //         `?page=${page_number}&per_page=1000`
  //     )
  //     .then(async ({ data }) => {
  //       console.log("log data", data?.results);
  //       await setListThongBao(data?.results);
  //     })
  //     .catch((error) => {
  //       toast.error(error?.response?.data?.msg, {
  //         position: "top-right",
  //         autoClose: 2000,
  //         hideProgressBar: false,
  //         closeOnClick: true,
  //         pauseOnHover: true,
  //         draggable: true,
  //         progress: undefined,
  //         toastId: "error",
  //       });
  //     })
  //     .finally(() => {
  //       setIsLoading(false);
  //     });
  // };

  const seenNoti = async (thongbao) => {
    axios
      .put(api.API_XEM_TIN_NHAN + "/" + thongbao.id)
      .then(async ({ data }) => {
        if (data) {
          console.log("log data", data);
          setDetailNoti(thongbao);
          showModal();
          // getList({ page_number: 1 });
          dispatch(notiActions.getNoti(currentUser?.id));
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  const notyBox = {
    borderRadius: "16px",
    boxShadow: "0px 0px 6px 0px rgb(0 0 0 / 16%)",
    marginLeft: "auto",
    marginRight: "auto",
    marginBottom: "10px",
    marginTop: "10px",
    minHeight: "121px",
  };

  const usersBreadcrumbs = [
    {
      title: "Trang chá»§",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];

  const fetchMoreData = () => {
    // a fake async api call like which sends
    // 20 more records in 1.5 secs
    setPage(page + 1);
    // getList({ page_number: page == 1 ? 2 : page });
  };

  return (
    <div id="main-content" className="container-fluid">
      <LoadingOverlay spinner active={isLoading} text="Äang táº£i dá»¯ liá»u ...">
        <PageTitle breadcrumbs={usersBreadcrumbs}>
          Danh sÃ¡ch thÃ´ng bÃ¡o
        </PageTitle>
        <section className="section-50">
          <div className="container">
            <div className="notification-ui_dd-content">
              {ListNoti && ListNoti?.length > 0
                ? ListNoti.map((e, i) => {
                    return (
                      <div
                        onClick={() => {
                          seenNoti(e);
                        }}
                        key={e?.id}
                        className={`notification-list ${
                          !e?.read_at && "notification-list--unread"
                        } `}
                      >
                        <div className="notification-list_content">
                          <div className="notification-list_img">
                            <img src="/media/assets/notifications.png"></img>
                          </div>
                          <div className="notification-list_detail">
                            <p>
                              <b>{e?.thong_bao?.tieu_de} </b>
                            </p>
                            <p className="text-muted">
                              {e?.thong_bao?.noi_dung}
                            </p>
                            <p className="text-muted">
                              <small>10 mins ago</small>
                            </p>
                          </div>
                        </div>
                        <div className="notification-list_feature-img"></div>
                      </div>
                    );
                  })
                : null}

              {/* <div className="notification-list notification-list--unread">
              <div className="notification-list_content">
                <div className="notification-list_img"></div>
                <div className="notification-list_detail">
                  <p>
                    <b>Richard Miles</b> liked your post
                  </p>
                  <p className="text-muted">
                    Lorem ipsum dolor sit amet consectetur, adipisicing elit.
                    Unde, dolorem.
                  </p>
                  <p className="text-muted">
                    <small>10 mins ago</small>
                  </p>
                </div>
              </div>
              <div className="notification-list_feature-img"></div>
            </div>
            <div className="notification-list">
              <div className="notification-list_content">
                <div className="notification-list_img"></div>
                <div className="notification-list_detail">
                  <p>
                    <b>Brian Cumin</b> reacted to your post
                  </p>
                  <p className="text-muted">
                    Lorem ipsum dolor sit amet consectetur, adipisicing elit.
                    Unde, dolorem.
                  </p>
                  <p className="text-muted">
                    <small>10 mins ago</small>
                  </p>
                </div>
              </div>
              <div className="notification-list_feature-img"></div>
            </div> */}
            </div>

            {/* <div className="text-center">
            <a href="#!" className="dark-link">
              Load more activity
            </a>
          </div> */}
          </div>
        </section>

        <Modal
          backdrop="static"
          keyboard={false}
          show={showDetail}
          onHide={closeModal}
          centered
          size="lg"
        >
          <Modal.Header closeButton className="bg-blue2">
            <Modal.Title className="text-white">
              {detailNoti?.thong_bao?.tieu_de}
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div className="form-group mb-5">
              {detailNoti?.thong_bao?.noi_dung}
            </div>
          </Modal.Body>
        </Modal>
      </LoadingOverlay>
    </div>
  );
}
