import axios from "axios";
import { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
// import { useDispatch } from "react-redux";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { TextField } from "@mui/material";
import { enAU } from "date-fns/locale";
import Select from "react-select";
import { toast } from "react-toastify";
import { KTCard, useDebounce } from "../../../../../_metronic/helpers";
import api from "../../../../configs/api";
import { TrangThaiLienKetDuocSi } from "../../../../data";
import moment from "moment";
import { useNavigate } from "react-router-dom";

export function YeuCauLienKetDuocSi() {
  const navigate = useNavigate();

  const [listHoSoThuLy, setListHoSoThuLy] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [trang_thai, setTrang_thai] = useState(true);

  //open modal delete
  const [searchMaHoSo, setSearchMaHoSo] = useState("");
  const [searchHoTen, setSearchHoTen] = useState("");
  const [dateNopDen, setDateNopDen] = useState("");
  const [dateNopTu, setDateNopTu] = useState("");
  const [valueTrangThai, setValueTrangThai] = useState({
    label: "Tất cả",
    value: "",
  });

  const filterHoTen = JSON.parse(localStorage.getItem("filterHoTen"));
  const filters = JSON.parse(localStorage.getItem("filters"));

  const debouncedSearchHoTen = useDebounce(searchHoTen, 1000);
  useEffect(() => {
    if (filters) {
      setSearchHoTen(filterHoTen);
      setValueTrangThai(filters);
      getList({
        page_number: 1,
        trang_thai: filters?.value,
      });
      localStorage.removeItem("filterHoTen");
      localStorage.removeItem("filters");
    }
  }, []);

  useEffect(
    () => {
      if (!filters) {
        if (debouncedSearchHoTen !== undefined && searchMaHoSo !== undefined) {
          getList({
            page_number: 1,
            ten_search: debouncedSearchHoTen,
          });
        }
      }
    },
    [debouncedSearchHoTen] // Only call effect if debounced search term changes
  );

  const getList = ({
    page_number = page,
    size = perPage,
    ten_search = searchHoTen,
    trang_thai_filter = valueTrangThai?.value || "",
    to_date = dateNopDen !== "" ? new Date(dateNopDen).setHours(23, 59, 59, 0) / 1000 : "",
    from_date = dateNopTu !== "" ? new Date(dateNopTu).setHours(0, 0, 0, 0) / 1000 : "",
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_DANH_SACH_YEU_CAU_LIEN_KET_DUOC_SI +
          `?page=${page_number}&per_page=${size}`,
        {
          ten_search: ten_search,
          trang_thai_filter: trang_thai_filter,
          to_date: to_date,
          from_date: from_date,
        }
      )
      .then(async ({ data }) => {
        setListHoSoThuLy(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  // handle api

  const resetState = async () => {
    await setSearchHoTen("");
    await setSearchMaHoSo("");
    await setDateNopDen(null);
    await setDateNopTu(null);
    await setValueTrangThai({
      label: "Tất cả",
      value: "",
    });
  };

  // DETAIL
  const detail = async (data) => {
    setTrang_thai(data?.trang_thai);
    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${page}&per_page=${newPerPage}`,
        {}
      )
      .then(({ data }) => {
        if (data?.results) {
          setListHoSoThuLy(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  const handleRowClicked = (row) => {};

  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );

  const checkStatus = (id) => {
    let status = TrangThaiLienKetDuocSi.find((e) => e.id === id);
    return <span className={`text-${status.color}`}>{status.name}</span>;
  };

  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };

  const columns = [
    {
      name: "Họ và tên",
      selector: (row) => (
        <span
          onClick={() => {
            navigate(`${row?.id}`);
            localStorage.setItem("filterHoTen", JSON.stringify(searchHoTen));
            localStorage.setItem(
              "filters",
              JSON.stringify({ ...valueTrangThai })
            );
          }}
        >
          {row.ho_ten}
        </span>
      ),
      grow: 1,
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Ngày sinh",
      selector: (row) => (
        <span>{moment(row.ngay_sinh * 1000).format("DD/MM/YYYY")}</span>
      ),
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Giới tính",
      selector: (row) => <span>{row.gioi_tinh}</span>,
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Thời gian liên kết",
      selector: (row) => (
        <span>
          {row.thoi_gian_lien_ket
            ? moment(row.thoi_gian_lien_ket * 1000).format("DD/MM/YYYY")
            : ""}
        </span>
      ),
      grow: 1,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trạng thái",
      selector: (row) => <div>{checkStatus(row?.trang_thai)}</div>,
      grow: 0.5,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
  ];

  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];

  return (
    <KTCard>
      {/* <div className="page"> */}
      <div className="container-fluid" style={{ marginTop: " 30px" }}>
        <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
          <div className="card-box">
            <div className="row mb-7">
              <div className="row col-12 mb-2">
                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Tên</label>
                  <Form.Control
                    style={{ height: 38 }}
                    type="text"
                    placeholder="Nhập tên..."
                    onChange={(e) => {
                      setSearchHoTen(e.target.value);
                    }}
                    value={searchHoTen}
                  />
                </div>
                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                  <Select
                    value={valueTrangThai}
                    key={"id"}
                    options={TrangThaiLienKetDuocSi}
                    formatGroupLabel={formatGroupLabel}
                    onChange={(data) => {
                      setValueTrangThai(data);
                      getList({ trang_thai_filter: data?.value });
                    }}
                  />
                </div>

                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Nộp từ</label>
                  <DesktopDatePicker
                    inputFormat="dd/MM/yyyy"
                    value={dateNopTu}
                    label=" "
                    onChange={(value) => {
                      setDateNopTu(value);
                      getList({
                        from_date: new Date(value).setHours(0, 0, 0, 0) / 1000,
                      });
                      console.log(
                        "log value",
                        new Date(value).setHours(0, 0, 0, 0)
                      );
                    }}
                    maxDate={new Date()}
                    renderInput={(params) => (
                      <TextField size="small" {...params} />
                    )}
                  />
                </div>
                <div className="col-3">
                  <label className="fw-bold fs-6 mb-2">Nộp đến</label>
                  <DesktopDatePicker
                    label=" "
                    inputFormat="dd/MM/yyyy"
                    value={dateNopDen}
                    onChange={(value) => {
                      setDateNopDen(value);
                      getList({
                        to_date: new Date(value).setHours(23, 59, 59, 0) / 1000,
                      });
                      console.log(
                        "log value",
                        new Date(value).setHours(23, 59, 59, 0)
                      );
                    }}
                    maxDate={new Date()}
                    renderInput={(params) => (
                      <TextField size="small" {...params} />
                    )}
                  />
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-md-12">
                <DataTable
                  noDataComponent={
                    <span className="py-5">Không có dữ liệu.</span>
                  }
                  persistTableHead={true}
                  sortServer
                  progressPending={isLoading}
                  columns={columns}
                  data={listHoSoThuLy}
                  customStyles={customStyles}
                  pagination
                  highlightOnHover
                  pointerOnHover
                  paginationServer
                  paginationTotalRows={totalRows}
                  onChangeRowsPerPage={handlePerRowsChange}
                  onChangePage={handlePageChange}
                  paginationComponentOptions={paginationOptions}
                  conditionalRowStyles={conditionalRowStyles}
                  progressComponent={
                    <div
                      style={{
                        padding: "24px",
                      }}
                    >
                      <Oval
                        arialLabel="loading-indicator"
                        color="#007bff"
                        height={40}
                      />
                    </div>
                  }
                />
              </div>
            </div>
          </div>
        </LocalizationProvider>
      </div>
      {/* </div> */}
    </KTCard>
  );
}
