import { vi } from "date-fns/locale";
import { LocalizationProvider } from "@mui/x-date-pickers";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";

import DataTable from "react-data-table-component";
import { useEffect, useState } from "react";
import api from "../../../configs/api";
import axios from "axios";
import { toast } from "react-toastify";
import Status from "../../../components/Status";
import ReactTooltip from "react-tooltip";
import { Oval } from "react-loader-spinner";
import moment from "moment";
import { useDebounce } from "../../../../_metronic/helpers";


export function DanhSachChungChi() {

  const [isLoading, setIsLoading] = useState(false)
  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(10);
  const [totalRows, setTotalRows] = useState(0);

  const [chungChiList, setChungChiList] = useState([])

  const [searchTerm, setSearchTerm] = useState("");
  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getListChungChi(1, perPage, debouncedSearchTerm);
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getListChungChi = (page_number = page, size = perPage, search_key = "") => {
    axios
      .post(api.API_CHUNG_CHI_HANH_NGHE_DA_CAP + `/get_list?page=${page_number}&per_page=${size}`, {})
      .then(({ data }) => {
        setChungChiList(data?.results);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      })
      .finally(() => {
        setIsLoading(false);
      });
  }

  const handlePerRowsChange = async (newPerPage, page) => {
    setIsLoading(true)
    axios
      .post(api.API_CHUNG_CHI_HANH_NGHE_DA_CAP + `/get_list?page=${page}&per_page=${newPerPage}`, {})
      .then(({ data }) => {
        if (data?.results) {
          setChungChiList(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => { })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handlePageChange = (page) => {
    setPage(page);
    getListChungChi({ page_number: page });
  };

  const columns = [
    {
      name: "STT",
      grow: 1,
      selector: (row) => (
        <div>
          <span>1</span>
        </div>
      ),
      style: {
        fontWeight: 400,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Số CCHND",
      grow: 4,
      selector: (row) => (
        <div>
          <span>{row.so_giay_phep}</span>
        </div>
      ),
      style: {
        fontWeight: 400,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Ngày cấp",
      grow: 3,
      selector: (row) => (
        <div>
          <span>{row.ngay_cap ? moment(Number(row.ngay_cap)).format("DD/MM/YYYY") : ""}</span>
        </div>
      ),
      style: {
        fontWeight: 400,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Họ và tên",
      grow: 4,
      selector: (row) => (
        <div>
          <span>{row.ho_ten}</span>
          <ReactTooltip place="top" type="dark" effect="float" />
        </div>
      ),
      style: {
        color: "#1251ff",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "CMND",
      grow: 3,
      selector: (row) => (
        <div>
          <span>{row.cmnd}</span>
        </div>
      ),
      style: {
        fontWeight: 400,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Nơi làm việc",
      grow: 4,
      selector: (row) => (
        <div>
          <span>{row.co_quan_cap}</span>
        </div>
      ),
      style: {
        fontWeight: 400,
        width: 300,
        cursor: "pointer",
      },
    },

  ];


  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];
  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns} locale={vi}>
      <div className="bg-white py-5 px-5 my-5">
        <div className="row" style={{ padding: "8px 16px 8px 16px" }}>
          <div className="col-12 text-center">
            <label className="text-primary fw-bold fs-1">DANH SÁCH CHỨNG CHỈ</label>
          </div>
          <div className="col-9 mt-5">
            <input className="form-control" type="text" placeholder="Tìm kiếm chứng chỉ"></input>
          </div>
          <div className="col-3 mt-5 text-end">
            <button className="btn btn-success ms-3">Import</button>
            <button className="btn btn-primary ms-3">Export</button>

          </div>

          <div className="col-12 mt-6">
            <DataTable
              noDataComponent={"Không có dữ liệu."}
              sortServer
              progressPending={isLoading}
              columns={columns}
              data={chungChiList}
              customStyles={customStyles}
              pagination
              highlightOnHover
              pointerOnHover
              paginationServer
              paginationTotalRows={totalRows}
              onChangeRowsPerPage={handlePerRowsChange}
              onChangePage={handlePageChange}
              paginationComponentOptions={paginationOptions}
              conditionalRowStyles={conditionalRowStyles}
              progressComponent={
                <div
                  style={{
                    padding: "24px",
                  }}
                >
                  <Oval
                    arialLabel="loading-indicator"
                    color="#007bff"
                    height={40}
                  />
                </div>
              }
            />

          </div>
        </div>
      </div>
    </LocalizationProvider>
  )
}
