import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    loading: false,
    listTinhThanh: [],
};

export const tinhThanhSlice = createSlice({
    name: "tinhThanh",
     initialState,
     reducers: {
        getTinhThanh(state, action) {
            state.loading = true;
        },
        getTinhThanhSuccess(state, action) {
            state.loading = false;
            state.listTinhThanh = action.payload;
        },
        getTinhThanhFailed(state) {
            state.loading = false;
        }
     }
})

export const tinhThanhActions = tinhThanhSlice.actions

const tinhThanhReducer = tinhThanhSlice.reducer;
export default tinhThanhReducer;

export const selectLoading = (state) => state.tinhThanh.loading;
export const selectListTinhThanh = (state) => state.tinhThanh.listTinhThanh;
