import axios from "axios";
import { useEffect, useState } from "react";
import { Button, Form } from "react-bootstrap";
import LoadingOverlay from "react-loading-overlay";
import { useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import { PageTitle } from "../../../../_metronic/layout/core";
import api from "../../../configs/api";
import { selectRoleUser } from "../../../redux-module/auth/authSlice";
import { LichSuThucHanh } from "../duoc-si/lich-su-thuc-hanh/LichSuThucHanh";
import ThongTinCaNhan from "./components/thong-tin-ca-nhan/ThongTinCaNhan";

LoadingOverlay.propTypes = undefined;
export function ChiTietHoSoThuLyYeuCauBoSung(props) {
  const navigate = useNavigate();
  const roleUser = useSelector(selectRoleUser)[0].ten_en;
  const { id } = useParams();

  useEffect(() => {
    console.log("roleUser", roleUser);
    if (id) {
      getThongTinHoSo(id);
    }
  }, []);

  const [isLoading, setIsLoading] = useState(false);
  const [thongTinHoSo, setThongTinHoSo] = useState({});
  const [TypeHoSo, setTypeHoSo] = useState("");
  const [DeNghi, setDeNghi] = useState("");

  const getThongTinHoSo = async (id) => {
    await setIsLoading(true);
    axios
      .get(api.API_DETAIL_HO_SO_THU_LY_DUOC_SI + "/" + id)
      .then(async ({ data }) => {
        const { result } = data;
        if (result) {
          setTypeHoSo(result?.trang_thai_ho_so);
          setDeNghi(result?.de_nghi);
          setThongTinHoSo(result);
          setForm({
            noi_dung_tham_xet: result?.yeu_cau_cchnd?.noi_dung_tham_xet,
            noi_dung_yeu_cau_bo_sung:
              roleUser === "lanhdao"
                ? result?.yeu_cau_cchnd?.noi_dung_yeu_cau_bo_sung_lanh_dao
                : roleUser === "chuyenvien"
                ? result?.yeu_cau_cchnd?.noi_dung_yeu_cau_bo_sung_chuyen_vien
                : "",
          });
          // setField(
          //   "noi_dung_tham_xet",
          //   result?.yeu_cau_cchnd?.noi_dung_tham_xet
          // );
          // setField(
          //   "noi_dung_yeu_cau_bo_sung",
          //   roleUser === "lanhdao"
          //     ? result?.yeu_cau_cchnd?.noi_dung_yeu_cau_bo_sung_lanh_dao
          //     : roleUser === "chuyenvien"
          //     ? result?.yeu_cau_cchnd?.noi_dung_yeu_cau_bo_sung_chuyen_vien
          //     : ""
          // );
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        navigate("/error/404");
      })
      .finally(() => {
        setTimeout(() => {
          setIsLoading(false);
        }, 1000);
      });
  };

  //validate thêm vị trí hành nghề
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});

  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };

  const findFormErrors = () => {
    const { noi_dung_tham_xet, noi_dung_yeu_cau_bo_sung } = form;
    console.log("form", form);
    const newErrors = {};
    // name errors
    if (!noi_dung_tham_xet || noi_dung_tham_xet === "")
      newErrors.noi_dung_tham_xet = "Nội dung thẩm xét không được bỏ trống!";
    if (!noi_dung_yeu_cau_bo_sung || noi_dung_yeu_cau_bo_sung === "")
      newErrors.noi_dung_yeu_cau_bo_sung =
        "Nội dung yêu cầu bổ sung không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      // console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
      for (const property in newErrors) {
        toast.error(newErrors[property], {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
        return;
      }
    } else {
      // No errors! Put any logic here for the form submission!
      updateHoSo();
    }
  };

  const updateHoSo = async () => {
    await setIsLoading(true);
    const { noi_dung_tham_xet, noi_dung_yeu_cau_bo_sung } = form;
    const jsonUpdate = {
      trang_thai_ho_so:
        roleUser === "lanhdao" ? "8" : roleUser === "chuyenvien" && "5",
      noi_dung_tham_xet,
      noi_dung_yeu_cau_bo_sung,
    };
    axios
      .put(
        api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + `/update_inform/${id}`,
        jsonUpdate
      )
      .then(async ({ data }) => {
        toast.success("Cập nhật thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        setTimeout(() => {
          navigate(-1);
        }, 1000);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const usersBreadcrumbs = [
    {
      title: "Trang chủ",
      path: "/",
      isSeparator: false,
      isActive: false,
    },
    {
      title: "",
      path: "",
      isSeparator: true,
      isActive: false,
    },
  ];

  return (
    <div>
      <PageTitle breadcrumbs={usersBreadcrumbs}>Chi tiết hồ sơ</PageTitle>
      <LoadingOverlay spinner active={isLoading} text="Đang tải dữ liệu ...">
        {/* Thủ tục */}
        <div className="card py-6 px-6 mx-3 my-3">
          <label className="fw-bold fs-5">THỦ TỤC</label>
          <div className="text py-2">
            {thongTinHoSo?.thu_tuc ? thongTinHoSo?.thu_tuc?.ten : ""}
          </div>
        </div>

        {/* THÔNG TIN CÁ NHÂN */}
        <ThongTinCaNhan thongTinHoSo={thongTinHoSo} />

        {/* Ý KIÊN */}
        <div className="card-box py-6 px-6 mx-3 my-3">
          <div className="fw-bold fs-5 mb-2">Ý KIẾN</div>

          <div className="col-12 d-flex px-0">
            <div className="col-6">
              <label>1. Nội dung thẩm xét, lý do</label>
              <Form.Control
                as="textarea"
                rows={3}
                style={{ fontSize: 14, fontWeight: 500 }}
                value={
                  form.noi_dung_tham_xet === null ? "" : form.noi_dung_tham_xet
                }
                onChange={(e) => {
                  setField("noi_dung_tham_xet", e.target.value);
                }}
                isInvalid={!!errors.noi_dung_tham_xet}
              />
            </div>
            <div className="col-6 px-1">
              <label>2. Nội dung yêu cầu bổ sung</label>
              <Form.Control
                as="textarea"
                rows={3}
                style={{ fontSize: 14, fontWeight: 500 }}
                value={
                  form.noi_dung_yeu_cau_bo_sung === null
                    ? ""
                    : form.noi_dung_yeu_cau_bo_sung
                }
                onChange={(e) => {
                  setField("noi_dung_yeu_cau_bo_sung", e.target.value);
                }}
                isInvalid={!!errors.noi_dung_yeu_cau_bo_sung}
              />
            </div>
          </div>
        </div>

        <div className="row w-100">
          <div className="col-12 mt-4 mb-5 text-center">
            <div
              onClick={() => {
                navigate(-1);
              }}
              className="btn bg-primary2 text-white px-4 py-2 mx-2 font-weight-bold"
            >
              QUAY LẠI
            </div>

            <div
              onClick={(e) => {
                handleSubmit(e);
              }}
              className="btn btn-success text-white px-4 py-2 mx-2 font-weight-bold "
            >
              LƯU
            </div>
            <div
              onClick={() => {}}
              className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
            >
              IN PHIẾU THẨM XÉT
            </div>
            <div
              onClick={() => {}}
              className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold "
            >
              IN VĂN BẢN YÊU CẦU
            </div>
          </div>
        </div>
      </LoadingOverlay>
    </div>
  );
}
