import axios from "axios";
import { useEffect, useState } from "react";
import LoadingOverlay from "react-loading-overlay";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../../configs/api";

export function ThemHoSo() {
  useEffect(() => {
    getList();
  }, []);

  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };

  const [selectedThuTuc, setSelectedThuTuc] = useState(null);
  const [listThuTuc, setListThuTuc] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [IsAdd, setIsAdd] = useState(false);
  const navigate = useNavigate();

  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );

  // const getThongTinHoSo = async (id) => {
  //   await setIsLoading(true);
  //   axios
  //     .get(api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE + "/" + id)
  //     .then(async ({ data }) => {
  //       if (data) {
  //         console.log("data getThongTinHoSo", data);
  //       }
  //     })
  //     .catch((error) => {
  //       toast.error("Lấy thông tin chứng chỉ không thành công", {
  //         position: "top-right",
  //         autoClose: 2000,
  //         hideProgressBar: false,
  //         closeOnClick: true,
  //         pauseOnHover: true,
  //         draggable: true,
  //         progress: undefined,
  //         toastId: "error",
  //       });
  //     })
  //     .finally(() => {
  //       setTimeout(() => {
  //         setIsLoading(false);
  //       }, 1000);
  //     });
  // };

  const handleSubmit = async () => {
    await setIsAdd(true);
    if (!selectedThuTuc) {
      toast.error("Vui lòng chọn thủ tục", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        toastId: "error",
      });
    } else {
      await setIsLoading(true);
      axios
        .post(api.API_YEU_CAU_CHUNG_CHI_HANH_NGHE, {
          thu_tuc_id: selectedThuTuc?.id,
        })
        .then(async ({ data }) => {
          let { id } = data?.results;
          if (id) {
            console.log("id", id);
            // getThongTinHoSo(id);
            navigate(`/admin/nop-ho-so/dieuchinh/${id}`);
          }
        })
        .catch((error) => {
          toast.error(error?.response?.data?.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "error",
          });
        })
        .finally(() => {
          setTimeout(() => {
            setIsLoading(false);
          }, 1000);
        });
    }
  };

  const getList = () => {
    axios
      .post(api.API_THU_TUC_BY_USER, {})
      .then(async ({ data }) => {
        let resultsThuTuc = data.results;
        resultsThuTuc.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        setListThuTuc(resultsThuTuc);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };

  return (
    <div>
      <LoadingOverlay spinner active={isLoading} text="Loading your content...">
        <div className="card py-6 px-6 mx-3 my-3">
          <div className="text-title">
            <div className="fw-bold fs-5 mb-1">Chọn thủ tục</div>
            {IsAdd && !selectedThuTuc ? (
              <span style={{ color: "red" }}>(Chưa chọn)</span>
            ) : (
              ""
            )}{" "}
          </div>
          <div className="row pt-2">
            <div className="col-12 row">
              <div className="col-2 text-fix">
                Chọn thủ tục<span className="text-danger">*</span>
              </div>
              <div className="col-5 text-user-info">
                <Select
                  key={"id"}
                  options={listThuTuc}
                  value={selectedThuTuc}
                  placeholder="Chọn thủ tục"
                  formatGroupLabel={formatGroupLabel}
                  onChange={async (data) => {
                    setSelectedThuTuc(data);
                  }}
                />
              </div>
            </div>
          </div>
        </div>
        <div className="card py-6 px-6 mx-3 my-3">
          <div className="text-title">
            <div className="fw-bold fs-5 mb-1">Chi tiết thủ tục</div>
          </div>
          <div className="row pt-2">
            <div className="col-12">
              <div className="row pt-2">
                <div className="col-2 text-fix">Lĩnh vực:</div>
                <div className="col-10 text-user-info">
                  {selectedThuTuc?.linh_vuc}
                </div>
              </div>
              <div className="row pt-2">
                <div className="col-2 text-fix">Mức độ:</div>
                <div className="col-10 text-user-info">
                  {selectedThuTuc?.muc_do}
                </div>
              </div>
              <div className="row pt-2">
                <div className="col-2 text-fix">Đối tượng thực hiện:</div>
                <div className="col-10 text-user-info">
                  {selectedThuTuc?.doi_tuong}
                </div>
              </div>
              <div className="row pt-2">
                <div className="col-2 text-fix">Cơ quan thực hiện:</div>
                <div className="col-10 text-user-info">
                  {selectedThuTuc?.co_quan_thuc_hien}
                </div>
              </div>
              <div className="row pt-2">
                <div className="col-2 text-fix">Thời hạn giải quyết:</div>
                <div className="col-10 text-user-info">
                  {selectedThuTuc?.thoi_gian_giai_quyet}
                </div>
              </div>
