import axios from "axios";
import { useEffect, useState } from "react";
import BlockUi from "react-block-ui";
import { Form } from "react-bootstrap";
import DataTable from "react-data-table-component";
import { Oval } from "react-loader-spinner";
// import { useDispatch } from "react-redux";
import clsx from "clsx";
import { toast } from "react-toastify";
import { KTCard, KTSVG, useDebounce } from "../../../../../_metronic/helpers";
import Status from "../../../../components/Status";
import api from "../../../../configs/api";
import { PopupDelete } from "../../popup/PopupDelete";

export function ViTriHanhNghe() {
  const [listViTriHanhNghe, setListViTriHanhNghe] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalRows, setTotalRows] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const [page, setPage] = useState(1);
  const [showUpdate, setShowUpdate] = useState(false);
  const [trang_thai, setTrang_thai] = useState(true);

  //open modal delete
  const [isvisibleDelete, setIsvisibleDelete] = useState(false);
  const [idDelete, setIdDelete] = useState("");
  const closeModalDelete = () => setIsvisibleDelete(false);
  const showModalDelete = () => setIsvisibleDelete(true);
  const [searchTerm, setSearchTerm] = useState("");

  const debouncedSearchTerm = useDebounce(searchTerm, 1000);

  useEffect(
    () => {
      if (debouncedSearchTerm !== undefined && searchTerm !== undefined) {
        getList(1, perPage, debouncedSearchTerm);
      }
    },
    [debouncedSearchTerm] // Only call effect if debounced search term changes
  );

  const getList = ({
    page_number = page,
    size = perPage,
    search_key = searchTerm,
  }) => {
    setIsLoading(true);
    axios
      .post(
        api.API_LIST_VI_TRI_HANH_NGHE + `?page=${page_number}&per_page=${size}`,
        { search_ten: search_key }
      )
      .then(async ({ data }) => {
        setListViTriHanhNghe(data?.results);
        setTotalRows(data?.total);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  //validate thêm vị trí hành nghề
  const [form, setForm] = useState({});
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const findFormErrors = () => {
    const { ten } = form;
    const newErrors = {};
    // name errors
    if (!ten || ten === "") newErrors.ten = "Tên không được bỏ trống!";
    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // get our new errors
    const newErrors = findFormErrors();
    // Conditional logic:
    if (Object.keys(newErrors).length > 0) {
      console.log(newErrors);
      // We got errors!
      setErrors(newErrors);
    } else {
      // No errors! Put any logic here for the form submission!
      showUpdate ? updateViTriHanhNghe() : addViTriHanhNghe();
    }
  };

  // handle api
  const addViTriHanhNghe = async () => {
    setIsLoading(true);
    const { ten, loai, rut_gon } = form;
    const json = {
      ten,
      loai,
      rut_gon,
      trang_thai: trang_thai,
    };
    await axios
      .post(api.API_VI_TRI_HANH_NGHE + "_add", json)
      .then(async ({ data }) => {
        resetState();
        toast.success("Tạo vị trí hành nghề thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({ page_number: 1 });
        setIsLoading(false);
      })
      .catch((error) => {
        console.log("error", error?.response);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const updateViTriHanhNghe = async () => {
    setIsLoading(true);
    const { ten, loai, rut_gon, id } = form;
    const json = {
      ten,
      loai,
      rut_gon,
      trang_thai: trang_thai,
    };
    await axios
      .put(api.API_VI_TRI_HANH_NGHE + "/" + id, json)
      .then(async ({ data }) => {
        resetState();
        toast.success("Cập nhật vị trí hành nghề thành công", {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        getList({});
      })
      .catch((error) => {
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const resetState = () => {
    setForm({
      ten: "",
      rut_gon: "",
      loai: "",
    });
    setErrors({});
    setShowUpdate(false);
    const updatedData = listViTriHanhNghe?.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setData(updatedData);
  };

  // DETAIL
  const detail = async (data) => {
    setForm({
      ...data,
    });
    setTrang_thai(data?.trang_thai);
    setShowUpdate(true);
  };

  const handlePerRowsChange = async (newPerPage, page) => {
    await setIsLoading(true);
    axios
      .post(
        api.API_LIST_NOI_TOT_NGHIEP + `?page=${page}&per_page=${newPerPage}`,
        {}
      )
      .then(({ data }) => {
        if (data?.results) {
          setListViTriHanhNghe(data?.results);
          setPerPage(newPerPage);
        }
      })
      .catch(() => {})
      .finally(() => {
        setIsLoading(false);
      });
  };

  const deleteNTN = () => {
    axios
      .delete(api.API_VI_TRI_HANH_NGHE + "/" + idDelete)
      .then(async ({ data }) => {
        await toast.success(data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "success",
        });
        closeModalDelete();
        resetState();
        if (page == 1) {
          getList({ page_number: 1 });
        } else if (listViTriHanhNghe?.length == 1) {
          getList({ page_number: page - 1 });
        } else {
          getList({ page_number: page });
        }
      })
      .catch((error) => {
        console.log("error", error);
        toast.error(error?.data?.errors, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {
        setIsLoading(false);
      });
  };
  const handlePageChange = (page) => {
    setPage(page);
    getList({ page_number: page });
  };

  //đổi màu khi selected
  const [data, setData] = useState(listViTriHanhNghe);
  useEffect(() => {
    setData(listViTriHanhNghe);
  }, [listViTriHanhNghe]);

  const handleRowClicked = (row) => {
    setErrors({});
    const updatedData = listViTriHanhNghe?.map((item) => {
      if (row.id !== item.id) {
        return item;
      }
      return {
        ...item,
        toggleSelected: !item.toggleSelected,
      };
    });
    setData(updatedData);
  };

  const paginationOptions = {
    rowsPerPageText: "Dòng hiển thị",
    rangeSeparatorText: "trên",
  };
  const customStyles = {
    headRow: {
      style: {
        borderTopStyle: "solid",
        borderTopWidth: "1px",
        borderTopColor: "#e0e0e0",
        borderBottomColor: "#e0e0e0",
      },
    },
    headCells: {
      style: {
        color: "#202124",
        fontSize: "16px",
      },
    },
    rows: {
      highlightOnHoverStyle: {
        backgroundColor: "rgb(230, 244, 244)",
        borderBottomColor: "#FFFFFF",
        outline: "1px solid #FFFFFF",
      },
    },
    pagination: {
      style: {
        border: "none",
      },
    },
  };
  const columns = [
    {
      name: "Mã",
      grow: 3,
      selector: (row) => (
        <div
          onClick={() => {
            detail(row);
            handleRowClicked(row);
          }}
        >
          <span>{row.ma_hanh_nghe}</span>
        </div>
      ),
      style: {
        color: "#1251ff",
        fontSize: "14px",
        fontWeight: 500,
        width: 300,
        cursor: "pointer",
      },
    },
    {
      name: "Tên",
      selector: (row) => <span>{row.ten}</span>,
      grow: 8,
      style: {
        cursor: "pointer",
        color: "#202124",
      },
    },
    {
      name: "Trang thái",
      selector: (row) => <span>{row.trang_thai}</span>,
      grow: 3,
      cell: (row) => (
        <td>
          <Status status={row.trang_thai} />
        </td>
      ),
    },
    {
      name: "Thao tác",
      grow: 2,
      cell: (row) => (
        <div className="container my-3">
          <button
            onClick={async () => {
              await setIdDelete(row?.id);
              showModalDelete();
            }}
            className="btn btn-icon btn-bg-light btn-color-danger btn-sm"
          >
            <KTSVG
              path="/media/icons/duotune/general/gen027.svg"
              className="svg-icon-3"
            />
          </button>
        </div>
      ),
    },
  ];
  const conditionalRowStyles = [
    {
      when: (row) => row.toggleSelected,
      style: {
        backgroundColor: "#f5f5f5",
        userSelect: "none",
      },
    },
  ];
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  return (
    <KTCard>
      <div className="page">
        <div className="container-fluid">
          <div className="fv-row mb-7 mt-6">
            {/* begin::Label */}
            <label className="required fw-bold fs-6 mb-2">
              Tên vị trí hành nghề
            </label>
            {/* end::Label */}

            {/* begin::Input */}
            <div className="col-md-6">
              <input
                placeholder="Nhập tên tìm kiếm..."
                type="text"
                name="name"
                className={clsx("form-control form-control-solid mb-3 mb-lg-0")}
                autoComplete="off"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* end::Input */}
          </div>
          <BlockUi tag="div" blocking={isLoading} centerY={true} centerX={true}>
            <div className="row">
              <div className="col-md-8">
                <div className="" style={cardBox}>
                  <DataTable
                    noDataComponent={
                      <img
                        style={{ width: "30%", height: "auto" }}
                        src="/media/nodata-found.svg"
                      />
                    }
                    sortServer
                    progressPending={isLoading}
                    columns={columns}
                    data={data}
                    customStyles={customStyles}
                    pagination
                    highlightOnHover
                    pointerOnHover
                    paginationServer
                    paginationTotalRows={totalRows}
                    onChangeRowsPerPage={handlePerRowsChange}
                    onChangePage={handlePageChange}
                    paginationComponentOptions={paginationOptions}
                    conditionalRowStyles={conditionalRowStyles}
                    // onRowClicked={(data) => {
                    //   detail(data);
                    //   handleRowClicked(data);
                    // }}
                    progressComponent={
                      <div
                        style={{
                          padding: "24px",
                        }}
                      >
                        <Oval
                          arialLabel="loading-indicator"
                          color="#007bff"
                          height={40}
                        />
                      </div>
                    }
                  />
                </div>
              </div>
              <div className="col-md-4">
                <div className="" style={cardBox}>
                  <div className="">
                    <p className="font-weight-bold">
                      {showUpdate
                        ? "Cập nhật vị trí hành nghề"
                        : "Tạo mới vị trí hành nghề"}
                    </p>
                  </div>

                  {showUpdate && (
                    <div className="row mt-3">
                      <div className="col-4">
                        <p className="font-weight-bold">
                          Mã<span className="text-danger">*</span>
                        </p>
                      </div>
                      <div className="col-8">
                        <Form.Control
                          disabled={true}
                          type="text"
                          placeholder="Nhập tên mã vị trí hành nghề"
                          style={{ fontSize: 14, fontWeight: 500 }}
                          value={
                            form.ma_hanh_nghe === null ? "" : form.ma_hanh_nghe
                          }
                        />
                      </div>
                    </div>
                  )}

                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">
                        Tên<span className="text-danger">*</span>
                      </p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Nhập tên mã vị trí hành nghề"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.ten === null ? "" : form.ten}
                        onChange={(e) => {
                          setField("ten", e.target.value);
                        }}
                        isInvalid={!!errors.ten}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.ten}
                      </Form.Control.Feedback>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">Rút gọn</p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Nhập rút gọn"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.rut_gon === null ? "" : form.rut_gon}
                        onChange={(e) => {
                          setField("rut_gon", e.target.value);
                        }}
                        isInvalid={!!errors.rut_gon}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.rut_gon}
                      </Form.Control.Feedback>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      <p className="font-weight-bold">Loại</p>
                    </div>
                    <div className="col-8">
                      <Form.Control
                        placeholder="Nhập loại"
                        style={{ fontSize: 14, fontWeight: 500 }}
                        value={form.loai === null ? "" : form.loai}
                        onChange={(e) => {
                          setField("loai", e.target.value);
                        }}
                        isInvalid={!!errors.loai}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.loai}
                      </Form.Control.Feedback>
                    </div>
                  </div>

                  <div className="row mt-3">
                    <div className="col-4">
                      <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                    </div>
                    <div className="col-8">
                      <div className="form-check form-check-solid form-switch fv-row">
                        <input
                          className="form-check-input w-45px h-30px"
                          type="checkbox"
                          id="trang_thai"
                          checked={trang_thai}
                          onChange={(e) => {
                            setTrang_thai(!trang_thai);
                          }}
                        />
                        <label className="form-check-label"></label>
                      </div>
                    </div>
                  </div>
                  <div className="row mt-3">
                    <div className="col-4">
                      {showUpdate ? (
                        <button
                          className="btn text-white bg-blue2"
                          style={{ fontSize: ".9rem" }}
                          onClick={() => resetState()}
                        >
                          <i className="fas fa-sync-alt text-white"></i> Làm mới
                        </button>
                      ) : null}
                    </div>

                    <div className="col-8">
                      <button
                        className="btn text-white bg-blue2 mr-3"
                        style={{ fontSize: ".9rem" }}
                        onClick={(e) => {
                          handleSubmit(e);
                        }}
                      >
                        {showUpdate ? (
                          <i className="fa fa-pencil-alt text-white"></i>
                        ) : (
                          <i className="fas fa-save text-white"></i>
                        )}{" "}
                        {showUpdate ? "Cập nhật" : "Tạo vị trí hành nghề"}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <PopupDelete
              title="Bạn có đồng ý xóa vị trí hành nghề này không?"
              show={isvisibleDelete}
              onHide={closeModalDelete}
              onDetele={() => {
                deleteNTN();
              }}
            />
          </BlockUi>
        </div>
      </div>
    </KTCard>
  );
}
