import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import TextField from "@mui/material/TextField";
import { vi } from "date-fns/locale";
import { useState, useEffect } from "react";
import ImageUploading from "react-images-uploading";
import { useNavigate } from "react-router-dom";
import Select from "react-select";
import { selectCurrentUser } from "../../../redux-module/auth/authSlice";
import { useSelector } from "react-redux";
import { Form } from "react-bootstrap";
import { toast } from "react-toastify";
import api from "../../../configs/api";
import axios from "axios";
import { PageTitle } from "../../../../_metronic/layout/core";
import LoadingOverlay from "react-loading-overlay";
// import Switch from "react-switch";

export default function ThongTinToChuc() {

    let navigate = useNavigate();
    const thongTinToChuc = useSelector(selectCurrentUser);

    const [form, setForm] = useState({});
    const [errors, setErrors] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [disabledChuQuan, setdisabledChuQuan] = useState(true);

    const [listTinhThanhCS, setListTinhThanhCS] = useState([]);
    const [listQuanHuyenCS, setListQuanHuyenCS] = useState([]);
    const [listXaPhuongCS, setListXaPhuongCS] = useState([]);
    const [listTinhThanhTT, setListTinhThanhTT] = useState([]);
    const [listQuanHuyenTT, setListQuanHuyenTT] = useState([]);
    const [listXaPhuongTT, setListXaPhuongTT] = useState([]);

    const [selectedTTCS, setSelectedTTCS] = useState(null);
    const [selectedQHCS, setSelectedQHCS] = useState(null);
    const [selectedXPCS, setSelectedXPCS] = useState(null);

    const [selectedTTTT, setSelectedTTTT] = useState(null);
    const [selectedQHTT, setSelectedQHTT] = useState(null);
    const [selectedXPTT, setSelectedXPTT] = useState(null);

    // loai hinh kinh doanh
    const [selectedLoaiHinhKinhDoanh, setSelectedLoaiHinhKinhDoanh] = useState([]);
    const [listLoaiHinhKinhDoanh, setListLoaiHinhKinhDoanh] = useState([]);

    useEffect(() => {
        setForm(thongTinToChuc)
        getLoaiHinhKinhDoanh();
        getTinhThanh();
        console.log("thong tin to chuc", thongTinToChuc);
    }, []);

    const cardBox = {
        backgroundColor: "#fff",
        backgroundClip: "border-box",
        border: "1px solid #e7eaed",
        padding: "1.5rem",
        marginBottom: "24px",
        borderRadius: "0.5rem",
    };
    const usersBreadcrumbs = [
        {
            title: "Trang chá»§",
            path: "/",
            isSeparator: false,
            isActive: false,
        },
        {
            title: "",
            path: "",
            isSeparator: true,
            isActive: false,
        },
    ];
    const groupStyles = {
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
    };
    const formatGroupLabel = (listTemp) => (
        <div style={groupStyles}>
            <span>{listTemp.ten}</span>
        </div>
    );

    const setField = (field, value) => {
        setForm({
            ...form,
            [field]: value,
        });
        if (!!errors[field])
            setErrors({
                ...errors,
                [field]: null,
            });
    };

    const getTinhThanh = () => {
        axios
            .get(api.API_TINH_THANH)
            .then(({ data }) => {
                let resultsTinhThanh = data?.results;
                resultsTinhThanh.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });

                if (thongTinToChuc.tinh_thanh_id) {
                    const ttcs = resultsTinhThanh.find(
                        (e) => e.id === thongTinToChuc.tinh_thanh_id
                    );
                    setSelectedTTCS(ttcs);
                    getQuanHuyen(ttcs.id, "coso");
                }
                if (thongTinToChuc.tinh_thanh_hien_nay_id) {
                    const tttt = resultsTinhThanh.find(
                        (e) => e.id === thongTinToChuc.tinh_thanh_hien_nay_id
                    );
                    setSelectedTTTT(tttt);
                    getQuanHuyen(tttt.id, "tructhuoc");
                }

                setListTinhThanhCS(resultsTinhThanh);
                setListTinhThanhTT(resultsTinhThanh);

            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getQuanHuyen = (id, type) => {
        axios
            .get(`${api.API_QUAN_HUYEN}/${id}?per_page=100`)
            .then(({ data }) => {
                let resultsQuanHuyen = data?.results;
                resultsQuanHuyen.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });

                if (thongTinToChuc.quan_huyen_id === id) {
                    const qhcs = resultsQuanHuyen.find(
                        (e) => e.id === thongTinToChuc.xa_phuong_id
                    );
                    setSelectedQHCS(qhcs);
                    getXaPhuong(qhcs.id, "coso");
                }
                if (thongTinToChuc.quan_huyen_hien_nay_id === id) {
                    const qhtt = resultsQuanHuyen.find(
                        (e) => e.id === thongTinToChuc.xa_phuong_hien_nay_id
                    );
                    setSelectedQHTT(qhtt);
                    getXaPhuong(qhtt.id, "tructhuoc");
                }

                if (type === "coso") {
                    setListQuanHuyenCS(resultsQuanHuyen);
                } else if (type === "tructhuoc") {
                    setListQuanHuyenTT(resultsQuanHuyen);
                }

            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getXaPhuong = (id, type) => {
        axios
            .get(`${api.API_XA_PHUONG}/${id}?per_page=100`)
            .then(({ data }) => {
                let resultsXaPhuong = data?.results;
                resultsXaPhuong.forEach((e) => {
                    e.label = e.ten;
                    e.value = e.id;
                });

                if (thongTinToChuc.quan_huyen_id === id) {
                    const xpcs = resultsXaPhuong.find(
                        (e) => e.id === thongTinToChuc.xa_phuong_id
                    );
                    setSelectedXPCS(xpcs);
                }
                if (thongTinToChuc.quan_huyen_hien_nay_id === id) {
                    const xptt = resultsXaPhuong.find(
                        (e) => e.id === thongTinToChuc.xa_phuong_hien_nay_id
                    );
                    setSelectedXPTT(xptt);
                }

                if (type === "coso") {
                    setListXaPhuongCS(resultsXaPhuong);
                } else if (type === "tructhuoc") {
                    setListXaPhuongTT(resultsXaPhuong);
                }
            })
            .catch((error) => {
                if (error.response.data) {
                    toast.error(error.response.data.msg, {
                        position: "top-right",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        toastId: "success",
                    });
                }
            });
    };
    const getLoaiHinhKinhDoanh = () => {
        axios
            .post(api.API_LIST_LOAI_HINH_KINH_DOANH, {
                search_ten: "",
            })
            .then(async ({ data }) => {
                if (data) {
                    let resultsLoaiHinhKinhDoanh = data?.results;
