      <PopupThongTinHoSoDuocSi
        show={chitiet}
        hide={() => setChiTiet(false)}
        data={rowData}
      />
      <In
        show={isvisibleIn}
        onHide={closeModalIn}
        path={file_url}
        onIn={() => {
          inHoSo();
        }}
      />
      <Modal show={modalChucVu} onHide={closeModalChucVu}>
        <Modal.Header className="font-weight-bold">
          Nhập chức vụ và tên
        </Modal.Header>
        <Modal.Body>
          <div>
            <label className="fw-bold fs-6 mb-2">Tên chức vụ</label>
            <Select
              key={"id"}
              className="mt-2"
              options={TenChucVu}
              value={valueTenChucVu}
              style={{ height: "44px" }}
              placeholder={"Tên chức vụ"}
              onChange={(e) => {
                setValueTenChucVu(e);
                // setField("gioi_tinh", e.value);
              }}
            />
          </div>
          <div>
            <label className="fw-bold fs-6 mb-2 mt-5">Tên người ký:</label>
            <Form.Control
              type="text"
              placeholder="Tên người ký"
              style={{ fontSize: 14, fontWeight: 500 }}
            />
          </div>
        </Modal.Body>
        <Modal.Footer>
          <div className="row">
            <div className="col-12">
              <div
                onClick={() => {
                  closeModalChucVu();
                }}
                className="btn px-4 py-2 mx-2 font-weight-bold btn-trolai"
              >
                ĐÓNG
              </div>
              <div
                // onClick={(e) => {
                //   duyetLienKet(e);
                // }}
                className="btn btn-primary text-white px-4 py-2 mx-2 font-weight-bold ml-5"
              >
                LƯU
              </div>
            </div>
          </div>
        </Modal.Footer>
      </Modal>
    </KTCard>
  );
}
