                      type="text"
                      placeholder="Nhập nội dung ý kiến"
                      style={{
                        fontSize: 14,
                        fontWeight: 400,
                        height: "38px",
                        margin: "10px 0 10px 0",
                      }}
                      onChange={(e) => {
                        setRowDateValue(e.target.value, ykld, "noi_dung");
                      }}
                      value={ykld?.noi_dung}
                    />
                  </td>
                  <td className="px-3 py-0" style={{ verticalAlign: "middle" }}>
                    <span>
                      {moment(new Date(ykld?.thoi_gian)).format(
                        "hh:mm DD/MM/YYYY"
                      )}
                    </span>
                  </td>
                  <td
                    className="px-3 py-0"
                    style={{ verticalAlign: "middle", textAlign: "center" }}
                  >
                    <button
                      className="btn btn-danger px-0 py-0 mb-4"
                      style={{
                        width: "38px",
                        height: "38px",
                        fontSize: "12px",
                        margin: "10px 0 10px 0",
                      }}
                      onClick={() => {
                        removeRow(ykld.stt);
                      }}
                    >
                      X
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : null}
      </>
    );
