import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { TextField } from "@mui/material";
import axios from "axios";
// import { vi } from "date-fns/locale";
import { enAU } from "date-fns/locale";
import { useEffect, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../configs/api";
// import { GioiTinh, HinhThuc } from "../../../data";
// import moment from "moment";

export default function PopupQuanLyNguoiDung(props) {
  const [edit, setEdit] = useState(false);
  const [showUpdate, setShowUpdate] = useState(false);
  const [form, setForm] = useState({});
  const [trang_thai, setTrang_thai] = useState(true);
  const [selectedgioitinh, setSelectedgioitinh] = useState({
    label: "Nam",
    value: 1,
    id: 1,
  });

  useEffect(() => {
    if (props?.show) {
      //   resetState();
      getLocation();
      getLocationCOHN();
      getNoiTotNghiep();
      getCATinhThanh();
      getVanBangChuyenMon();
      setForm({
        ...props?.data,
        ngay_sinh: props?.data.ngay_sinh,
        ngay_cap: props?.data.ngay_cap,
      });
      let dataSelectedCMND = listNoiCapCMND.find(
        (e, i) => e.ten === props.data?.noi_cap
      );
      console.log("log dataSelectedCMND", props.data?.noi_cap);
      console.log("log listNoiCapCMND", listNoiCapCMND);

      setSelectedNoiCapCMND(dataSelectedCMND);
      setTrang_thai(props?.data?.active);
      setShowUpdate(true);
      console.log(props?.data);
      //   setForm({ ...props?.data, ngay_cap: props?.data.ngay_cap * 1 });
    }
    console.log("propsssss", props);
  }, [props?.show]);

  // selected ngay
  const selectedNS = async (date) => {
    // await setNgaySinh(date);
    await setField("ngay_sinh", new Date(date).getTime);
  };
  const selectedNCCMND = async (date) => {
    // await setNgayCapCmnd(date);
    await setField("ngay_cap", new Date(date).getTime);
  };
  const getLocation = () => {
    let { tinh_thanh_id, xa_phuong_id, quan_huyen_id } = props.data;

    if (tinh_thanh_id) {
      getTinhThanh(tinh_thanh_id);
    }
    if (tinh_thanh_id) {
      getQuanHuyen(tinh_thanh_id, quan_huyen_id);
    }
    if (quan_huyen_id) {
      getXaPhuong(quan_huyen_id, xa_phuong_id);
    }
  };

  const getLocationCOHN = () => {
    let {
      tinh_thanh_hien_nay_id,
      xa_phuong_hien_nay_id,
      quan_huyen_hien_nay_id,
    } = props.data;
    if (tinh_thanh_hien_nay_id) {
      getTinhThanhCOHN(tinh_thanh_hien_nay_id);
    }
    if (tinh_thanh_hien_nay_id) {
      getQuanHuyenCOHN(tinh_thanh_hien_nay_id, quan_huyen_hien_nay_id);
    }
    if (quan_huyen_hien_nay_id) {
      getXaPhuongCOHN(quan_huyen_hien_nay_id, xa_phuong_hien_nay_id);
    }
  };

  // noi tốt nghiệp
  const [selectedNoiTotNghiep, setSelectedNoiTotNghiep] = useState(null);
  const [listNoiTotNghiep, setListNoiTotNghiep] = useState([]);
  // van bang chuyen mon
  const [selectedVBCN, setSelectedVBCN] = useState(null);
  const [listVanBangChuyenMon, setListVanBangChuyenMon] = useState([]);
  const optionsGioiTinh = [
    {
      label: "Nam",
      value: 1,
      id: 1,
    },
    {
      label: "Nữ",
      value: 2,
      id: 2,
    },
    {
      label: "Khác",
      value: 3,
      id: 3,
    },
  ];
  const [selectedDoiTuong, setSelectedDoiTuong] = useState({
    label: "Dược sĩ",
    value: 1,
    id: 1,
  });
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const optionsDoiTuong = [
    {
      label: "Dược sĩ",
      value: 1,
      id: 1,
    },
    {
      label: "Tổ chức",
      value: 2,
      id: 2,
    },
  ];
  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );
  // location
  const [ListTinhThanh, setListTinhThanh] = useState([]);
  const [ListQuanHuyen, setListQuanHuyen] = useState([]);
  const [ListXaPhuong, setListXaPhuong] = useState([]);
  const [TinhThanhSelected, setTinhThanhSelected] = useState({});
  const [QuanHuyenSelected, setQuanHuyenSelected] = useState({});
  const [XaPhuongSelected, setXaPhuongSelected] = useState({});
  const [disQuanHuyen, setDisQuanHuyen] = useState(false);
  const [disXaPhuong, setDisXaPhuong] = useState(false);

  const [quanHuyenValue, setQuanHuyenValue] = useState(null);
  const [xaPhuongValue, setXaPhuongValue] = useState(null);
  // location Chỗ ở hiện nay
  const [ListTinhThanhCOHN, setListTinhThanhCOHN] = useState([]);
  const [ListQuanHuyenCOHN, setListQuanHuyenCOHN] = useState([]);
  const [ListXaPhuongCOHN, setListXaPhuongCOHN] = useState([]);
  const [TinhThanhCOHNSelected, setTinhThanhCOHNSelected] = useState({});
  const [QuanHuyenCOHNSelected, setQuanHuyenCOHNSelected] = useState({});
  const [XaPhuongCOHNSelected, setXaPhuongCOHNSelected] = useState({});
  const [disQuanHuyenCOHN, setDisQuanHuyenCOHN] = useState(false);
  const [disXaPhuongCOHN, setDisXaPhuongCOHN] = useState(false);

  const [quanHuyenValueCOHN, setQuanHuyenValueCOHN] = useState(null);
  const [xaPhuongValueCOHN, setXaPhuongValueCOHN] = useState(null);
  // cccd
  const [selectedNoiCapCMND, setSelectedNoiCapCMND] = useState(null);
  const [listNoiCapCMND, setListNoiCapCMND] = useState([]);

  // GET LOCATIONs
  const getTinhThanh = (value) => {
    axios
      .get(api.API_TINH_THANH)
      .then(({ data }) => {
        let resultsTinhThanh = data?.results;
        resultsTinhThanh.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        let TinhThanhSelected = resultsTinhThanh.find((e) => e.id === value);
        setListTinhThanh(resultsTinhThanh);
        setTinhThanhSelected(TinhThanhSelected);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getQuanHuyen = (id_tinh, value) => {
    axios
      .get(`${api.API_QUAN_HUYEN}/${id_tinh}?per_page=100`)
      .then(({ data }) => {
        let resultsQuanHuyen = data?.results;
        resultsQuanHuyen.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        let QuanHuyenSelected = resultsQuanHuyen.find((e) => e.id === value);
        setListQuanHuyen(resultsQuanHuyen);
        setQuanHuyenSelected(QuanHuyenSelected);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getXaPhuong = (id_xa, value) => {
    axios
      .get(`${api.API_XA_PHUONG}/${id_xa}?per_page=100`)
      .then(({ data }) => {
        let resultsXaPhuong = data?.results;
        resultsXaPhuong.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        let XaPhuongSelected = resultsXaPhuong.find((e) => e.id === value);
        setListXaPhuong(resultsXaPhuong);
        setXaPhuongSelected(XaPhuongSelected);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const onChangeTinhThanh = async (values) => {
    setField("tinh_thanh_id", values.id);
    getQuanHuyen(values.id);
    setDisQuanHuyen(false);
    setDisXaPhuong(true);
    setQuanHuyenValue(null);
    setXaPhuongValue(null);
  };
  const onChangeQuanHuyen = (values) => {
    setField("quan_huyen_id", values.id);
    getXaPhuong(values.id);
    setDisXaPhuong(false);
    setQuanHuyenValue(values);
    setXaPhuongValue(null);
  };
  const onChangeXaPhuong = (values) => {
    setField("phuong_xa_id", values.id);
    setXaPhuongValue(values);
  };
  // GET LOCATIONs COHN
  const getTinhThanhCOHN = (value) => {
    console.log("value", value);
    axios
      .get(api.API_TINH_THANH)
      .then(({ data }) => {
        let resultsTinhThanhCOHN = data?.results;
        resultsTinhThanhCOHN.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        let TinhThanhCOHNSelected = resultsTinhThanhCOHN.find(
          (e) => e.id === value
        );
        console.log("TinhThanhCOHNSelected", TinhThanhCOHNSelected);
        setListTinhThanhCOHN(resultsTinhThanhCOHN);
        setTinhThanhCOHNSelected(TinhThanhCOHNSelected);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getQuanHuyenCOHN = (id_tinh, value) => {
    axios
      .get(`${api.API_QUAN_HUYEN}/${id_tinh}?per_page=100`)
      .then(({ data }) => {
        let resultsQuanHuyenCOHN = data?.results;
        resultsQuanHuyenCOHN.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        let QuanHuyenCOHNSelected = resultsQuanHuyenCOHN.find(
          (e) => e.id === value
        );
        setListQuanHuyenCOHN(resultsQuanHuyenCOHN);
        setQuanHuyenCOHNSelected(QuanHuyenCOHNSelected);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const getXaPhuongCOHN = (id_xa, value) => {
    axios
      .get(`${api.API_XA_PHUONG}/${id_xa}?per_page=100`)
      .then(({ data }) => {
        let resultsXaPhuongCOHN = data?.results;
        resultsXaPhuongCOHN.forEach((e) => {
          e.label = e.ten;
          e.value = e.id;
        });
        let XaPhuongCOHNSelected = resultsXaPhuongCOHN.find(
          (e) => e.id === value
        );
        setListXaPhuongCOHN(resultsXaPhuongCOHN);
        setXaPhuongCOHNSelected(XaPhuongCOHNSelected);
      })
      .catch((error) => {
        if (error.response.data) {
          toast.error(error.response.data.msg, {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            toastId: "success",
          });
        }
      });
  };
  const onChangeTinhThanhCOHN = async (values) => {
    setField("tinh_thanh_hien_nay_id", values.id);
    getQuanHuyenCOHN(values.id);
    setDisQuanHuyenCOHN(false);
    setDisXaPhuongCOHN(true);
    setQuanHuyenValueCOHN(null);
    setXaPhuongValueCOHN(null);
  };
  const onChangeQuanHuyenCOHN = (values) => {
    setField("quan_huyen_hien_nay_id", values.id);
    getXaPhuongCOHN(values.id);
    setDisXaPhuongCOHN(false);
    setQuanHuyenValueCOHN(values);
    setXaPhuongValueCOHN(null);
  };
  const onChangeXaPhuongCOHN = (values) => {
    setField("xa_phuong_hien_nay_id", values.id);
    setXaPhuongValueCOHN(values);
  };
  const getNoiTotNghiep = () => {
    axios
      .post(api.API_LIST_NOI_TOT_NGHIEP_GET_LIST, {})
      .then(async ({ data }) => {
        let resultsNoiTotnghiep = data.results;
        resultsNoiTotnghiep.forEach((data) => {
          data.label = data.ten;
          data.value = data.id;
        });
        setListNoiTotNghiep(data.results);
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };
  // cccd/cmnd
  const [datareset, setDatareset] = useState(props?.data);
  useEffect(() => {
    setDatareset(props?.data);
  }, [props?.data]);
  const resetState = () => {
    setForm({});
    setErrors({});
    setShowUpdate(false);
    const updatedData = props.data.map((item) => {
      return {
        ...item,
        toggleSelected: false,
      };
    });
    setDatareset(updatedData);
  };

  const getCATinhThanh = () => {
    axios
      .get(api.API_CA_TINH_THANH)
      .then(async ({ data }) => {
        if (data) {
          let resultsCATinhThanh = data?.results;
          resultsCATinhThanh.forEach((e) => {
            e.label = e.ten;
            e.value = e.id;
          });
          setListNoiCapCMND(resultsCATinhThanh);
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };
  const getVanBangChuyenMon = () => {
    axios
      .post(api.API_LIST_VAN_BANG_CHUYEN_MON, { search_ten: "" })
      .then(async ({ data }) => {
        if (data) {
          let resultsVBCM = data?.results;
          resultsVBCM.forEach((e) => {
            e.label = e.ten;
            e.value = e.id;
          });
          setListVanBangChuyenMon(resultsVBCM);
        }
      })
      .catch((error) => {
        toast.error(error?.response?.data?.msg, {
          position: "top-right",
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          toastId: "error",
        });
      })
      .finally(() => {});
  };
  const groupStyles = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };
  const cardBox = {
    backgroundColor: "#fff",
    backgroundClip: "border-box",
    border: "1px solid #e7eaed",
    padding: "1.5rem",
    marginBottom: "24px",
    borderRadius: "0.5rem",
  };
  return (
    <Modal
      backdrop="static"
      keyboard={false}
      size="xl"
      show={props?.show}
      onHide={() => {
        props?.hide();
      }}
    >
      <LocalizationProvider dateAdapter={AdapterDateFns} locale={enAU}>
        <Modal.Header className="bg-blue2" closeButton>
          <Modal.Title className="text-white">
            {!edit ? "Thêm" : "Cập nhật"} người dùng
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <div className="row" style={cardBox}>
              <div className=" col-6 form-group">
                <div className=" form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Họ và tên
                  </label>
                  <Form.Control
                    type="text"
                    placeholder="Nhập họ và tên đệm"
                    style={{ height: 38 }}
                    value={form.ho === null ? "" : form.ho}
                    onChange={(e) => {
                      setField("ho", e.target.value);
                    }}
                    isInvalid={!!errors.ho}
                  />
                </div>
                <div className="form-group mt-2">
                  {/* <label>
                          Ngày sinh <span className="text-danger"> (*) </span>
                        </label>{" "}
                        <br /> */}
                  <label className=" required fw-bold fs-6 mb-2">
                    Ngày sinh
                  </label>
                  <DesktopDatePicker
                    inputFormat="dd/MM/yyyy"
                    value={form?.ngay_sinh ? form?.ngay_sinh : null}
                    onChange={selectedNS}
                    label=" "
                    maxDate={new Date()}
                    renderInput={(params) => (
                      <TextField size="small" {...params} />
                    )}
                  />
                </div>
                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">Email</label>
                  <Form.Control
                    placeholder="Nhập email"
                    style={{ height: 38 }}
                    value={form.email === null ? "" : form.email}
                    onChange={(e) => {
                      setField("email", e.target.value);
                    }}
                    isInvalid={!!errors.email}
                  />
                </div>
                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Giới tính
                  </label>
                  <Select
                    value={selectedgioitinh}
                    key={"id"}
                    options={optionsGioiTinh}
                    formatGroupLabel={formatGroupLabel}
                  />
                </div>
              </div>
              <div className="col-6 form-group">
                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">Tên</label>
                  <Form.Control
                    placeholder="Nhập tên"
                    style={{ height: 38 }}
                    value={form.ten === null ? "" : form.ten}
                    onChange={(e) => {
                      setField("ten", e.target.value);
                    }}
                    isInvalid={!!errors.ten}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.ten}
                  </Form.Control.Feedback>
                </div>
                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Số điện thoại
                  </label>
                  <Form.Control
                    placeholder="Nhập số điện thoại liên lạc"
                    style={{ height: 38 }}
                    value={form.dien_thoai === null ? "" : form.dien_thoai}
                    onChange={(e) => {
                      setField("dien_thoai", e.target.value);
                    }}
                    isInvalid={!!errors.dien_thoai}
                  />
                </div>

                <div className="form-group mt-2 mb-5">
                  <label className="required fw-bold fs-6 mb-2">Vai trò</label>
                  <Select
                    value={selectedDoiTuong}
                    key={"id"}
                    options={optionsDoiTuong}
                    formatGroupLabel={formatGroupLabel}
                    onChange={async (data) => {
                      setField("doi_tuong", data?.value);
                      setSelectedDoiTuong(data);
                    }}
                  />
                </div>
              </div>
            </div>
            <div className="row" style={cardBox}>
              <div>
                <label className="font-weight-bold text-uppercase">
                  Hộ khẩu thường trú{" "}
                  {/* <span className="text-danger"> (*) </span> */}
                </label>
              </div>
              <div className="col-6 form-group">
                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">
                    Tỉnh/Thành phố <span className="text-danger"> (*) </span>
                  </label>
                  <Select
                    key={"id"}
                    labelField={"ten"}
                    placeholder="Chọn tỉnh thành"
                    searchBy={"tenkhongdau"}
                    options={ListTinhThanh}
                    onChange={(values) => onChangeTinhThanh(values)}
                    closeOnSelect={true}
                    value={TinhThanhSelected}
                  />
                  {errors?.tinh_thanh !== null && !form.tinh_thanh_id ? (
                    <span className="text-danger"> {errors?.tinh_thanh}</span>
                  ) : null}
                </div>
                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">
                    Xã/Phường <span className="text-danger"> (*) </span>
                  </label>
                  <Select
                    key={"id"}
                    labelField={"ten"}
                    placeholder="Chọn xã phường"
                    options={ListXaPhuong}
                    onChange={(values) => onChangeXaPhuong(values)}
                    closeOnSelect={true}
                    isDisabled={disXaPhuong}
                    value={XaPhuongSelected}
                  />
                </div>
              </div>
              <div className="col-6 form-group">
                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">
                    Quận/Huyện <span className="text-danger"> (*) </span>
                  </label>

                  <Select
                    key={"id"}
                    className={"quan_huyen"}
                    labelField={"ten"}
                    placeholder="Chọn quận huyện"
                    options={ListQuanHuyen}
                    onChange={(values) => onChangeQuanHuyen(values)}
                    closeOnSelect={true}
                    multi={false}
                    isDisabled={disQuanHuyen}
                    value={QuanHuyenSelected}
                  />
                </div>
                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">
                    Số nhà, tên đường <span className="text-danger"> (*) </span>
                  </label>
                  <Form.Control
                    type="text"
                    placeholder="Nhập số nhà tên đường"
                    style={{ height: 38 }}
                    value={form.so_nha === null ? "" : form.so_nha}
                    onChange={(e) => {
                      setField("so_nha", e.target.value);
                    }}
                    isInvalid={!!errors.so_nha}
                  />
                </div>
              </div>
            </div>
            <div className="row" style={cardBox}>
              <div>
                <label className="font-weight-bold text-uppercase">
                  Chỗ ở hiện nay{" "}
                </label>
              </div>
              <div className="col-6 form-group">
                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">
                    Tỉnh/Thành phố <span className="text-danger"> (*) </span>
                  </label>
                  <Select
                    key={"id"}
                    labelField={"ten"}
                    placeholder="Chọn tỉnh thành"
                    searchBy={"tenkhongdau"}
                    options={ListTinhThanhCOHN}
                    onChange={(values) => onChangeTinhThanhCOHN(values)}
                    closeOnSelect={true}
                    value={TinhThanhCOHNSelected}
                  />
                  {errors?.tinh_thanh_hien_nay !== null &&
                  !form.tinh_thanh_hien_nay_id ? (
                    <span className="text-danger">
                      {" "}
                      {errors?.tinh_thanh_hien_nay}
                    </span>
                  ) : null}
                </div>
                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">
                    Xã/Phường <span className="text-danger"> (*) </span>
                  </label>
                  <Select
                    key={"id"}
                    labelField={"ten"}
                    placeholder="Chọn xã phường"
                    options={ListXaPhuongCOHN}
                    onChange={(values) => onChangeXaPhuongCOHN(values)}
                    closeOnSelect={true}
                    isDisabled={disXaPhuongCOHN}
                    value={XaPhuongCOHNSelected}
                  />
                </div>
                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    CMND/CCCD
                  </label>
                  <Form.Control
                    placeholder="Nhập cmnd/cccd"
                    style={{ height: 38 }}
                    value={form.ma_cong_dan === null ? "" : form.ma_cong_dan}
                    onChange={(e) => {
                      setField("ma_cong_dan", e.target.value);
                    }}
                    isInvalid={!!errors.ma_cong_dan}
                  />
                </div>
                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Nơi cấp CMND/CCCD
                  </label>
                  <Select
                    value={selectedNoiCapCMND}
                    key={"id"}
                    options={listNoiCapCMND}
                    placeholder="Chọn nơi cấp CMND/CCCD"
                    formatGroupLabel={formatGroupLabel}
                    onChange={(e) => {
                      setSelectedNoiCapCMND(e);
                    }}
                  />
                </div>
                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Nơi tốt nghiệp
                  </label>
                  <Select
                    value={selectedNoiTotNghiep}
                    placeholder="Chọn nơi tốt nghiệp"
                    key={"id"}
                    options={listNoiTotNghiep}
                    formatGroupLabel={formatGroupLabel}
                    onChange={async (data) => {
                      setSelectedNoiTotNghiep(data);
                    }}
                  />
                </div>

                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">Trạng thái</label>
                  <div className="form-check form-check-solid form-switch fv-row">
                    <input
                      className="form-check-input w-45px h-30px"
                      type="checkbox"
                      id="trang_thai"
                      // value={trang_thai}
                      checked={trang_thai}
                      onChange={(e) => {
                        setTrang_thai(!trang_thai);
                      }}
                    />
                    <label className="form-check-label"></label>
                  </div>
                </div>
              </div>
              <div className="col-6 form-group">
                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">
                    Quận/Huyện <span className="text-danger"> (*) </span>
                  </label>
                  <Select
                    key={"id"}
                    className={"quan_huyen"}
                    labelField={"ten"}
                    placeholder="Chọn quận huyện"
                    options={ListQuanHuyenCOHN}
                    onChange={(values) => onChangeQuanHuyenCOHN(values)}
                    closeOnSelect={true}
                    multi={false}
                    isDisabled={disQuanHuyenCOHN}
                    value={QuanHuyenCOHNSelected}
                  />
                </div>
                <div className="form-group mt-2">
                  <label className="fw-bold fs-6 mb-2">
                    Số nhà, tên đường <span className="text-danger"> (*) </span>
                  </label>
                  <Form.Control
                    type="text"
                    placeholder="Nhập số nhà tên đường"
                    style={{ height: 38 }}
                    value={form.so_nha === null ? "" : form.so_nha}
                    onChange={(e) => {
                      setField("so_nha", e.target.value);
                    }}
                    isInvalid={!!errors.so_nha}
                  />
                </div>
                <div className="form-group mt-2">
                  {/* <label>
                          Ngày sinh <span className="text-danger"> (*) </span>
                        </label>{" "}
                        <br /> */}
                  <label className="fw-bold fs-6 mb-2">Ngày cấp</label>
                  <DesktopDatePicker
                    inputFormat="dd/MM/yyyy"
                    // value={form?.ngay_cap}
                    onChange={selectedNCCMND}
                    label=" "
                    maxDate={new Date()}
                    renderInput={(params) => (
                      <TextField size="small" {...params} />
                    )}
                  />
                </div>

                <div className="form-group mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Văn bằng chuyên môn
                  </label>
                  <Select
                    value={selectedVBCN}
                    key={"id"}
                    options={listVanBangChuyenMon}
                    placeholder="Chọn văn bằng chuyên môn"
                    formatGroupLabel={formatGroupLabel}
                    onChange={(e) => {
                      setSelectedVBCN(e);
                    }}
                  />
                </div>

                <div className="form-group mb-5 mt-2">
                  <label className="required fw-bold fs-6 mb-2">
                    Năm tốt nghiệp
                  </label>
                  <Form.Control
                    placeholder="Nhập năm tốt nghiệp"
                    style={{ height: 38 }}
                  />
                </div>
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => {
              props?.hide();
            }}
          >
            <i className="fas fa-times"></i> Hủy
          </Button>
          <Button variant="btn text-white bg-blue2">
            {edit ? (
              <i className="fa fa-pencil-alt text-white"></i>
            ) : (
              <i className="fas fa-save text-white"></i>
            )}{" "}
            {edit ? "CẬP NHẬT" : "THÊM"}
          </Button>
        </Modal.Footer>
      </LocalizationProvider>
    </Modal>
  );
}
