import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { TextField } from "@mui/material";
import axios from "axios";
// import { vi } from "date-fns/locale";
import { enAU } from "date-fns/locale";
import { useEffect, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import Select from "react-select";
import { toast } from "react-toastify";
import api from "../../../configs/api";
// import { GioiTinh, HinhThuc } from "../../../data";
// import moment from "moment";

export default function PopupQuanLyNguoiDung(props) {
  const [edit, setEdit] = useState(false);
  const [showUpdate, setShowUpdate] = useState(false);
  const [form, setForm] = useState({});
  const [trang_thai, setTrang_thai] = useState(true);
  const [selectedgioitinh, setSelectedgioitinh] = useState({
    label: "Nam",
    value: 1,
    id: 1,
  });

  useEffect(() => {
    if (props?.show) {
      //   resetState();
      getLocation();
      getLocationCOHN();
      getNoiTotNghiep();
      getCATinhThanh();
      getVanBangChuyenMon();
      setForm({
        ...props?.data,
        ngay_sinh: props?.data.ngay_sinh * 1,
        ngay_cap: props?.data.ngay_cap * 1000,
      });
      let dataSelectedCMND = listNoiCapCMND.find(
        (e, i) => e.ten === props.data?.noi_cap
      );
      console.log("log dataSelectedCMND", props.data?.noi_cap);
      console.log("log listNoiCapCMND", listNoiCapCMND);

      setSelectedNoiCapCMND(dataSelectedCMND);
      setTrang_thai(props?.data?.active);
      setShowUpdate(true);
      console.log(props?.data);
      //   setForm({ ...props?.data, ngay_cap: props?.data.ngay_cap * 1 });
    }
    console.log("propsssss", props);
  }, [props?.show]);

  // selected ngay
  const selectedNS = async (date) => {
    // await setNgaySinh(date);
    await setField("ngay_sinh", date);
  };
  const selectedNCCMND = async (date) => {
    // await setNgayCapCmnd(date);
    await setField("ngay_cap", date);
  };
  const getLocation = () => {
    let { tinh_thanh_id, xa_phuong_id, quan_huyen_id } = props.data;

    if (tinh_thanh_id) {
      getTinhThanh(tinh_thanh_id);
    }
    if (tinh_thanh_id) {
      getQuanHuyen(tinh_thanh_id, quan_huyen_id);
    }
    if (quan_huyen_id) {
      getXaPhuong(quan_huyen_id, xa_phuong_id);
    }
  };

  const getLocationCOHN = () => {
    let {
      tinh_thanh_hien_nay_id,
      xa_phuong_hien_nay_id,
      quan_huyen_hien_nay_id,
    } = props.data;
    if (tinh_thanh_hien_nay_id) {
      getTinhThanhCOHN(tinh_thanh_hien_nay_id);
    }
    if (tinh_thanh_hien_nay_id) {
      getQuanHuyenCOHN(tinh_thanh_hien_nay_id, quan_huyen_hien_nay_id);
    }
    if (quan_huyen_hien_nay_id) {
      getXaPhuongCOHN(quan_huyen_hien_nay_id, xa_phuong_hien_nay_id);
    }
  };

  // noi tốt nghiệp
  const [selectedNoiTotNghiep, setSelectedNoiTotNghiep] = useState(null);
  const [listNoiTotNghiep, setListNoiTotNghiep] = useState([]);
  // van bang chuyen mon
  const [selectedVBCN, setSelectedVBCN] = useState(null);
  const [listVanBangChuyenMon, setListVanBangChuyenMon] = useState([]);
  const optionsGioiTinh = [
    {
      label: "Nam",
      value: 1,
      id: 1,
    },
    {
      label: "Nữ",
      value: 2,
      id: 2,
    },
    {
      label: "Khác",
      value: 3,
      id: 3,
    },
  ];
  const [selectedDoiTuong, setSelectedDoiTuong] = useState({
    label: "Dược sĩ",
    value: 1,
    id: 1,
  });
  const [errors, setErrors] = useState({});
  const setField = (field, value) => {
    setForm({
      ...form,
      [field]: value,
    });
    if (!!errors[field])
      setErrors({
        ...errors,
        [field]: null,
      });
  };
  const optionsDoiTuong = [
    {
      label: "Dược sĩ",
      value: 1,
      id: 1,
    },
    {
      label: "Tổ chức",
      value: 2,
      id: 2,
    },
  ];
  const formatGroupLabel = (listTemp) => (
    <div style={groupStyles}>
      <span>{listTemp.ten}</span>
    </div>
  );
  // location
  const [ListTinhThanh, setListTinhThanh] = useState([]);
  const [ListQuanHuyen, setListQuanHuyen] = useState([]);
  const [ListXaPhuong, setListXaPhuong] = useState([]);
  const [TinhThanhSelected, setTinhThanhSelected] = useState({});
